        [1778] = {20187001,"맹획","산길이 좁은데 조심하지 않아 당신의 계략에 걸려든 것이니 어떻게 굴복할 수 있겠소?","산길이 좁은데 조심하지 않아 당신의 계략에 걸려든 것이니 어떻게 굴복할 수 있겠소?","제갈량","","",},
        [1779] = {20187001,"제갈량","굴복하지 않으니 놔주려고 하는데 어떻습니까?","굴복하지 않으니 놔주려고 하는데 어떻습니까?","맹획","","",},
        [1780] = {20187001,"맹획","나를 놓아주면 군마를 다시 정비하여 결판을 낼 것이오. 다시 잡히면 그때 굴복하겠소.","나를 놓아주면 군마를 다시 정비하여 결판을 낼 것이오. 다시 잡히면 그때 굴복하겠소.","제갈량","","",},
        [1781] = {20187002,"맹획","제갈량, 왜 내 아내를 붙잡은 것이오!","제갈량, 왜 내 아내를 붙잡은 것이오!","제갈량","","",},
        [1782] = {20187002,"제갈량","화내지 마시오. 단지 부인과 우리 군의 장억과 마충 두 장수를 바꾸려고 했소.","화내지 마시오. 단지 부인과 우리 군의 장억과 마충 두 장수를 바꾸려고 했소.","맹획","","",},
