        [1789] = {20187003,"맹획","저의 자자손손 모두 목숨을 살려주신 은혜에 감격했는데, 어찌 복종하지 않겠습니까? 흐으으윽...","저의 자자손손 모두 목숨을 살려주신 은혜에 감격했는데, 어찌 복종하지 않겠습니까? 흐으으윽...","제갈량","","",},
        [1790] = {20187101,"조조","동탁은 폐하를 보좌하면서 온갖 못된 짓을 저질렀습니다. 동탁이 죽었으니, 제가 폐하를 보좌하겠습니다.","동탁은 폐하를 보좌하면서 온갖 못된 짓을 저질렀습니다. 동탁이 죽었으니, 제가 폐하를 보좌하겠습니다.","한헌제","","",},
        [1791] = {20187101,"조조","제 딸을 황후로 시집보내고자 하는데 어떻습니까?","제 딸을 황후로 시집보내고자 하는데 어떻습니까?","한헌제","","",},
        [1792] = {20187101,"한헌제","승상의 뜻에 따르겠소.","승상의 뜻에 따르겠소.","조조","","",},
        [1793] = {20187102,"한헌제","경의 조상은 어떻게 되는가?","경의 조상은 어떻게 되는가?","유비","","",},
        [1794] = {20187102,"유비","신은 중산정왕의 후예로 효경황제의 현손이며, 유웅의 손자이고, 유홍의 아들입니다.","신은 중산정왕의 후예로 효경황제의 현손이며, 유웅의 손자이고, 유홍의 아들입니다.","한헌제","","",},
        [1795] = {20187102,"한헌제","족보를 따져보니, 현덕은 짐의 황숙이오.","족보를 따져보니, 현덕은 짐의 황숙이오.","유비","","",},
        [1796] = {20187102,"한헌제","조조가 권력을 농단하여 국사를 짐이 주관할 수 없는데 지금 이런 영웅이 숙부이니 짐을 도와주시오!","조조가 권력을 농단하여 국사를 짐이 주관할 수 없는데 지금 이런 영웅이 숙부이니 짐을 도와주시오!","유비","","",},
        [1797] = {20187103,"한헌제","짐이 즉위한 이래 간웅이 일어나서 먼저는 동탁의 재앙, 그 뒤에는 이각, 곽사의 난이 있었소. 조조를 얻었을 때 사직을 위한 신하인 줄 알았는데, 뜻밖에 권력을 농단하고 마구 휘두르고 있소.","짐이 즉위한 이래 간웅이 일어나서 먼저는 동탁의 재앙, 그 뒤에는 이각, 곽사의 난이 있었소. 조조를 얻었을 때 사직을 위한 신하인 줄 알았는데, 뜻밖에 권력을 농단하고 마구 휘두르고 있소.","","","",},
        [1798] = {20187103,"한헌제"," 짐이 볼 때마다 등 뒤에서 가시가 찌르듯 하오. 조정에 가득한 대신들이 모두 한나라 녹을 먹는데 국난을 구할 이 하나 없는가?"," 짐이 볼 때마다 등 뒤에서 가시가 찌르듯 하오. 조정에 가득한 대신들이 모두 한나라 녹을 먹는데 국난을 구할 이 하나 없는가?","","","",},
        [1799] = {20187103,"한헌제","짐이 혈서를 써서 옥대에 숨겨 동승에게 주니, 충성스러운 신하를 모아 간사한 무리를 멸하고, 사직을 다시 세우기를 바랄 뿐이오!","짐이 혈서를 써서 옥대에 숨겨 동승에게 주니, 충성스러운 신하를 모아 간사한 무리를 멸하고, 사직을 다시 세우기를 바랄 뿐이오!","","","",},
        [1800] = {20187201,"한당","광풍이 불어 깃발이 부러진 것을 보니 불길한데, 철수하시죠.","광풍이 불어 깃발이 부러진 것을 보니 불길한데, 철수하시죠.","손견","","",},
        [1801] = {20187201,"손견","싸움에서 계속 이겨 양양을 곧 점령할 것인데, 깃발이 부러졌다고 어찌 철수할 수 있겠느냐?","싸움에서 계속 이겨 양양을 곧 점령할 것인데, 깃발이 부러졌다고 어찌 철수할 수 있겠느냐?","한당","","",},
        [1802] = {20187201,"한당","하지만 불길한 예감이 드니 조심하시는 것이 좋겠습니다!","하지만 불길한 예감이 드니 조심하시는 것이 좋겠습니다!","손견","","",},
        [1803] = {20187202,"손책","강동이 이렇게 넓은 땅을 가질 수 있었던 것은 한당이 나를 도와 많은 싸움에서 이기고 여러 차례 공을 세웠기 때문이오!","강동이 이렇게 넓은 땅을 가질 수 있었던 것은 한당이 나를 도와 많은 싸움에서 이기고 여러 차례 공을 세웠기 때문이오!","한당","","",},
        [1804] = {20187202,"한당","당초 저는 손견 장군을 따라 많은 싸움을 치렀습니다. 이제는 최선을 다하여 주공을 도와 오나라의 대업을 이루겠습니다.","당초 저는 손견 장군을 따라 많은 싸움을 치렀습니다. 이제는 최선을 다하여 주공을 도와 오나라의 대업을 이루겠습니다.","손책","","",},
        [1805] = {20187203,"한당","나를 주태, 여몽과 비교하여 누가 더 센지 묻는 사람이 있다더군요.","나를 주태, 여몽과 비교하여 누가 더 센지 묻는 사람이 있다더군요.","","","",},
        [1806] = {20187203,"한당","무력으로 따지면 주태가 낫고, 지략으로 따지면 여몽이 더 대단하지요.","무력으로 따지면 주태가 낫고, 지략으로 따지면 여몽이 더 대단하지요.","","","",},
        [1807] = {20187203,"한당","하지만 경험으로 따지면 제가 최고지요!","하지만 경험으로 따지면 제가 최고지요!","","","",},
        [1808] = {20187301,"조비","백령균을 사마의에게 주어 부인으로 삼게 할 것이니 내 호의를 저버리지 마시오.","백령균을 사마의에게 주어 부인으로 삼게 할 것이니 내 호의를 저버리지 마시오.","장춘화","","",},
        [1809] = {20187301,"장춘화","분명 조비가 우리 집을 감시하려고 첩자로 보낸 것인데, 어찌 안심하고 받아들일 수 있겠어요?","분명 조비가 우리 집을 감시하려고 첩자로 보낸 것인데, 어찌 안심하고 받아들일 수 있겠어요?","사마의","","",},
        [1810] = {20187301,"사마의","부인 말이 맞소. 받아들이지 않고 명령을 거부하여 죽을죄를 지으리다.","부인 말이 맞소. 받아들이지 않고 명령을 거부하여 죽을죄를 지으리다.","장춘화","","",},
