        [1812] = {20187302,"사마의","3남 1녀를 낳아 기르느라 정말 고생이 많소.","3남 1녀를 낳아 기르느라 정말 고생이 많소.","장춘화","","",},
        [1813] = {20187302,"장춘화","당신은 내게 잘해주지 않지만, 아이들이 효도하니 그것으로 됐어요.","당신은 내게 잘해주지 않지만, 아이들이 효도하니 그것으로 됐어요.","사마의","","",},
        [1814] = {20187302,"사마의","그게 무슨 소리요. 부인은 '춘소태세'라 불리는 여장부인데, 내가 어찌 감히 잘하지 않을 수 있겠소?","그게 무슨 소리요. 부인은 '춘소태세'라 불리는 여장부인데, 내가 어찌 감히 잘하지 않을 수 있겠소?","장춘화","","",},
        [1815] = {20187302,"장춘화","흥, 말씀은 잘하시네요!","흥, 말씀은 잘하시네요!","사마의","","",},
        [1816] = {20187303,"사마의","아무리 화가 나도 밥은 거르지 마시오. 아이들도 따라서 먹지 않게 되니 좋지 않소. 내가 사과할 테니 용서해 주시오.","아무리 화가 나도 밥은 거르지 마시오. 아이들도 따라서 먹지 않게 되니 좋지 않소. 내가 사과할 테니 용서해 주시오.","장춘화","","",},
        [1817] = {20187303,"장춘화","좋아요. 밥을 먹고 나서 다시 화를 내지요.","좋아요. 밥을 먹고 나서 다시 화를 내지요.","사마의","","",},
