        [1819] = {20187401,"감녕","","","여몽","","",},
        [1820] = {20187401,"여몽","그러지 말고, 한발 물러서서 양보하시오. 그래도 주공의 사촌이지 않소.","그러지 말고, 한발 물러서서 양보하시오. 그래도 주공의 사촌이지 않소.","감녕","","",},
        [1821] = {20187401,"감녕","주공의 사촌이라도 함부로 남을 모욕할 수는 없소. 성심성의껏 군주를 모시고 있지만, 억지 논리에는 굴복하지 않을 것이오.","주공의 사촌이라도 함부로 남을 모욕할 수는 없소. 성심성의껏 군주를 모시고 있지만, 억지 논리에는 굴복하지 않을 것이오.","여몽","","",},
        [1822] = {20187401,"여몽","자네도 한 고집 하는구려.","자네도 한 고집 하는구려.","감녕","","",},
        [1823] = {20187402,"손권","요직에 있어 임무가 막중하니, 공부도 게을리하지 마시오.","요직에 있어 임무가 막중하니, 공부도 게을리하지 마시오.","여몽","","",},
        [1824] = {20187402,"여몽","군대에 일이 많아서 시간이 없습니다.","군대에 일이 많아서 시간이 없습니다.","손권","","",},
        [1825] = {20187402,"손권","깊이 연구하여 무슨 학자가 되라는 소리가 아니라, 대략 읽고 이전의 일을 이해하면 그것으로 충분하오.","깊이 연구하여 무슨 학자가 되라는 소리가 아니라, 대략 읽고 이전의 일을 이해하면 그것으로 충분하오.","여몽","","",},
        [1826] = {20187402,"손권","아무리 바빠도 나보다 바쁘겠소? 나도 매일 책을 읽는데, 도움이 아주 많이 되오.","아무리 바빠도 나보다 바쁘겠소? 나도 매일 책을 읽는데, 도움이 아주 많이 되오.","여몽","","",},
        [1827] = {20187402,"여몽","알겠습니다. 저도 분발하여 열심히 공부하겠습니다!","알겠습니다. 저도 분발하여 열심히 공부하겠습니다!","손권","","",},
        [1828] = {20187403,"여몽","중책을 맡고 관우와 인접해 있는데, 어떤 계책으로 만일의 상황을 대비할 생각입니까?","중책을 맡고 관우와 인접해 있는데, 어떤 계책으로 만일의 상황을 대비할 생각입니까?","노숙","","",},
        [1829] = {20187403,"노숙","그건...상황을 봐가면서 적절히 대응할 것이오.","그건...상황을 봐가면서 적절히 대응할 것이오.","여몽","","",},
        [1830] = {20187403,"여몽","지금 손권과 유비가 비록 연합하였지만, 관우는 호랑이 같은 자이니 미리 대비하여 계략을 세워야 하지 않겠습니까?","지금 손권과 유비가 비록 연합하였지만, 관우는 호랑이 같은 자이니 미리 대비하여 계략을 세워야 하지 않겠습니까?","노숙","","",},
        [1831] = {20187403,"노숙","여몽, 자네의 책략이 이 정도 경지에 오른 줄 몰랐소. 더이상 동오의 아몽이 아니구려.","여몽, 자네의 책략이 이 정도 경지에 오른 줄 몰랐소. 더이상 동오의 아몽이 아니구려.","여몽","","",},
        [1832] = {20187403,"여몽","선비는 헤어졌다 사흘이 지나면 다시 눈을 비비고 보아야 하지요.","선비는 헤어졌다 사흘이 지나면 다시 눈을 비비고 보아야 하지요.","노숙","","",},
        [1833] = {20187501,"마초","이곳에 백여 마리의 명마가 있는데 방덕은 어째서 여위고 못생긴 검은 입의 황마를 골랐소?","이곳에 백여 마리의 명마가 있는데 방덕은 어째서 여위고 못생긴 검은 입의 황마를 골랐소?","방덕","","",},
        [1834] = {20187501,"방덕","비록 못생겼지만, 속도에서는 준마도 따라오지 못합니다. 실력으로 따져야지요!","비록 못생겼지만, 속도에서는 준마도 따라오지 못합니다. 실력으로 따져야지요!","마초","","",},
        [1835] = {20187501,"마초","방덕은 참으로 말을 볼 줄 아는구려.","방덕은 참으로 말을 볼 줄 아는구려.","방덕","","",},
        [1836] = {20187502,"조조","적군의 방덕은 서량의 용장으로, 우리 편이 된다면 반드시 나를 도와 큰일을 이룰 것이오.","적군의 방덕은 서량의 용장으로, 우리 편이 된다면 반드시 나를 도와 큰일을 이룰 것이오.","가후","","",},
        [1837] = {20187502,"가후","제게 방법이 있으니 이렇게 하시지요.","제게 방법이 있으니 이렇게 하시지요.","조조","","",},
        [1838] = {20187502,"조조","방덕 장군, 내가 직접 풀어주겠소. 장로는 이 싸움에서 지면 그대를 죽인다고 말했는데, 지금 나한테 붙잡혀 있구려.","방덕 장군, 내가 직접 풀어주겠소. 장로는 이 싸움에서 지면 그대를 죽인다고 말했는데, 지금 나한테 붙잡혀 있구려.","방덕","","",},
        [1839] = {20187502,"조조","돌아가도 죽은 목숨이니, 항복하고 나를 도와 대업을 이루면 어떻겠소?","돌아가도 죽은 목숨이니, 항복하고 나를 도와 대업을 이루면 어떻겠소?","방덕","","",},
        [1840] = {20187502,"방덕","그들은 원래부터 어질지 못했으니 항복하여 조공의 명령에 따르겠습니다!","그들은 원래부터 어질지 못했으니 항복하여 조공의 명령에 따르겠습니다!","조조","","",},
        [1841] = {20187503,"관우","경의 형은 한중에 있소. 나는 경을 장수로 세우려 생각하고 있었는데, 어째서 빨리 항복하지 않았소?","경의 형은 한중에 있소. 나는 경을 장수로 세우려 생각하고 있었는데, 어째서 빨리 항복하지 않았소?","방덕","","",},
        [1842] = {20187503,"방덕","흥, 위왕에게는 정예병 백만이 있으며, 천하에 그 위세가 대단하다. 유비 따위는 우리의 적수가 되지 않는다!","흥, 위왕에게는 정예병 백만이 있으며, 천하에 그 위세가 대단하다. 유비 따위는 우리의 적수가 되지 않는다!","관우","","",},
        [1843] = {20187503,"방덕","죽이려면 죽여라. 나는 귀신이 될지언정 도둑의 장수는 되지 않을 것이다!","죽이려면 죽여라. 나는 귀신이 될지언정 도둑의 장수는 되지 않을 것이다!","관우","","",},
        [1844] = {20187601,"장성채","은병 언니, 언니 이름을 우리 아버지가 지어주었대요!","은병 언니, 언니 이름을 우리 아버지가 지어주었대요!","관은병","","",},
        [1845] = {20187601,"관은병","맞아. 작은아버지가 진귀한 진주도 주셨지. 여포의 자금관에서 나온 것이라고 들었어.","맞아. 작은아버지가 진귀한 진주도 주셨지. 여포의 자금관에서 나온 것이라고 들었어.","장성채","","",},
        [1846] = {20187601,"장성채","행운을 가져다준다면서요! 나한테는 안 주시고 언니만 너무 편애하는 것 같아요.","행운을 가져다준다면서요! 나한테는 안 주시고 언니만 너무 편애하는 것 같아요.","관은병","","",},
        [1847] = {20187602,"손권","관우에게 딸이 있다고 들었소. 제갈근 자네가 가서 손등의 혼담을 꺼내, 유비와 동오의 연합에 대한 성의를 보이면 어떻겠소?","관우에게 딸이 있다고 들었소. 제갈근 자네가 가서 손등의 혼담을 꺼내, 유비와 동오의 연합에 대한 성의를 보이면 어떻겠소?","제갈근","","",},
        [1848] = {20187602,"제갈근","그렇게 되면 좋지만 관 장군이 거절하면...조조와 연합하여 형주를 치는 것에 대해 주공은 어떻게 생각하십니까?","그렇게 되면 좋지만 관 장군이 거절하면...조조와 연합하여 형주를 치는 것에 대해 주공은 어떻게 생각하십니까?","손권","","",},
        [1849] = {20187603,"제갈근","주공께서 세자 손등과 장군의 딸을 결혼시키고자 하는데 장군의 생각은 어떠십니까?","주공께서 세자 손등과 장군의 딸을 결혼시키고자 하는데 장군의 생각은 어떠십니까?","관우","","",},
        [1850] = {20187603,"관우","흥, 범의 자식을 어찌 개의 자식에게 줄 수 있습니까?","흥, 범의 자식을 어찌 개의 자식에게 줄 수 있습니까?","제갈근","","",},
        [1851] = {20187701,"포삼낭","영웅께서 나쁜 토호들을 쫓아주셔서 감사합니다.","영웅께서 나쁜 토호들을 쫓아주셔서 감사합니다.","관소","","",},
        [1852] = {20187701,"관소","낭자, 당연한 일을 한 것뿐이오.","낭자, 당연한 일을 한 것뿐이오.","포삼낭","","",},
        [1853] = {20187701,"포삼낭","생명의 은인에게 보답하고자 몸과 마음을 바치겠습니다.","생명의 은인에게 보답하고자 몸과 마음을 바치겠습니다.","관소","","",},
        [1854] = {20187702,"관소","아버지, 오랜 세월 전전하다가 드디어 아버지를 찾았습니다!","아버지, 오랜 세월 전전하다가 드디어 아버지를 찾았습니다!","관우","","",},
        [1855] = {20187702,"관우","그동안 외지를 떠돌며 고생이 많았구나.","그동안 외지를 떠돌며 고생이 많았구나.","관소","","",},
