        [1857] = {20187703,"관소","부친의 뒤를 이어 촉한을 위해 목숨을 걸고 싸워 천하를 평정하고 싶습니다.","부친의 뒤를 이어 촉한을 위해 목숨을 걸고 싸워 천하를 평정하고 싶습니다.","제갈량","","",},
        [1858] = {20187703,"관소","승상, 우리 부부는 승상을 따라 남만을 정벌하고 남중을 지키기 원합니다.","승상, 우리 부부는 승상을 따라 남만을 정벌하고 남중을 지키기 원합니다.","제갈량","","",},
        [1859] = {20187703,"제갈량","그 아버지에 그 아들이군요. 장군도 관우 장군의 기질이 있으니, 촉한을 위해 관 장군 부자가 돕는 것은 참으로 기쁜 일이지요.","그 아버지에 그 아들이군요. 장군도 관우 장군의 기질이 있으니, 촉한을 위해 관 장군 부자가 돕는 것은 참으로 기쁜 일이지요.","관소","","",},
        [1860] = {20187801,"육손","지금 영웅들이 서로 패권을 다투는 시기에, 적을 이기려면 많은 인마가 필요합니다.","지금 영웅들이 서로 패권을 다투는 시기에, 적을 이기려면 많은 인마가 필요합니다.","손권","","",},
        [1861] = {20187801,"육손","지금 나라 안에 산적들이 난동을 부리니, 군대를 확충하고 정예병을 선발해야 합니다.","지금 나라 안에 산적들이 난동을 부리니, 군대를 확충하고 정예병을 선발해야 합니다.","육손","","",},
        [1862] = {20187801,"손권","맞는 말이오. 그대가 마음에 드니, 내 조카를 자네에게 베필로 주겠소.","맞는 말이오. 그대가 마음에 드니, 내 조카를 자네에게 베필로 주겠소.","육손","","",},
        [1863] = {20187802,"육손","관우는 자신의 용기만 믿고 다른 사람을 업신여기지요. 만일 장군께서 병에 걸렸다고 소문을 내면 틀림없이 방비가 허술해질 것입니다. 그때 가서 상의하면 좋을 것 같습니다.","관우는 자신의 용기만 믿고 다른 사람을 업신여기지요. 만일 장군께서 병에 걸렸다고 소문을 내면 틀림없이 방비가 허술해질 것입니다. 그때 가서 상의하면 좋을 것 같습니다.","여몽","","",},
        [1864] = {20187802,"여몽","관우가 용맹한 것은 다 알고 있으니, 먼저 공격할 생각은 하지 마시오.","관우가 용맹한 것은 다 알고 있으니, 먼저 공격할 생각은 하지 마시오.","육손","","",},
        [1865] = {20187802,"손권","누가 그대를 대신할 수 있겠소?","누가 그대를 대신할 수 있겠소?","여몽","","",},
        [1866] = {20187802,"여몽","육손은 사려 깊고, 중임을 담당할 만합니다. 그리고 아직 이름이 알려지지 않아 관우가 개의치 않을 것이니 그만한 사람이 없지요.","육손은 사려 깊고, 중임을 담당할 만합니다. 그리고 아직 이름이 알려지지 않아 관우가 개의치 않을 것이니 그만한 사람이 없지요.","육손","","",},
        [1867] = {20187802,"여몽","그를 임용하여 외부에 알리지 않고 숨겼다가 기회를 봐서 일격을 가하면 성공할 것입니다.","그를 임용하여 외부에 알리지 않고 숨겼다가 기회를 봐서 일격을 가하면 성공할 것입니다.","육손","","",},
        [1868] = {20187901,"오국태","난 이제껏 유 황숙을 인정한 적이 없다. 내일 감로사에서 만나 보고, 마음에 들면 시집을 보낼 것이고, 마음에 들지 않으면 너희 뜻에 따르겠다.","난 이제껏 유 황숙을 인정한 적이 없다. 내일 감로사에서 만나 보고, 마음에 들면 시집을 보낼 것이고, 마음에 들지 않으면 너희 뜻에 따르겠다.","손권","","",},
        [1869] = {20187901,"손권","도부수를 매복시키고 상황을 보겠습니다.","도부수를 매복시키고 상황을 보겠습니다.","오국태","","",},
        [1870] = {20187902,"오국태","음...그의 집안이나 외모, 성격 모두 마음에 든다. 이번 혼사가 아주 흡족하구나.","음...그의 집안이나 외모, 성격 모두 마음에 든다. 이번 혼사가 아주 흡족하구나.","손상향","","",},
        [1871] = {20187902,"손상향","어머니 뜻에 따르겠어요.","어머니 뜻에 따르겠어요.","오국태","","",},
        [1872] = {20187902,"오국태","유비가 내 딸에게 잘해줬으면 좋겠구나.","유비가 내 딸에게 잘해줬으면 좋겠구나.","손상향","","",},
        [1873] = {20188001,"여포","장료! 나의 용맹한 부하이니, 조조의 간계에 빠진 나를 어서 와서 구하시오!","장료! 나의 용맹한 부하이니, 조조의 간계에 빠진 나를 어서 와서 구하시오!","장료","","",},
        [1874] = {20188001,"조조","그동안 여포 밑에서 장군의 재능을 발휘하지 못하고 있었구려. 이제는 나를 따르시오. 내 휘하에 몇 안 되는 맹장이 될 것이오.","그동안 여포 밑에서 장군의 재능을 발휘하지 못하고 있었구려. 이제는 나를 따르시오. 내 휘하에 몇 안 되는 맹장이 될 것이오.","장료","","",},
        [1875] = {20188001,"장료","좋습니다. 저 장료는 조공을 따라 대업을 이루겠습니다.","좋습니다. 저 장료는 조공을 따라 대업을 이루겠습니다.","조조","","",},
        [1876] = {20188002,"관우","긴말할 필요 없소. 난 항복하지 않을 것이오!","긴말할 필요 없소. 난 항복하지 않을 것이오!","장료","","",},
        [1877] = {20188002,"장료","관운장, 먼저 투항하시오. 내가 있으니 두려워할 것 없소. 유비의 소식을 들으면 그때 떠나도 될 것이오.","관운장, 먼저 투항하시오. 내가 있으니 두려워할 것 없소. 유비의 소식을 들으면 그때 떠나도 될 것이오.","관우","","",},
        [1878] = {20188002,"관우","그 말이 정말이오?","그 말이 정말이오?","장료","","",},
        [1879] = {20188002,"장료","나를 믿으시오.","나를 믿으시오.","관우","","",},
        [1880] = {20188003,"장료","이번 승리는 우리 군이 준비를 잘했기 때문이다. 병사들이 죽을 각오로 싸워 큰 승리를 거둘 수 있었다.","이번 승리는 우리 군이 준비를 잘했기 때문이다. 병사들이 죽을 각오로 싸워 큰 승리를 거둘 수 있었다.","","","",},
        [1881] = {20188003,"손권","장료는 참으로 맹장이다. 팔백 기병으로 우리 대군을 무찌르다니 내가 얕잡아 보았구나.","장료는 참으로 맹장이다. 팔백 기병으로 우리 대군을 무찌르다니 내가 얕잡아 보았구나.","장료","","",},
        [1882] = {20188003,"손권","장료의 이름을 들으면 우는 아이도 울음을 그친다고 들었다. 이번 전쟁으로 강동에 장료의 위세가 대단하구나.","장료의 이름을 들으면 우는 아이도 울음을 그친다고 들었다. 이번 전쟁으로 강동에 장료의 위세가 대단하구나.","장료","","",},
        [1883] = {20188101,"조식","나는 덕이 없어 번후의 작위에 있으면서, 백성에게 은혜를 베풀고, 영세의 업을 세우고, 큰 공을 세웠습니다.","나는 덕이 없어 번후의 작위에 있으면서, 백성에게 은혜를 베풀고, 영세의 업을 세우고, 큰 공을 세웠습니다.","양수","","",},
        [1884] = {20188101,"조식","어찌 공훈이나 기록하기 위해 글을 짓고, 군자가 되기 위해 사부나 짓는 무리겠습니까?","어찌 공훈이나 기록하기 위해 글을 짓고, 군자가 되기 위해 사부나 짓는 무리겠습니까?","양수","","",},
        [1885] = {20188101,"조식","뜻을 이루지 못하고, 방법을 행할 수 없다면, 사관의 실록을 모아, 속세의 득실을 변별하고, 인의의 본질을 평가하여, 나만의 학설을 이룰 것입니다.","뜻을 이루지 못하고, 방법을 행할 수 없다면, 사관의 실록을 모아, 속세의 득실을 변별하고, 인의의 본질을 평가하여, 나만의 학설을 이룰 것입니다.","양수","","",},
        [1886] = {20188101,"조식","문장을 명산에 숨길 수 없다면 같은 뜻을 가진 사람에게 전할 것이고, 오늘만이 아니라 늙을 때까지 계속될 것입니다! 이 말을 부끄러워하지 않는 것은 혜시가 장자에게 조언한 것처럼 그대가 혜시의 역할을 해 줄 것을 믿기 때문입니다.","문장을 명산에 숨길 수 없다면 같은 뜻을 가진 사람에게 전할 것이고, 오늘만이 아니라 늙을 때까지 계속될 것입니다! 이 말을 부끄러워하지 않는 것은 혜시가 장자에게 조언한 것처럼 그대가 혜시의 역할을 해 줄 것을 믿기 때문입니다.","양수","","",},
        [1887] = {20188101,"양수","신을 알아주시니 목숨을 다해 모시겠습니다. 성공하지 못하면 목숨을 바치겠습니다.","신을 알아주시니 목숨을 다해 모시겠습니다. 성공하지 못하면 목숨을 바치겠습니다.","조식","","",},
        [1888] = {20188102,"사마의","승상이 조비와 조식의 재주를 알아보려고 업성 성문을 나가라고 했는데, 몰래 문지기에게는 내보내지 말라고 말하였소.","승상이 조비와 조식의 재주를 알아보려고 업성 성문을 나가라고 했는데, 몰래 문지기에게는 내보내지 말라고 말하였소.","양수","","",},
        [1889] = {20188102,"사마의","조식에게 왕명을 받들어 그를 막는 병사를 죽이라고 일러주었소?","조식에게 왕명을 받들어 그를 막는 병사를 죽이라고 일러주었소?","양수","","",},
        [1890] = {20188102,"양수","알겠습니다!","알겠습니다!","사마의","","",},
        [1891] = {20188102,"사마의","양수, 그대의 재주를 낭비하지 마십시오. 승상의 저의를 알고 그렇게 행동하라 했겠지요.","양수, 그대의 재주를 낭비하지 마십시오. 승상의 저의를 알고 그렇게 행동하라 했겠지요.","양수","","",},
        [1892] = {20188102,"사마의","그대가 알려준 방법이라는 것을 승상이 알면 오히려 조식에게 해가 갈 것입니다.","그대가 알려준 방법이라는 것을 승상이 알면 오히려 조식에게 해가 갈 것입니다.","양수","","",},
        [1893] = {20188103,"조조","하후돈, 왜 군영 내의 군사들이 행장을 준비하고 있소?","하후돈, 왜 군영 내의 군사들이 행장을 준비하고 있소?","하후돈","","",},
        [1894] = {20188103,"하후돈","주부 양수가 대왕이 돌아가려고 한다고 말했습니다.","주부 양수가 대왕이 돌아가려고 한다고 말했습니다.","조조","","",},
        [1895] = {20188103,"조조","양수가 왜 그런 말을 한 것이오?","양수가 왜 그런 말을 한 것이오?","하후돈","","",},
        [1896] = {20188103,"하후돈","오늘밤 계륵이라고 명했으니, 위왕이 곧 철수 할 것이라고 했습니다. 계륵이라는 것은, 먹을려고 하면 먹을 것이 없고, 버리자니 아까운 뜻이라고 했습니다.","오늘밤 계륵이라고 명했으니, 위왕이 곧 철수 할 것이라고 했습니다. 계륵이라는 것은, 먹을려고 하면 먹을 것이 없고, 버리자니 아까운 뜻이라고 했습니다.","조조","","",},
        [1897] = {20188103,"하후돈","지금 나아가면 이길 수 없고, 물러나면 사람들이 비웃을 것이며, 무익하다면 일찍 돌아가는 것이 나아, 위왕은 반드시 철수시킬 것이니, 떠날 때 혼란스럽지 않도록 미리 행장을 꾸리라고 하였습니다.","지금 나아가면 이길 수 없고, 물러나면 사람들이 비웃을 것이며, 무익하다면 일찍 돌아가는 것이 나아, 위왕은 반드시 철수시킬 것이니, 떠날 때 혼란스럽지 않도록 미리 행장을 꾸리라고 하였습니다.","조조","","",},
        [1898] = {20188103,"조조","양수가 내 마음을 읽고 똑똑하다고 잘난 체를 하는구나!","양수가 내 마음을 읽고 똑똑하다고 잘난 체를 하는구나!","하후돈","","",},
        [1899] = {20188103,"조조","도부수에게 그를 참하게 하고, 원문 밖에 머리를 매달아라.","도부수에게 그를 참하게 하고, 원문 밖에 머리를 매달아라.","하후돈","","",},
        [1900] = {20188201,"사마의","자상, 자원, 이리 오너라.","자상, 자원, 이리 오너라.","사마소","","",},
        [1901] = {20188201,"사마소","여기 왔습니다.","여기 왔습니다.","사마의","","",},
        [1902] = {20188201,"사마의","오랫동안 위나라 조정에 있으면서 태부라는 최고의 자리에 올랐다. 많은 사람이 내가 다른 마음이 있다고 의심하여 이 때문에 자주 두렵구나.","오랫동안 위나라 조정에 있으면서 태부라는 최고의 자리에 올랐다. 많은 사람이 내가 다른 마음이 있다고 의심하여 이 때문에 자주 두렵구나.","사마소","","",},
        [1903] = {20188201,"사마의","내가 죽은 후에 너희는 응당 나라를 잘 다스리고 황제를 도와 신주를 평정해야 한다!","내가 죽은 후에 너희는 응당 나라를 잘 다스리고 황제를 도와 신주를 평정해야 한다!","사마소","","",},
        [1904] = {20188202,"사마소","위로는 조모, 아래로는 조 씨 종친, 모든 명문 가문이 우리 사마 일가를 멸하고 싶어 한다.","위로는 조모, 아래로는 조 씨 종친, 모든 명문 가문이 우리 사마 일가를 멸하고 싶어 한다.","","","",},
        [1905] = {20188202,"사마소","성제의 검이 조모의 가슴을 관통하여, 나는 이제 돌아갈 수 없는 길에 올랐구나.","성제의 검이 조모의 가슴을 관통하여, 나는 이제 돌아갈 수 없는 길에 올랐구나.","","","",},
