        [2026] = {20189402,"등애","모든 것이 종회의 비방과 모함이다. 종회는 반역을 꾀하고 있다!","모든 것이 종회의 비방과 모함이다. 종회는 반역을 꾀하고 있다!","","","",},
        [2027] = {20189402,"등애","백기의 처지가 오늘 다시 되풀이되니, 억울하도다.","백기의 처지가 오늘 다시 되풀이되니, 억울하도다.","","","",},
        [2028] = {20189501,"유비","관공이 패전하여 맥성에 포위되어 매우 긴박하오.","관공이 패전하여 맥성에 포위되어 매우 긴박하오.","맹달","","",},
        [2029] = {20189501,"유비","구원병이 오더라도 짧은 시간 내에 오기 힘들 것 같소.","구원병이 오더라도 짧은 시간 내에 오기 힘들 것 같소.","맹달","","",},
        [2030] = {20189501,"유비","장군께서 어서 군사를 이끌고 이 위기에서 구해주시오. 지체하면 관공이 잡힐 것이오.","장군께서 어서 군사를 이끌고 이 위기에서 구해주시오. 지체하면 관공이 잡힐 것이오.","맹달","","",},
        [2031] = {20189501,"맹달","우리가 지금 간다고 해서 한잔의 물로 어떻게 한 수레의 장작불을 끌 수 있겠습니까?","우리가 지금 간다고 해서 한잔의 물로 어떻게 한 수레의 장작불을 끌 수 있겠습니까?","유비","","",},
        [2032] = {20189502,"장합","조비, 환계, 하후상이 죽고, 맹달이 매우 불안해하고 있습니다.","조비, 환계, 하후상이 죽고, 맹달이 매우 불안해하고 있습니다.","사마의","","",},
        [2033] = {20189502,"장합","제갈량의 설득에 넘어가 촉한으로 돌아가려고 합니다.","제갈량의 설득에 넘어가 촉한으로 돌아가려고 합니다.","사마의","","",},
        [2034] = {20189502,"사마의","누가 알려준 것이오?","누가 알려준 것이오?","장합","","",},
        [2035] = {20189502,"장합","맹달과 위흥태수 신의의 사이가 좋지 않아, 신의가 맹달의 계획을 제게 알려주었습니다.","맹달과 위흥태수 신의의 사이가 좋지 않아, 신의가 맹달의 계획을 제게 알려주었습니다.","사마의","","",},
        [2036] = {20189502,"사마의","과거에는 유비를 버리더니 이제 우리 위를 버려? 네놈이 죽음을 자초하는구나!","과거에는 유비를 버리더니 이제 우리 위를 버려? 네놈이 죽음을 자초하는구나!","장합","","",},
        [2037] = {20189502,"사마의","먼저 맹달에게 편지를 써서 안심시키고, 몰래 군대를 보내 팔일 내에 천 이백 리를 가서 그를 처치할 것이오.","먼저 맹달에게 편지를 써서 안심시키고, 몰래 군대를 보내 팔일 내에 천 이백 리를 가서 그를 처치할 것이오.","장합","","",},
        [2038] = {20189601,"장간","제가 어려서부터 주유와 동창이었습니다. 강동으로 가서 세 치 혀로 그를 항복하도록 설득해보겠습니다.","제가 어려서부터 주유와 동창이었습니다. 강동으로 가서 세 치 혀로 그를 항복하도록 설득해보겠습니다.","조조","","",},
        [2039] = {20189601,"조조","그대가 주유와 친분이 두텁소?","그대가 주유와 친분이 두텁소?","장간","","",},
        [2040] = {20189601,"장간","걱정하지 마십시오, 강좌에 이르면 반드시 성공할 것입니다.","걱정하지 마십시오, 강좌에 이르면 반드시 성공할 것입니다.","조조","","",},
        [2041] = {20189601,"조조","무엇을 가지고 가겠소?","무엇을 가지고 가겠소?","장간","","",},
        [2042] = {20189601,"장간","수행할 동자 한 명과 배를 저을 노복 둘이면 충분합니다.","수행할 동자 한 명과 배를 저을 노복 둘이면 충분합니다.","조조","","",},
        [2043] = {20189602,"장간","주유, 그동안 잘 지내셨소?","주유, 그동안 잘 지내셨소?","주유","","",},
        [2044] = {20189602,"주유","장간, 고생이 많으십니다. 조 씨의 중재자 역할을 하려고 이 머나먼 강호까지 발걸음을 하다니요.","장간, 고생이 많으십니다. 조 씨의 중재자 역할을 하려고 이 머나먼 강호까지 발걸음을 하다니요.","장간","","",},
        [2045] = {20189602,"장간","하하하하. 오랫동안 만나지 못해 일부러 옛정을 나누려고 왔는데, 나를 중재자로 의심하는 것이오?","하하하하. 오랫동안 만나지 못해 일부러 옛정을 나누려고 왔는데, 나를 중재자로 의심하는 것이오?","주유","","",},
        [2046] = {20189602,"주유","내가 사광만큼 총명하지는 못해도, 현가를 듣고 그 고상한 뜻은 알 수 있지요.","내가 사광만큼 총명하지는 못해도, 현가를 듣고 그 고상한 뜻은 알 수 있지요.","장간","","",},
        [2047] = {20189602,"장간","나를 이렇게 대한다면 그만 가보겠소.","나를 이렇게 대한다면 그만 가보겠소.","주유","","",},
        [2048] = {20189602,"주유","조 씨의 중재자로 온 것인지 걱정했을 뿐이오. 그런 마음이 없다면 왜 이리 서둘러 가시오?","조 씨의 중재자로 온 것인지 걱정했을 뿐이오. 그런 마음이 없다면 왜 이리 서둘러 가시오?","장간","","",},
        [2049] = {20189603,"조조","두 장군은 어서 출병하시오.","두 장군은 어서 출병하시오.","채모","","",},
        [2050] = {20189603,"채모","아직 훈련이 부족하여 출병할 수 없습니다.","아직 훈련이 부족하여 출병할 수 없습니다.","조조","","",},
        [2051] = {20189603,"조조","군을 훈련시켜 내 머리를 주유에게 주려고 했겠다!","군을 훈련시켜 내 머리를 주유에게 주려고 했겠다!","채모","","",},
        [2052] = {20189603,"채모","무슨 말씀이신지 모르겠습니다.","무슨 말씀이신지 모르겠습니다.","조조","","",},
        [2053] = {20189603,"조조","장간이 주유의 장막에서 너와 장윤의 항복 문서를 가져왔다!","장간이 주유의 장막에서 너와 장윤의 항복 문서를 가져왔다!","채모","","",},
        [2054] = {20189603,"채모","신은 정말 모르는 일이옵니다.","신은 정말 모르는 일이옵니다.","조조","","",},
        [2055] = {20189603,"조조","변명은 듣기 싫다. 이 자를 끌어내어 목을 베거라!","변명은 듣기 싫다. 이 자를 끌어내어 목을 베거라!","채모","","",},
        [2056] = {20189701,"왕윤","내가 십만 황건적의 항복을 받았을 때 군에서 너 장양이 쓴 서신을 찾아냈다.","내가 십만 황건적의 항복을 받았을 때 군에서 너 장양이 쓴 서신을 찾아냈다.","장양","","",},
        [2057] = {20189701,"왕윤","황건적과 관련된 내용이 쓰여있었다.","황건적과 관련된 내용이 쓰여있었다.","장양","","",},
        [2058] = {20189701,"왕윤","황건적과 내통하여 반란을 일으키려고 했던 것 아니냐!","황건적과 내통하여 반란을 일으키려고 했던 것 아니냐!","장양","","",},
        [2059] = {20189701,"장양","나는 조정에 충성을 다하였고, 황건적과 내통하고 반역하려는 뜻은 추호도 없소.","나는 조정에 충성을 다하였고, 황건적과 내통하고 반역하려는 뜻은 추호도 없소.","왕윤","","",},
        [2060] = {20189701,"장양","나를 질투하여 모함하려는 것 아니오!","나를 질투하여 모함하려는 것 아니오!","왕윤","","",},
        [2061] = {20189702,"여포","형세가 위급합니다! 사도께서 말을 타시고 함께 관 밖으로 나가서 좋은 계책을 냅시다.","형세가 위급합니다! 사도께서 말을 타시고 함께 관 밖으로 나가서 좋은 계책을 냅시다.","왕윤","","",},
        [2062] = {20189702,"왕윤","사직을 지키는 영들로 국가를 지키는 것이 나의 소원입니다. 그럴 수 없다면 내 목숨을 바치겠습니다.","사직을 지키는 영들로 국가를 지키는 것이 나의 소원입니다. 그럴 수 없다면 내 목숨을 바치겠습니다.","여포","","",},
        [2063] = {20189702,"왕윤","어려움을 모면하는 것은 내가 할 일이 아닙니다. 나를 대신하여 관동의 제공에게 감사의 뜻을 표하고 나라를 위해 노력합시다!","어려움을 모면하는 것은 내가 할 일이 아닙니다. 나를 대신하여 관동의 제공에게 감사의 뜻을 표하고 나라를 위해 노력합시다!","여포","","",},
        [2064] = {20189702,"여포","어서 말을 타십시오!","어서 말을 타십시오!","왕윤","","",},
        [2065] = {20189702,"왕윤","나는 가지 않겠소.","나는 가지 않겠소.","여포","","",},
        [2066] = {20189702,"여포","아...그럼 몸 조심하십시오.","아...그럼 몸 조심하십시오.","왕윤","","",},
        [2067] = {20189801,"유비","같은 한나리 황실의 후손이지 않습니까. 계속 북방에게 져서 오늘 형주에 의탁하러 왔습니다.","같은 한나리 황실의 후손이지 않습니까. 계속 북방에게 져서 오늘 형주에 의탁하러 왔습니다.","유표","","",},
        [2068] = {20189801,"유표","현덕이 이렇게 온 것은 당연히 환영할 일이오.","현덕이 이렇게 온 것은 당연히 환영할 일이오.","유비","","",},
        [2069] = {20189801,"유표","먼저 신야에 주둔하면 어떻겠소?","먼저 신야에 주둔하면 어떻겠소?","유비","","",},
        [2070] = {20189801,"유비","배려해 주셔서 감사합니다.","배려해 주셔서 감사합니다.","유표","","",},
        [2071] = {20189802,"유표","내가 병이 깊어 곧 죽을 것 같으니 아들을 잘 부탁하오.","내가 병이 깊어 곧 죽을 것 같으니 아들을 잘 부탁하오.","유비","","",},
        [2072] = {20189802,"유표","아들이 재주가 없어 부업을 잇기 힘드니 내가 죽으면 아우가 형주를 다스리시오.","아들이 재주가 없어 부업을 잇기 힘드니 내가 죽으면 아우가 형주를 다스리시오.","유비","","",},
        [2073] = {20189802,"유비","힘을 다해 조카를 보좌해야지 어떻게 그럴 수 있습니까!","힘을 다해 조카를 보좌해야지 어떻게 그럴 수 있습니까!","유표","","",},
        [2074] = {20189901,"손권","후계자 문제를 정하지 못해, 당쟁이 일어나고 조정이 불안정하오.","후계자 문제를 정하지 못해, 당쟁이 일어나고 조정이 불안정하오.","정봉","","",},
        [2075] = {20189901,"손권","올해 일이 많아 동오를 손량에게 맡기려고 하오.","올해 일이 많아 동오를 손량에게 맡기려고 하오.","정봉","","",},
        [2076] = {20189901,"손권","정봉 그대는 손량을 성심을 다해 보좌하여, 동오의 정국을 안정시키시오.","정봉 그대는 손량을 성심을 다해 보좌하여, 동오의 정국을 안정시키시오.","정봉","","",},
        [2077] = {20189901,"정봉","신은 명을 받들어, 목숨을 다해 지키겠습니다.","신은 명을 받들어, 목숨을 다해 지키겠습니다.","손권","","",},
        [2078] = {20189902,"정봉","손량이 이제 막 황위에 올랐는데 제갈탄과 호준 등이 동흥을 공격하였다.","손량이 이제 막 황위에 올랐는데 제갈탄과 호준 등이 동흥을 공격하였다.","","","",},
        [2079] = {20189902,"정봉","동오의 정국을 안정시키기 위해 동흥전투는 반드시 이겨야 한다.","동오의 정국을 안정시키기 위해 동흥전투는 반드시 이겨야 한다.","","","",},
        [2080] = {20189902,"정봉","공을 세워 상을 받을 때가 되었다!","공을 세워 상을 받을 때가 되었다!","","","",},
        [2081] = {20189902,"정봉","각 병사는 나와 함께 갑옷과 투구를 벗고, 짧은 병기를 들고 빠르게 진격하여 적을 죽일 것이다.","각 병사는 나와 함께 갑옷과 투구를 벗고, 짧은 병기를 들고 빠르게 진격하여 적을 죽일 것이다.","","","",},
        [2082] = {201810001,"양호","이것은 내가 직접 제조한 약이오. 병이 있다고 들었으니, 이것을 먼저 드시오.","이것은 내가 직접 제조한 약이오. 병이 있다고 들었으니, 이것을 먼저 드시오.","육항","","",},
        [2083] = {201810001,"동오수령","약에 무엇을 넣었을지도 모르니 드시지 마십시오.","약에 무엇을 넣었을지도 모르니 드시지 마십시오.","육항","","",},
        [2084] = {201810001,"육항","양호가 독을 쓸 사람이겠느냐!","양호가 독을 쓸 사람이겠느냐!","동오수령","","",},
        [2085] = {201810001,"육항","상대가 덕으로 대하는데 우리가 거칠게 대하면, 상대가 싸우지 않고 이기는 것입니다. 각자의 땅을 지키면서 작은 이익을 구하지 말아야 합니다.","상대가 덕으로 대하는데 우리가 거칠게 대하면, 상대가 싸우지 않고 이기는 것입니다. 각자의 땅을 지키면서 작은 이익을 구하지 말아야 합니다.","동오수령","","",},
        [2086] = {201810002,"육항","국가를 창건하고 가업을 계승하려면 소인을 임용하지 않고, 소인의 참언에 귀 기울이지 않고, 사악한 자를 등용하지 말아야 합니다.","국가를 창건하고 가업을 계승하려면 소인을 임용하지 않고, 소인의 참언에 귀 기울이지 않고, 사악한 자를 등용하지 말아야 합니다.","","","",},
        [2087] = {201810002,"육항","그들에게 조정의 중책을 맡기고, 독단적인 권력을 주고서, 태평성대를 바라고, 맑은 사회 기풍을 바라는 것은 불가능한 일입니다.","그들에게 조정의 중책을 맡기고, 독단적인 권력을 주고서, 태평성대를 바라고, 맑은 사회 기풍을 바라는 것은 불가능한 일입니다.","","","",},
        [2088] = {201810002,"육항","지금 관리로서 특별한 재능을 가진 사람은 적지만, 그들이나 왕실 귀족의 후손들은 어려서부터 도덕적 교화에 젖어 있습니다.","지금 관리로서 특별한 재능을 가진 사람은 적지만, 그들이나 왕실 귀족의 후손들은 어려서부터 도덕적 교화에 젖어 있습니다.","","","",},
        [2089] = {201810002,"육항","어떤 이는 청렴하고 각고의 노력으로 자립한 자로서 그들의 자질과 재능은 임용하기에 충분하고, 그들의 재능에 따라 관직을 주고 소인들을 억누를 수 있습니다.","어떤 이는 청렴하고 각고의 노력으로 자립한 자로서 그들의 자질과 재능은 임용하기에 충분하고, 그들의 재능에 따라 관직을 주고 소인들을 억누를 수 있습니다.","","","",},
        [2090] = {201810002,"육항","그렇게 되면 사회적 기풍이 맑아지고, 정무에 오점이 사라지게 될 것입니다.","그렇게 되면 사회적 기풍이 맑아지고, 정무에 오점이 사라지게 될 것입니다.","","","",},
        [2091] = {1701,"주인공","이번에 평원현에 가서 형님에게 편지를 전달하는데, 조자룡 장군을 만날 수 있을지 모르겠네요! 형님과 관계가 좋다고 들었거든요.","이번에 평원현에 가서 형님에게 편지를 전달하는데, 조자룡 장군을 만날 수 있을지 모르겠네요! 형님과 관계가 좋다고 들었거든요.","관은병","","",},
        [2092] = {1701,"관은병","정말요? 전설 속 조자룡 장군을 만난다고요! 생각만 해도 설레네요!","정말요? 전설 속 조자룡 장군을 만난다고요! 생각만 해도 설레네요!","","","",},
        [2093] = {1701,"장성채","관은병, 또 시작이군!","관은병, 또 시작이군!","","","",},
        [2094] = {1701,"관은병","빛나는 갑옷과 투구에 백룡처럼 은창을 들고 난세에 누구와도 싸워 이기는 조자룡!","빛나는 갑옷과 투구에 백룡처럼 은창을 들고 난세에 누구와도 싸워 이기는 조자룡!","","","",},
        [2095] = {1701,"관은병","젊고 잘생기고 싸움도 잘하는데 어떻게 안 좋아할 수 있어?","젊고 잘생기고 싸움도 잘하는데 어떻게 안 좋아할 수 있어?","","","",},
        [2096] = {1701,"장성채","충분히 이해해요! 나도 이 세상에서 제일 멋지다고 생각하거든요!","충분히 이해해요! 나도 이 세상에서 제일 멋지다고 생각하거든요!","","","",},
        [2097] = {1701,"주인공","하하하! 또 날 띄우는군요! 알겠어요. 절대 실망시키지 않을게요!","하하하! 또 날 띄우는군요! 알겠어요. 절대 실망시키지 않을게요!","","","",},
        [2098] = {1702,"주인공","전해, 조조가 서주를 공격한다는 소식 들었죠? 북해 공융은 서주를 돕기로 했는데, 당신은요?","전해, 조조가 서주를 공격한다는 소식 들었죠? 북해 공융은 서주를 돕기로 했는데, 당신은요?","전해","","",},
        [2099] = {1702,"전해","서주와 청주는 가까우니 돕는 게 맞겠죠. 하지만 청주는 지금 기주의 원소와 전쟁 중이라서, 병력이 분산되면...","서주와 청주는 가까우니 돕는 게 맞겠죠. 하지만 청주는 지금 기주의 원소와 전쟁 중이라서, 병력이 분산되면...","","","",},
        [2100] = {1702,"전해","하지만 조조가 서주를 친다면 곧 청주도 공격할 텐데...어쩌면 좋을까요.","하지만 조조가 서주를 친다면 곧 청주도 공격할 텐데...어쩌면 좋을까요.","","","",},
        [2101] = {1702,"주인공","전 정말 우유부단한 사람이 제일 싫어요. 빨리 결정하세요!","전 정말 우유부단한 사람이 제일 싫어요. 빨리 결정하세요!","","","",},
        [2102] = {1702,"전해","잠깐만요. 조조는 원소보다 훨씬 흉악한 사람이니 서주를 돕겠어요!","잠깐만요. 조조는 원소보다 훨씬 흉악한 사람이니 서주를 돕겠어요!","","","",},
        [2103] = {1702,"주인공","좋아요. 이제 좀 시원하네요!","좋아요. 이제 좀 시원하네요!","","","",},
        [2104] = {1703,"주인공","조조가 서주를 공격할 거예요. 원래 도겸을 도와 북해 공융이 출병하려고 했는데 북해가 황건적에게 포위됐어요. 공융이 형님께 편지를 보내 서주를 도와달라고 하는데요.","조조가 서주를 공격할 거예요. 원래 도겸을 도와 북해 공융이 출병하려고 했는데 북해가 황건적에게 포위됐어요. 공융이 형님께 편지를 보내 서주를 도와달라고 하는데요.","유비","","",},
        [2105] = {1703,"유비","나도 북해 공융을 잘 안다! 하지만 평원현이 작아 병력이 부족하구나.","나도 북해 공융을 잘 안다! 하지만 평원현이 작아 병력이 부족하구나.","","","",},
        [2106] = {1703,"주인공","공손찬에게 가서 삼 천 병마를 빌리고, 조자룡도 부탁해서 함께 가면 어떨까요?","공손찬에게 가서 삼 천 병마를 빌리고, 조자룡도 부탁해서 함께 가면 어떨까요?","","","",},
        [2107] = {1703,"유비","좋은 생각이구나. 어서 공손찬을 찾으러 가자.","좋은 생각이구나. 어서 공손찬을 찾으러 가자.","","","",},
        [2108] = {1704,"공손찬","조조와 원한도 없는데 왜 출병한단 말이오?","조조와 원한도 없는데 왜 출병한단 말이오?","유비","","",},
        [2109] = {1704,"유비","이미 공융에게 약속하여 신의를 저버릴 수 없습니다.","이미 공융에게 약속하여 신의를 저버릴 수 없습니다.","","","",},
        [2110] = {1704,"공손찬","현덕은 너무 덕이 많아 탈이오! 알겠소. 삼천 정예병을 빌려주겠소. 조자룡은 테스트를 통과해야 할 거요!","현덕은 너무 덕이 많아 탈이오! 알겠소. 삼천 정예병을 빌려주겠소. 조자룡은 테스트를 통과해야 할 거요!","","","",},
        [2111] = {1704,"주인공","조자룡의 테스트는 제가 할게요! 고수를 오랫동안 못 만나 몸이 근질거리던 참이었거든요!","조자룡의 테스트는 제가 할게요! 고수를 오랫동안 못 만나 몸이 근질거리던 참이었거든요!","","","",},
        [2112] = {1705,"조운","당신의 무예는 정말 대단하시오!","당신의 무예는 정말 대단하시오!","주인공","","",},
        [2113] = {1705,"주인공","과찬이십니다! 저와 함께 서주에 같이 가주시겠어요?","과찬이십니다! 저와 함께 서주에 같이 가주시겠어요?","","","",},
        [2114] = {1705,"조운","당연하오! 테스트를 통과했으니 약속을 지켜야지요!","당연하오! 테스트를 통과했으니 약속을 지켜야지요!","","","",},
        [2115] = {1705,"조운","난 유비를 잘 알고, 당신은 유비의 아우잖소. 만난 기념으로 명장령을 주겠소!","난 유비를 잘 알고, 당신은 유비의 아우잖소. 만난 기념으로 명장령을 주겠소!","","","",},
        [2116] = {1705,"주인공","잘 됐어요! 전해, 공융 그리고 조자룡과 형님의 군마가 있으면 서주의 포위를 뚫을 수 있을 거예요!","잘 됐어요! 전해, 공융 그리고 조자룡과 형님의 군마가 있으면 서주의 포위를 뚫을 수 있을 거예요!","","","",},
        [2117] = {1705,"장성채","정말 대단해요! 해결하지 못하는 일이 없네요!","정말 대단해요! 해결하지 못하는 일이 없네요!","","","",},
        [2118] = {1705,"주인공","하하하! 성채, 은병, 어서 서주로 가요!","하하하! 성채, 은병, 어서 서주로 가요!","","","",},
        [2119] = {1801,"조조","여포, 나와 원한도 없는데 왜 내가 서주로 간 사이 연주와 복양을 빼앗았느냐?","여포, 나와 원한도 없는데 왜 내가 서주로 간 사이 연주와 복양을 빼앗았느냐?","여포","","",},
        [2120] = {1801,"여포","한의 성지가 나뉘었는데 어찌 모두 조조의 것입니까? 진궁과 장막이 나를 연주목으로 삼았으니 이것도 나의 능력이지요!","한의 성지가 나뉘었는데 어찌 모두 조조의 것입니까? 진궁과 장막이 나를 연주목으로 삼았으니 이것도 나의 능력이지요!","","","",},
        [2121] = {1801,"조조","내가 복양성을 빼앗고, 반드시 네 놈을 죽이겠다!","내가 복양성을 빼앗고, 반드시 네 놈을 죽이겠다!","","","",},
        [2122] = {1801,"여포","나 전신 여포가 무서워할 것 같소? 복양성에 들어와면 돌아갈 수 없을 것이오!","나 전신 여포가 무서워할 것 같소? 복양성에 들어와면 돌아갈 수 없을 것이오!","","","",},
        [2123] = {1802,"장성채","조조와 여포가 싸우고 있어요! 조조가 편지를 보냈는데 서주에서 철수할 테니 복양에 와서 도와달라는데요.","조조와 여포가 싸우고 있어요! 조조가 편지를 보냈는데 서주에서 철수할 테니 복양에 와서 도와달라는데요.","주인공","","",},
        [2124] = {1802,"주인공","여포의 병마가 언제 이렇게 많았죠?","여포의 병마가 언제 이렇게 많았죠?","","","",},
        [2125] = {1802,"장성채","이각과 곽사가 장안에서 난을 일으키고, 여포가 원술에게 갔잖아요. 원술이 속이 좁고 여포는 야심이 클 줄 아무도 몰랐죠.","이각과 곽사가 장안에서 난을 일으키고, 여포가 원술에게 갔잖아요. 원술이 속이 좁고 여포는 야심이 클 줄 아무도 몰랐죠.","","","",},
        [2126] = {1802,"장성채","조조가 서주를 치러 간 사이 진류태수 장막과 진궁이 여포를 연주목으로 삼았어요.","조조가 서주를 치러 간 사이 진류태수 장막과 진궁이 여포를 연주목으로 삼았어요.","","","",},
        [2127] = {1802,"주인공","원래 조조가 철수하면 만나지 않으려고 했는데 나에게 도움을 요청하다니! 다시 도와주죠. 뭐!","원래 조조가 철수하면 만나지 않으려고 했는데 나에게 도움을 요청하다니! 다시 도와주죠. 뭐!","","","",},
        [2128] = {1803,"장막","누가 복양에서 소란을 피우느냐? 조조가 보낸 첩자냐?","누가 복양에서 소란을 피우느냐? 조조가 보낸 첩자냐?","주인공","","",},
        [2129] = {1803,"장성채","장막은 그 유명한 팔주 중 한명이니 조심해야 해요.","장막은 그 유명한 팔주 중 한명이니 조심해야 해요.","","","",},
        [2130] = {1803,"주인공","팔주? 주방장인가요? 음식을 잘하겠네요.","팔주? 주방장인가요? 음식을 잘하겠네요.","","","",},
        [2131] = {1803,"장성채","팔주는 장막, 동상 등 여덞 명을 말하고, 재물로 다른 사람을 돕는다는 뜻이에요.","팔주는 장막, 동상 등 여덞 명을 말하고, 재물로 다른 사람을 돕는다는 뜻이에요.","","","",},
        [2132] = {1803,"장성채","장막은 어려서 의협심이 강하고 집안의 재산을 아까워하지 않고 가난한 사람을 도와주어 많은 명사가 그를 찾아왔지요. 조조, 원소도 장막의 친구고요.","장막은 어려서 의협심이 강하고 집안의 재산을 아까워하지 않고 가난한 사람을 도와주어 많은 명사가 그를 찾아왔지요. 조조, 원소도 장막의 친구고요.","","","",},
        [2133] = {1803,"장성채","점점 유명해져서 나중에 조정에서 진류태수로 임명했어요.","점점 유명해져서 나중에 조정에서 진류태수로 임명했어요.","","","",},
        [2134] = {1803,"장막","흥! 내 칼을 음식에 쓰는 칼이 아니고 보배로운 칼이다!","흥! 내 칼을 음식에 쓰는 칼이 아니고 보배로운 칼이다!","","","",},
        [2135] = {1803,"주인공","장막, 실력이 좋아 보이기는 하는데, 나한테는 별것 아니죠!","장막, 실력이 좋아 보이기는 하는데, 나한테는 별것 아니죠!","","","",},
        [2136] = {1804,"장막","장군, 날 이기다니 정말 대단하시오! 이 보배로운 칼을 드리리다!","장군, 날 이기다니 정말 대단하시오! 이 보배로운 칼을 드리리다!","주인공","","",},
        [2137] = {1804,"주인공","칼끝이 자꾸 말려서 난 이것으로 파나 썰어야겠네요.","칼끝이 자꾸 말려서 난 이것으로 파나 썰어야겠네요.","","","",},
        [2138] = {1804,"장막","팔주 장막이 무시당할 날이 올 줄이야!","팔주 장막이 무시당할 날이 올 줄이야!","","","",},
        [2139] = {1805,"우금","성 밖에 계셔서 우리가 먼저 입성했습니다.","성 밖에 계셔서 우리가 먼저 입성했습니다.","조조","","",},
        [2140] = {1805,"조조","누가 감히 먼저 나아갔느냐!","누가 감히 먼저 나아갔느냐!","우금","","",},
        [2141] = {1805,"주인공","조조는요? 방금 대화하는 소리가 들렸는데 안 보이네요.","조조는요? 방금 대화하는 소리가 들렸는데 안 보이네요.","장성채","","",},
        [2142] = {1805,"장성채","조조가 진궁과 여포의 간계에 빠져 성을 공격하려고 갔어요!","조조가 진궁과 여포의 간계에 빠져 성을 공격하려고 갔어요!","","","",},
        [2143] = {1805,"장성채","안에는 연기가 자욱한데 어떻게 됐는지 모르겠네요! 방금 악진, 이전, 전위가 조조를 찾더라고요!","안에는 연기가 자욱한데 어떻게 됐는지 모르겠네요! 방금 악진, 이전, 전위가 조조를 찾더라고요!","","","",},
        [2144] = {1805,"주인공","여포를 공격해서 어서 구해야겠어요.","여포를 공격해서 어서 구해야겠어요.","","","",},
        [2145] = {1806,"여포","하하, 잘싸웠소!  당신이 없었다면 나와 초선이 이루어질 수 없었을 거요! 당신을 봐서 조조를 풀어주겠소!","하하, 잘싸웠소!  당신이 없었다면 나와 초선이 이루어질 수 없었을 거요! 당신을 봐서 조조를 풀어주겠소!","주인공","","",},
        [2146] = {1806,"주인공","좋아요, 성격 한번 호탕하시군요!","좋아요, 성격 한번 호탕하시군요!","여포","","",},
        [2147] = {1806,"조조","방금 성문에서 불기둥이 떨어져 팔에 화상을 입었소!","방금 성문에서 불기둥이 떨어져 팔에 화상을 입었소!","주인공","","",},
        [2148] = {1806,"조조","그대가 오지 않았다면 정말 위험했을 것이오! 감사의 표시로 이 명장령을 드리겠소!","그대가 오지 않았다면 정말 위험했을 것이오! 감사의 표시로 이 명장령을 드리겠소!","","","",},
        [2149] = {1806,"주인공","그럼 서주에서 철수해 주시죠!","그럼 서주에서 철수해 주시죠!","","","",},
        [2150] = {1806,"조조","이미 사람을 보내 철수했소. 지난번에 피하고 만나지 않은 것은 용서하시오.","이미 사람을 보내 철수했소. 지난번에 피하고 만나지 않은 것은 용서하시오.","","","",},
        [2151] = {1806,"주인공","좋아요. 약속을 지켰다면 그것으로 됐어요. 저는 서주로 돌아가 형님을 찾아야겠어요.","좋아요. 약속을 지켰다면 그것으로 됐어요. 저는 서주로 돌아가 형님을 찾아야겠어요.","","","",},
        [2152] = {1806,"조조","조심히 가시오! 이 복양성을 곧 빼앗아 오겠소!","조심히 가시오! 이 복양성을 곧 빼앗아 오겠소!","","","",},
        [2153] = {1901,"도겸","어서 서주의 도장을 가지고 오너라!","어서 서주의 도장을 가지고 오너라!","유비","","",},
        [2154] = {1901,"유비","무슨 뜻이오?","무슨 뜻이오?","","","",},
        [2155] = {1901,"도겸","자네는 어질고 재주가 뛰어나고 한실의 종친이잖소. 서주를 양보하고 싶소!","자네는 어질고 재주가 뛰어나고 한실의 종친이잖소. 서주를 양보하고 싶소!","","","",},
        [2156] = {1901,"유비","비록 한실의 종친이지만 덕이 부족하오. 왜 원소에게 양보하지 않소?","비록 한실의 종친이지만 덕이 부족하오. 왜 원소에게 양보하지 않소?","","","",},
        [2157] = {1901,"도겸","원소는 무덤의 고목 같은 사람인데 어떻게 당신과 비교하겠소!","원소는 무덤의 고목 같은 사람인데 어떻게 당신과 비교하겠소!","","","",},
        [2158] = {1901,"유비","서주를 구하러 온 것은 대의 때문이지 사심이 있어서가 아니오. 도공의 부탁을 받아들일 수 없소.","서주를 구하러 온 것은 대의 때문이지 사심이 있어서가 아니오. 도공의 부탁을 받아들일 수 없소.","","","",},
        [2159] = {1902,"도겸","그대는 젊고 포부가 큰 사람이니 내가 부탁 하나만 하겠소.","그대는 젊고 포부가 큰 사람이니 내가 부탁 하나만 하겠소.","주인공","","",},
        [2160] = {1902,"주인공","말씀하시죠.","말씀하시죠.","","","",},
        [2161] = {1902,"도겸","오늘 같은 난세에 나는 늙고 자식은 못나서 중책을 맡길 수가 없소. 서주태수의 자리를 유비에게 맡기고 싶은데 계속 사양하고 있다오. 유비 좀 설득해주시오.","오늘 같은 난세에 나는 늙고 자식은 못나서 중책을 맡길 수가 없소. 서주태수의 자리를 유비에게 맡기고 싶은데 계속 사양하고 있다오. 유비 좀 설득해주시오.","","","",},
        [2162] = {1902,"주인공","이상하군요. 왜 아들에게 물려주지 않는 거죠?","이상하군요. 왜 아들에게 물려주지 않는 거죠?","","","",},
        [2163] = {1902,"도겸","내게는 도상, 도응 두 아들이 있는데 둘 다 재주가 없으니 어떻게 하겠소? (말하며 연신 눈물을 훔친다.)","내게는 도상, 도응 두 아들이 있는데 둘 다 재주가 없으니 어떻게 하겠소? (말하며 연신 눈물을 훔친다.)","","","",},
        [2164] = {1902,"주인공","알겠어요. 제가 설득해 볼게요!","알겠어요. 제가 설득해 볼게요!","","","",},
        [2165] = {1903,"관우","아우, 형님이 알고 나와 장비에게 너를 막으라고 하셨다.","나와 장비에게 너를 막으라고 하셨다.","주인공","","",},
        [2166] = {1903,"주인공","그럼 한번 붙어보면 어떨까요?","그럼 한번 붙어보면 어떨까요?","","","",},
        [2167] = {1904,"장성채","아버지, 왜  막고 그러세요?","아버지, 왜  막고 그러세요?","장비","","",},
        [2168] = {1904,"장비","군령이 먼저니 날 탓하지 말거라.","군령이 먼저니 날 탓하지 말거라.","","","",},
        [2169] = {1904,"장성채","싸울 때  다치게 해선 안 돼요!","싸울 때  다치게 해선 안 돼요!","","","",},
        [2170] = {1904,"장비","알았다. 우리 딸 말대로 하마!","알았다. 우리 딸 말대로 하마!","","","",},
        [2171] = {1905,"주인공","형님, 서주는 요충지라서 북쪽에는 조조가 연주를 점령하고 남으로는 원술이 노리고 있어요. 형님이 서주를 거절하시면 백성들이 위험에 빠질 거예요!","서주는 요충지라서 북쪽에는 조조가 연주를 점령하고 남으로는 원술이 노리고 있어요. 서주를 거절하시면 백성들이 위험에 빠질 거예요!","유비","","",},
        [2172] = {1905,"유비","내가 생각이 짧았구나. 하지만 나 혼자서 어떻게 그런 큰 직책을 맡겠느냐!","내가 생각이 짧았구나. 하지만 나 혼자서 어떻게 그런 큰 직책을 맡겠느냐!","","","",},
        [2173] = {1905,"주인공","손건, 미축, 진등이 도와줄 거예요. 우리도 있잖아요. 그러니 사양하지 마세요!","손건, 미축, 진등이 도와줄 거예요. 우리도 있잖아요. 그러니 사양하지 마세요!","","","",},
        [2174] = {1905,"유비","알았다. 아우 말을 들으마!","알았다. 아우 말을 들으마!","","","",},
        [2175] = {1905,"유비","이 명장령은 도겸이 죽기 전에 네게 주라고 부탁한 것이다. 조조를 물리치고 서주를 구해줘서 고맙다고 하더라!","이 명장령은 도겸이 죽기 전에 네게 주라고 부탁한 것이다. 조조를 물리치고 서주를 구해줘서 고맙다고 하더라!","","","",},
        [2176] = {1905,"주인공","그럼 감사히 받겠습니다. 하하!","그럼 감사히 받겠습니다. 하하!","","","",},
        [2177] = {2001,"주인공","서주도 이제 안정이 되었으니 저는 장안으로 가서 상황을 봐야겠어요.","서주도 이제 안정이 되었으니 저는 장안으로 가서 상황을 봐야겠어요.","","","",},
        [2178] = {2001,"주인공","지난번 동탁을 죽일 때 장안은 번화했는데 이제는 황폐해졌네요! 성채, 무슨 일이 있었죠?","지난번 동탁을 죽일 때 장안은 번화했는데 이제는 황폐해졌네요! 성채, 무슨 일이 있었죠?","장성채","","",},
        [2179] = {2001,"장성채","이각과 곽사가 장안을 점령하고 천자를 위협해 조정을 장악했어요. 그후 서로 몇 달을 싸워서 몇 만명이 죽고 장안을 폐허로 만들었죠.","이각과 곽사가 장안을 점령하고 천자를 위협해 조정을 장악했어요. 그후 서로 몇 달을 싸워서 몇 만명이 죽고 장안을 폐허로 만들었죠.","","","",},
        [2180] = {2001,"관은병","흥, 그때 가후가 이각과 곽사를 견제하여 백관과 천자를 보호할 수 있다고 하지 않았나요?","흥, 그때 가후가 이각과 곽사를 견제하여 백관과 천자를 보호할 수 있다고 하지 않았나요?","","","",},
        [2181] = {2001,"주인공","이각과 곽사를 멸하고, 가후를 찾아 천자를 구하러 가요!","이각과 곽사를 멸하고, 가후를 찾아 천자를 구하러 가요!","","","",},
        [2182] = {2002,"한헌제","내가 가후를 오해했다. 그가 중재하지 않았다면 난 이미 죽은 목숨일 것이다.","내가 가후를 오해했다. 그가 중재하지 않았다면 난 이미 죽은 목숨일 것이다.","주인공","","",},
        [2183] = {2002,"주인공","그랬군요. 가후, 황제를 봐서 살려주겠어요.","그랬군요. 가후, 황제를 봐서 살려주겠어요.","","","",},
        [2184] = {2002,"가후","폐하, 이각과 곽사가 저를 죽이려고 하니 어서 장안을 떠나겠습니다. (가후가 떠난다.)","폐하, 이각과 곽사가 저를 죽이려고 하니 어서 장안을 떠나겠습니다. (가후가 떠난다.)","","","",},
        [2185] = {2002,"한헌제","가후도 떠나면, 앞으로 누가 짐을 보호한단 말이냐?","가후도 떠나면, 앞으로 누가 짐을 보호한단 말이냐?","","","",},
        [2186] = {2002,"주인공","폐하, 장안을 떠나 낙양으로 가는 것이 좋겠습니다.","폐하, 장안을 떠나 낙양으로 가는 것이 좋겠습니다.","","","",},
        [2187] = {2002,"한헌제","이각과 곽사가 반대할까 걱정이오.","이각과 곽사가 반대할까 걱정이오.","","","",},
        [2188] = {2002,"주인공","폐하, 걱정하지 마십시오! 곽사는 이미 달아났고, 이각을 찾아 동의하도록 만들겠습니다!","폐하, 걱정하지 마십시오! 곽사는 이미 달아났고, 이각을 찾아 동의하도록 만들겠습니다!","","","",},
        [2189] = {2003,"관은병","양봉, 나라의 신하가 폐하를 보호하지 않고 나쁜 짓을 일삼는 거죠?","양봉, 나라의 신하가 폐하를 보호하지 않고 나쁜 짓을 일삼는 거죠?","양봉","","",},
        [2190] = {2003,"관은병","이각은 탐욕스럽고, 사악한 방법을 좋아하고, 군사에게 상을 내리지 않고 무녀에게 상을 내리잖아요. 왜 그와 함께 하는 거죠?","이각은 탐욕스럽고, 사악한 방법을 좋아하고, 군사에게 상을 내리지 않고 무녀에게 상을 내리잖아요. 왜 그와 함께 하는 거죠?","","","",},
        [2191] = {2003,"양봉","맞소! 그동안의 공로가 무녀만도 못하다니! 병사를 모아 이각에게 반기를 들겠소.","맞소! 그동안의 공로가 무녀만도 못하다니! 병사를 모아 이각에게 반기를 들겠소.","","","",},
        [2192] = {2003,"관은병","야호, 대장 한 명이 우리편이 되었어요! 양봉의 군마가 있으니 이제 걱정하지 마세요.","야호, 대장 한 명이 우리편이 되었어요! 양봉의 군마가 있으니 이제 걱정하지 마세요.","주인공","","",},
        [2193] = {2003,"주인공","양봉은 별로인데 도끼를 들고 있는 그 부하는 맘에 들었어요. 그는 누구죠?","양봉은 별로인데 도끼를 들고 있는 그 부하는 맘에 들었어요. 그는 누구죠?","","","",},
        [2194] = {2003,"관은병","하동 양현 사람으로 성은 서이고 이름은 황이며, 자는 공명이에요.","하동 양현 사람으로 성은 서이고 이름은 황이며, 자는 공명이에요.","","","",},
        [2195] = {2003,"주인공","명장 서황이군요. 양봉을 따르다니 정말 안타깝네요.","명장 서황이군요. 양봉을 따르다니 정말 안타깝네요.","","","",},
        [2196] = {2004,"한헌제","전에는 동탁이 낙양에 불을 지르고 짐을 장안으로 끌고오더니, 동탁이 죽은 후에는 이각과 곽사가 장안을...","전에는 동탁이 낙양에 불을 지르고 짐을 장안으로 끌고오더니, 동탁이 죽은 후에는 이각과 곽사가 장안을...","동승","","",},
        [2197] = {2004,"한헌제","어렵게 낙양으로 돌아왔는데, 한섬이 공을 세웠다고 자기 마음대로 하고, 기근도 만났으니, 모두 굶어죽게 생겼소!","어렵게 낙양으로 돌아왔는데, 한섬이 공을 세웠다고 자기 마음대로 하고, 기근도 만났으니, 모두 굶어죽게 생겼소!","","","",},
        [2198] = {2004,"동승","폐하, 연주목 조조에게 밀서를 보내십시오. 조조는 병사와 식량이 많아 앞으로의 걱정을 덜어줄 것입니다.","폐하, 연주목 조조에게 밀서를 보내십시오. 조조는 병사와 식량이 많아 앞으로의 걱정을 덜어줄 것입니다.","","","",},
        [2199] = {2004,"주인공","조조는 제가 잘 알고 있으니, 제가 가서 알리겠어요.","조조는 제가 잘 알고 있으니, 제가 가서 알리겠어요.","","","",},
        [2200] = {2004,"동승","봉양이 내키지 않아하면 그 부하 서황도 다루기 어려울 것이오.","봉양이 내키지 않아하면 그 부하 서황도 다루기 어려울 것이오.","","","",},
        [2201] = {2004,"주인공","봉양, 한섬은 됐고, 서황이 정말 훌륭한 장수더군요. 가서 만나봐야겠어요.","봉양, 한섬은 됐고, 서황이 정말 훌륭한 장수더군요. 가서 만나봐야겠어요.","","","",},
        [2202] = {2005,"주인공","참으로 용맹하고 지략이 있는데 왜 양봉, 한섬을 따르고 있죠?","참으로 용맹하고 지략이 있는데 왜 양봉, 한섬을 따르고 있죠?","서황","","",},
        [2203] = {2005,"서황","오랫동안 양봉과 한섬을 알아 떠나지 않고 있었소.","오랫동안 양봉과 한섬을 알아 떠나지 않고 있었소.","","","",},
        [2204] = {2005,"주인공","좋은 새는 나무를 택해 서식하고, 현명한 신하는 군주를 선택해 일하죠. 연주목 조조가 난세의 영웅이니 그에게 가면 좋을 것 같군요.","좋은 새는 나무를 택해 서식하고, 현명한 신하는 군주를 선택해 일하죠. 연주목 조조가 난세의 영웅이니 그에게 가면 좋을 것 같군요.","","","",},
        [2205] = {2005,"서황","그대가 큰 깨달음을 주시니 고맙소. 이 명장령으로 사례하겠소. (서황이 떠난다.)","그대가 큰 깨달음을 주시니 고맙소. 이 명장령으로 사례하겠소. (서황이 떠난다.)","","","",},
        [2206] = {2005,"주인공","황제가 낙양으로는 왔지만 나라 살림이 궁핍하고 군대도 없으니 오래 가기 힘들었어요.","황제가 낙양으로는 왔지만 나라 살림이 궁핍하고 군대도 없으니 오래 가기 힘들었어요.","관은병","","",},
        [2207] = {2005,"관은병","황제도 참 답답하겠어요. 자유롭게 다니며 괴롭히는 사람도 없는 편이 낫네요.","황제도 참 답답하겠어요. 자유롭게 다니며 괴롭히는 사람도 없는 편이 낫네요.","","","",},
        [2208] = {2005,"주인공","하하, 전 달라요! 황제는 아니지만 가장 강하지요! 이제 실력으로 말하는 세상이잖아요!","하하, 전 달라요! 황제는 아니지만 가장 강하지요! 이제 실력으로 말하는 세상이잖아요!","","","",},
        [2209] = {2005,"관은병","이제 어디로 갈까요?","이제 어디로 갈까요?","","","",},
        [2210] = {2005,"주인공","헤헤, 재밌는 곳으로 데려다 주죠.","헤헤, 재밌는 곳으로 데려다 주죠.","","","",},
        [2211] = {2101,"장성채","거기가 어딘데요?","거기가 어딘데요?","주인공","","",},
        [2212] = {2101,"주인공","원소와 조조가 지금 가장 큰 세력이잖아요. 가서 낙양으로 돌아온 것을 어떻게 생각하는지 알아봐요.","원소와 조조가 지금 가장 큰 세력이잖아요. 가서 낙양으로 돌아온 것을 어떻게 생각하는지 알아봐요.","","","",},
        [2213] = {2101,"장성채","이미 다 생각이 있는 것 같은데요?","이미 다 생각이 있는 것 같은데요?","","","",},
        [2214] = {2101,"주인공","비밀이에요!","비밀이에요!","","","",},
        [2215] = {2101,"관은병","하하, 구경하는 것도 재밌겠어요. 어서 가요!","하하, 구경하는 것도 재밌겠어요. 어서 가요!","","","",},
        [2216] = {2102,"저수","장군은 재상의 가문이고 충의를 다하고 있지요. 지금 황상이 떠돌고 있으니 장군이 모시고 업성에 수도를 세우십시오.","장군은 재상의 가문이고 충의를 다하고 있지요. 지금 황상이 떠돌고 있으니 장군이 모시고 업성에 수도를 세우십시오.","원소","","",},
        [2217] = {2102,"저수","천자를 끼고 제후를 호령하고 간신들을 토벌하면 누가 막을 수 있겠습니까!","천자를 끼고 제후를 호령하고 간신들을 토벌하면 누가 막을 수 있겠습니까!","","","",},
        [2218] = {2102,"원소","곽도, 어떻게 생각하시오?","곽도, 어떻게 생각하시오?","","","",},
        [2219] = {2102,"곽도","천자를 맞이하는 것은 적절하지 않습니다! 한실은 이미 기울어 회복되기 어렵습니다!","천자를 맞이하는 것은 적절하지 않습니다! 한실은 이미 기울어 회복되기 어렵습니다!","","","",},
        [2220] = {2102,"곽도","천자를 곁에 두면 항상 표를 올려 지시를 받아야 하지요. 명령에 복종하면 권력을 잃고, 복종하지 않으면 죄가 되니 좋은 방법이 아닙니다.","천자를 곁에 두면 항상 표를 올려 지시를 받아야 하지요. 명령에 복종하면 권력을 잃고, 복종하지 않으면 죄가 되니 좋은 방법이 아닙니다.","","","",},
        [2221] = {2102,"원소","당시 동탁이 낙양을 도륙하고 나를 죽이려고 할 때, 나 혼자서 발해에서 일으켜 천하 영웅을 모아 동탁을 토벌하고, 18로 제후를 통솔하였소.","당시 동탁이 낙양을 도륙하고 나를 죽이려고 할 때, 나 혼자서 발해에서 일으켜 천하 영웅을 모아 동탁을 토벌하고, 18로 제후를 통솔하였소.","","","",},
        [2222] = {2102,"원소","이제 기주의 주인이 되었고, 흑산적, 황건적을 토벌하였소. 하북을 곧 통일할 텐데 뭣 하러 천자의 지시를 받겠소.","이제 기주의 주인이 되었고, 흑산적, 황건적을 토벌하였소. 하북을 곧 통일할 텐데 뭣 하러 천자의 지시를 받겠소.","","","",},
        [2223] = {2102,"저수","장군, 기회를 잡으셔야 합니다. 선수를 치지 않으면 다른 사람의 손에 들어가게 됩니다!","장군, 기회를 잡으셔야 합니다. 선수를 치지 않으면 다른 사람의 손에 들어가게 됩니다!","","","",},
        [2224] = {2102,"원소","더는 말하지 마시오. 이미 결정했소!","더는 말하지 마시오. 이미 결정했소!","","","",},
        [2225] = {2103,"순욱","옛적 진나라 문공은 주나라 양양을 받아주었고, 이로 인해 제후들이 그를 따랐습니다. 한 고조 유방은 죽은 의제를 위해 장례를 치러, 천하의 민심을 얻었지요.","옛적 진나라 문공은 주나라 양양을 받아주었고, 이로 인해 제후들이 그를 따랐습니다. 한 고조 유방은 죽은 의제를 위해 장례를 치러, 천하의 민심을 얻었지요.","조조","","",},
        [2226] = {2103,"순욱","지금 천자가 보위를 잃고 도망 다니는 상황에서 장군이 의병을 일으켜 천자를 섬기시는 것이 백성의 기대에 부응하는 것이고, 서두르지 않으면, 다른 사람이 먼저 할 것입니다.","지금 천자가 보위를 잃고 도망 다니는 상황에서 장군이 의병을 일으켜 천자를 섬기시는 것이 백성의 기대에 부응하는 것이고, 서두르지 않으면, 다른 사람이 먼저 할 것입니다.","","","",},
        [2227] = {2103,"동소","낙양에는 다른 생각을 가진 많은 사람들이 있습니다. 큰일을 도모하시려면 천자를 맞이하실 뿐만 아니라 수도를 허창으로 옮기는 것이 상책입니다!","낙양에는 다른 생각을 가진 많은 사람들이 있습니다. 큰일을 도모하시려면 천자를 맞이하실 뿐만 아니라 수도를 허창으로 옮기는 것이 상책입니다!","","","",},
        [2228] = {2103,"조조","어서 서둘러야 겠소. 내가 천자께 아뢰어 허도로 모시겠소","어서 서둘러야 겠소. 내가 천자께 아뢰어 허도로 모시겠소","","","",},
        [2229] = {2104,"조조","지금 이각과 곽사의 죄가 너무 큽니다. 신에게는 이십 만의 정예병이 있으니 이를 이용하시어 옥체를 보존하시고 사직을 세우소서.","지금 이각과 곽사의 죄가 너무 큽니다. 신에게는 이십 만의 정예병이 있으니 이를 이용하시어 옥체를 보존하시고 사직을 세우소서.","한헌제","","",},
        [2230] = {2104,"한헌제","경의 말에 따르겠소!","경의 말에 따르겠소!","","","",},
        [2231] = {2104,"조조","폐하, 동도는 폐허가 되어 보수하기 어렵고 식량도 부족합니다. 허도 성곽에 궁궐과 재물과 식량이 풍부하니 허도로 옮기시는 것이 좋을 것 같습니다.","폐하, 동도는 폐허가 되어 보수하기 어렵고 식량도 부족합니다. 허도 성곽에 궁궐과 재물과 식량이 풍부하니 허도로 옮기시는 것이 좋을 것 같습니다.","","","",},
        [2232] = {2104,"한헌제","계속 떠돌다가 이제 안정되었는데 다시 천도한다면 사람들의 비웃음거리가 될 것이오.","계속 떠돌다가 이제 안정되었는데 다시 천도한다면 사람들의 비웃음거리가 될 것이오.","","","",},
        [2233] = {2104,"조조","폐하, 지체하시면 안 됩니다. 어서 허도로 옮기셔야 합니다.","폐하, 지체하시면 안 됩니다. 어서 허도로 옮기셔야 합니다.","","","",},
        [2234] = {2104,"한헌제","그럼, 경의 뜻대로 허창으로 천도하겠소!","그럼, 경의 뜻대로 허창으로 천도하겠소!","","","",},
        [2235] = {2105,"주인공","하하, 역시 예상했던 대로군요. 조조가 천자를 맞이한 영웅이 되었어요!","하하, 역시 예상했던 대로군요. 조조가 천자를 맞이한 영웅이 되었어요!","관은병","","",},
        [2236] = {2105,"관은병","원소가 눈이 먼 거죠. 저수의 말을 안 듣다니!","원소가 눈이 먼 거죠. 저수의 말을 안 듣다니!","","","",},
        [2237] = {2105,"장성채","조조가 천자를 끼고 있으면 이제부터 대권은 조조에게 돌아가는데 이제 한나라는 유명무실해졌고, 때가 되면 설마 헌제가...","조조가 천자를 끼고 있으면 이제부터 대권은 조조에게 돌아가는데 이제 한나라는 유명무실해졌고, 때가 되면 설마 헌제가...","","","",},
        [2238] = {2105,"주인공","걱정 마세요! 지금 상황에서 조조가 시국을 안정시키려면 헌제를 죽일 수 없을 거예요.","걱정 마세요! 지금 상황에서 조조가 시국을 안정시키려면 헌제를 죽일 수 없을 거예요.","","","",},
        [2239] = {2105,"장성채","말씀에 일리가 있네요.","말씀에 일리가 있네요.","","","",},
        [2240] = {2105,"주인공","헌제가 저도 양위장군에 봉하셨으니, 최선을 다해 허도까지 호위해야겠어요! 허창으로 가요!","헌제가 저도 양위장군에 봉하셨으니, 최선을 다해 허도까지 호위해야겠어요! 허창으로 가요!","","","",},
        [2241] = {2106,"조조","희지재가 죽은 이후로 일을 계획할 사람이 없소. 영천에는 뛰어난 인물이 많다던데, 누가 그를 계승할 수 있겠소?","희지재가 죽은 이후로 일을 계획할 사람이 없소. 영천에는 뛰어난 인물이 많다던데, 누가 그를 계승할 수 있겠소?","순욱","","",},
        [2242] = {2106,"순욱","영천에 곽가라는 뛰어난 책략가가 있습니다.","영천에 곽가라는 뛰어난 책략가가 있습니다.","","","",},
        [2243] = {2106,"주인공","그 유명한 곽가 말이군요! 조조, 제가 먼저 가서 만나 볼게요!","그 유명한 곽가 말이군요! 조조, 제가 먼저 가서 만나 볼게요!","","","",},
        [2244] = {2107,"순욱","이 분이 전에 말했던 그대가오. 십상시의 난을 평정하고 봉의정에서 동탁을 죽였다오. 이각, 곽사를 물리치고 천자를 낙양으로 호송했소.","이 분이 전에 말했던 그대가오. 십상시의 난을 평정하고 봉의정에서 동탁을 죽였다오. 이각, 곽사를 물리치고 천자를 낙양으로 호송했소.","곽가","","",},
        [2245] = {2107,"순욱","천자께서 그를 양위장군으로 삼았는데 공명과 관록에 관심이 없어 관직만 받았다오.","천자께서 그를 양위장군으로 삼았는데 공명과 관록에 관심이 없어 관직만 받았다오.","","","",},
        [2246] = {2107,"곽가","그대가 양위장군이었군요! 익히 들었지요.","그대가 양위장군이었군요! 익히 들었지요.","주인공","","",},
        [2247] = {2107,"곽가","장군은 무략이 뛰어나고 배포가 넓으니 참으로 당대의 호걸이군요. 이 명장령을 장군에게 주겠소!","장군은 무략이 뛰어나고 배포가 넓으니 참으로 당대의 호걸이군요. 이 명장령을 장군에게 주겠소!","","","",},
        [2248] = {2107,"주인공","과찬이십니다, 하하하하!","과찬이십니다, 하하하하!","","","",},
        [2249] = {35,"관은병","성채야, 내가 삼국의 비밀을 많이 알고 있는데, 알고 싶어?","성채야, 내가 삼국의 비밀을 많이 알고 있는데, 알고 싶어?","장성채","","",},
        [2250] = {35,"장성채","당연히 알고 싶죠! 무슨 비밀인데요?","당연히 알고 싶죠! 무슨 비밀인데요?","","","",},
        [2251] = {35,"관은병","예를 들면, 조조는 어렸을 때 원소와 신부를 납치했대. 서서는 어려서 의협심이 강했고, 원술은 모든 이의 아버지이고...","예를 들면, 조조는 어렸을 때 원소와 신부를 납치했대. 서서는 어려서 의협심이 강했고, 원술은 모든 이의 아버지이고...","","","",},
        [2252] = {35,"장성채","하하, 명장들에게 이런 재밌는 얘기가 있었네요. 어디서 본 거예요?","하하, 명장들에게 이런 재밌는 얘기가 있었네요. 어디서 본 거예요?","","","",},
        [2253] = {35,"관은병","명장 던전에서! 명장 던전에 도전하면 더 많은 명장을 만나 뒷이야기를 알 수 있지.","명장 던전에서! 명장 던전에 도전하면 더 많은 명장을 만나 뒷이야기를 알 수 있지.","","","",},
        [2254] = {35,"장성채","명장 던전에 가서 도전해 봐요.","명장 던전에 가서 도전해 봐요.","","","",},
        [2255] = {36,"관은병","처음으로 명장 던전을 돌파하면 많은 금화 보상을 획득할 수 있는데, 금화는 정말 좋은 물건이에요.","처음으로 명장 던전을 돌파하면 많은 금화 보상을 획득할 수 있는데, 금화는 정말 좋은 물건이에요.","장성채","","",},
        [2256] = {36,"장성채","명장 던전에 도전하면 장혼도 얻을 수 있어요. 장혼은 장수상점에서 높은품질의 장수로으로 교환할수 있어요!","명장 던전에 도전하면 장혼도 얻을 수 있어요. 장혼은 장수상점에서 높은품질의 장수로으로 교환할수 있어요!","","","",},
        [2257] = {36,"관은병","매일 명장 던전에 도전하는 것 잊지 마세요!","매일 명장 던전에 도전하는 것 잊지 마세요!","","","",},
        [2258] = {37,"주인공","성채, 많은 남색장수와 녹색장수를 얻었는데, 등급이 너무 낮아 쓸모가 없는 것 같아요!","성채, 많은 남색장수와 녹색장수를 얻었는데, 등급이 너무 낮아 쓸모가 없는 것 같아요!","장성채","","",},
        [2259] = {37,"장성채","녹색 이상의 장수는 모두 #분해#할 수 있어요.  유용한 재료를 얻을수 있어요.","녹색 이상의 장수는 모두 #분해#할 수 있어요.  유용한 재료를 얻을수 있어요.","","","",},
        [2260] = {37,"장성채","어떻게 장수를 #분해#하는지 알려줄게요.","어떻게 장수를 #분해#하는지 알려줄게요.","","","",},
        [2261] = {38,"장성채","장수를 회수해서 얻은 장혼으로 장수상점에서 장수와 필요한 재료를 얻을수 있어요.","장수를 회수해서 얻은 장혼으로 장수상점에서 장수와 필요한 재료를 얻을수 있어요.","주인공","","",},
        [2262] = {38,"주인공","봤어요! 장혼으로 장수, 돌파단과 장수 경험을 구매할 수 있어요. 하하, 남색장수와 녹색장수를 전부 회수해서 장혼으로 바꾸겠어요!","봤어요! 장혼으로 장수, 돌파단과 장수 경험을 구매할 수 있어요. 하하, 남색장수와 녹색장수를 전부 회수해서 장혼으로 바꾸겠어요!","","","",},
        [2263] = {38,"장성채","육성 한 자색장수와 황색장수가 필요 없다면 복원할 수 있고, 사용한 육성재료도 돌려줘요.","육성 한 자색장수와 황색장수가 필요 없다면 복원할 수 있고, 사용한 육성재료도 돌려줘요.","","","",},
        [2264] = {38,"주인공","와, 정말 좋네요! 육성에 사용한 아이템들을 돌려 주네요.","와, 정말 좋네요! 육성에 사용한 아이템들을 돌려 주네요.","","","",},
        [2265] = {38,"장성채","맞아요! 장수 외에 장비, 신병 모두 분해복원 할 수 있어요.  기억해 주세요.","맞아요! 장수 외에 장비, 신병 모두 분해복원 할 수 있어요.  기억해 주세요.","","","",},
        [2266] = {38,"주인공","성채, 고마워요. 꼭 기억할게요!","성채, 고마워요. 꼭 기억할게요!","","","",},
        [2267] = {39,"관은병","서남쪽 관문에 #남만 침입#이 자주 있어요. 정말 교활해서 항상 우리가 메인 전투와 소탕에 있을 때 침입하지요.","서남쪽 관문에 #남만 침입#이 자주 있어요. 정말 교활해서 항상 우리가 메인 전투와 소탕에 있을 때 침입하지요.","주인공","","",},
        [2268] = {39,"주인공","세상에, 남만이 이렇게 교활하다니!","세상에, 남만이 이렇게 교활하다니!","","","",},
        [2269] = {39,"관은병","남만을 물리치는 것이 좋은 전투 기회가 될 수 있어요! 남만을 물리치면 신병 재료를 획득할 수 있거든요.","남만을 물리치는 것이 좋은 전투 기회가 될 수 있어요! 남만을 물리치면 신병 재료를 획득할 수 있거든요.","","","",},
        [2270] = {39,"주인공","신병이요? 신병이 뭐죠? 대단할 것 같은데요.","신병이요? 신병이 뭐죠? 대단할 것 같은데요.","","","",},
        [2271] = {39,"관은병","모든 장수마다 자기의 #전용신병#이 있어요. 예를 들면, 우리 아버지 관우의 신병은 청룡언월도이고, 대교의 신병은 연화유산이에요.","모든 장수마다 자기의 #전용신병#이 있어요. 예를 들면, 우리 아버지 관우의 신병은 청룡언월도이고, 대교의 신병은 연화유산이에요.","","","",},
        [2272] = {39,"관은병","남만을 여러 번 처치하면 신병상점에 가서 신병을 구매할 수 있어요.","남만을 여러 번 처치하면 신병상점에 가서 신병을 구매할 수 있어요.","","","",},
        [2273] = {39,"주인공","정말 신병을 빨리 얻고 싶네요! 남만을 물리치러 어서 가요!","정말 신병을 빨리 얻고 싶네요! 남만을 물리치러 어서 가요!","","","",},
        [2274] = {40,"장성채","와, 58레벨 이예요! 58레벨이 되면 #영혼#을 장착할 수 있어요!","와, 58레벨 이예요! 58레벨이 되면 #영혼#을 장착할 수 있어요!","주인공","","",},
        [2275] = {40,"주인공","#영혼#? 영혼은 어떻게 사용하죠?","#영혼#? 영혼은 어떻게 사용하죠?","","","",},
        [2276] = {40,"장성채","영혼은 장수의 특성을 강화할 수 있어요. 예를 들면, 관은병은 공격이에요. 관은병 영혼을 장착하면 공격이 증가해요. 초선은 분노감소예요. 초선 영혼을 장착하면 분노를 감소시킬 수 있어요.","영혼은 장수의 특성을 강화할 수 있어요. 예를 들면, 관은병은 공격이에요. 관은병 영혼을 장착하면 공격이 증가해요. 초선은 분노감소예요. 초선 영혼을 장착하면 분노를 감소시킬 수 있어요.","","","",},
        [2277] = {40,"장성채","간단하게 말하자면, 영혼을 다양하게 조합하면 더욱더 강해질수 있어요.","간단하게 말하자면, 영혼을 다양하게 조합하면 더욱더 강해질수 있어요.","","","",},
        [2278] = {40,"주인공","영혼도 정말 좋은 거군요.","영혼도 정말 좋은 거군요.","","","",},
        [2279] = {40,"장성채","영혼을 한번 장착해보세요.","영혼을 한번 장착해보세요.","","","",},
        [2280] = {41,"장성채","대단해요! 관은병 영혼을 장착하니 공격이 바로 1000점 상승했어요!","대단해요! 관은병 영혼을 장착하니 공격이 바로 1000점 상승했어요!","주인공","","",},
        [2281] = {41,"주인공","자색장수 영혼이 이 정도라면, 황색장수 영혼은 더 대단하겠군요?","자색장수 영혼이 이 정도라면, 황색장수 영혼은 더 대단하겠군요?","","","",},
        [2282] = {41,"장성채","정말 현명하세요! 영혼의 품질이 높을수록, 특성도 더 강해져요.","정말 현명하세요! 영혼의 품질이 높을수록, 특성도 더 강해져요.","","","",},
        [2283] = {41,"주인공","그럼 영혼을 어떻게 획득하죠?","그럼 영혼을 어떻게 획득하죠?","","","",},
        [2284] = {41,"장성채","영혼과 관직인은 입찰을 통해 획득할 수 있어요! 군단보스, 군단시련, 군단 퀴즈 등에서 영혼을 얻을수 있어요.","영혼과 관직인은 입찰을 통해 획득할 수 있어요! 군단보스, 군단시련, 군단 퀴즈 등에서 영혼을 얻을수 있어요.","","","",},
        [2285] = {41,"주인공","아, 군단 이벤트와 입찰을 많이 참여하면 영혼을 얻을 수 있군요.","아, 군단 이벤트와 입찰을 많이 참여하면 영혼을 얻을 수 있군요.","","","",},
        [2286] = {41,"장성채","맞아요. 영혼 도감을 보고 어떻게 영혼을 조합하면 더 강해지는지 연구해보세요.","맞아요. 영혼 도감을 보고 어떻게 영혼을 조합하면 더 강해지는지 연구해보세요.","","","",},
        [2287] = {41,"주인공","알겠어요. 고마워요, 성채!","알겠어요. 고마워요, 성채!","","","",},
        [2288] = {41,"장성채","별말씀을요! 장성채는 영원히 함께 할거예요!","별말씀을요! 장성채는 영원히 함께 할거예요!","","","",},
        [2289] = {42,"관은병","이미 70레벨이 되어, 실력으로 천하에 위세를 떨치고, 전투력이 높아져서 펫도 함께 출전 싶어해요!","이미 70레벨이 되어, 실력으로 천하에 위세를 떨치고, 전투력이 높아져서 펫도 함께 출전 싶어해요!","주인공","","",},
        [2290] = {42,"주인공","펫이라면 청룡, 백호, 주작, 현무 같은 것을 말하나요?","펫이라면 청룡, 백호, 주작, 현무 같은 것을 말하나요?","","","",},
        [2291] = {42,"관은병","맞아요. 우리 땅에는 예로부터 펫에 대한 이야기가 전해지고 있어요. 진정한 강자만이 그들의 모습을 볼 수 있죠. 우리 아버지 관우는 청룡의 비호를 받은 적이 있어요.","맞아요. 우리 땅에는 예로부터 펫에 대한 이야기가 전해지고 있어요. 진정한 강자만이 그들의 모습을 볼 수 있죠. 우리 아버지 관우는 청룡의 비호를 받은 적이 있어요.","","","",},
        [2292] = {42,"주인공","청룡의 비호를 받아 그렇게 강했던 거군! 나도 펫을 구해야겠어.","청룡의 비호를 받아 그렇게 강했던 거군! 나도 펫을 구해야겠어.","","","",},
        [2293] = {42,"관은병","서두를 필요 없어요. 우리가 이미 첫 번째 펫을 준비해 놓았거든요. 저와 같이 가서 봐요.","서두를 필요 없어요. 우리가 이미 첫 번째 펫을 준비해 놓았거든요. 저와 같이 가서 봐요.","","","",},
        [2294] = {43,"관은병","이것이 첫 번째 펫이에요!","이것이 첫 번째 펫이에요!","주인공","","",},
        [2295] = {43,"주인공","팬더 부자의 조합이군요! 아빠 팬더는 싸움을 잘할 것 같고, 아기 팬더는 너무 귀엽네요!","팬더 부자의 조합이군요! 아빠 팬더는 싸움을 잘할 것 같고, 아기 팬더는 너무 귀엽네요!","","","",},
        [2296] = {43,"관은병","펫은 전투 시 7번째 위치에 놓을 수 있어요. 강한 장수를 한 명 더한 것과 같죠.","펫은 전투 시 7번째 위치에 놓을 수 있어요. 강한 장수를 한 명 더한 것과 같죠.","","","",},
        [2297] = {43,"주인공","은병, 팬더는 남색품질인데 더 강한 펫을 얻고 싶으면 어떻게 얻죠?","은병, 팬더는 남색품질인데 더 강한 펫을 얻고 싶으면 어떻게 얻죠?","","","",},
        [2298] = {43,"관은병","펫 상점에서 #수혼#으로 펫을 교환할 수 있어요.","펫 상점에서 #수혼#으로 펫을 교환할 수 있어요.","","","",},
        [2299] = {43,"주인공","그럼 #수혼#은 어떻게 얻어요?","그럼 #수혼#은 어떻게 얻어요?","","","",},
        [2300] = {43,"관은병","도원에서 수혼을 생산할 수 있어요. 제가 보여드릴게요.","도원에서 수혼을 생산할 수 있어요. 제가 보여드릴게요.","","","",},
        [2301] = {44,"관은병","여기가 도원이에요.","여기가 도원이에요.","주인공","","",},
        [2302] = {44,"관은병","도원에 나무 한 그루가 있어요. 천지의 영기를 흡수해서 성장하죠. 도원 레벨이 높을수록 생산하는 수혼도 많아져요.","도원에 나무 한 그루가 있어요. 천지의 영기를 흡수해서 성장하죠. 도원 레벨이 높을수록 생산하는 수혼도 많아져요.","","","",},
        [2303] = {44,"주인공","알겠어요! 도원을 레벨업하면 더 많은 수혼을 얻을 수 있겠군요! 수혼이 있으면 펫 상점에서 고급 펫으로 교환할 수 있고요.","알겠어요! 도원을 레벨업하면 더 많은 수혼을 얻을 수 있겠군요! 수혼이 있으면 펫 상점에서 고급 펫으로 교환할 수 있고요.","","","",},
        [2304] = {44,"관은병","좋아요! 도원을 성장 시키는 것을 꼭 기억하세요!","좋아요! 도원을 성장 시키는 것을 꼭 기억하세요!","","","",},
        [2305] = {44,"주인공","당연하죠. 세계에서 가장 강한 펫을 갖고 싶어요!","당연하죠. 세계에서 가장 강한 펫을 갖고 싶어요!","","","",},
        [2306] = {44,"관은병","힘내세요. 삼국을 차치하는 일이 얼마 안 남았어요!","힘내세요. 삼국을 차치하는 일이 얼마 안 남았어요!","","","",},
        [2307] = {2201,"조조","유비가 서주에 주둔하여 다스리고 있고, 여포는 패잔병이 투항하여 소패를 내주었소. 두 사람이 군대를 이끌고 오면 큰일인데 무슨 묘책이 있소?","유비가 서주에 주둔하여 다스리고 있고, 여포는 패잔병이 투항하여 소패를 내주었소. 두 사람이 군대를 이끌고 오면 큰일인데 무슨 묘책이 있소?","순욱","","",},
        [2308] = {2201,"순욱","허도가 안정된 지 얼마 되지 않아 다시 군대를 쓸 수 없습니다. 일명 '이호경식'이라는 계책이 있는데 유비가 여포를 죽이게 만드는 겁니다.","허도가 안정된 지 얼마 되지 않아 다시 군대를 쓸 수 없습니다. 일명 '이호경식'이라는 계책이 있는데 유비가 여포를 죽이게 만드는 겁니다.","","","",},
        [2309] = {2201,"조조","방금 정탐군이 왔는데 이 계획은 이미 유비 때문에 와해되었고, 여포도 죽이지 않았다고 하오.","방금 정탐군이 왔는데 이 계획은 이미 유비 때문에 와해되었고, 여포도 죽이지 않았다고 하오.","","","",},
        [2310] = {2201,"순욱","'구호탄랑'이라는 계책이 또 있습니다. 유비에게는 원술을 치라고 하고, 원술에게 사람을 보내 유비가 남군을 친다고 흘리십시오. 원술이 분노해서 유비를 칠 것입니다. 둘이 싸우면 여포가 딴마음을 가질 겁니다.","'구호탄랑'이라는 계책이 또 있습니다. 유비에게는 원술을 치라고 하고, 원술에게 사람을 보내 유비가 남군을 친다고 흘리십시오. 원술이 분노해서 유비를 칠 것입니다. 둘이 싸우면 여포가 딴마음을 가질 겁니다.","","","",},
        [2311] = {2201,"조조","좋은 계책이오! 그대로 따르겠소!","좋은 계책이오! 그대로 따르겠소!","","","",},
        [2312] = {2201,"주인공","큰일이군. 조조가 형님에게 맞서려고 하는구나! 조조의 간계에 빠지지 않도록 형님을 찾아가야겠어!","큰일이군. 조조가 맞서려고 하는구나! 조조의 간계에 빠지지 않도록 유비를 찾아가야겠어!","","","",},
        [2313] = {2202,"원술","짚신이나 삼던 자가 서주를 차지하고 제후들과 어깨를 나란히 하고 있다. 내가 그를 없애려고 했는데 오히려 나를 치려고 하다니!","짚신이나 삼던 자가 서주를 차지하고 제후들과 어깨를 나란히 하고 있다. 내가 그를 없애려고 했는데 오히려 나를 치려고 하다니!","유비","","",},
        [2314] = {2202,"유비","천자의 조서를 받들어 토벌하려고 하는데 감히 맞서다니 죽음을 면치 못할 것이다!","천자의 조서를 받들어 토벌하려고 하는데 감히 맞서다니 죽음을 면치 못할 것이다!","","","",},
        [2315] = {2202,"주인공","형님, 원술이 저렇게 날뛰니 제가 가서 기를 꺾고 놓고 올게요!","원술이 저렇게 날뛰니 제가 가서 기를 꺾고 놓고 올게요!","","","",},
        [2316] = {2203,"주인공","형님, 원술을 공격하지 마세요. 이것은 조조의 계략입니다.","형님, 원술을 공격하지 마세요. 이것은 조조의 계략입니다.","","","",},
        [2317] = {2203,"유비","아무리 계략이라도 왕명을 거스를 순 없다. 아우들 중 누가 성을 지킬 수 있느냐?","아무리 계략이라도 왕명을 거스를 순 없다. 아우들 중 누가 성을 지킬 수 있느냐?","","","",},
        [2318] = {2203,"장비","제가 지키겠습니다.","제가 지키겠습니다.","","","",},
        [2319] = {2203,"유비","넌 안 된다. 첫째, 술을 먹으면 힘이 세져서 병사들을 때리지 않느냐. 둘째, 모든 일을 쉽게 받아들이지 않으니 안심할 수가 없구나.","넌 안 된다. 첫째, 술을 먹으면 힘이 세져서 병사들을 때리지 않느냐. 둘째, 모든 일을 쉽게 받아들이지 않으니 안심할 수가 없구나.","","","",},
        [2320] = {2203,"장비","앞으로 술도 안 마시고, 군사도 때리지 않고 다른 사람의 말도 잘 들을게요.","앞으로 술도 안 마시고, 군사도 때리지 않고 다른 사람의 말도 잘 들을게요.","","","",},
        [2321] = {2203,"주인공","셋째 형님, 성을 잘 지키세요. 저는 원술과 싸우러 갈게요.","셋째 형님, 성을 잘 지키세요. 저는 원술과 싸우러 갈게요.","","","",},
        [2322] = {2204,"주인공","여포는 정말 나쁜 사람이에요. 우리가 출병한 사이 서주를 빼앗았잖아요.","여포는 정말 나쁜 사람이에요. 우리가 출병한 사이 서주를 빼앗았잖아요.","유비","","",},
        [2323] = {2204,"유비","여포는 이미 부인 두 명과 가족들을 송환하였고 양식과 비단도 보내주었다. 이 일은 셋째의 잘못이고 난 이미 여포와 화해하였단다. 여포는 서추를 차지하고 나는 소패를 차지하면 된다.","여포는 이미 부인 두 명과 가족들을 송환하였고 양식과 비단도 보내주었다. 이 일은 셋째의 잘못이고 난 이미 여포와 화해하였단다. 여포는 서추를 차지하고 나는 소패를 차지하면 된다.","","","",},
        [2324] = {2204,"유비","지금 네가 여포와 싸우면 조조의 계략에 말려들어 조조와 원술이 득을 보게 될 거다.","지금 네가 여포와 싸우면 조조의 계략에 말려들어 조조와 원술이 득을 보게 될 거다.","","","",},
        [2325] = {2204,"주인공","그렇지만 정말 참을 수가 없어요. 형님, 제가 여포를 찾아 정의가 무엇인지 보여줄게요.","그렇지만 정말 참을 수가 없어요. 형님, 제가 여포를 찾아 정의가 무엇인지 보여줄게요.","","","",},
        [2326] = {2205,"기령","유비는 어디 있느냐? 원술의 명을 받들어 십만 병사를 이끌고 유비를 잡으러 왔다!","유비는 어디 있느냐? 원술의 명을 받들어 십만 병사를 이끌고 유비를 잡으러 왔다!","주인공","","",},
        [2327] = {2205,"주인공","기령, 무슨 경거망동입니까! 우리 형님은 황건적을 토벌한 공신인데 형님을 해치려고 하는 것이오?","기령, 무슨 경거망동입니까! 우리 형님은 황건적을 토벌한 공신인데 형님을 해치려고 하는 것이오?","","","",},
        [2328] = {2205,"여포","내 말 좀 들어보시오!","내 말 좀 들어보시오!","","","",},
        [2329] = {2205,"주인공","서주를 빼았다니, 죽어야 할 자는 바로 너다!","서주를 빼았다니, 죽어야 할 자는 바로 너다!","","","",},
        [2330] = {2206,"여포","하하, 화가 단단히 났군. 두 사람을 화해시키려고 왔소.","하하, 화가 단단히 났군. 두 사람을 화해시키려고 왔소.","주인공","","",},
        [2331] = {2206,"여포","창을 원문에서 백 오십 보 떨어진 곳에 두고 활을 쏘아 창의 곁가지를 맞추면 양 진영은 철수하고, 맞추지 못하면 각자 진영으로 돌아가 싸울 준비를 하시오.","창을 원문에서 백 오십 보 떨어진 곳에 두고 활을 쏘아 창의 곁가지를 맞추면 양 진영은 철수하고, 맞추지 못하면 각자 진영으로 돌아가 싸울 준비를 하시오.","","","",},
        [2332] = {2206,"여포","얏! (여포가 쏜 화살이 방천화극의 곁가지를 맞추었다.) 하하하, 이제 철수하시오.","얏! (여포가 쏜 화살이 방천화극의 곁가지를 맞추었다.) 하하하, 이제 철수하시오.","","","",},
        [2333] = {2206,"주인공","정말 하늘의 별도 쏘아 떨어뜨릴 기세네요! 여포가 화해를 권했으니 이제 됐어요. 기령, 또 찾아오면 그땐 가만두지 않겠어요.","정말 하늘의 별도 쏘아 떨어뜨릴 기세네요! 여포가 화해를 권했으니 이제 됐어요. 기령, 또 찾아오면 그땐 가만두지 않겠어요.","","","",},
        [2334] = {2206,"기령","즉시 철수하겠다!","즉시 철수하겠다!","","","",},
        [2335] = {2206,"여포","현덕, 이 명장령을 사과의 의미로 주고, 서주도 돌려주겠소!","현덕, 이 명장령을 사과의 의미로 주고, 서주도 돌려주겠소!","","","",},
        [2336] = {2206,"유비","아니오. 이미 넘겨준 지 오래 되었고, 소패에 주둔하면서 뿔의 형세를 이루어 적을 방어하는 것이 낫습니다.","아니오. 이미 넘겨준 지 오래 되었고, 소패에 주둔하면서 뿔의 형세를 이루어 적을 방어하는 것이 낫습니다.","","","",},
        [2337] = {2206,"유비","넷째야. 이 명장령은 네가 가져라.","넷째야. 이 명장령은 네가 가져라.","","","",},
        [2338] = {2206,"주인공","감사해요. 제가 있는 한 다시는 원술이 쳐들어오지 못할 거예요!","감사해요. 제가 있는 한 다시는 원술이 쳐들어오지 못할 거예요!","","","",},
        [2339] = {2301,"기령","여포가 원문에 창을 맞춰서 철수했습니다. 저와는 상관없는 일입니다.","여포가 원문에 창을 맞춰서 철수했습니다. 저와는 상관없는 일입니다.","원술","","",},
        [2340] = {2301,"원술","여포에게 그렇게 많은 식량과 옷감을 보냈는데 챙기기만 하고 오히려 유비를 두둔하다니!","여포에게 그렇게 많은 식량과 옷감을 보냈는데 챙기기만 하고 오히려 유비를 두둔하다니!","","","",},
        [2341] = {2301,"원술","여봐라, 군대를 일으켜 직접 유비를 정벌하고 여포를 토벌하겠다.","여봐라, 군대를 일으켜 직접 유비를 정벌하고 여포를 토벌하겠다.","","","",},
        [2342] = {2301,"기령","여포는 매우 용맹하고 서주도 차지하였습니다. 여포와 유비가 협력하면 더욱 복잡해집니다.","여포는 매우 용맹하고 서주도 차지하였습니다. 여포와 유비가 협력하면 더욱 복잡해집니다.","","","",},
        [2343] = {2301,"기령","여포에게 혼기가 찬 딸이 있다고 들었는데, 아드남이 있으시니 사람을 보내 혼인을 청하시지요.","여포에게 혼기가 찬 딸이 있다고 들었는데, 아드남이 있으시니 사람을 보내 혼인을 청하시지요.","","","",},
        [2344] = {2301,"원술","좋은 생각이다. 내가 여포와 사돈이 되면 천하에 나를 막을 자가 누가 있겠는가. 황제가 될 날도 멀지 않았구나.","좋은 생각이다. 내가 여포와 사돈이 되면 천하에 나를 막을 자가 누가 있겠는가. 황제가 될 날도 멀지 않았구나.","","","",},
        [2345] = {2302,"여포","원술이 사람을 보내 혼인을 청하였소. 어떻게 생각하시오?","원술이 사람을 보내 혼인을 청하였소. 어떻게 생각하시오?","엄씨","","",},
        [2346] = {2302,"엄씨","원술은 회남에 있으면서 병사와 식량이 풍부해 조만간 황제가 될 거예요. 아들이 하나뿐이니, 우리 딸이 시집을 가면 나중에 황후가 되겠네요.","원술은 회남에 있으면서 병사와 식량이 풍부해 조만간 황제가 될 거예요. 아들이 하나뿐이니, 우리 딸이 시집을 가면 나중에 황후가 되겠네요.","","","",},
        [2347] = {2302,"진궁","지금 제후들이 서로 패권을 다투는데, 협력하시면 분명 제후들이 질투할 것입니다. 복병을 두어 빼앗길 수 있으니, 영저를 수춘으로 보내 별관에 먼저 머물게 하시는 것이 낫겠습니다.","지금 제후들이 서로 패권을 다투는데, 협력하시면 분명 제후들이 질투할 것입니다. 복병을 두어 빼앗길 수 있으니, 영저를 수춘으로 보내 별관에 먼저 머물게 하시는 것이 낫겠습니다.","","","",},
        [2348] = {2302,"여포","하하, 그럼 여령저를 시집보내겠소. 여봐라, 여포가 사돈이 되길 원한다고 원술에게 전하거라.","하하, 그럼 여령저를 시집보내겠소. 여봐라, 여포가 사돈이 되길 원한다고 원술에게 전하거라.","","","",},
        [2349] = {2303,"진규","장군을 문상하기 위해 왔습니다.","장군을 문상하기 위해 왔습니다.","여포","","",},
        [2350] = {2303,"여포","무슨 말씀이시오?","무슨 말씀이시오?","","","",},
        [2351] = {2303,"진규","원술이 스스로 황제라고 하는 것은 반역과 같습니다. 그런 자와 한집안이 되면 천하가 가만있지 않을 것입니다.","원술이 스스로 황제라고 하는 것은 반역과 같습니다. 그런 자와 한집안이 되면 천하가 가만있지 않을 것입니다.","","","",},
        [2352] = {2303,"주인공","이제야 도착했네요! 여포, 딸을 원술의 아들에게 시집보낸다고 들었는데, 절대 안 돼요!","이제야 도착했네요! 여포, 딸을 원술의 아들에게 시집보낸다고 들었는데, 절대 안 돼요!","","","",},
        [2353] = {2303,"여포","왜 그런 말을 하시오?","왜 그런 말을 하시오?","","","",},
        [2354] = {2303,"주인공","이각과 곽사 이후 원술에게 의탁했을 때 그가 당신을 배척했잖아요. 원술 같은 사람을 따르면 큰 화를 당할 거예요.","이각과 곽사 이후 원술에게 의탁했을 때 그가 당신을 배척했잖아요. 원술 같은 사람을 따르면 큰 화를 당할 거예요.","","","",},
        [2355] = {2303,"여포","당신을 믿겠소! 진궁 내가 잘못 생각했다! 여봐라, 어서 령저를 데리고 와라!","당신을 믿겠소! 진궁 내가 잘못 생각했다! 여봐라, 어서 령저를 데리고 와라!","","","",},
        [2356] = {2303,"주인공","일이 급박하니 제가 갈게요!","일이 급박하니 제가 갈게요!","","","",},
        [2357] = {2304,"초선","봉선, 그분이 우리 령저를 데리고 왔어요!","봉선, 그분이 우리 령저를 데리고 왔어요!","주인공","","",},
        [2358] = {2304,"주인공","초선 낭자가 부탁한 일이니 당연히 열심히 해야죠!","초선 낭자가 부탁한 일이니 당연히 열심히 해야죠!","","","",},
        [2359] = {2304,"초선","봉선, 결혼은 부모의 중매로 이루어지지만, 령저의 의견도 중요해요. 본인이 원하지 않으면 평생을 그르치지요.","봉선, 결혼은 부모의 중매로 이루어지지만, 령저의 의견도 중요해요. 본인이 원하지 않으면 평생을 그르치지요.","","","",},
        [2360] = {2304,"여포","초선의 말이 맞소.","초선의 말이 맞소.","","","",},
        [2361] = {2304,"주인공","여포, 당대의 호걸이 왜 자신의 딸로 도박을 하세요?","여포, 당대의 호걸이 왜 자신의 딸로 도박을 하세요?","","","",},
        [2362] = {2304,"여포","맞소! 내가 누구를 두려워한단 말이오! 내 딸에게 본인이 원하는 결혼을 시키겠소!","맞소! 내가 누구를 두려워한단 말이오! 내 딸에게 본인이 원하는 결혼을 시키겠소!","","","",},
        [2363] = {2305,"여포","이 명장령을 받으시오. 당신이 아니었다면 큰일이 일어날뻔했소!","이 명장령을 받으시오. 당신이 아니었다면 큰일이 일어날뻔했소!","주인공","","",},
        [2364] = {2305,"주인공","좋아요! 감사합니다!","좋아요! 감사합니다!","","","",},
        [2365] = {2305,"여령저","아버지, 원술의 아들에게 시집가고 싶지 않아요. 저는 영웅에게 시집가겠어요!","아버지, 원술의 아들에게 시집가고 싶지 않아요. 저는 영웅에게 시집가겠어요!","","","",},
        [2366] = {2305,"여포","령저야, 다시는 네게 결혼을 강요하지 않으마. 어느 영웅에게 시집가고 싶은 것이냐?","령저야, 다시는 네게 결혼을 강요하지 않으마. 어느 영웅에게 시집가고 싶은 것이냐?","","","",},
        [2367] = {2305,"여령저","천자가 젊고 무예가 뛰어난 한 분을 양위장군에 봉했다고 들었어요. 그와 결혼하고 싶어요!","천자가 젊고 무예가 뛰어난 한 분을 양위장군에 봉했다고 들었어요. 그와 결혼하고 싶어요!","","","",},
        [2368] = {2305,"주인공","나에게 시집오겠다고요? (그러면 초선이 나의 장모님이 되는 건데, 그건 안 되죠!)","나에게 시집오겠다고요? (그러면 초선이 나의 장모님이 되는 건데, 그건 안 되죠!)","","","",},
        [2369] = {2305,"주인공","뜻은 고맙지만  아직은 결혼 생각이 없어요. 그럼 이만!","뜻은 고맙지만  아직은 결혼 생각이 없어요. 그럼 이만!","","","",},
        [2370] = {2305,"여령저","양위장군, 가지 마요. 당신이 아무리 먼 곳으로 가도 전 꼭 당신에게 시집갈 거라고요!","양위장군, 가지 마요. 당신이 아무리 먼 곳으로 가도 전 꼭 당신에게 시집갈 거라고요!","","","",},
        [2371] = {2401,"여포","내가 원문에서 극을 쏘아 큰 어려움에서 구해주었는데, 어째서 내 말 백 오십 필을 빼앗았소?","내가 원문에서 극을 쏘아 큰 어려움에서 구해주었는데, 어째서 내 말 백 오십 필을 빼앗았소?","유비","","",},
        [2372] = {2401,"유비","말이 부족하여 사람을 시켜 사 오게 했는데 어찌 말을 빼앗았다고 하십니까?","말이 부족하여 사람을 시켜 사 오게 했는데 어찌 말을 빼앗았다고 하십니까?","","","",},
        [2373] = {2401,"장비","여포, 내가 당신 말을 뺏었소. 왜 우리 형님에게 누명을 씌우시오?","여포, 내가 당신 말을 뺏었소. 왜 우리 형님에게 누명을 씌우시오?","","","",},
        [2374] = {2401,"여포","네가 감히 나를 여러 번 무시하는구나!","네가 감히 나를 여러 번 무시하는구나!","","","",},
        [2375] = {2401,"장비","말을 빼앗아서 화가 나면, 서주를 빼앗긴 우리 형은 어떻겠느냐?","말을 빼앗아서 화가 나면, 서주를 빼앗긴 우리 형은 어떻겠느냐?","","","",},
        [2376] = {2401,"여포","내가 곧 군대를 이끌고 와서 말을 다시 가져가겠다!","내가 곧 군대를 이끌고 와서 말을 다시 가져가겠다!","","","",},
        [2377] = {2402,"관은병","큰일 났어요! 여포가 군대를 이끌고 소패를 공격해요! 여포의 병마가 아버지보다 몇 배는 많은데, 어쩌죠?","큰일 났어요! 여포가 군대를 이끌고 소패를 공격해요! 여포의 병마가 아버지보다 몇 배는 많은데, 어쩌죠?","주인공","","",},
        [2378] = {2402,"장성채","우리 아버지가 술 마시고 홧김에 또 일을 저질렀나 봐요! 어떻게 해야 하죠?","우리 아버지가 술 마시고 홧김에 또 일을 저질렀나 봐요! 어떻게 해야 하죠?","","","",},
        [2379] = {2402,"주인공","성채, 은병, 조급해하지 마요. 내가 포위를 뚫어 볼게요!","성채, 은병, 조급해하지 마요. 내가 포위를 뚫어 볼게요!","","","",},
        [2380] = {2402,"주인공","여포, 내가 당신을 도와 말을 찾아줄 테니, 철수하세요. 찾지 못하면 그때 출병하시죠!","여포, 내가 당신을 도와 말을 찾아줄 테니, 철수하세요. 찾지 못하면 그때 출병하시죠!","","","",},
        [2381] = {2402,"여포","좋소,  그렇게 하겠소.","좋소,  그렇게 하겠소.","","","",},
        [2382] = {2403,"관우","넷째, 왜 다른 사람을 도와 형님을 공격하는 것이냐?","넷째, 왜 다른 사람을 도와 형님을 공격하는 것이냐?","주인공","","",},
        [2383] = {2403,"주인공","제게 다 생각이 있어요!","제게 다 생각이 있어요!","","","",},
        [2384] = {2404,"장비","만약 팔이 밖으로 굽어서 그 녀석을 도왔다간 널 가만두지 않을 거다!","만약 팔이 밖으로 굽어서 그 녀석을 도왔다간 널 가만두지 않을 거다!","주인공","","",},
        [2385] = {2404,"주인공","오해하지 마시고, 제 말을 끝까지 들어보세요.","오해하지 마시고, 제 말을 끝까지 들어보세요.","","","",},
        [2386] = {2404,"장비","누가 네 말을 듣겠느냐! 자! 내 창을 받아라!","누가 네 말을 듣겠느냐! 자! 내 창을 받아라!","","","",},
        [2387] = {2405,"주인공","형님들을 도우려고 온 거예요. 이건 완병지계라고요.","형님들을 도우려고 온 거예요. 이건 완병지계라고요.","장비","","",},
        [2388] = {2405,"주인공","여포 곁에는 항상 진궁이 있는데, 진궁은 줄곧 형님에겐 약했어요. 여포가 군대를 정비하는 동안 형님들을 호위할 테니 성을 나가시죠. 소패는 필요 없어요!","여포 곁에는 항상 진궁이 있는데, 진궁은 장비에게는 약하지. 여포가 군대를 정비하는 동안 호위할 테니 성을 나가시죠. 소패는 필요 없어요!","","","",},
        [2389] = {2405,"장비","그런 거였군. 내가 잘못했으니 너무 마음에 담아두지 말아라.","그런 거였군. 내가 잘못했으니 너무 마음에 담아두지 말아라.","","","",},
        [2390] = {2405,"장성채","아버지, 술을 너무 좋아해서 일을 그르치시니 이제 술 좀 그만 마시세요.","아버지, 술을 너무 좋아해서 일을 그르치시니 이제 술 좀 그만 마시세요.","","","",},
        [2391] = {2405,"주인공","성을 버리고 허도로 가서 조조에게 의탁해야겠어요.","성을 버리고 허도로 가서 조조에게 의탁해야겠어요.","","","",},
        [2392] = {2405,"주인공","조조가 원한을 품은 자는 여포예요. 전에 여포가 연주를 공격해 조조가 목숨을 잃을 뻔했죠.","조조가 원한을 품은 자는 여포예요. 전에 여포가 연주를 공격해 조조가 목숨을 잃을 뻔했죠.","","","",},
        [2393] = {2405,"유비","넷째 말에 일리가 있구나.","넷째 말에 일리가 있구나.","","","",},
        [2394] = {2405,"장비","내가 널 오해했어. 명장령을 줄 테니 화내지 말아라.","내가 널 오해했어. 명장령을 줄 테니 화내지 말아라.","","","",},
        [2395] = {2501,"주인공","형님들은 이미 허도에 무사히 도착했어요. 성채, 은병, 우리 오랫동안 유람도 못 했는데 좀 둘러볼까요?","이미 허도에 무사히 도착했어요. 성채, 은병, 우리 오랫동안 유람도 못 했는데 좀 둘러볼까요?","장성채","","",},
        [2396] = {2501,"장성채","계속 저희 아버지때문에 고생 많으셨어요.","계속 저희 아버지때문에 고생 많으셨어요.","","","",},
        [2397] = {2501,"관은병","우리 어디로 가죠? 중원에 오랫동안 머물렀으니, 좀 새로운 강동으로 가면 어떨까요?","우리 어디로 가죠? 중원에 오랫동안 머물렀으니, 좀 새로운 강동으로 가면 어떨까요?","","","",},
        [2398] = {2501,"주인공","손견이 죽고 손책은 원술을 따라 강남에 은거하면서 인재와 함께하고 있어요. 도움이 필요한지 가서 보죠.","손견이 죽고 손책은 원술을 따라 강남에 은거하면서 인재와 함께하고 있어요. 도움이 필요한지 가서 보죠.","","","",},
        [2399] = {2501,"관은병","좋아요. 강동 소패왕은 잘 생기고 패기도 넘치잖아요. 그를 찾아봐요.","좋아요. 강동 소패왕은 잘 생기고 패기도 넘치잖아요. 그를 찾아봐요.","","","",},
        [2400] = {2502,"주인공","손 형, 오랜만이에요. 왜 얼굴빛이 좋지 않죠?","손책, 오랜만이에요. 왜 얼굴빛이 좋지 않죠?","손책","","",},
        [2401] = {2502,"손책","원술은 변덕이 심해 말을 믿을 수가 없소. 나에게 구강태수의 자리를 약속했다가 측근으로 바꾸었다오. 후에 내게 여강태수 육강을 치게 하고 그 자리를 약속했는데 결국 자신의 심복 유훈을 임명했소. 너무 실망했다오.","원술은 변덕이 심해 말을 믿을 수가 없소. 나에게 구강태수의 자리를 약속했다가 측근으로 바꾸었다오. 후에 내게 여강태수 육강을 치게 하고 그 자리를 약속했는데 결국 자신의 심복 유훈을 임명했소. 너무 실망했다오.","","","",},
        [2402] = {2502,"손책","부친 손견은 영웅이셨는데, 나는 남에게 의지하며 부친의 뜻을 잇지 못하고 있구려.","부친 손견은 영웅이셨는데, 나는 남에게 의지하며 부친의 뜻을 잇지 못하고 있구려.","","","",},
        [2403] = {2502,"주인공","손책, 원술은 믿을 만한 사람이 못 돼요. 부친의 옛 부하를 모아 강동에서 유업을 잇는 게 좋겠어요.","손책, 원술은 믿을 만한 사람이 못 돼요. 부친의 옛 부하를 모아 강동에서 유업을 잇는 게 좋겠어요.","","","",},
        [2404] = {2502,"손책","원술이 날 놔주지 않을 거요.","원술이 날 놔주지 않을 거요.","","","",},
        [2405] = {2502,"주인공","원술은 황제가 되고 싶어 해요. 부친이 남긴 전국옥새를 원술에게 주면 동의할 거예요.","원술은 황제가 되고 싶어 해요. 부친이 남긴 전국옥새를 원술에게 주면 동의할 거예요.","","","",},
        [2406] = {2502,"손책","그러면 되겠군요! 외삼촌 단양태수 오경이 양주자사 유요에게 핍박받고 있는데, 이번 기회를 빌려야겠어요.","그러면 되겠군요! 외삼촌 단양태수 오경이 양주자사 유요에게 핍박받고 있는데, 이번 기회를 빌려야겠어요.","","","",},
        [2407] = {2502,"주인공","그가 동의하지 않으면 때려서라도 동의하게 할게요.","그가 동의하지 않으면 때려서라도 동의하게 할게요.","","","",},
        [2408] = {2503,"원술","나 원술은 모든 이의 아버지인데, 이런 식으로 대하면 안 되지.","나 원술은 모든 이의 아버지인데, 이런 식으로 대하면 안 되지.","주인공","","",},
        [2409] = {2503,"관은병"," 패장이 감히 자기가 모든 이의 아버지라고 하네요. 손 좀 봐주세요!"," 패장이 감히 자기가 모든 이의 아버지라고 하네요. 손 좀 봐주세요!","","","",},
        [2410] = {2503,"주인공","오늘 정말 아버지 소리가 나오도록 때려주겠다.","오늘 정말 아버지 소리가 나오도록 때려주겠다.","","","",},
        [2411] = {2504,"손책","이번에 강동으로 돌아온 것은 모두 그대 덕분이오. 정말 감사하오!","이번에 강동으로 돌아온 것은 모두 그대 덕분이오. 정말 감사하오!","주인공","","",},
        [2412] = {2504,"주인공","남을 돕는 것은 당연한 일이죠!","남을 돕는 것은 당연한 일이죠!","","","",},
        [2413] = {2504,"주인공","혼자서는 어려우니 강동에 기업을 다지려면 먼저 강동의 인재를 모으세요.","혼자서는 어려우니 강동에 기업을 다지려면 먼저 강동의 인재를 모으세요.","","","",},
        [2414] = {2504,"주인공","오군의 사대 명문 가문인 장주육고에서 장가의 장소를 수령으로 삼으세요. 장소는 하늘이 내린 인재입니다. 장소를 얻고 예로서 대하면 다른 가문은 자연스럽게 의탁할 거예요.","오군의 사대 명문 가문인 장주육고에서 장가의 장소를 수령으로 삼으세요. 장소는 하늘이 내린 인재입니다. 장소를 얻고 예로서 대하면 다른 가문은 자연스럽게 의탁할 거예요.","","","",},
        [2415] = {2505,"관은병","앞에 있는 저 사람은 누구죠? 멋있고 잘생긴 꽃미남이에요.","앞에 있는 저 사람은 누구죠? 멋있고 잘생긴 꽃미남이에요.","주인공","","",},
        [2416] = {2505,"주인공","정말 기개가 범상치 않네요. 누군지 아세요?","정말 기개가 범상치 않네요. 누군지 아세요?","","","",},
        [2417] = {2505,"손책","주유, 주공근이요. 수춘에 있을 때 알게 되었다오. 부친이 형주를 지나 동탁을 토벌할 때, 주유의 집에 머물렀소. 허물없이 지내 사람들이 총각지교라 불렀다오.","주유, 주공근이요. 수춘에 있을 때 알게 되었다오. 부친이 형주를 지나 동탁을 토벌할 때, 주유의 집에 머물렀소. 허물없이 지내 사람들이 총각지교라 불렀다오.","","","",},
        [2418] = {2505,"주인공","아, 명장 주유였군요! 잘됐네요. 그와 먼저 한 번 겨뤄봐야겠어요!","아, 명장 주유였군요! 잘됐네요. 그와 먼저 한 번 겨뤄봐야겠어요!","","","",},
        [2419] = {2506,"손책","주유, 이분은 내가 말했던 그분이요. 천자가 양위장군에 책봉했소. 덕분에 원술에게서 벗어나고 부친의 옛 부하도 찾았다네.","주유, 이분은 내가 말했던 그분이요. 천자가 양위장군에 책봉했소. 덕분에 원술에게서 벗어나고 부친의 옛 부하도 찾았다네.","주유","","",},
        [2420] = {2506,"주유","양위장군이었군요! 익히 들었지요.","양위장군이었군요! 익히 들었지요.","","","",},
        [2421] = {2506,"손책","우리 셋은 나이도 비슷하고 이렇게 만났으니 의형제를 맺는 것이 좋겠소.","우리 셋은 나이도 비슷하고 이렇게 만났으니 의형제를 맺는 것이 좋겠소.","","","",},
        [2422] = {2506,"주인공","저는 이미 유비, 관우, 장비와 의형제를 맺었어요. 두 분이 의형제를 맺으면 제가 증인이 돼드리죠.","저는 이미 유비, 관우, 장비와 의형제를 맺었어요. 두 분이 의형제를 맺으면 제가 증인이 돼드리죠.","","","",},
        [2423] = {2506,"손책","하늘을 위로하고, 땅을 아래에 두고 나 손책과 주유는 오늘 의형제를 맺습니다. 앞으로 서로에게 솔직하고 진심으로 대하겠습니다. 부친의 뜻을 이어 주유와 천하를 도모하겠습니다.","하늘을 위로하고, 땅을 아래에 두고 나 손책과 주유는 오늘 의형제를 맺습니다. 앞으로 서로에게 솔직하고 진심으로 대하겠습니다. 부친의 뜻을 이어 주유와 천하를 도모하겠습니다.","","","",},
        [2424] = {2506,"주유","하늘을 위로하고, 땅을 아래에 두고 나 주유와 손책은 오늘 의형제를 맺습니다. 앞으로 서로에게 솔직하고 진심으로 대하겠습니다. 주유는 사력을 다해 손책과 대사를 도모하겠습니다.","하늘을 위로하고, 땅을 아래에 두고 나 주유와 손책은 오늘 의형제를 맺습니다. 앞으로 서로에게 솔직하고 진심으로 대하겠습니다. 주유는 사력을 다해 손책과 대사를 도모하겠습니다.","","","",},
        [2425] = {2506,"손책","이 명장령을 우리의 증인이 되어 준 당신에게 주겠소.","이 명장령을 우리의 증인이 되어 준 당신에게 주겠소.","","","",},
        [2426] = {2601,"주인공","손형, 강동의 전투에서 어디를 먼저 공격할 건가요?","손형, 강동의 전투에서 어디를 먼저 공격할 건가요?","손책","","",},
        [2427] = {2601,"손책","곡아에 주둔하고 있는 양주자사 유요를 먼저 쳐야겠소.","곡아에 주둔하고 있는 양주자사 유요를 먼저 쳐야겠소.","","","",},
        [2428] = {2601,"주인공","양주자사가 왜 곡아에 있죠? 강을 사이에 두고 있잖아요.","양주자사가 왜 곡아에 있죠? 강을 사이에 두고 있잖아요.","","","",},
        [2429] = {2601,"손책","조정에서 유요를 양주자사로 임명했는데, 치소는 수춘에 있소. 하지만 수춘은 이미 원술이 차지했다오.","조정에서 유요를 양주자사로 임명했는데, 치소는 수춘에 있소. 하지만 수춘은 이미 원술이 차지했다오.","","","",},
        [2430] = {2601,"손책","외삼촌 단양태수 오경과 당형 손분이 유요를 도와 곡아에 치소를 건설했는데, 어느 정도 안정이 되자 유요가 외삼촌을 쫓아내고 땅을 차지했소.","외삼촌 단양태수 오경과 당형 손분이 유요를 도와 곡아에 치소를 건설했는데, 어느 정도 안정이 되자 유요가 외삼촌을 쫓아내고 땅을 차지했소.","","","",},
        [2431] = {2601,"주인공","정말 배은망덕하군요. 저도 돕겠어요!","정말 배은망덕하군요. 저도 돕겠어요!","","","",},
        [2432] = {2602,"주인공","양주자사 유요가 그런 사람이었군요.","양주자사 유요가 그런 사람이었군요.","장성채","","",},
        [2433] = {2602,"장성채","유요가 상대나 되겠어요!","유요가 상대나 되겠어요!","","","",},
        [2434] = {2602,"주인공","하지만 유요의 부하 중에 인물이 있어요. 공융을 도와 편지를 보냈던 태사자가 기억나네요. 그는 왜 유요를 따를까요!","하지만 유요의 부하 중에 인물이 있어요. 공융을 도와 편지를 보냈던 태사자가 기억나네요. 그는 왜 유요를 따를까요!","","","",},
        [2435] = {2602,"장성채","기억력이 좋네요! 태사자는 원숭이 같은 팔에 활을 잘 쏘는 신궁이죠. 유요와 동향이라 서로 돕나봐요.","기억력이 좋네요! 태사자는 원숭이 같은 팔에 활을 잘 쏘는 신궁이죠. 유요와 동향이라 서로 돕나봐요.","","","",},
        [2436] = {2602,"주인공","그렇군요. 태사자를 설득시킬 수 있다면 맹장을 얻게 될 텐데요.","그렇군요. 태사자를 설득시킬 수 있다면 맹장을 얻게 될 텐데요.","","","",},
        [2437] = {2603,"엄백호","누구냐? 나는 동오 덕왕 엄백호이다! 무섭지?","누구냐? 나는 동오 덕왕 엄백호이다! 무섭지?","주인공","","",},
        [2438] = {2603,"주인공","백호가 대수인가요? 저는 좌청룡, 우백호에 허리에는 소가 있고 용머리가 가슴에 있다고요! 누구도 막을 수 없죠!","백호가 대수인가요? 저는 좌청룡, 우백호에 허리에는 소가 있고 용머리가 가슴에 있다고요! 누구도 막을 수 없죠!","","","",},
        [2439] = {2603,"엄백호","젊은 친구가 허풍이 너무 세군!","젊은 친구가 허풍이 너무 세군!","","","",},
        [2440] = {2603,"장성채","엄백호는 산적 출신으로 스스로 동오 덕왕에 봉한 후에 오군을 점령했어요. 그와 싸울 때 각별히 조심하세요!","엄백호는 산적 출신으로 스스로 동오 덕왕에 봉한 후에 오군을 점령했어요. 그와 싸울 때 각별히 조심하세요!","","","",},
        [2441] = {2603,"주인공","보잘것없는 산적이 오군을 점거했다니 정신 못 차릴 정도로 흠씬 두들겨줘야겠네요!","보잘것없는 산적이 오군을 점거했다니 정신 못 차릴 정도로 흠씬 두들겨줘야겠네요!","","","",},
        [2442] = {2604,"왕랑","손책, 비록 패했지만 난 절대 따르지 않을 것이다.","손책, 비록 패했지만 난 절대 따르지 않을 것이다.","손책","","",},
        [2443] = {2604,"손책","왕랑, 네 뜻이 그렇다면 강요하지 않겠다! 조조가 표를 올려 너를 허창으로 보내기로 했으니 내가 사람을 보내 너를 허창으로 보내겠다.","왕랑, 네 뜻이 그렇다면 강요하지 않겠다! 조조가 표를 올려 너를 허창으로 보내기로 했으니 내가 사람을 보내 너를 허창으로 보내겠다.","","","",},
        [2444] = {2604,"주인공","왕랑이 호의를 저버리다니. 손 형의 넓은 마음에 탄복했어요!","왕랑이 호의를 저버리다니. 손 형의 넓은 마음에 탄복했어요!","","","",},
        [2445] = {2604,"손책","하하하, 과찬이시오.","하하하, 과찬이시오.","","","",},
        [2446] = {2605,"주인공","이제 태사자가 남았군요. 태사자와 싸우는 것을 도울게요!","이제 태사자가 남았군요. 태사자와 싸우는 것을 도울게요!","손책","","",},
        [2447] = {2605,"손책","지난번 신정에서 태사자와 싸울 때 내가 그의 등에서 수극을 빼앗고, 태사자는 나의 투구를 빼앗으면서까지 승부가 나지 않았다오. 어떻게 쉽게 당신에게 양보하겠소?","지난번 신정에서 태사자와 싸울 때 내가 그의 등에서 수극을 빼앗고, 태사자는 나의 투구를 빼앗으면서까지 승부가 나지 않았다오. 어떻게 쉽게 당신에게 양보하겠소?","","","",},
        [2448] = {2605,"주인공","그럼 우리 중 승자가 태사자와 싸우는 것은 어떨까요?","그럼 우리 중 승자가 태사자와 싸우는 것은 어떨까요?","","","",},
        [2449] = {2605,"손책","하하하, 좋소. 붙어봅시다!","하하하, 좋소. 붙어봅시다!","","","",},
        [2450] = {2606,"태사자","양위장군은 정말 대단하십니다. 이 명장령을 드리겠습니다.","양위장군은 정말 대단하십니다. 이 명장령을 드리겠습니다.","주인공","","",},
        [2451] = {2606,"주인공","감사합니다!","감사합니다!","","","",},
        [2452] = {2606,"손책","정말 대장부요! 유요가 어리석어 장군을 제대로 쓰지 않아 패한 것이오. 앞으로 나와 함께 헤쳐나갑시다.","정말 대장부요! 유요가 어리석어 장군을 제대로 쓰지 않아 패한 것이오. 앞으로 나와 함께 헤쳐나갑시다.","","","",},
        [2453] = {2606,"태사자","저는 이미 마음이 떠났으니, 남은 이들을 수습하고 와서 돕겠습니다.","저는 이미 마음이 떠났으니, 남은 이들을 수습하고 와서 돕겠습니다.","","","",},
        [2454] = {2606,"손책","좋소. 내일 낮에 다시 만납시다. (태사자가 응답하고 간다.)","좋소. 내일 낮에 다시 만납시다. (태사자가 응답하고 간다.)","","","",},
        [2455] = {2606,"주인공","손형, 태사자가 오지 않으면 어떻하죠?","손형, 태사자가 오지 않으면 어떻하죠?","","","",},
        [2456] = {2606,"손책","태사자는 신의를 지키는 사람이오.","태사자는 신의를 지키는 사람이오.","","","",},
        [2457] = {2606,"주인공","사실 조조가 태사자의 명성을 듣고 서신을 보냈는데, 안에 안부 글은 없고 당귀만 있었대요. 조조에게 의탁할 줄 알았는데 결국 손 형에게 가는군요. 하하하!","사실 조조가 태사자의 명성을 듣고 서신을 보냈는데, 안에 안부 글은 없고 당귀만 있었대요. 조조에게 의탁할 줄 알았는데 결국 손 형에게 가는군요. 하하하!","","","",},
        [2458] = {2606,"손책","나는 사람을 쓸 때 의심을 하지 않소. 태사자는 분명 동오의 대장이오. 하하!","나는 사람을 쓸 때 의심을 하지 않소. 태사자는 분명 동오의 대장이오. 하하!","","","",},
        [2459] = {2701,"관은병","저 사람은 뭐하고 있는 거죠? 무술을 연마하는 건가요?","저 사람은 뭐하고 있는 거죠? 무술을 연마하는 건가요?","화타","","",},
        [2460] = {2701,"화타","(오금희를 연마하며 혼자서 중얼거린다.) 호거, 호복, 녹저, 녹분, 웅운, 웅황, 원제, 원적, 조신, 조비...","(오금희를 연마하며 혼자서 중얼거린다.) 호거, 호복, 녹저, 녹분, 웅운, 웅황, 원제, 원적, 조신, 조비...","","","",},
        [2461] = {2701,"장성채","아, 우리 스승님이세요! 스승님! 여기서 뵐 줄 몰랐어요!","아, 우리 스승님이세요! 스승님! 여기서 뵐 줄 몰랐어요!","","","",},
        [2462] = {2701,"화타","하하, 성채구나! 방방곡곡을 다니며 의술을 행하고 있는데 마침 강동에 왔단다.","하하, 성채구나! 방방곡곡을 다니며 의술을 행하고 있는데 마침 강동에 왔단다.","","","",},
        [2463] = {2701,"장성채","이분은 스승이신 화타예요!","이분은 스승이신 화타예요!","","","",},
        [2464] = {2701,"화타","아, 양위장군이군요! 참으로 젊고 능력이 많네요! (좌자가 말한 천명지인이로군!)","아, 양위장군이군요! 참으로 젊고 능력이 많네요! (좌자가 말한 천명지인이로군!)","","","",},
        [2465] = {2701,"주인공","만나 봬서 영광입니다. 명의 화타셨군요!","만나 봬서 영광입니다. 명의 화타셨군요!","","","",},
        [2466] = {2701,"화타","성채야, 이 오금희를 받아서 그분께 전해 주렴. 건강하게 오래 살고 젊음을 유지해준단다. 난 회계에 일이 있어 이만 가봐야겠다.","성채야, 이 오금희를 받아서 그분께 전해 주렴. 건강하게 오래 살고 젊음을 유지해준단다. 난 회계에 일이 있어 이만 가봐야겠다.","","","",},
        [2467] = {2701,"관은병","성채, 네 사부님 정말 젊고...어, 어디 가셨지? 희한하네!","성채, 네 사부님 정말 젊고...어, 어디 가셨지? 희한하네!","","","",},
        [2468] = {2701,"장성채","스승님은 원래 그러시니 이상하게 보지 마세요.","스승님은 원래 그러시니 이상하게 보지 마세요.","","","",},
        [2469] = {2702,"장성채","선성이 왜 산적에게 포위된 거죠? 산적이 정말 많네요!","선성이 왜 산적에게 포위된 거죠? 산적이 정말 많네요!","주인공","","",},
        [2470] = {2702,"주인공","선성은 손책의 동생 손권이 지키는 곳이잖아요! 어서 손권에게 가봐요!","선성은 손책의 동생 손권이 지키는 곳이잖아요! 어서 손권에게 가봐요!","","","",},
        [2471] = {2702,"관은병","정말 많군요! 하하, 산적을 없애겠어요!","정말 많군요! 하하, 산적을 없애겠어요!","","","",},
        [2472] = {2703,"주태","구해주셔서 감사합니다! 손권 장군을 지켜주세요! (말을 마치고 중상을 입은 주태가 쓰러진다.)","구해주셔서 감사합니다! 손권 장군을 지켜주세요! (말을 마치고 중상을 입은 주태가 쓰러진다.)","주인공","","",},
        [2473] = {2703,"손권","주태 장군! 날 구하려다가 이렇게 많은 상처를 입다니!","주태 장군! 날 구하려다가 이렇게 많은 상처를 입다니!","","","",},
        [2474] = {2703,"주인공","주태 같은 충직한 분이 제일 존경스러워요! 성채, 어서 치료할 방법을 찾아봐요!","주태 같은 충직한 분이 제일 존경스러워요! 성채, 어서 치료할 방법을 찾아봐요!","","","",},
        [2475] = {2703,"장성채","상처가 몸에 이십 군데나 있고 매우 심각해요. 침을 놓아 잠시 생명을 지킬 수는 있지만, 완전히 치료하려면 화타 스승님을 찾아가야 해요.","상처가 몸에 이십 군데나 있고 매우 심각해요. 침을 놓아 잠시 생명을 지킬 수는 있지만, 완전히 치료하려면 화타 스승님을 찾아가야 해요.","","","",},
        [2476] = {2703,"손권","고맙소, 낭자! 화타는 지금 어디 있소?","고맙소, 낭자! 화타는 지금 어디 있소?","","","",},
        [2477] = {2703,"장성채","성에 들어오기 전에 만났는데 회계로 간다고 하셨어요.","성에 들어오기 전에 만났는데 회계로 간다고 하셨어요.","","","",},
        [2478] = {2703,"손권","회계로 사람을 보내 신의 화타를 모셔오겠어요!","회계로 사람을 보내 신의 화타를 모셔오겠어요!","","","",},
        [2479] = {2704,"화타","양위장군은 정말 대단하십니다. 이 명장령을 드리겠습니다.","양위장군은 정말 대단하십니다. 이 명장령을 드리겠습니다.","주인공","","",},
        [2480] = {2704,"주인공","전에 오금희도 주셨는데 명장령도 주시니 정말 감사해요!","전에 오금희도 주셨는데 명장령도 주시니 정말 감사해요!","","","",},
        [2481] = {2704,"화타","장군은 이 난세를 끝낼 천명지인이잖아요! 제가 연구한 오금희를 연습하면 젊고 건강을 유지할 수 있지요.","장군은 이 난세를 끝낼 천명지인이잖아요! 제가 연구한 오금희를 연습하면 젊고 건강을 유지할 수 있지요.","","","",},
        [2482] = {2704,"주인공","그럼 신의가 이렇게 젊은 것도 오금희를 연마해서 그런 건가요?","그럼 신의가 이렇게 젊은 것도 오금희를 연마해서 그런 건가요?","","","",},
        [2483] = {2704,"화타","맞아요!","맞아요!","","","",},
        [2484] = {2704,"주인공","젊음을 유지하도록 매일 아침저녁으로 한 번씩 할게요! 하하! 성채, 은병도 같이 해요. 나중에 좋은 신랑감을 찾아줄게요.","젊음을 유지하도록 매일 아침저녁으로 한 번씩 할게요! 하하! 성채, 은병도 같이 해요. 나중에 좋은 신랑감을 찾아줄게요.","","","",},
        [2485] = {2704,"장성채","필요 없어요. 전 영원히 당신만 따라다닐 거라고요!","필요 없어요. 전 영원히 당신만 따라다닐 거라고요!","","","",},
        [2486] = {2704,"관은병","저도요! 계속 같이 싸울 거예요!","저도요! 계속 같이 싸울 거예요!","","","",},
        [2487] = {2704,"주인공","하하하하! 좋아요! 진류에 또 황건적 잔당이 있대요! 황건적을 섬멸하러 가요!","하하하하! 좋아요! 진류에 또 황건적 잔당이 있대요! 황건적을 섬멸하러 가요!","","","",},
        [2488] = {2801,"주인공","진류는 중원의 중심인데 도처에 굶주린 사람들이 왜 이렇게 많죠?","진류는 중원의 중심인데 도처에 굶주린 사람들이 왜 이렇게 많죠?","장성채","","",},
        [2489] = {2801,"장성채","이 년 동안 메뚜기가 나타나 모든 벼를 먹어버렸어요. 관동 일대에 곡식 한 곡이 오십 전에 달하면서 정말 아비규환이 되었죠.","이 년 동안 메뚜기가 나타나 모든 벼를 먹어버렸어요. 관동 일대에 곡식 한 곡이 오십 전에 달하면서 정말 아비규환이 되었죠.","주인공","","",},
        [2490] = {2801,"주인공","이곳은 조조의 세력 안에 있으니 가서 양식을 구해 백성을 구해야 겠어요.","이곳은 조조의 세력 안에 있으니 가서 양식을 구해 백성을 구해야 겠어요.","장성채","","",},
        [2491] = {2802,"주인공","강동에서 오는 길에 진류에 들렀는데, 곳곳이 이재민이었어요. 식량을 가지고 가서 구제해주면 안 될까요?","강동에서 오는 길에 진류에 들렀는데, 곳곳이 이재민이었어요. 식량을 가지고 가서 구제해주면 안 될까요?","조조","","",},
        [2492] = {2802,"조조","최근 메뚜기 떼로 인해 우리 군대에도 양식이 별로 없다오. 여포와 교전 중이라 나도 걱정 중이었소.","최근 메뚜기 떼로 인해 우리 군대에도 양식이 별로 없다오. 여포와 교전 중이라 나도 걱정 중이었소.","주인공","","",},
        [2493] = {2802,"순욱","황건적 잔당 하의와 황소 등이 주군을 약탈하여 금과 비단, 식량이 많습니다.","황건적 잔당 하의와 황소 등이 주군을 약탈하여 금과 비단, 식량이 많습니다.","주인공","","",},
        [2494] = {2802,"순욱","쉽게 물리칠 수 있으니 그 양식으로 삼군을 양성하면 조정과 백성이 기뻐할 것입니다.","쉽게 물리칠 수 있으니 그 양식으로 삼군을 양성하면 조정과 백성이 기뻐할 것입니다.","주인공","","",},
        [2495] = {2802,"주인공","좋은 생각이에요. 황건적 섬멸은 제가 앞장서죠. 승전보를 가져올게요.","좋은 생각이에요. 황건적 섬멸은 제가 앞장서죠. 승전보를 가져올게요.","순욱","","",},
        [2496] = {2803,"황소","내가 절천야차 황소다! 너희들은 누구냐?","내가 절천야차 황소다! 너희들은 누구냐?","관은병","","",},
        [2497] = {2803,"관은병","이 분은 천자가 봉한 양위장군이시다. 너희들을 모조리 잡아주마!","이 분은 천자가 봉한 양위장군이시다. 너희들을 모조리 잡아주마!","황소","","",},
        [2498] = {2803,"황소","양위장군은 들어보지도 못했다! 황건천군에게 덤비다니 오늘 완전히 작살을 내주마!","양위장군은 들어보지도 못했다! 황건천군에게 덤비다니 오늘 완전히 작살을 내주마!","관은병","","",},
        [2499] = {2804,"황소","양위장군을 몰라뵈었습니다. 목숨만은 살려주십시오!","양위장군을 몰라뵈었습니다. 목숨만은 살려주십시오!","관은병","","",},
        [2500] = {2804,"관은병","이제서야 싹싹 비는군! 흥!","이제서야 싹싹 비는군! 흥!","황소","","",},
        [2501] = {2804,"주인공","좋아요. 대신 양식과 금, 비단을 내놓으면 살려주겠어요.","좋아요. 대신 양식과 금, 비단을 내놓으면 살려주겠어요.","황소","","",},
        [2502] = {2804,"황소","사실 어떤 남자가 우리 진영에서 수 백 기를 사로잡아 갔으니, 곡식을 얻으려면 그를 이겨야 할 겁니다.","사실 어떤 남자가 우리 진영에서 수 백 기를 사로잡아 갔으니, 곡식을 얻으려면 그를 이겨야 할 겁니다.","주인공","","",},
        [2503] = {2804,"관은병","감히 우리 물건을 빼앗아가다니, 어서 가요!","감히 우리 물건을 빼앗아가다니, 어서 가요!","황소","","",},
        [2504] = {2805,"관은병","이 뚱보가 우리 물건을 빼앗아 갔나 봐요.","이 뚱보가 우리 물건을 빼앗아 갔나 봐요.","주인공","","",},
        [2505] = {2805,"주인공","팔 척 장신에 허리가 열 아름이나 되는데요.","팔 척 장신에 허리가 열 아름이나 되는데요.","관은병","","",},
        [2506] = {2805,"관은병","그냥 한마디로 뚱뚱한 거네요. 뚱보, 어서 황건적 잔당을 내놓아라!","그냥 한마디로 뚱뚱한 거네요. 뚱보, 어서 황건적 잔당을 내놓아라!","주인공","","",},
        [2507] = {2805,"허저","백 팔십 근의 유성추를 이긴다면, 너희들에게 주마! 어떠냐?","백 팔십 근의 유성추를 이긴다면, 너희들에게 주마! 어떠냐?","주인공","","",},
        [2508] = {2805,"주인공","은병, 유성추에 다칠 수 있으니 내 뒤로 와요!","은병, 유성추에 다칠 수 있으니 내 뒤로 와요!","허저","","",},
        [2509] = {2806,"허저","완전히 승복합니다. 이 명장령을 장군에게 드리고 싶군요! 허저는 장군을 따르겠습니다!","완전히 승복합니다. 이 명장령을 장군에게 드리고 싶군요! 허저는 장군을 따르겠습니다!","주인공","","",},
        [2510] = {2806,"주인공","나는 오랫동안 유람 중이니, 조조를 따르는 것이 좋겠어요.","나는 오랫동안 유람 중이니, 조조를 따르는 것이 좋겠어요.","허저","","",},
        [2511] = {2806,"주인공","조조, 허저라는 장군을 추천하려고 왔어요. 무예가 전위만큼 뛰어나요.","조조, 허저라는 장군을 추천하려고 왔어요. 무예가 전위만큼 뛰어나요.","조조","","",},
        [2512] = {2806,"조조","하하, 좌 전위, 우 허저라는 두 명의 호위를 얻게 되었소. 이제 누구도 날 해칠 수 없을 것이오.","하하, 좌 전위, 우 허저라는 두 명의 호위를 얻게 되었소. 이제 누구도 날 해칠 수 없을 것이오.","주인공","","",},
        [2513] = {2901,"조조","장수가 가후를 모사로 삼고 유표와 결연하여 완성에 주둔하고 있소. 어떻게 보시오?","장수가 가후를 모사로 삼고 유표와 결연하여 완성에 주둔하고 있소. 어떻게 보시오?","주인공","","",},
        [2514] = {2901,"주인공","사람을 보내 장수에게 항복을 권유하는 것이 좋겠어요. 가후라는 자를 전에 만났는데 과감하면서 눈치가 빨랐어요. 조조를 도와 투항을 권하면 희생 없이 완성을 얻을 수 있을 거예요.","사람을 보내 장수에게 항복을 권유하는 것이 좋겠어요. 가후라는 자를 전에 만났는데 과감하면서 눈치가 빨랐어요. 조조를 도와 투항을 권하면 희생 없이 완성을 얻을 수 있을 거예요.","조조","","",},
        [2515] = {2901,"조조","아주 좋은 생각이오!","아주 좋은 생각이오!","주인공","","",},
        [2516] = {2902,"조조","부인은 성이 어떻게 되오?","부인은 성이 어떻게 되오?","추씨","","",},
        [2517] = {2902,"추씨","저는 장제의 아내 추씨입니다.","저는 장제의 아내 추씨입니다.","조조","","",},
        [2518] = {2902,"조조","부인은 나를 아시오?","부인은 나를 아시오?","추씨","","",},
        [2519] = {2902,"추씨","명성은 익히 들어 알고 있는데 오늘 이렇게 뵙게 되었네요.","명성은 익히 들어 알고 있는데 오늘 이렇게 뵙게 되었네요.","조조","","",},
        [2520] = {2902,"조조","부인을 봐서 장수의 항복을 받아들였소. 아니면 멸족을 당했을 거요.","부인을 봐서 장수의 항복을 받아들였소. 아니면 멸족을 당했을 거요.","추씨","","",},
        [2521] = {2902,"조조","부인을 만난 것이 행운이오. 오늘 밤을 같이 지내고 부귀영화를 함께 누리는 것은 어떻소?","부인을 만난 것이 행운이오. 오늘 밤을 같이 지내고 부귀영화를 함께 누리는 것은 어떻소?","추씨","","",},
        [2522] = {2902,"추씨","승상을 따라가겠습니다.","승상을 따라가겠습니다.","조조","","",},
        [2523] = {2903,"장수","조조가 숙모를 모욕하고, 나를 욕보였소! 조조를 반드시 죽여 한을 풀어야겠소!","조조가 숙모를 모욕하고, 나를 욕보였소! 조조를 반드시 죽여 한을 풀어야겠소!","가후","","",},
        [2524] = {2903,"가후","절대 누설되어서는 안 됩니다. 나중에 조조가 공무를 논의할 때 항복한 병사 중에 도망자가 많다고 하여 중군을 지키겠다고 하십시오.","절대 누설되어서는 안 됩니다. 나중에 조조가 공무를 논의할 때 항복한 병사 중에 도망자가 많다고 하여 중군을 지키겠다고 하십시오.","장수","","",},
        [2525] = {2903,"장수","전위가 특히 잘 싸우는데 어떻게 하면 좋겠소?","전위가 특히 잘 싸우는데 어떻게 하면 좋겠소?","가후","","",},
        [2526] = {2903,"가후","내일 술을 마시자고 청한 뒤에 술에 취해 돌아가면 사람을 보내 극을 뺏으십시오.","내일 술을 마시자고 청한 뒤에 술에 취해 돌아가면 사람을 보내 극을 뺏으십시오.","장수","","",},
        [2527] = {2903,"장수","좋은 계책이오! 반드시 조조를 죽이리다.","좋은 계책이오! 반드시 조조를 죽이리다.","가후","","",},
        [2528] = {2904,"주인공","성 안이 왜 이렇게 혼란 스럽죠? 장수가 조조에게 투항하지 않았나요?","성 안이 왜 이렇게 혼란 스럽죠? 장수가 조조에게 투항하지 않았나요?","관은병","","",},
        [2529] = {2904,"관은병","장수가 투항한 후에 조조가 완성에서 장수의 숙모 추씨와 놀아난 것을 장수가 알고 지금 조조와 싸우고 있어요.","장수가 투항한 후에 조조가 완성에서 장수의 숙모 추씨와 놀아난 것을 장수가 알고 지금 조조와 싸우고 있어요.","주인공","","",},
        [2530] = {2904,"주인공","어서 조조를 구하러 가야겠어요!","어서 조조를 구하러 가야겠어요!","관은병","","",},
        [2531] = {2905,"장성채","한발 늦게 와서, 전위가 조조를 보호하려다가 중상을 입었어요.","한발 늦게 와서, 전위가 조조를 보호하려다가 중상을 입었어요.","주인공","","",},
        [2532] = {2905,"주인공","성채, 전위 좀 봐줘요.","성채, 전위 좀 봐줘요.","장성채","","",},
        [2533] = {2905,"장성채","제 힘으로는 안 되겠어요. 전 장군의 병세가 심각해요!","제 힘으로는 안 되겠어요. 전 장군의 병세가 심각해요!","주인공","","",},
        [2534] = {2905,"전위","내가 병영 문을 막는 사이 승상은 말을 달려 도망쳤습니다. 하지만 추격병이 많아 위험합니다.","내가 병영 문을 막는 사이 승상은 말을 달려 도망쳤습니다. 하지만 추격병이 많아 위험합니다.","주인공","","",},
        [2535] = {2905,"전위","양위장군, 승상과 신분이 두터우니 어서 승상을 구해주십시오! 그렇지 않으면 죽어도 눈을 감지 못할 것입니다.","양위장군, 승상과 신분이 두터우니 어서 승상을 구해주십시오! 그렇지 않으면 죽어도 눈을 감지 못할 것입니다.","주인공","","",},
        [2536] = {2905,"주인공","걱정 마세요. 제가 꼭 조조를 구할게요!","걱정 마세요. 제가 꼭 조조를 구할게요!","전위","","",},
        [2537] = {2905,"전위","주공, 어서...가세요. (전위가 한 마디를 조용히 외치고 죽는다.)","주공, 어서...가세요. (전위가 한 마디를 조용히 외치고 죽는다.)","주인공","","",},
        [2538] = {3001,"장성채","왜 조조가 안 보이죠? 이 길이 아닌 건가요?","왜 조조가 안 보이죠? 이 길이 아닌 건가요?","주인공","","",},
        [2539] = {3001,"주인공","조조가 급하게 도망쳐 어디로 갔는지 모르겠어요. 저쪽에 조조 부하 우금이 오는 것 같아요.","조조가 급하게 도망쳐 어디로 갔는지 모르겠어요. 저쪽에 조조 부하 우금이 오는 것 같아요.","장성채","","",},
        [2540] = {3001,"우금","양위장군, 완성이 혼란스럽다고 들었는데, 승상을 보았습니까?","양위장군, 완성이 혼란스럽다고 들었는데, 승상을 보았습니까?","주인공","","",},
        [2541] = {3001,"주인공","막 완성에서 나오는 길이에요. 장수가 갑자기 반란을 일으켜 전위는 전사하고, 승상은 어디로 갔는지 모르겠어요.","막 완성에서 나오는 길이에요. 장수가 갑자기 반란을 일으켜 전위는 전사하고, 승상은 어디로 갔는지 모르겠어요.","우금","","",},
        [2542] = {3001,"우금","그런 일이 있었군요! 저는 연주로 가서 군대를 이끌고 오겠습니다!","그런 일이 있었군요! 저는 연주로 가서 군대를 이끌고 오겠습니다!","주인공","","",},
        [2543] = {3001,"주인공","장수 쪽에 사람이 많고 기세가 등등하니 우리는 두 갈래로 나눠서 승상을 찾아보죠!","장수 쪽에 사람이 많고 기세가 등등하니 우리는 두 갈래로 나눠서 승상을 찾아보죠!","우금","","",},
        [2544] = {3002,"조앙","아버지의 말은 이미 세 발의 화살을 맞았고, 이번에는 눈을 맞았습니다. 제 말을 타고 빨리 가십시오!","아버지의 말은 이미 세 발의 화살을 맞았고, 이번에는 눈을 맞았습니다. 제 말을 타고 빨리 가십시오!","조조","","",},
        [2545] = {3002,"조조","내가 네 말을 타면, 넌 어쩌려고 그러느냐?","내가 네 말을 타면, 넌 어쩌려고 그러느냐?","조앙","","",},
        [2546] = {3002,"조앙","걱정 마십시오. 무예가 뛰어나니 남아서 뒤를 끊겠습니다. (말의 엉덩이를 치니 말이 조조를 태우고 쏜살같이 달린다. 뒤에서는 화살이 빗발치고, 조앙은 필사적으로 싸운다.)","걱정 마십시오. 무예가 뛰어나니 남아서 뒤를 끊겠습니다. (말의 엉덩이를 치니 말이 조조를 태우고 쏜살같이 달린다. 뒤에서는 화살이 빗발치고, 조앙은 필사적으로 싸운다.)","조조","","",},
        [2547] = {3003,"정부인","(정부인은 조앙이 전사했다는 소식을 듣고 화를 내며 고향으로 간다.) 당신이 내 아들을 죽인 거예요. 난 이제 어떤 미련도 없어요.","(정부인은 조앙이 전사했다는 소식을 듣고 화를 내며 고향으로 간다.) 당신이 내 아들을 죽인 거예요. 난 이제 어떤 미련도 없어요.","조조","","",},
        [2548] = {3003,"조조","부인, 우리 같이 집으로 가는 게 어떻소?","부인, 우리 같이 집으로 가는 게 어떻소?","정부인","","",},
        [2549] = {3003,"정부인","아니요!","아니요!","조조","","",},
        [2550] = {3003,"조조","부인, 같이 안 가겠소?","부인, 같이 안 가겠소?","정부인","","",},
        [2551] = {3003,"정부인","아니요!","아니요!","조조","","",},
        [2552] = {3003,"조조","","","정부인","","",},
        [2553] = {3004,"하후돈","양위장군, 승상과의 친분으로 존경하고 있었습니다. 그런데 내가 이끄는 청주병은 왜 죽였습니까?","양위장군, 승상과의 친분으로 존경하고 있었습니다. 그런데 내가 이끄는 청주병은 왜 죽였습니까?","주인공","","",},
        [2554] = {3004,"주인공","왜 나한테 묻지요? 당신의 청주군은 원래 내가 승상을 도와 굴복시켰던 황건적 잔당이에요.","왜 나한테 묻지요? 당신의 청주군은 원래 내가 승상을 도와 굴복시켰던 황건적 잔당이에요.","하후돈","","",},
        [2555] = {3004,"주인공","완성에서 승상을 구하고 돌아오는데 청주병이 민가를 약탈하길래 죽인 거예요!","완성에서 승상을 구하고 돌아오는데 청주병이 민가를 약탈하길래 죽인 거예요!","하후돈","","",},
        [2556] = {3004,"하후돈","그렇게 된 거군요! 군대를 엄히 다스리지 않은 제 탓입니다!","그렇게 된 거군요! 군대를 엄히 다스리지 않은 제 탓입니다!","주인공","","",},
        [2557] = {3005,"조조","우금, 청주병이 네가 완성에서 혼란스러운 틈을 타 반역을 꾀했다고 말했다. 네 죄를 알렸다!","우금, 청주병이 네가 완성에서 혼란스러운 틈을 타 반역을 꾀했다고 말했다. 네 죄를 알렸다!","우금","","",},
        [2558] = {3005,"주인공","이것은 정말 억울한 일이예요!","이것은 정말 억울한 일이예요!","조조","","",},
        [2559] = {3005,"조조","뭐라?","뭐라?","주인공","","",},
        [2560] = {3005,"주인공","청주병은 원래 황건적 도적이었죠. 일부 습성을 버리지 못한 자들이 민가를 약탈하여 저와 우금이 보고 혼쭐을 내준 거예요.","청주병은 원래 황건적 도적이었죠. 일부 습성을 버리지 못한 자들이 민가를 약탈하여 저와 우금이 보고 혼쭐을 내준 거예요.","조조","","",},
        [2561] = {3005,"조조","우금, 날 원망하지 마시오. 왜 진작 말하지 않았소?","우금, 날 원망하지 마시오. 왜 진작 말하지 않았소?","우금","","",},
        [2562] = {3005,"우금","당시 상황이 급박하고 적이 뒤에서 쫓아와 먼저 적과 싸웠습니다. 조공은 지혜로우신 분이라 이해하실 줄 알았습니다.","당시 상황이 급박하고 적이 뒤에서 쫓아와 먼저 적과 싸웠습니다. 조공은 지혜로우신 분이라 이해하실 줄 알았습니다.","조조","","",},
        [2563] = {3005,"조조","장군이 바쁜 중에도 보루를 견고히 하고, 비방을 당하고도 열심히 싸워 승리를 거두니 참으로 훌륭한 명장이오!","장군이 바쁜 중에도 보루를 견고히 하고, 비방을 당하고도 열심히 싸워 승리를 거두니 참으로 훌륭한 명장이오!","주인공","","",},
        [2564] = {3005,"주인공","정말 현명하세요!","정말 현명하세요!","조조","","",},
        [2565] = {3005,"조조","완성에서 아들과 조카를 잃었으나, 지금 우는 이유는 바로 전위 때문이오.","완성에서 아들과 조카를 잃었으나, 지금 우는 이유는 바로 전위 때문이오.","주인공","","",},
        [2566] = {3101,"원술","옛날 한 고조는 사상의 정장에 불과했지만, 천하를 가졌고, 4백년이 흘러, 운이 다하고 나라가 들끓고 있소.","옛날 한 고조는 사상의 정장에 불과했지만, 천하를 가졌고, 4백년이 흘러, 운이 다하고 나라가 들끓고 있소.","염상","","",},
        [2567] = {3101,"원술","내 가문은 사세삼공이고, 백성들이 내게 돌아왔소. 하늘의 뜻을 따라 제위에 오르려고 하는데, 어떻게 생각하시오?","내 가문은 사세삼공이고, 백성들이 내게 돌아왔소. 하늘의 뜻을 따라 제위에 오르려고 하는데, 어떻게 생각하시오?","염상","","",},
        [2568] = {3101,"염상","안 됩니다. 옛날 주 후직이 덕을 쌓고, 문왕에 이르러 천하의 삼 분의 이를 가졌지만, 은의 속국이 되었지요. 가문이 귀하다지만 주의 번성에 못 미치고, 한실이 쇠하였지만 은의 주왕만큼 폭정을 일삼지도 않습니다.","안 됩니다. 옛날 주 후직이 덕을 쌓고, 문왕에 이르러 천하의 삼 분의 이를 가졌지만, 은의 속국이 되었지요. 가문이 귀하다지만 주의 번성에 못 미치고, 한실이 쇠하였지만 은의 주왕만큼 폭정을 일삼지도 않습니다.","원술","","",},
        [2569] = {3101,"원술","원 씨는 진에서 나왔고, 순의 후예이다. 토로 화를 잇는 것이 맞고, 한을 대신하는 자는 길이 높다고 하였는데 내 자가 바로 공로이니 이 예언이 맞다.","원 씨는 진에서 나왔고, 순의 후예이다. 토로 화를 잇는 것이 맞고, 한을 대신하는 자는 길이 높다고 하였는데 내 자가 바로 공로이니 이 예언이 맞다.","염상","","",},
        [2570] = {3101,"원술","또 전국옥새도 가지고 있으니 내가 임금이 안 되면 하늘의 도에 어긋나는 것이다. 내 뜻은 정해졌으니 반대하는 자는 죽이겠다!","또 전국옥새도 가지고 있으니 내가 임금이 안 되면 하늘의 도에 어긋나는 것이다. 내 뜻은 정해졌으니 반대하는 자는 죽이겠다!","염상","","",},
        [2571] = {3102,"장성채","원술이 겁도 없이 수춘에서 황제라 칭했대요.","원술이 겁도 없이 수춘에서 황제라 칭했대요.","주인공","","",},
        [2572] = {3102,"관은병","원술은 매사에 항상 삐딱했는데, 스스로 황제라고 하다니! 정말! 이분이야말로 한제국 천자가 직접 봉한 양위장군인데!","원술은 매사에 항상 삐딱했는데, 스스로 황제라고 하다니! 정말! 이분이야말로 한제국 천자가 직접 봉한 양위장군인데!","주인공","","",},
        [2573] = {3102,"주인공","원술이 그렇게 쉽게 황제에 오를 수 있다면, 제가 옷을 벗고 낙양성을 세 번 오르겠어요! 흥!","원술이 그렇게 쉽게 황제에 오를 수 있다면, 제가 옷을 벗고 낙양성을 세 번 오르겠어요! 흥!","관은병","","",},
        [2574] = {3102,"장성채","요즘 날씨가 추워서 그러면 얼어 죽어요!","요즘 날씨가 추워서 그러면 얼어 죽어요!","주인공","","",},
        [2575] = {3102,"관은병","성채, 너무 썰렁해","성채, 너무 썰렁해","주인공","","",},
        [2576] = {3102,"주인공","(어색하게 웃으며) 원술과 싸우러 가죠!","(어색하게 웃으며) 원술과 싸우러 가죠!","관은병","","",},
        [2577] = {3103,"주인공","원술, 멋대로 황제라 칭하여서 제후들이 분노하고 있어요. 저는 조조, 유비, 여포와 연합하였고, 지금 대군들이 수춘으로 진격 중이에요.","원술, 멋대로 황제라 칭하여서 제후들이 분노하고 있어요. 저는 조조, 유비, 여포와 연합하였고, 지금 대군들이 수춘으로 진격 중이에요.","원술","","",},
        [2578] = {3103,"원술","옥새가 내 손안에 있어 천하는 나의 것이다! 나를 막는 자는 죽임을 당하리라!","옥새가 내 손안에 있어 천하는 나의 것이다! 나를 막는 자는 죽임을 당하리라!","주인공","","",},
        [2579] = {3104,"관은병","원술은 정말 웃기네요! 승산이 없으니 바로 도망갔어요! 그래도 명장령을 얻었으니 헛걸음은 안 했네요!","원술은 정말 웃기네요! 승산이 없으니 바로 도망갔어요! 그래도 명장령을 얻었으니 헛걸음은 안 했네요!","주인공","","",},
        [2580] = {3104,"주인공","원술을 이기기는 했지만, 아직도 세력이 커요. 방법을 더 생각해봐야겠어요.","원술을 이기기는 했지만, 아직도 세력이 커요. 방법을 더 생각해봐야겠어요.","관은병","","",},
        [2581] = {3104,"관은병","원술은 인구도 많고 식량도 풍족한 남양, 여남과 강회 일대를 차지하고 있어요. 세력을 약하게 할 방법을 찾아야 해요.","원술은 인구도 많고 식량도 풍족한 남양, 여남과 강회 일대를 차지하고 있어요. 세력을 약하게 할 방법을 찾아야 해요.","주인공","","",},
        [2582] = {3104,"주인공","괜찮아요! 다른 건 몰라도 강동에서만큼은 내쫓아야겠어요!","괜찮아요! 다른 건 몰라도 강동에서만큼은 내쫓아야겠어요!","관은병","","",},
        [2583] = {3104,"관은병","당시 손견이 낙양에서 옥새를 찾아서 강동으로 왔는데, 나중에 원술에게 뺏겼죠. 손책에게 생각이 있을 거예요!","당시 손견이 낙양에서 옥새를 찾아서 강동으로 왔는데, 나중에 원술에게 뺏겼죠. 손책에게 생각이 있을 거예요!","주인공","","",},
        [2584] = {3104,"주인공","강동으로 가서 손책과 얘기해 보죠!","강동으로 가서 손책과 얘기해 보죠!","관은병","","",},
        [2585] = {3201,"손책","강동에서 여러 번 나를 도와주었으니 오늘 제대로 대접하겠네! 여봐라, 좋은 술과 음식을 내오너라!","강동에서 여러 번 나를 도와주었으니 오늘 제대로 대접하겠네! 여봐라, 좋은 술과 음식을 내오너라!","주인공","","",},
        [2586] = {3201,"주인공","그럼 진귀한 명주도 아까워하지 마시고 가져오세요, 하하!","그럼 진귀한 명주도 아까워하지 마시고 가져오세요, 하하!","손책","","",},
        [2587] = {3201,"손책","좋소, 자, 한 잔 받으시오!","좋소, 자, 한 잔 받으시오!","주인공","","",},
        [2588] = {3201,"주인공","(술기운이 돈다.) 손 형, 드릴 말씀이 있어요!","(술기운이 돈다.) 손 형, 드릴 말씀이 있어요!","손책","","",},
        [2589] = {3201,"손책","맘 놓고 말하시오!","맘 놓고 말하시오!","주인공","","",},
        [2590] = {3201,"주인공","원술이 천자라 칭하여 사람들이 떠나면서 비난하고 있어요. 그와 단교하는 것이 좋겠어요.","원술이 천자라 칭하여 사람들이 떠나면서 비난하고 있어요. 그와 단교하는 것이 좋겠어요.","손책","","",},
        [2591] = {3201,"손책","하하, 보는 눈이 비슷하군요. 일찌기 편지를 보내 원술을 말렸는데 듣지 않았소. 오늘 이 말을 들으니 이제 그와는 관계를 끊어야겠소.","하하, 보는 눈이 비슷하군요. 일찌기 편지를 보내 원술을 말렸는데 듣지 않았소. 오늘 이 말을 들으니 이제 그와는 관계를 끊어야겠소.","주인공","","",},
        [2592] = {3201,"주인공","좋은 생각이에요!","좋은 생각이에요!","손책","","",},
        [2593] = {3201,"손책","마침 여강을 공격하여 강동 육군을 통일하려고 하는데 그대가 도와주시겠소?","마침 여강을 공격하여 강동 육군을 통일하려고 하는데 그대가 도와주시겠소?","주인공","","",},
        [2594] = {3201,"주인공","목숨을 아끼지 않고 돕겠어요!","목숨을 아끼지 않고 돕겠어요!","손책","","",},
        [2595] = {3201,"손책","하하하, 그대는 역시 기개가 하늘을 찌르는군요!","하하하, 그대는 역시 기개가 하늘을 찌르는군요!","주인공","","",},
        [2596] = {3202,"손책","여강태수 유훈은 허창으로 도망하여 조조에게 갔소.","여강태수 유훈은 허창으로 도망하여 조조에게 갔소.","주인공","","",},
        [2597] = {3202,"주인공","황조도 달아났어요. 아쉽게도 손 형 부친의 원수를 갚지 못했어요.","황조도 달아났어요. 아쉽게도 손 형 부친의 원수를 갚지 못했어요.","손책","","",},
        [2598] = {3202,"손책","내가 강동을 통일하는 날에 그의 머리를 베어 아버지께 제사를 지낼 것이오!","내가 강동을 통일하는 날에 그의 머리를 베어 아버지께 제사를 지낼 것이오!","주인공","","",},
        [2599] = {3202,"주인공","이제 예장만 남았어요. 더 힘을 내서 전부 손에 넣도록 해요.","이제 예장만 남았어요. 더 힘을 내서 전부 손에 넣도록 해요.","손책","","",},
        [2600] = {3203,"주인공","오늘 기분이 아주 좋아보이네요.","오늘 기분이 아주 좋아보이네요.","손책","","",},
        [2601] = {3203,"손책","하하, 우번이 항복하여 힘 안 들이고 예장을 얻었으니 기쁘지 않겠소.","하하, 우번이 항복하여 힘 안 들이고 예장을 얻었으니 기쁘지 않겠소.","주인공","","",},
        [2602] = {3203,"주인공","손 형 휘하에는 인재가 많군요. 어, 저기 젊은이는 누구죠? 딱 봐도 모습이 범상치 않은 것이 명문가의 자제인데요.","손 형 휘하에는 인재가 많군요. 어, 저기 젊은이는 누구죠? 딱 봐도 모습이 범상치 않은 것이 명문가의 자제인데요.","손책","","",},
        [2603] = {3203,"손책","그대가 그렇게 칭찬을 하니 앞에 가서 자세히 봐야겠소.","그대가 그렇게 칭찬을 하니 앞에 가서 자세히 봐야겠소.","주인공","","",},
        [2604] = {3204,"손책","나는 손책인데, 당신은 누구시오?","나는 손책인데, 당신은 누구시오?","육손","","",},
        [2605] = {3204,"육손","소생은 육손이고, 자는 백언이며, 오군의 인사입니다.","소생은 육손이고, 자는 백언이며, 오군의 인사입니다.","손책","","",},
        [2606] = {3204,"손책","오군에 사대 명문 가문 육, 고, 주, 장이 있다고 들었는데, 바로 그 육가가 맞소?","오군에 사대 명문 가문 육, 고, 주, 장이 있다고 들었는데, 바로 그 육가가 맞소?","육손","","",},
        [2607] = {3204,"육손","그렇습니다.","그렇습니다.","손책","","",},
        [2608] = {3204,"손책","겸손하고 대범한 것이 책을 읽은 자의 모습이며, 앞으로 크게 될 인물이오. 혹시 결혼은 했소?","겸손하고 대범한 것이 책을 읽은 자의 모습이며, 앞으로 크게 될 인물이오. 혹시 결혼은 했소?","육손","","",},
        [2609] = {3204,"육손","아직 안 했습니다.","아직 안 했습니다.","손책","","",},
        [2610] = {3204,"손책","잘됐소. 자네를 내 사위로 삼으려고 하오! 내 딸이 혼기가 되면 자네에게 시집보내겠소.","잘됐소. 자네를 내 사위로 삼으려고 하오! 내 딸이 혼기가 되면 자네에게 시집보내겠소.","육손","","",},
        [2611] = {3205,"손책","내가 얻은 사위가 어떤 것 같소?","내가 얻은 사위가 어떤 것 같소?","주인공","","",},
        [2612] = {3205,"주인공","명문 가문에 겸손하고 온화하며, 책을 많이 읽어 장래가 촉망되는 젊은이네요.","명문 가문에 겸손하고 온화하며, 책을 많이 읽어 장래가 촉망되는 젊은이네요.","손책","","",},
        [2613] = {3205,"손책","내 생각도 그렇소. 오늘 기분도 좋은데 같이 사냥이나 나갑시다!","내 생각도 그렇소. 오늘 기분도 좋은데 같이 사냥이나 나갑시다!","주인공","","",},
        [2614] = {3205,"주인공","밖에 나갈 때는 수행하는 사람과 동행하세요.","밖에 나갈 때는 수행하는 사람과 동행하세요.","손책","","",},
        [2615] = {3205,"손책","소패왕 손책이 뭐가 무섭겠소! 이미 육군을 평정하고 강동의 주인이 되었다오! 명장령을 좋아하는 것을 알고 있으니 사람을 시켜 모은 뒤 당신에게 주겠소!","소패왕 손책이 뭐가 무섭겠소! 이미 육군을 평정하고 강동의 주인이 되었다오! 명장령을 좋아하는 것을 알고 있으니 사람을 시켜 모은 뒤 당신에게 주겠소!","주인공","","",},
        [2616] = {3205,"주인공","역시 호탕하시군요. 감사해요!","역시 호탕하시군요. 감사해요!","손책","","",},
        [2617] = {3301,"손책","내 한 가지 부탁을 해도 되겠소!","내 한 가지 부탁을 해도 되겠소!","주인공","","",},
        [2618] = {3301,"주인공","교공의 두 딸 대교, 소교를 데리고 와달라는 거죠?","교공의 두 딸 대교, 소교를 데리고 와달라는 거죠?","손책","","",},
        [2619] = {3301,"손책","그대는 정말 대단하시오.","그대는 정말 대단하시오.","주인공","","",},
        [2620] = {3301,"주인공","하하, 성채와 소교는 서로 아는 사이죠. 소교가 우리에게 알려줬어요.","하하, 성채와 소교는 서로 아는 사이죠. 소교가 우리에게 알려줬어요.","손책","","",},
        [2621] = {3301,"손책","그랬군요. 진심으로 부탁하겠소. 나와 주유는 두 자매와 우연히 만나 사랑하게 되었다오.","그랬군요. 진심으로 부탁하겠소. 나와 주유는 두 자매와 우연히 만나 사랑하게 되었다오.","주인공","","",},
        [2622] = {3301,"손책","하지만 교공은 내가 완성을 파괴했다는 이유로 우리에게 딸들을 주지 않았소. 당신은 천자가 봉한 양위장군이니 우리 대신 혼담을 꺼내면 그가 거절하지는 않을 거요.","하지만 교공은 내가 완성을 파괴했다는 이유로 우리에게 딸들을 주지 않았소. 당신은 천자가 봉한 양위장군이니 우리 대신 혼담을 꺼내면 그가 거절하지는 않을 거요.","주인공","","",},
        [2623] = {3301,"주인공","좋아요. 제가 해보죠. 곧 국수를 먹게 되겠네요!","좋아요. 제가 해보죠. 곧 국수를 먹게 되겠네요!","손책","","",},
        [2624] = {3302,"주인공","성채, 여긴 웬일이에요?","성채, 여긴 웬일이에요?","장성채","","",},
        [2625] = {3302,"장성채","교공이 말하길  대교, 소교 자매와 시합을 해서 통과해야 이 혼사를 허락하겠대요.","교공이 말하길  대교, 소교 자매와 시합을 해서 통과해야 이 혼사를 허락하겠대요.","주인공","","",},
        [2626] = {3302,"주인공","하하, 그건 식은 죽 먹기죠. 쉽게 통과할 수 있어요!","하하, 그건 식은 죽 먹기죠. 쉽게 통과할 수 있어요!","장성채","","",},
