        [2628] = {3303,"관은병","손상향 언니, 무예가 이렇게 뛰어나니 나중에 뛰어난 장수와 결혼하세요!","손상향 언니, 무예가 이렇게 뛰어나니 나중에 뛰어난 장수와 결혼하세요!","손상향","","",},
        [2629] = {3303,"손상향","시집은 가서 뭐해요? 전 전쟁터에 나가 싸울 거예요!","시집은 가서 뭐해요? 전 전쟁터에 나가 싸울 거예요!","관은병","","",},
        [2630] = {3303,"관은병","뛰어난 장수와 결혼하면 남편과 전쟁에 나갈 기회가 많잖아요! 보통사람과 결혼하면 집에서 아이나 키워야겠죠!","뛰어난 장수와 결혼하면 남편과 전쟁에 나갈 기회가 많잖아요! 보통사람과 결혼하면 집에서 아이나 키워야겠죠!","손상향","","",},
        [2631] = {3303,"손상향","일리가 있는 말이네요. 어머니께 영웅이 아니면 결혼 안 하겠다고 말할래요!","일리가 있는 말이네요. 어머니께 영웅이 아니면 결혼 안 하겠다고 말할래요!","관은병","","",},
