        [3085] = {5008,"원소","이번에도 관우가 문추를 죽였소. 어떻게 이럴 수가 있소!","이번에도 관우가 문추를 죽였소. 어떻게 이럴 수가 있소!","유비","","",},
        [3086] = {5008,"유비","내가 무슨 죄를 지었소?","내가 무슨 죄를 지었소?","원소","","",},
        [3087] = {5008,"원소","또 네 아우가 우리 장수를 죽였는데 어떻게 죄가 없겠느냐?","또 네 아우가 우리 장수를 죽였는데 어떻게 죄가 없겠느냐?","유비","","",},
        [3088] = {5008,"유비","한마디만 하겠습니다. 조조는 내가 명공 쪽에 있는 것을 알고 일부러 관우에게 두 장수를 죽이게 한 것입니다.","한마디만 하겠습니다. 조조는 내가 명공 쪽에 있는 것을 알고 일부러 관우에게 두 장수를 죽이게 한 것입니다.","원소","","",},
        [3089] = {5008,"유비","공의 손을 빌려 저를 죽이려는 것이니 부디 잘 생각해 주십시오.","공의 손을 빌려 저를 죽이려는 것이니 부디 잘 생각해 주십시오.","원소","","",},
        [3090] = {5008,"원소","당신 말이 맞소. 너희들이 내게 현명한 자를 죽였다는 악명을 씌우려는구나!","당신 말이 맞소. 너희들이 내게 현명한 자를 죽였다는 악명을 씌우려는구나!","유비","","",},
        [3091] = {5008,"유비","은혜를 입고 보답을 못했으니, 심복에게 밀서를 보내 관우를 만나게 하시오.","은혜를 입고 보답을 못했으니, 심복에게 밀서를 보내 관우를 만나게 하시오.","원소","","",},
        [3092] = {5008,"유비","제 소식을 알면 한걸음에 달려와 명공을 보좌하고 조조를 죽일 것입니다. 안량과 문추 두 장수의 원한도 갚을 수 있는데 어떻습니까?","제 소식을 알면 한걸음에 달려와 명공을 보좌하고 조조를 죽일 것입니다. 안량과 문추 두 장수의 원한도 갚을 수 있는데 어떻습니까?","원소","","",},
        [3093] = {5008,"원소","관우를 얻으면 안량, 문추의 열 배를 얻는 것과 같소.","관우를 얻으면 안량, 문추의 열 배를 얻는 것과 같소.","유비","","",},
