        [3295] = {5504,"손책","하지만 인재를 모아 강동을 지키는 것은 경이 나보다 낫소. 아버지와 형의 기업을 잘 이어가길 바라오!","하지만 인재를 모아 강동을 지키는 것은 경이 나보다 낫소. 아버지와 형의 기업을 잘 이어가길 바라오!","손권","","",},
        [3296] = {5504,"손권","아직 어려 큰 임무를 수행할 수 있을지 걱정입니다.","아직 어려 큰 임무를 수행할 수 있을지 걱정입니다.","손책","","",},
        [3297] = {5504,"손책","아우는 나보다 열 배 더 잘할 것이오. 내부에 일이 생기면 장소에게 물으시오.","아우는 나보다 열 배 더 잘할 것이오. 내부에 일이 생기면 장소에게 물으시오.","손권","","",},
        [3298] = {5504,"손책","외부 일은 주유에게 물으면 되오.","외부 일은 주유에게 물으면 되오.","손권","","",},
        [3299] = {5504,"손책","양위장군은 어디 있소?","양위장군은 어디 있소?","손권","","",},
        [3300] = {5504,"주인공","여기 있어요.","여기 있어요.","손책","","",},
        [3301] = {5504,"손책","영웅의 도움이 있어 다행이오. 아우에게 무슨 일이 생기면 꼭 도와주시오.","영웅의 도움이 있어 다행이오. 아우에게 무슨 일이 생기면 꼭 도와주시오.","주인공","","",},
        [3302] = {5504,"주인공","하늘의 도리에 어긋나지 않는다면 최선을 다해 돕겠어요.","하늘의 도리에 어긋나지 않는다면 최선을 다해 돕겠어요.","손책","","",},
        [3303] = {5504,"손책","주유에게 내 아우를 잘 부탁한다고 전해주시오. (말을 마치고 눈을 감고 죽는다.)","주유에게 내 아우를 잘 부탁한다고 전해주시오. (말을 마치고 눈을 감고 죽는다.)","주인공","","",},
        [3304] = {5601,"손권","부형의 유업을 어떻게 지켜야겠소?","부형의 유업을 어떻게 지켜야겠소?","주유","","",},
        [3305] = {5601,"주유","예로부터 인재를 얻으면 번창하고 잃으면 망한다고 하였으니, 뛰어난 인재를 모으면 강동이 안정될 것입니다.","예로부터 인재를 얻으면 번창하고 잃으면 망한다고 하였으니, 뛰어난 인재를 모으면 강동이 안정될 것입니다.","손권","","",},
        [3306] = {5601,"손권","형님께서 내부 일은 장소에게 외부 일은 주유에게 맡기라고 유언하셨소.","형님께서 내부 일은 장소에게 외부 일은 주유에게 맡기라고 유언하셨소.","주유","","",},
        [3307] = {5601,"주유","장소는 현명하여 잘할 것입니다. 저는 재주가 없으니 장군을 보좌할 사람을 추천하겠습니다.","장소는 현명하여 잘할 것입니다. 저는 재주가 없으니 장군을 보좌할 사람을 추천하겠습니다.","손권","","",},
        [3308] = {5601,"손권","그가 누구요?","그가 누구요?","주유","","",},
        [3309] = {5601,"주유","이름은 노숙으로 자는 자경이며 임회 동천 사람입니다.","이름은 노숙으로 자는 자경이며 임회 동천 사람입니다.","손권","","",},
        [3310] = {5601,"주유","뛰어난 책략가입니다. 어려서 아버지를 여의고 어머니를 모셔 효성이 지극합니다. 집이 부유하여 가난한 자들을 많이 구제하였습니다.","뛰어난 책략가입니다. 어려서 아버지를 여의고 어머니를 모셔 효성이 지극합니다. 집이 부유하여 가난한 자들을 많이 구제하였습니다.","손권","","",},
        [3311] = {5601,"주유","일전에 식량이 부족하였을 때 노숙의 곳간 두 곳에 각각 삼천 곡이 있다고 들어 찾아갔습니다.","일전에 식량이 부족하였을 때 노숙의 곳간 두 곳에 각각 삼천 곡이 있다고 들어 찾아갔습니다.","손권","","",},
        [3312] = {5601,"주유","곳간 하나를 전부 털어줄 만큼 배포가 큽니다. 평소 기마를 즐기며 곡아에 살았고, 조모가 죽고 동성으로 무덤을 옮겼지요.","곳간 하나를 전부 털어줄 만큼 배포가 큽니다. 평소 기마를 즐기며 곡아에 살았고, 조모가 죽고 동성으로 무덤을 옮겼지요.","손권","","",},
        [3313] = {5601,"주유","친구 유자양이 그를 데리고 소호로 가서 정보에게 의탁하고자 했으나 가지 않았으니, 빨리 부르셔야 합니다.","친구 유자양이 그를 데리고 소호로 가서 정보에게 의탁하고자 했으나 가지 않았으니, 빨리 부르셔야 합니다.","손권","","",},
        [3314] = {5601,"손권","잘됐소. 그 말에 따르겠소.","잘됐소. 그 말에 따르겠소.","주유","","",},
        [3315] = {5602,"주유","손권이 당신에게 큰 관심이 있소.","손권이 당신에게 큰 관심이 있소.","노숙","","",},
        [3316] = {5602,"노숙","유자양이 소호에 간다고 해서 따라가려고 합니다.","유자양이 소호에 간다고 해서 따라가려고 합니다.","주유","","",},
        [3317] = {5602,"주유","에전에 마원 장군이 광무제에게 지금은 임금도 신하를 선택하지만, 신하도 임금을 선택한다고 말했지요.","에전에 마원 장군이 광무제에게 지금은 임금도 신하를 선택하지만, 신하도 임금을 선택한다고 말했지요.","노숙","","",},
        [3318] = {5602,"주유","우리 손 장군은 인재를 귀하게 여기는 분이니 나와 함께 동오로 갑시다.","우리 손 장군은 인재를 귀하게 여기는 분이니 나와 함께 동오로 갑시다.","노숙","","",},
