        [3340] = {5607,"원소","기주, 청주, 유주에 칠십 여만 인마를 일으켜 허창을 공격하시오.","기주, 청주, 유주에 칠십 여만 인마를 일으켜 허창을 공격하시오.","진진","","",},
        [3341] = {5701,"저수","아군이 비록 많으나 용맹한 장수가 부족합니다. 적군이 비록 정예병이나 군량은 우리가 낫습니다.","아군이 비록 많으나 용맹한 장수가 부족합니다. 적군이 비록 정예병이나 군량은 우리가 낫습니다.","원소","","",},
        [3342] = {5701,"저수","적군은 식량이 없어 빨리 싸워야하고, 아군은 식량이 있어 천천히 싸워도 되지요.","적군은 식량이 없어 빨리 싸워야하고, 아군은 식량이 있어 천천히 싸워도 되지요.","원소","","",},
