        [3344] = {5701,"원소","전풍이 군심을 느슨하게 만들어 내가 죽이려고 하는데 어찌 너까지 그런 말을 하느냐!","전풍이 군심을 느슨하게 만들어 내가 죽이려고 하는데 어찌 너까지 그런 말을 하느냐!","저수","","",},
        [3345] = {5701,"원소","저수를 군중에 가두고, 조조를 격파한 후 전풍과 함께 죄를 묻을 것이다!","저수를 군중에 가두고, 조조를 격파한 후 전풍과 함께 죄를 묻을 것이다!","저수","","",},
