        [3510] = {6401,"조조","기주에 도착하였지만 원담과 원상이 성을 지키고, 원희와 고간이 삼십 리 밖에 진영을 세워 공격을 못하고 있소.","기주에 도착하였지만 원담과 원상이 성을 지키고, 원희와 고간이 삼십 리 밖에 진영을 세워 공격을 못하고 있소.","곽가","","",},
        [3511] = {6401,"조조","어떻게 하면 좋겠소?","어떻게 하면 좋겠소?","곽가","","",},
        [3512] = {6401,"곽가","원 씨 집안은 장자를 후계자로 삼지 않았고 형제들 사이에 권력 다툼이 있어 급하게 하면 서로 도울 것이고, 시간을 끌면 서로 싸울 것입니다.","원 씨 집안은 장자를 후계자로 삼지 않았고 형제들 사이에 권력 다툼이 있어 급하게 하면 서로 도울 것이고, 시간을 끌면 서로 싸울 것입니다.","조조","","",},
        [3513] = {6401,"곽가","남쪽 형주로 군대를 일으켜 유표를 정복하고, 원 씨 형제들에게 변고가 생기기를 기다리시지요.","남쪽 형주로 군대를 일으켜 유표를 정복하고, 원 씨 형제들에게 변고가 생기기를 기다리시지요.","조조","","",},
