        [3558] = {6503,"조조","예전부터 원소와 형제처럼 지냈는데 오늘 죽었으니 눈물을 안 흘릴 수가 있겠소!","예전부터 원소와 형제처럼 지냈는데 오늘 죽었으니 눈물을 안 흘릴 수가 있겠소!","","","",},
        [3559] = {6503,"조조","양식을 원소의 처 유 씨에게 주고 하북 백성은 재난을 입었으니 세금을 면해주시오.","양식을 원소의 처 유 씨에게 주고 하북 백성은 재난을 입었으니 세금을 면해주시오.","","","",},
        [3560] = {6504,"허유","내가 없었으면 이 문을 들어갈 수 있었겠느냐?","내가 없었으면 이 문을 들어갈 수 있었겠느냐?","허저","","",},
        [3561] = {6504,"허저","우리가 목숨을 걸고 싸워서 얻은 성지인데, 어째서 그렇게 말하느냐!","우리가 목숨을 걸고 싸워서 얻은 성지인데, 어째서 그렇게 말하느냐!","허유","","",},
        [3562] = {6504,"허유","필부에 지나지 않는 놈한테 내가 무슨 말을 하겠느냐!","필부에 지나지 않는 놈한테 내가 무슨 말을 하겠느냐!","허저","","",},
        [3563] = {6504,"허저","무례하게 굴다니 내가 널 죽여 주마!","무례하게 굴다니 내가 널 죽여 주마!","허유","","",},
        [3564] = {6505,"허저","허유가 너무 무례하여 제가 죽였습니다. (허유의 목을 올린다.)","허유가 너무 무례하여 제가 죽였습니다. (허유의 목을 올린다.)","조조","","",},
        [3565] = {6505,"조조","허유는 나와 친구라서 농담을 한 것인데 왜 죽였느냐!","허유는 나와 친구라서 농담을 한 것인데 왜 죽였느냐!","허저","","",},
        [3566] = {6505,"허저","항상 무례하게 굴고 허튼소리를 해서 죽여버렸습니다.","항상 무례하게 굴고 허튼소리를 해서 죽여버렸습니다.","조조","","",},
        [3567] = {6505,"조조","너무 경솔했소! 허유의 장례를 성대하게 치를 것이오.","너무 경솔했소! 허유의 장례를 성대하게 치를 것이오.","허저","","",},
        [3568] = {6601,"조조","기주를 평정하였는데, 원담, 원상, 원희 등의 소식이 있소?","기주를 평정하였는데, 원담, 원상, 원희 등의 소식이 있소?","","","",},
        [3569] = {6601,"주인공","원상은 싸울 마음이 없어 원희에게 의탁하러 유주로 갔어요.","원상은 싸울 마음이 없어 원희에게 의탁하러 유주로 갔어요.","조조","","",},
        [3570] = {6601,"주인공","원담은 군대를 이끌고 감릉, 안평, 발해, 하간 등을 약탈하고, 다시 기주를 넘보고 있어요.","원담은 군대를 이끌고 감릉, 안평, 발해, 하간 등을 약탈하고, 다시 기주를 넘보고 있어요.","조조","","",},
        [3571] = {6601,"조조","대군을 이끌고 원정에 나서야겠소!","대군을 이끌고 원정에 나서야겠소!","주인공","","",},
        [3572] = {6602,"주인공","원담이 유표에게 구원을 요청했지만 유표는 뜻이 없고, 우리와 적수가 안 된다는 것을 알고 남피를 지키려고 갔어요.","원담이 유표에게 구원을 요청했지만 유표는 뜻이 없고, 우리와 적수가 안 된다는 것을 알고 남피를 지키려고 갔어요.","조조","","",},
        [3573] = {6602,"조조","쫓아가야겠소!","쫓아가야겠소!","주인공","","",},
        [3574] = {6603,"주인공","저와 조홍이 원담과 칼싸움 끝에 원담을 죽였어요.","저와 조홍이 원담과 칼싸움 끝에 원담을 죽였어요.","조조","","",},
        [3575] = {6603,"주인공","곽도가 성안으로 들어오던 중 화살에 맞아 성밖 해자에서 말과 함께 빠져 죽었어요.","곽도가 성안으로 들어오던 중 화살에 맞아 성밖 해자에서 말과 함께 빠져 죽었어요.","조조","","",},
        [3576] = {6603,"조조","양위장군은 재주가 참으로 비범하오!","양위장군은 재주가 참으로 비범하오!","주인공","","",},
        [3577] = {6603,"조조","원담이 죽었으니, 원상, 원희를 치러가겠소!","원담이 죽었으니, 원상, 원희를 치러가겠소!","주인공","","",},
        [3578] = {6604,"조조","원상이 원희에게 갔는데 그들을 잡으려면 어떻게 해야겠소?","원상이 원희에게 갔는데 그들을 잡으려면 어떻게 해야겠소?","곽가","","",},
        [3579] = {6604,"곽가","항복한 초촉과 장남 등에게 그들을 공격하라고 하십시오.","항복한 초촉과 장남 등에게 그들을 공격하라고 하십시오.","조조","","",},
        [3580] = {6604,"조조","","","곽가","","",},
        [3581] = {6604,"조조","이전, 악진은 장합과 함께 병주를 치고 고간을 공격하시오.","이전, 악진은 장합과 함께 병주를 치고 고간을 공격하시오.","곽가","","",},
        [3582] = {6605,"조홍","고간이 유표를 찾아가던 중 상락에서 도위 왕염에게 살해되었다고 합니다.","고간이 유표를 찾아가던 중 상락에서 도위 왕염에게 살해되었다고 합니다.","조조","","",},
        [3583] = {6605,"주인공","원희, 원상이 패하여 세력이 약해졌어요.","원희, 원상이 패하여 세력이 약해졌어요.","조조","","",},
        [3584] = {6605,"주인공","서쪽을 공격하는데 유비와 유표가 그 틈에 허도를 급습하면 구할 수 없으니 군대를 되돌리는 것이 좋겠어요.","서쪽을 공격하는데 유비와 유표가 그 틈에 허도를 급습하면 구할 수 없으니 군대를 되돌리는 것이 좋겠어요.","조조","","",},
        [3585] = {6605,"곽가","아닙니다. 비록 천하에 위엄을 떨치고 있지만, 사막 사람들은 멀리 떨어져 있어 방비가 허술할 것입니다.","아닙니다. 비록 천하에 위엄을 떨치고 있지만, 사막 사람들은 멀리 떨어져 있어 방비가 허술할 것입니다.","조조","","",},
        [3586] = {6605,"곽가","그 틈을 타 공격하면 물리칠 수 있습니다. 원소와 오환은 은혜를 베푼 사이고 원상과 원희가 아직 있으니 제거해야 합니다.","그 틈을 타 공격하면 물리칠 수 있습니다. 원소와 오환은 은혜를 베푼 사이고 원상과 원희가 아직 있으니 제거해야 합니다.","조조","","",},
        [3587] = {6605,"곽가","유표는 자신이 유비를 거느리기에 부족하다는 것을 알고, 그에게 중요한 임무를 맡기지 않고 있지요.","유표는 자신이 유비를 거느리기에 부족하다는 것을 알고, 그에게 중요한 임무를 맡기지 않고 있지요.","조조","","",},
        [3588] = {6605,"곽가","멀리 원정을 나가셔도 걱정할 것 없습니다.","멀리 원정을 나가셔도 걱정할 것 없습니다.","조조","","",},
        [3589] = {6605,"조조","맞는 말이오. 군대와 수레 수천 량을 이끌고 진격하겠소.","맞는 말이오. 군대와 수레 수천 량을 이끌고 진격하겠소.","곽가","","",},
        [3590] = {6606,"조조","사막을 평정하고 싶어 공을 멀리까지 데리고 왔다가 병을 얻었으니 내 마음이 편치 않소! (풍토가 맞지 않아 병으로 수레에 누워있는 곽가를 본다.)","사막을 평정하고 싶어 공을 멀리까지 데리고 왔다가 병을 얻었으니 내 마음이 편치 않소! (풍토가 맞지 않아 병으로 수레에 누워있는 곽가를 본다.)","곽가","","",},
        [3591] = {6606,"곽가","승상께서 베풀어준 은혜는 죽는다고 해도 만분의 일도 갚지 못합니다.","승상께서 베풀어준 은혜는 죽는다고 해도 만분의 일도 갚지 못합니다.","조조","","",},
        [3592] = {6606,"조조","북쪽 땅이 험난하여 회군하고 싶은데 어떻소?","북쪽 땅이 험난하여 회군하고 싶은데 어떻소?","곽가","","",},
        [3593] = {6606,"곽가","병귀신속이라 했지요. 천 리 길을 가는데 군수 물자가 많으면 불리하니 가볍게 무장하여 나아가십시오.","병귀신속이라 했지요. 천 리 길을 가는데 군수 물자가 많으면 불리하니 가볍게 무장하여 나아가십시오.","조조","","",},
        [3594] = {6606,"조조","경은 역주에서 치료하시오. 내가 향도관에게 길을 인도하라고 하겠소.","경은 역주에서 치료하시오. 내가 향도관에게 길을 인도하라고 하겠소.","곽가","","",},
        [3595] = {6607,"조조","곽가가 죽은 것은 하늘이 나를 죽인 것과 같소! (조조가 역주에 도달했을 때 곽가는 이미 세상을 떠났다.)","곽가가 죽은 것은 하늘이 나를 죽인 것과 같소! (조조가 역주에 도달했을 때 곽가는 이미 세상을 떠났다.)","","","",},
        [3596] = {6607,"조조","제군들 중 곽가가 가장 어렸소. 그에게 후사를 맡기고자 했는데 이렇게 요절을 하다니 가슴이 찢어지는 것 같소!","제군들 중 곽가가 가장 어렸소. 그에게 후사를 맡기고자 했는데 이렇게 요절을 하다니 가슴이 찢어지는 것 같소!","","","",},
        [3597] = {6607,"주인공","곽가가 죽음이 임박하여 글을 남겼어요. '글대로 하면 요동의 일은 평정될 것이다'라고 썼어요.","곽가가 죽음이 임박하여 글을 남겼어요. '글대로 하면 요동의 일은 평정될 것이다'라고 썼어요.","조조","","",},
        [3598] = {6607,"하후돈","서신에 쓴 것이 요동을 빨리 정복하라는 것입니까?","서신에 쓴 것이 요동을 빨리 정복하라는 것입니까?","주인공","","",},
        [3599] = {6607,"주인공","아니요. 며칠 뒤 공손강이 직접 두 원 씨의 머리를 보낼 거예요.","아니요. 며칠 뒤 공손강이 직접 두 원 씨의 머리를 보낼 거예요.","하후돈","","",},
        [3600] = {6608,"원상","요동은 군사가 수만이어서 조조와 겨룰 만하오.","요동은 군사가 수만이어서 조조와 겨룰 만하오.","원희","","",},
        [3601] = {6608,"원상","잠시 의탁한 뒤 공손강을 죽여 그의 땅을 빼앗아 세력을 키워 중원에 대항하면 하북을 다시 회복할 수 있을 것이오.","잠시 의탁한 뒤 공손강을 죽여 그의 땅을 빼앗아 세력을 키워 중원에 대항하면 하북을 다시 회복할 수 있을 것이오.","원희","","",},
        [3602] = {6608,"원희","나도 그렇게 생각하고 있었소!","나도 그렇게 생각하고 있었소!","원상","","",},
        [3603] = {6609,"하후돈","요동 공손강이 관역에서 원희, 원상을 베어 머리를 보내왔습니다.","요동 공손강이 관역에서 원희, 원상을 베어 머리를 보내왔습니다.","조조","","",},
        [3604] = {6609,"조조","역시 곽가가 생각한 대로 되었소!","역시 곽가가 생각한 대로 되었소!","하후돈","","",},
        [3605] = {6609,"하후돈","무엇이 그렇다는 것입니까?","무엇이 그렇다는 것입니까?","조조","","",},
        [3606] = {6609,"주인공","글에 이렇게 썼어요.","글에 이렇게 썼어요.","하후돈","","",},
        [3607] = {6609,"주인공","원희, 원상이 요동으로 갔다고하니 병사를 늘리지 마십시오.","원희, 원상이 요동으로 갔다고하니 병사를 늘리지 마십시오.","하후돈","","",},
        [3608] = {6609,"주인공","공손강은 원 씨를 두려워해서, 이번에 가면 분명 의심할 것입니다. 그곳을 공격하면 협력해서 저항할 것입니다.","공손강은 원 씨를 두려워해서, 이번에 가면 분명 의심할 것입니다. 그곳을 공격하면 협력해서 저항할 것입니다.","하후돈","","",},
        [3609] = {6609,"주인공","서두르지 않으면 공손강과 원 씨는 반드시 서로 다툴 것입니다.","서두르지 않으면 공손강과 원 씨는 반드시 서로 다툴 것입니다.","하후돈","","",},
        [3610] = {6609,"조조","곽가는 호걸 중에서도 으뜸이며, 배 속에는 경전이 있고 가슴에는 갑병을 숨겼구나!","곽가는 호걸 중에서도 으뜸이며, 배 속에는 경전이 있고 가슴에는 갑병을 숨겼구나!","주인공","","",},
        [3611] = {6609,"조조","곽가의 관을 허도로 가져가 장례를 성대하게 치르겠다!","곽가의 관을 허도로 가져가 장례를 성대하게 치르겠다!","주인공","","",},
        [3612] = {6701,"정욱","북방을 평정했으니 이제 허도로 가서 강남에 대한 대책을 세워야 합니다.","북방을 평정했으니 이제 허도로 가서 강남에 대한 대책을 세워야 합니다.","조조","","",},
