        [3916] = {7401,"여몽","황조의 부하 장수가 투항했습니다.","황조의 부하 장수가 투항했습니다.","손권","","",},
        [3917] = {7401,"손권","누구요? 자세히 말해보시오!","누구요? 자세히 말해보시오!","여몽","","",},
        [3918] = {7401,"여몽","감녕으로 파군의 임강 사람입니다. 역사에 정통하고 힘이 좋고 유협을 좋아합니다.","감녕으로 파군의 임강 사람입니다. 역사에 정통하고 힘이 좋고 유협을 좋아합니다.","손권","","",},
        [3919] = {7401,"여몽","허리에 방울을 달아 그 소리를 듣고 모두 달아납니다. 서천의 비단으로 돛을 만들어 사람들이 금범적이라고 부릅니다.","허리에 방울을 달아 그 소리를 듣고 모두 달아납니다. 서천의 비단으로 돛을 만들어 사람들이 금범적이라고 부릅니다.","손권","","",},
        [3920] = {7401,"여몽","잘못을 뉘우치고 선행을 살면서 무리를 이끌고 유표에게 의탁했지요.","잘못을 뉘우치고 선행을 살면서 무리를 이끌고 유표에게 의탁했지요.","손권","","",},
        [3921] = {7401,"여몽","동오로 오려고 했으나 황조 때문에 하구에 남아있었습니다. 전에 동오가 황조를 쳤을 때 감녕의 힘으로 지킬 수 있었지만 그를 박하게 대했습니다.","동오로 오려고 했으나 황조 때문에 하구에 남아있었습니다. 전에 동오가 황조를 쳤을 때 감녕의 힘으로 지킬 수 있었지만 그를 박하게 대했습니다.","손권","","",},
        [3922] = {7401,"여몽","도독 소비가 황조에게 여러 번 천거했지만, 감녕이 예전에 도적이었다며 중용하지 않아 감녕이 한을 품게 되었지요.","도독 소비가 황조에게 여러 번 천거했지만, 감녕이 예전에 도적이었다며 중용하지 않아 감녕이 한을 품게 되었지요.","손권","","",},
        [3923] = {7401,"여몽","주공은 인재를 얻는 데 있어 옛일은 기억하지 않는다고 말했습니다. 곧 강을 건너 주공을 뵈러 올 것입니다.","주공은 인재를 얻는 데 있어 옛일은 기억하지 않는다고 말했습니다. 곧 강을 건너 주공을 뵈러 올 것입니다.","손권","","",},
        [3924] = {7401,"손권","양위장군은 일찍이 손책을 도와 강동을 구했소. 오늘 일은 어떻게 생각하시오?","양위장군은 일찍이 손책을 도와 강동을 구했소. 오늘 일은 어떻게 생각하시오?","여몽","","",},
        [3925] = {7401,"주인공","감녕을 얻으시고 황조를 격파하는 것이 좋겠어요.","감녕을 얻으시고 황조를 격파하는 것이 좋겠어요.","손권","","",},
        [3926] = {7401,"손권","어서 데려오시오.","어서 데려오시오.","주인공","","",},
        [3927] = {7402,"손권","감녕이 이렇게 왔는데 내가 어떻게 원한을 품겠소? 의심하지 말고, 황조를 격파할 계책을 가르쳐 주시오.","감녕이 이렇게 왔는데 내가 어떻게 원한을 품겠소? 의심하지 말고, 황조를 격파할 계책을 가르쳐 주시오.","감녕","","",},
        [3928] = {7402,"감녕","지금 한조는 위태로워, 조조가 결국 찬탈할 것입니다. 남쪽 형주도 조조가 차지할 것입니다. 유표는 생각이 없고 아들은 어리석어 기업을 이을 수 없으니 명공께서 도모하십시오.","지금 한조는 위태로워, 조조가 결국 찬탈할 것입니다. 남쪽 형주도 조조가 차지할 것입니다. 유표는 생각이 없고 아들은 어리석어 기업을 이을 수 없으니 명공께서 도모하십시오.","손권","","",},
        [3929] = {7402,"감녕","늦으면 조조가 먼저 도모할 것입니다. 먼저 황조를 치십시오. 황조는 우매하고 돈만 밝혀 백성의 원망을 사고 있지요. 무기는 고치지 않고 군법도 없습니다.","늦으면 조조가 먼저 도모할 것입니다. 먼저 황조를 치십시오. 황조는 우매하고 돈만 밝혀 백성의 원망을 사고 있지요. 무기는 고치지 않고 군법도 없습니다.","손권","","",},
        [3930] = {7402,"감녕","명공이 공격하시면 분명 이기실 겁니다. 그다음 서쪽의 초관을 점령하고 파, 촉을 도모하면 패업을 이룰 수 있습니다.","명공이 공격하시면 분명 이기실 겁니다. 그다음 서쪽의 초관을 점령하고 파, 촉을 도모하면 패업을 이룰 수 있습니다.","손권","","",},
        [3931] = {7402,"손권","참으로 귀한 말씀이오!","참으로 귀한 말씀이오!","감녕","","",},
        [3932] = {7402,"손권","주유를 대도독으로 삼아 수륙 군병을 통솔하고, 여몽과 양위장군은 선봉에 서고, 동습과 감녕은 부장으로 삼겠소.","주유를 대도독으로 삼아 수륙 군병을 통솔하고, 여몽과 양위장군은 선봉에 서고, 동습과 감녕은 부장으로 삼겠소.","감녕","","",},
        [3933] = {7402,"손권","내가 직접 십만 대군을 이끌고 황조를 토벌하겠소.","내가 직접 십만 대군을 이끌고 황조를 토벌하겠소.","감녕","","",},
        [3934] = {7403,"황조","손권이 대군을 십만 대군을 이끌고 온다고 첩자가 알려줬소.","손권이 대군을 십만 대군을 이끌고 온다고 첩자가 알려줬소.","","","",},
        [3935] = {7403,"황조","소비를 대장으로 하고, 진취와 등룡을 선봉으로 하여 맞서 싸우시오.","소비를 대장으로 하고, 진취와 등룡을 선봉으로 하여 맞서 싸우시오.","","","",},
        [3936] = {7404,"주인공","소비, 진취, 등룡 모두 죽였고, 황조 대군을 크게 무찔렀어요.","소비, 진취, 등룡 모두 죽였고, 황조 대군을 크게 무찔렀어요.","손권","","",},
        [3937] = {7404,"주인공","황조가 강하를 버리고 형주고 달아았어요.","황조가 강하를 버리고 형주고 달아았어요.","손권","","",},
        [3938] = {7404,"손권","쫓아가야겠소!","쫓아가야겠소!","주인공","","",},
        [3939] = {7405,"황조","내가 너를 가볍히 대하지 않았는데 왜 위협하느냐?","내가 너를 가볍히 대하지 않았는데 왜 위협하느냐?","감녕","","",},
        [3940] = {7405,"감녕","내가 강하에 있을 때 많은 공을 세웠는데도 나를 도적 취급하더니 그게 무슨 말이냐!","내가 강하에 있을 때 많은 공을 세웠는데도 나를 도적 취급하더니 그게 무슨 말이냐!","황조","","",},
        [3941] = {7405,"주인공","변명 따윈 필요 없다, 죽어라!","변명 따윈 필요 없다, 죽어라!","황조","","",},
        [3942] = {7406,"손권","황조의 머리를 부친의 영전 앞에 바쳐야겠소.","황조의 머리를 부친의 영전 앞에 바쳐야겠소.","","","",},
        [3943] = {7406,"손권","삼군에게 상을 내리고 감녕은 도위로 승격시키시오.","삼군에게 상을 내리고 감녕은 도위로 승격시키시오.","","","",},
        [3944] = {7406,"장소","고립된 성을 지키지 못한다면 강동으로 돌아가는 것이 낫습니다. 우리가 황조를 죽인 것을 유표가 알면 복수하러 올 것입니다.","고립된 성을 지키지 못한다면 강동으로 돌아가는 것이 낫습니다. 우리가 황조를 죽인 것을 유표가 알면 복수하러 올 것입니다.","손권","","",},
        [3945] = {7406,"장소","다시 힘을 비축하면 유표를 칠 수 있습니다. 유표를 물리치면 그 기세로 형양을 얻을 수 있지요.","다시 힘을 비축하면 유표를 칠 수 있습니다. 유표를 물리치면 그 기세로 형양을 얻을 수 있지요.","손권","","",},
        [3946] = {7406,"손권","좋소. 강하를 버리고 강동으로 돌아가겠소.","좋소. 강하를 버리고 강동으로 돌아가겠소.","장소","","",},
        [3947] = {7501,"유비","유표가 사람을 보내 나를 형주로 불렀소.","유표가 사람을 보내 나를 형주로 불렀소.","제갈량","","",},
        [3948] = {7501,"제갈량","강동이 황조를 격파했기 때문에 복수할 대책을 세우기 위함입니다. 제게 계책이 있으니 같이 가겠습니다.","강동이 황조를 격파했기 때문에 복수할 대책을 세우기 위함입니다. 제게 계책이 있으니 같이 가겠습니다.","유비","","",},
        [3949] = {7501,"유비","유표를 만나면 뭐라고 대답해야 합니까?","유표를 만나면 뭐라고 대답해야 합니까?","제갈량","","",},
        [3950] = {7501,"제갈량","먼저 형양의 일에 유감을 표하십시오. 강동 정벌을 권하면 수락하지 마시고 신야에서 군마를 정돈해야 한다고 말씀하십시오.","먼저 형양의 일에 유감을 표하십시오. 강동 정벌을 권하면 수락하지 마시고 신야에서 군마를 정돈해야 한다고 말씀하십시오.","유비","","",},
        [3951] = {7502,"유표","아우가 해를 당한 일을 알고 있소. 채모를 죽이려고 했으나 사람들이 말려 용서해 주었소.","아우가 해를 당한 일을 알고 있소. 채모를 죽이려고 했으나 사람들이 말려 용서해 주었소.","유비","","",},
        [3952] = {7502,"유비","채모는 관련이 없고 아랫사람이 한 일이라고 생각합니다.","채모는 관련이 없고 아랫사람이 한 일이라고 생각합니다.","유표","","",},
        [3953] = {7502,"유표","강하를 잃고, 황조는 죽었소. 복수할 방법을 알려주시오.","강하를 잃고, 황조는 죽었소. 복수할 방법을 알려주시오.","유비","","",},
        [3954] = {7502,"유비","황조는 성격이 난폭하고 사람을 잘 쓰지 못해 화를 당했지요. 남쪽을 정벌하려다가 조조가 북에서 오면 어떻게 하시렵니까?","황조는 성격이 난폭하고 사람을 잘 쓰지 못해 화를 당했지요. 남쪽을 정벌하려다가 조조가 북에서 오면 어떻게 하시렵니까?","유표","","",},
        [3955] = {7502,"유표","내가 늙고 병들어 일할 수 없으니 아우가 나를 좀 도와주시오. 내가 죽은 후 형주를 맡아주면 좋겠소.","내가 늙고 병들어 일할 수 없으니 아우가 나를 좀 도와주시오. 내가 죽은 후 형주를 맡아주면 좋겠소.","유비","","",},
        [3956] = {7502,"유비","제가 어찌 그런 중요한 일을 감당하겠습니까?","제가 어찌 그런 중요한 일을 감당하겠습니까?","유표","","",},
