        [3993] = {7508,"황월영","부친과 천하의 큰일을 이야기할 때 당신이 품은 큰 뜻을 이야기할 때는 희색이 만연했죠.","부친과 천하의 큰일을 이야기할 때 당신이 품은 큰 뜻을 이야기할 때는 희색이 만연했죠.","제갈량","","",},
        [3994] = {7508,"황월영","조조와 손권을 이야기할 때는 미간에 주름이 생겨 이 부채로 얼굴을 가리는 데 쓰면 좋을 것 같아요.","조조와 손권을 이야기할 때는 미간에 주름이 생겨 이 부채로 얼굴을 가리는 데 쓰면 좋을 것 같아요.","제갈량","","",},
        [3995] = {7508,"제갈량","대장부가 일을 도모할 때에는 침착하고 감정에 치우쳐서는 안 되고, 다른 사람이 알아채고 경시하면 큰일을 이룰 수 없지요.","대장부가 일을 도모할 때에는 침착하고 감정에 치우쳐서는 안 되고, 다른 사람이 알아채고 경시하면 큰일을 이룰 수 없지요.","황월영","","",},
