        [4016] = {7603,"유비","하후연이 십만을 이끌고 오는데 어떻게 막아야합니까?","하후연이 십만을 이끌고 오는데 어떻게 막아야합니까?","제갈량","","",},
        [4017] = {7603,"제갈량","관우와 장비가 제 명령을 듣지 않을 수 있으니, 제게 군대를 맡기시려면 검인을 주십시오.","관우와 장비가 제 명령을 듣지 않을 수 있으니, 제게 군대를 맡기시려면 검인을 주십시오.","유비","","",},
        [4018] = {7603,"유비","검인을 맡기겠소.","검인을 맡기겠소.","제갈량","","",},
        [4019] = {7604,"유비","검인을 이미 제갈량에게 맡겼다. 반드시 명령을 듣도록 하여라.","검인을 이미 제갈량에게 맡겼다. 반드시 명령을 듣도록 하여라.","장비","","",},
        [4020] = {7604,"장비","명령을 들어보고 그가 어떻게 배치하는지 보죠.","명령을 들어보고 그가 어떻게 배치하는지 보죠.","유비","","",},
        [4021] = {7604,"제갈량","박망 왼쪽에 있는 산은 예산이고 오른쪽에 있는 숲은 안림인데 군마가 매복할 수 있습니다.","박망 왼쪽에 있는 산은 예산이고 오른쪽에 있는 숲은 안림인데 군마가 매복할 수 있습니다.","장비","","",},
        [4022] = {7604,"제갈량","관우는 일천 군대를 이끌고 예산에 매복하고 있다가 그냥 보내고, 군량과 마초가 뒤에 있을 것이니, 남쪽에서 불이 나면 그 군량과 마초를 불태우십시오.","관우는 일천 군대를 이끌고 예산에 매복하고 있다가 그냥 보내고, 군량과 마초가 뒤에 있을 것이니, 남쪽에서 불이 나면 그 군량과 마초를 불태우십시오.","장비","","",},
        [4023] = {7604,"제갈량","장비는 일천 군대를 이끌고 안림 뒤 계곡에 매복하고 있다가, 남쪽에서 불이 나면, 박망성의 군량과 마초에 불을 지르십시오.","장비는 일천 군대를 이끌고 안림 뒤 계곡에 매복하고 있다가, 남쪽에서 불이 나면, 박망성의 군량과 마초에 불을 지르십시오.","장비","","",},
        [4024] = {7604,"제갈량","관평과 유봉은 오백 병사를 이끌고 불쏘시개를 준비해, 박망성 뒤 양쪽에서 기다리다가 초경에 병사가 도착하면 불을 붙이십시오.","관평과 유봉은 오백 병사를 이끌고 불쏘시개를 준비해, 박망성 뒤 양쪽에서 기다리다가 초경에 병사가 도착하면 불을 붙이십시오.","장비","","",},
        [4025] = {7604,"제갈량","번성에서 조운을 데려와 선두에 세우고 이기지 않아도 됩니다. 일군을 이끌고 뒤에서 지원하십시오. 실수 없이 계획에 따라야 합니다.","번성에서 조운을 데려와 선두에 세우고 이기지 않아도 됩니다. 일군을 이끌고 뒤에서 지원하십시오. 실수 없이 계획에 따라야 합니다.","장비","","",},
        [4026] = {7604,"관우","우리 모두 싸우러 나가는데 군사는 무엇을 하실겁니까?","우리 모두 싸우러 나가는데 군사는 무엇을 하실겁니까?","제갈량","","",},
