        [4105] = {7806,"유비","유기가 강하를 지키고 있는데, 채부인과 채모가 부고를 알리지 않고, 유언을 꾸며 유종을 주인으로 세웠소.","유기가 강하를 지키고 있는데, 채부인과 채모가 부고를 알리지 않고, 유언을 꾸며 유종을 주인으로 세웠소.","","","",},
        [4106] = {7806,"유비","유종이 형양 구군을 조조에게 바쳤다고 하오.","유종이 형양 구군을 조조에게 바쳤다고 하오.","","","",},
        [4107] = {7806,"유비","아들들을 내게 부탁하셨는데, 내가 그 땅을 뺏으면 구천에서 무슨 낯으로 형님을 만나겠소?","아들들을 내게 부탁하셨는데, 내가 그 땅을 뺏으면 구천에서 무슨 낯으로 형님을 만나겠소?","","","",},
        [4108] = {7806,"주인공","정말 너무하네요. 제가 가서 혼쭐을 내줘야겠어요.","정말 너무하네요. 제가 가서 혼쭐을 내줘야겠어요.","유비","","",},
