        [4112] = {7901,"유비","조조 군이 박망에 이르렀다고 하니 막을 방법을 상의해봅시다.","조조 군이 박망에 이르렀다고 하니 막을 방법을 상의해봅시다.","제갈량","","",},
        [4113] = {7901,"제갈량","지난번 불로 하후돈의 군대를 절반 가까이 없앴기 때문에 분명 이에 대한 대책을 세웠을 겁니다.","지난번 불로 하후돈의 군대를 절반 가까이 없앴기 때문에 분명 이에 대한 대책을 세웠을 겁니다.","유비","","",},
        [4114] = {7901,"유비","무슨 계책이 있습니까?","무슨 계책이 있습니까?","제갈량","","",},
