        [4116] = {7901,"제갈량","신야의 남녀노소에게 원하는 사람은 저와 함께 오늘 번성으로 잠시 피하라고 알려주십시오.","신야의 남녀노소에게 원하는 사람은 저와 함께 오늘 번성으로 잠시 피하라고 알려주십시오.","유비","","",},
        [4117] = {7902,"유비","적은 어떻게 막지요?","적은 어떻게 막지요?","제갈량","","",},
        [4118] = {7902,"제갈량","손건은 강가로 배를 옮겨 백성을 구하고, 미축은 관리들을 번성으로 호송하며, 관우는 일천 군사를 이끌고 백하 상류로 가서 매복하고 각자 모래로 채운 포대로 강을 막아야 합니다.","손건은 강가로 배를 옮겨 백성을 구하고, 미축은 관리들을 번성으로 호송하며, 관우는 일천 군사를 이끌고 백하 상류로 가서 매복하고 각자 모래로 채운 포대로 강을 막아야 합니다.","유비","","",},
        [4119] = {7902,"제갈량","내일  삼경이 지나 하류에서 소리가 들리면 포대를 치워 물에 잠기게 합니다.","내일  삼경이 지나 하류에서 소리가 들리면 포대를 치워 물에 잠기게 합니다.","유비","","",},
        [4120] = {7902,"제갈량","장비는 일천 군대를 이끌고 박릉 나루터에 매복하십시오. 조조 군이 물에 잠기면 물살이 느린 이곳으로 올 것입니다.","장비는 일천 군대를 이끌고 박릉 나루터에 매복하십시오. 조조 군이 물에 잠기면 물살이 느린 이곳으로 올 것입니다.","유비","","",},
        [4121] = {7902,"제갈량","조운은 삼천 병사를 네 개로 나누어 장군과 일대는 동문 밖에 매복하고 나머지는 각각 서, 남, 북으로 흩어지게 합니다. 먼저 성 안의 인가 옥상에 인화 물질을 숨겨놓습니다.","조운은 삼천 병사를 네 개로 나누어 장군과 일대는 동문 밖에 매복하고 나머지는 각각 서, 남, 북으로 흩어지게 합니다. 먼저 성 안의 인가 옥상에 인화 물질을 숨겨놓습니다.","유비","","",},
        [4122] = {7902,"제갈량","조조 군이 입성하면 민가에서 쉴 겁니다. 내일 황혼 후 바람이 불면, 서, 남, 북에 매복했던 군사들이 성으로 불화살을 쏩니다.","조조 군이 입성하면 민가에서 쉴 겁니다. 내일 황혼 후 바람이 불면, 서, 남, 북에 매복했던 군사들이 성으로 불화살을 쏩니다.","유비","","",},
        [4123] = {7902,"제갈량","불길이 거세지면 성 밖에서 소리를 질러 가세하고, 동문으로 달아날 때 뒤에서 공격하면 됩니다.","불길이 거세지면 성 밖에서 소리를 질러 가세하고, 동문으로 달아날 때 뒤에서 공격하면 됩니다.","유비","","",},
        [4124] = {7902,"제갈량","동이 트면 관우, 장비와 함께 번성으로 돌아갑니다. 미방과 유봉은 이천 군사를 거느리고 각각 홍기와 청기를 들고 신야성 밖 삼십 리 작미파에서 주둔하게 하십시오.","동이 트면 관우, 장비와 함께 번성으로 돌아갑니다. 미방과 유봉은 이천 군사를 거느리고 각각 홍기와 청기를 들고 신야성 밖 삼십 리 작미파에서 주둔하게 하십시오.","유비","","",},
        [4125] = {7902,"제갈량","조조 군이 오면 홍기는 왼쪽, 청기는 오른쪽으로 도망쳐 매복합니다.","조조 군이 오면 홍기는 왼쪽, 청기는 오른쪽으로 도망쳐 매복합니다.","유비","","",},
