        [4220] = {8109,"감부인","나와 미부인이 쫓기다 수레를 버리고 백성들 틈에 섞여서 걷는데 한 무리의 군마가 들이닥쳤어요.","나와 미부인이 쫓기다 수레를 버리고 백성들 틈에 섞여서 걷는데 한 무리의 군마가 들이닥쳤어요.","조운","","",},
        [4221] = {8109,"감부인","미부인과 아두는 어디로 갔는지 모르고, 저 혼자 이곳으로 도망쳐 왔습니다.","미부인과 아두는 어디로 갔는지 모르고, 저 혼자 이곳으로 도망쳐 왔습니다.","조운","","",},
        [4222] = {8109,"조운","어서 말을 타십시오. 제가 길을 내어 장판성까지 모셔다드리겠습니다.","어서 말을 타십시오. 제가 길을 내어 장판성까지 모셔다드리겠습니다.","감부인","","",},
        [4223] = {8110,"장비","자룡! 왜 형님을 배반했느냐?","자룡! 왜 형님을 배반했느냐?","조운","","",},
        [4224] = {8110,"조운","주인마님과 작은 주인을 찾지 못해 뒤처진 것뿐인데, 왜 배반했다고 하시오?","주인마님과 작은 주인을 찾지 못해 뒤처진 것뿐인데, 왜 배반했다고 하시오?","장비","","",},
        [4225] = {8110,"장비","만약 간옹이 먼저 와서 알리지 않았다면, 내가 왜 자네를 보고도 싸우지 않겠는가!","만약 간옹이 먼저 와서 알리지 않았다면, 내가 왜 자네를 보고도 싸우지 않겠는가!","조운","","",},
        [4226] = {8110,"조운","주공은 어디 계십니까?","주공은 어디 계십니까?","장비","","",},
        [4227] = {8110,"장비","여기서 멀지 않은 곳에 있소.","여기서 멀지 않은 곳에 있소.","조운","","",},
        [4228] = {8110,"조운","감부인을 먼저 보호해 주시오. 미부인과 작은 주인을 찾으러 가겠소.","감부인을 먼저 보호해 주시오. 미부인과 작은 주인을 찾으러 가겠소.","장비","","",},
        [4229] = {8110,"장비","몸조심하시오. 양위장군도 적진에 들어가 주인마님과 아두를 찾고 있소.","몸조심하시오. 양위장군도 적진에 들어가 주인마님과 아두를 찾고 있소.","조운","","",},
        [4230] = {8110,"조운","좋소. 양위장군과 함께 찾겠소.","좋소. 양위장군과 함께 찾겠소.","장비","","",},
        [4231] = {8111,"조운","자룡이 늦었습니다.","자룡이 늦었습니다.","미부인","","",},
        [4232] = {8111,"미부인","장군을 만나다니 아두는 이제 살았습니다. 그의 부친이 반평생을 떠돌다 얻은 혈육입니다.","장군을 만나다니 아두는 이제 살았습니다. 그의 부친이 반평생을 떠돌다 얻은 혈육입니다.","조운","","",},
        [4233] = {8111,"미부인","장군께서 이 아이를 데려가 부친을 만나게 해주시면 첩은 죽어도 여한이 없습니다!","장군께서 이 아이를 데려가 부친을 만나게 해주시면 첩은 죽어도 여한이 없습니다!","조운","","",},
        [4234] = {8111,"조운","부인이 어려움에 처한 것은 저의 죄입니다. 어서 말에 타십시오. 저는 걸어가면서 포위를 뚫겠습니다.","부인이 어려움에 처한 것은 저의 죄입니다. 어서 말에 타십시오. 저는 걸어가면서 포위를 뚫겠습니다.","미부인","","",},
        [4235] = {8111,"미부인","아닙니다! 장군께서 말은 타지 않다니요! 이 아이는 장군께서 지켜주십시오. 첩은 중상을 입어 죽어도 안타깝지 않습니다!","아닙니다! 장군께서 말은 타지 않다니요! 이 아이는 장군께서 지켜주십시오. 첩은 중상을 입어 죽어도 안타깝지 않습니다!","조운","","",},
        [4236] = {8111,"미부인","어서 아이를 데리고 가십시오. 누가 되고 싶지 않습니다.","어서 아이를 데리고 가십시오. 누가 되고 싶지 않습니다.","조운","","",},
        [4237] = {8111,"조운","함성이 가까운 것을 보니 추격병이 왔나 봅니다. 어서 말을 타십시오.","함성이 가까운 것을 보니 추격병이 왔나 봅니다. 어서 말을 타십시오.","미부인","","",},
