        [4239] = {8111,"미부인","이 아이의 생명은 장군에게 달렸습니다! (미부인이 아두를 조운에게 준다.)","이 아이의 생명은 장군에게 달렸습니다! (미부인이 아두를 조운에게 준다.)","조운","","",},
        [4240] = {8111,"조운","부인이 말을 듣지 않으시다가, 추격군이 들이닥치면 어찌하시렵니까?","부인이 말을 듣지 않으시다가, 추격군이 들이닥치면 어찌하시렵니까?","미부인","","",},
        [4241] = {8111,"미부인","아두를 잘 부탁합니다. (미부인이 아두를 땅에 내려놓고, 말라붙은 우물 안으로 투신하여 죽는다.)","아두를 잘 부탁합니다. (미부인이 아두를 땅에 내려놓고, 말라붙은 우물 안으로 투신하여 죽는다.)","조운","","",},
        [4242] = {8111,"조운","미부인! (부인이 죽자, 조군이 시체를 훔칠까봐 흙담을 밀어넣어 우물을 덮는다. 아두를 가슴에 품고 창을 쥐고 말을 달린다.)","미부인! (부인이 죽자, 조군이 시체를 훔칠까봐 흙담을 밀어넣어 우물을 덮는다. 아두를 가슴에 품고 창을 쥐고 말을 달린다.)","미부인","","",},
        [4243] = {8112,"주인공","자룡! 주인마님과 아두를 찾았나요?","자룡! 주인마님과 아두를 찾았나요?","조운","","",},
        [4244] = {8112,"조운","감부인은 이미 안전하게 장판교를 건넜지요.","감부인은 이미 안전하게 장판교를 건넜지요.","주인공","","",},
        [4245] = {8112,"조운","아두는 내 품에 있고, 미부인은...","아두는 내 품에 있고, 미부인은...","주인공","","",},
        [4246] = {8112,"주인공","미부인은 어떻게 됐는데요?","미부인은 어떻게 됐는데요?","조운","","",},
        [4247] = {8112,"조운","미부인은 중상을 입고, 누가 된다며 우물에 빠졌어요.","미부인은 중상을 입고, 누가 된다며 우물에 빠졌어요.","주인공","","",},
        [4248] = {8112,"주인공","장군이 죽기로 싸워 유씨 후손을 남기니, 용감과 결단은 여장부 덕분이군요.","장군이 죽기로 싸워 유씨 후손을 남기니, 용감과 결단은 여장부 덕분이군요.","조운","","",},
        [4249] = {8112,"주인공","자룡! 같이 조조 진영을 공격해 미부인의 원한을 갚아요.","자룡! 같이 조조 진영을 공격해 미부인의 원한을 갚아요.","조운","","",},
        [4250] = {8201,"조운","뒤에 추격병이 있소. 장비, 나를 도와주시오!","뒤에 추격병이 있소. 장비, 나를 도와주시오!","장비","","",},
        [4251] = {8201,"장비","어서 가시오. 추격병은 내가 맡겠소.","어서 가시오. 추격병은 내가 맡겠소.","조운","","",},
        [4252] = {8202,"조운","저의 죄는 만 번을 죽어도 부족합니다! 미부인께서 중상을 입고 말을 타지 않으시더니 우물로 뛰어들어 돌아가셨습니다. 흙담을 밀어 그곳을 덮었습니다.","저의 죄는 만 번을 죽어도 부족합니다! 미부인께서 중상을 입고 말을 타지 않으시더니 우물로 뛰어들어 돌아가셨습니다. 흙담을 밀어 그곳을 덮었습니다.","유비","","",},
        [4253] = {8202,"조운","아두를 품에 넣고 포위를 뚫었습니다. 양위장군 덕분에 위험에서 벗어날 수 있었습니다.","아두를 품에 넣고 포위를 뚫었습니다. 양위장군 덕분에 위험에서 벗어날 수 있었습니다.","유비","","",},
        [4254] = {8202,"조운"," 다행히 공자는 무사하십니다! (조운이 두 손으로 아두를 유비에게 건넨다.)"," 다행히 공자는 무사하십니다! (조운이 두 손으로 아두를 유비에게 건넨다.)","유비","","",},
        [4255] = {8202,"유비","이 녀석 때문에 양위장군과 자룡을 잃을 뻔했구나! (유비가 아두를 받아 땅에 내던진다.)","이 녀석 때문에 양위장군과 자룡을 잃을 뻔했구나! (유비가 아두를 받아 땅에 내던진다.)","조운","","",},
        [4256] = {8202,"조운","제 목숨을 잃어도 갚을 길이 없습니다! (조운이 황급하게 아두를 안아 올린다.)","제 목숨을 잃어도 갚을 길이 없습니다! (조운이 황급하게 아두를 안아 올린다.)","유비","","",},
        [4257] = {8203,"악진","장비가 호랑이 수염을 거꾸로 세우고, 고리눈을 크게 뜨고, 손에 사모를 움켜쥐고, 장판교에 있습니다!","장비가 호랑이 수염을 거꾸로 세우고, 고리눈을 크게 뜨고, 손에 사모를 움켜쥐고, 장판교에 있습니다!","조조","","",},
        [4258] = {8203,"악진","동쪽 수풀 뒤에 먼지가 크게 일어나는 것을 보니 공명의 계략이 있는 것 같습니다.","동쪽 수풀 뒤에 먼지가 크게 일어나는 것을 보니 공명의 계략이 있는 것 같습니다.","조조","","",},
        [4259] = {8203,"조조","운장이 전에 말하기를 익덕에게 백만 군사 중 상장의 머리를 취하는 것은 식은 죽 먹기라고 했소.","운장이 전에 말하기를 익덕에게 백만 군사 중 상장의 머리를 취하는 것은 식은 죽 먹기라고 했소.","악진","","",},
        [4260] = {8203,"조조","오늘 이렇게 만났으니, 제갈공명의 계략과 상관없이 가볍게 대적해선 안 될 것이오.","오늘 이렇게 만났으니, 제갈공명의 계략과 상관없이 가볍게 대적해선 안 될 것이오.","악진","","",},
        [4261] = {8204,"장비","연인 장익덕이 여기 있다! 누가 나와서 싸우겠느냐?","연인 장익덕이 여기 있다! 누가 나와서 싸우겠느냐?","조조","","",},
        [4262] = {8204,"장비","싸우려면 싸우고, 물러나려면 물러날 것이지 뭐 하는 짓이냐!","싸우려면 싸우고, 물러나려면 물러날 것이지 뭐 하는 짓이냐!","조조","","",},
        [4263] = {8204,"조조","모두 서쪽으로 머리를 돌려 달아나야겠소.","모두 서쪽으로 머리를 돌려 달아나야겠소.","장비","","",},
        [4264] = {8205,"장료","승상, 놀라지 마십시오. 장비 한 사람인데 왜 놀라십니까! 지금 군사를 돌려 공격하면 유비를 잡을 수 있습니다.","승상, 놀라지 마십시오. 장비 한 사람인데 왜 놀라십니까! 지금 군사를 돌려 공격하면 유비를 잡을 수 있습니다.","조조","","",},
        [4265] = {8205,"조조","장군은 허저와 함께 장판교로 가서 소식을 알아보시오.","장군은 허저와 함께 장판교로 가서 소식을 알아보시오.","장료","","",},
        [4266] = {8206,"장료","장비가 다리를 끊고 돌아갔습니다.","장비가 다리를 끊고 돌아갔습니다.","조조","","",},
        [4267] = {8206,"조조","다리를 끊은 것은 겁이 났기 때문이오.","다리를 끊은 것은 겁이 났기 때문이오.","장료","","",},
        [4268] = {8206,"조조","속히 부교 세 개를 놓아 오늘 밤에 건너갈 수 있도록 하시오.","속히 부교 세 개를 놓아 오늘 밤에 건너갈 수 있도록 하시오.","장료","","",},
