        [4271] = {8207,"유비","앞은 큰 강이고 뒤에는 추격병이 있으니 어쩌면 좋겠소?","앞은 큰 강이고 뒤에는 추격병이 있으니 어쩌면 좋겠소?","주인공","","",},
        [4272] = {8207,"주인공","걱정 마세요. 저와 자룡이 있잖아요!","걱정 마세요. 저와 자룡이 있잖아요!","유비","","",},
        [4273] = {8208,"조조","이제 유비는 솥 안의 물고기, 함정에 빠진 호랑이요. 이번에 잡지 못하면 물고기를 바다로, 호랑이를 산으로 보내는 것과 같소.","이제 유비는 솥 안의 물고기, 함정에 빠진 호랑이요. 이번에 잡지 못하면 물고기를 바다로, 호랑이를 산으로 보내는 것과 같소.","","","",},
        [4274] = {8208,"조조","장군들은 사력을 다해 전진하시오.","장군들은 사력을 다해 전진하시오.","","","",},
        [4275] = {8208,"관우","여기서 오래 기다리고 있었소! (관우는 강하로 가서 군사 일만을 얻고, 장판교에서의 싸움을 알고는 이곳을 가로막은 것이다.)","여기서 오래 기다리고 있었소! (관우는 강하로 가서 군사 일만을 얻고, 장판교에서의 싸움을 알고는 이곳을 가로막은 것이다.)","조조","","",},
        [4276] = {8208,"조조","또 제갈량의 계략에 빠졌구나!","또 제갈량의 계략에 빠졌구나!","관우","","",},
