        [4311] = {8306,"제갈량","창오태수 오신과 친분이 있어 그곳에 가려고 하오.","창오태수 오신과 친분이 있어 그곳에 가려고 하오.","노숙","","",},
        [4312] = {8306,"노숙","오신은 양식과 군대도 적어 스스로 지키기도 힘든데, 남을 받아들일 수 있겠소?","오신은 양식과 군대도 적어 스스로 지키기도 힘든데, 남을 받아들일 수 있겠소?","제갈량","","",},
