        [4314] = {8306,"노숙","손장군은 6군을 차지하여 군사와 양식이 많고, 강동의 많은 영웅이 그에게 모이고 있소.","손장군은 6군을 차지하여 군사와 양식이 많고, 강동의 많은 영웅이 그에게 모이고 있소.","제갈량","","",},
        [4315] = {8306,"노숙","선생의 계책을 위해 동오로 심복을 보내 대사를 함께 도모하면 좋겠소.","선생의 계책을 위해 동오로 심복을 보내 대사를 함께 도모하면 좋겠소.","제갈량","","",},
        [4316] = {8306,"제갈량","유 사군이 손 장군과 친분이 없어 이야기를 들어줄 사람이 없을 것 같고, 따로 보낼 심복도 없소.","유 사군이 손 장군과 친분이 없어 이야기를 들어줄 사람이 없을 것 같고, 따로 보낼 심복도 없소.","노숙","","",},
        [4317] = {8306,"노숙","선생의 형이 강동의 참모로 있어 만나고 싶어 하오. 내가 재주는 없지만, 손 장군과 함께 만나 대사를 논의했으면 하오.","선생의 형이 강동의 참모로 있어 만나고 싶어 하오. 내가 재주는 없지만, 손 장군과 함께 만나 대사를 논의했으면 하오.","제갈량","","",},
