        [4631] = {8706,"노숙","조조 군사가 오면 어쩌려고 그러시오?","조조 군사가 오면 어쩌려고 그러시오?","제갈량","","",},
        [4632] = {8706,"제갈량","안개가 짙어 쉽게 출격할 수 없소. 우리는 술을 즐기다가 안개가 걷히고 돌아가면 되오.","안개가 짙어 쉽게 출격할 수 없소. 우리는 술을 즐기다가 안개가 걷히고 돌아가면 되오.","노숙","","",},
        [4633] = {8707,"우금","승상, 동오에서 강에 배를 띄워 북을 치고 함성을 지릅니다.","승상, 동오에서 강에 배를 띄워 북을 치고 함성을 지릅니다.","조조","","",},
        [4634] = {8707,"조조","안개가 짙은데 적군이 갑자기 왔으니 매복이 있을 것이오. 수군의 궁노수를 배치해 화살을 쏘시오.","안개가 짙은데 적군이 갑자기 왔으니 매복이 있을 것이오. 수군의 궁노수를 배치해 화살을 쏘시오.","우금","","",},
        [4635] = {8707,"조조","장료, 서황은 궁노군 각각 삼천을 이끌고 강변으로 가서 도우시오.","장료, 서황은 궁노군 각각 삼천을 이끌고 강변으로 가서 도우시오.","우금","","",},
