        [4638] = {8708,"조조","뭣이라! 어서 가서 추격하라.","뭣이라! 어서 가서 추격하라.","제갈량","","",},
        [4639] = {8708,"우금","물살이 급해 이미 이십 리를 달아나 추격할 수 없습니다.","물살이 급해 이미 이십 리를 달아나 추격할 수 없습니다.","조조","","",},
