        [4664] = {8802,"조조","내일 군사 오백을 거느리고, 배 몇 척을 타고 남쪽 강가로 가시오.","내일 군사 오백을 거느리고, 배 몇 척을 타고 남쪽 강가로 가시오.","채화","","",},
        [4665] = {8802,"채화","알겠습니다!","알겠습니다!","조조","","",},
        [4666] = {8803,"채화","우리 형이 죄가 없는데 조조가 죽였습니다. 우리 두 사람이 형의 원수를 갚고자 이렇게 항복하러 왔습니다. 받아주시면 선봉에 서겠습니다.","우리 형이 죄가 없는데 조조가 죽였습니다. 우리 두 사람이 형의 원수를 갚고자 이렇게 항복하러 왔습니다. 받아주시면 선봉에 서겠습니다.","주유","","",},
        [4667] = {8803,"주유","동오에 온 것을 환영하오. 큰 상을 내리겠소! 감녕과 군을 이끌고 선봉에 서시오.","동오에 온 것을 환영하오. 큰 상을 내리겠소! 감녕과 군을 이끌고 선봉에 서시오.","채화","","",},
        [4668] = {8803,"채화","감사합니다.","감사합니다.","주유","","",},
        [4669] = {8804,"주유","채중, 채화는 가솔을 데려오지 않았으니 정말로 투항한 것이 아니오. 조조가 보낸 첩자이니 적의 계략을 역이용하여 소식을 전하게 하겠소.","채중, 채화는 가솔을 데려오지 않았으니 정말로 투항한 것이 아니오. 조조가 보낸 첩자이니 적의 계략을 역이용하여 소식을 전하게 하겠소.","감녕","","",},
        [4670] = {8804,"주유","그대는 잘 대해주되 항상 조심하시오. 출병하는 날 그들을 죽여 제기에 바칠 것이오. 실수하지 않도록 조심하시오.","그대는 잘 대해주되 항상 조심하시오. 출병하는 날 그들을 죽여 제기에 바칠 것이오. 실수하지 않도록 조심하시오.","감녕","","",},
