        [4673] = {8805,"주유","밤에 오시다니 좋은 계책이라도 있으시오?","밤에 오시다니 좋은 계책이라도 있으시오?","황개","","",},
        [4674] = {8805,"황개","적은 많고 우리는 적어 지체해서는 안 되오. 왜 화공을 쓰지 않으시오?","적은 많고 우리는 적어 지체해서는 안 되오. 왜 화공을 쓰지 않으시오?","주유","","",},
