        [4683] = {8806,"주유","조조가 백만을 이끌고 삼백여 리를 잇고 있어 하루에 격파할 수 없소. 장수들은 각각 3개월 치 식량을 가지고 막을 준비를 하시오.","조조가 백만을 이끌고 삼백여 리를 잇고 있어 하루에 격파할 수 없소. 장수들은 각각 3개월 치 식량을 가지고 막을 준비를 하시오.","","","",},
        [4684] = {8806,"황개","3개월이 아니라 30개월 치 식량을 가지고도 성공할 수 없소! 이번 달에 격파할 수 있다면 빨리 공격해야 하오.","3개월이 아니라 30개월 치 식량을 가지고도 성공할 수 없소! 이번 달에 격파할 수 있다면 빨리 공격해야 하오.","주유","","",},
        [4685] = {8806,"황개","이달 안에 격파하지 못하면 장소 말대로 갑옷과 무기를 버리고 항복해야 할 것이오.","이달 안에 격파하지 못하면 장소 말대로 갑옷과 무기를 버리고 항복해야 할 것이오.","주유","","",},
        [4686] = {8806,"주유","병력을 감독해 조조를 격파하라는 주공의 명을 받들고 있다. 항복을 말하는 자를 반드시 참하라고 하셨다. 양군이 대치하고 있는 상황에서 이런 말로 사기를 떨어뜨리니 그대를 참하여 사람들을 복종시킬 것이다!","병력을 감독해 조조를 격파하라는 주공의 명을 받들고 있다. 항복을 말하는 자를 반드시 참하라고 하셨다. 양군이 대치하고 있는 상황에서 이런 말로 사기를 떨어뜨리니 그대를 참하여 사람들을 복종시킬 것이다!","황개","","",},
        [4687] = {8806,"황개","나는 파로장군을 따라 종횡무진하며 삼세를 걸쳐 싸웠을 때 네가 있었느냐?","나는 파로장군을 따라 종횡무진하며 삼세를 걸쳐 싸웠을 때 네가 있었느냐?","주유","","",},
        [4688] = {8806,"주유","어서 끌어내어 참하라!","어서 끌어내어 참하라!","황개","","",},
        [4689] = {8806,"감녕","황개는 동오의 오랜 신하이니 용서해 주십시오.","황개는 동오의 오랜 신하이니 용서해 주십시오.","주유","","",},
        [4690] = {8806,"주유","어찌 쓸데없는 말로 내 법도를 어지럽히느냐! 감녕을 끌어내 매우 쳐라.","어찌 쓸데없는 말로 내 법도를 어지럽히느냐! 감녕을 끌어내 매우 쳐라.","감녕","","",},
        [4691] = {8806,"노숙","황개의 죄는 죽어 마땅하나 아군에 불리하니 너그러이 용서하시오. 조조를 격파한 후에 죽여도 늦지 않소.","황개의 죄는 죽어 마땅하나 아군에 불리하니 너그러이 용서하시오. 조조를 격파한 후에 죽여도 늦지 않소.","주유","","",},
        [4692] = {8806,"주유","관리들이 아니었다면 마땅히 죽었을 것이오! 잠시 미뤄두겠소! 척장 일백 대를 때려 그 죄를 벌할 것이오!","관리들이 아니었다면 마땅히 죽었을 것이오! 잠시 미뤄두겠소! 척장 일백 대를 때려 그 죄를 벌할 것이오!","노숙","","",},
        [4693] = {8807,"노숙","오늘 주유가 노하여 황개를 꾸짖는데 우리는 그의 부하라서 감히 직언할 수 없었소. 선생은 손님인데 왜 수수방관하면서 한마디도 하지 않으셨소?","오늘 주유가 노하여 황개를 꾸짖는데 우리는 그의 부하라서 감히 직언할 수 없었소. 선생은 손님인데 왜 수수방관하면서 한마디도 하지 않으셨소?","제갈량","","",},
        [4694] = {8807,"제갈량","나를 속이고 있구려.","나를 속이고 있구려.","노숙","","",},
        [4695] = {8807,"노숙","선생과 강을 건너온 이래 속인 적이 한 번도 없는데 왜 그런 말을 하시오?","선생과 강을 건너온 이래 속인 적이 한 번도 없는데 왜 그런 말을 하시오?","제갈량","","",},
        [4696] = {8807,"제갈량","주유가 오늘 황개를 때린 것은 그의 계책인데, 왜 내게 권하라 하시오?","주유가 오늘 황개를 때린 것은 그의 계책인데, 왜 내게 권하라 하시오?","노숙","","",},
        [4697] = {8807,"노숙","이제야 알았소!","이제야 알았소!","제갈량","","",},
        [4698] = {8807,"제갈량","고육계를 쓰지 않고 어떻게 조조를 속이겠소? 이제 황개를 시켜 거짓 항복을 하게 하면 채중과 채화가 그 사정을 알릴 것이오.","고육계를 쓰지 않고 어떻게 조조를 속이겠소? 이제 황개를 시켜 거짓 항복을 하게 하면 채중과 채화가 그 사정을 알릴 것이오.","노숙","","",},
        [4699] = {8808,"함택","장군이 도독과 원수를 지신 건 아닙니까?","장군이 도독과 원수를 지신 건 아닙니까?","황개","","",},
        [4700] = {8808,"황개","아니오.","아니오.","함택","","",},
        [4701] = {8808,"함택","그렇다면 공이 오늘 받은 벌은 고육계가 아닙니까?","그렇다면 공이 오늘 받은 벌은 고육계가 아닙니까?","황개","","",},
        [4702] = {8808,"황개","어떻게 알았소?","어떻게 알았소?","함택","","",},
        [4703] = {8808,"함택","주유의 행동을 보고 대충 알았습니다.","주유의 행동을 보고 대충 알았습니다.","황개","","",},
        [4704] = {8808,"황개","제가 오 제후 삼세의 은혜를 입었으나, 보답할 길이 없어 그 계책으로 조조를 격파하려 하오. 나는 고통을 받아도 여한이 없소.","제가 오 제후 삼세의 은혜를 입었으나, 보답할 길이 없어 그 계책으로 조조를 격파하려 하오. 나는 고통을 받아도 여한이 없소.","함택","","",},
        [4705] = {8808,"황개","군중에 믿을 사람이 없으나, 공은 충의의 마음을 지녔기에 내가 마음을 터놓고 말하는 것이오.","군중에 믿을 사람이 없으나, 공은 충의의 마음을 지녔기에 내가 마음을 터놓고 말하는 것이오.","함택","","",},
        [4706] = {8808,"함택","내게 알려주셨으니 이제 거짓으로 항복하는 서신을 보내야 하지 않습니까?","내게 알려주셨으니 이제 거짓으로 항복하는 서신을 보내야 하지 않습니까?","황개","","",},
        [4707] = {8808,"황개","그럴 생각인데 공께서 해주시겠소?","그럴 생각인데 공께서 해주시겠소?","함택","","",},
        [4708] = {8808,"함택","대장부로서 업적을 세우지 못하면 초목과 더불어 썩어 갈 일만 남지 않겠습니까? 공은 몸도 훼손하셨는데 어찌 보잘것없는 목숨을 아까워하겠습니까?","대장부로서 업적을 세우지 못하면 초목과 더불어 썩어 갈 일만 남지 않겠습니까? 공은 몸도 훼손하셨는데 어찌 보잘것없는 목숨을 아까워하겠습니까?","황개","","",},
        [4709] = {8808,"황개","뭐라 감사의 말을 해야 할지 모르겠소. 절을 받으시오.","뭐라 감사의 말을 해야 할지 모르겠소. 절을 받으시오.","함택","","",},
        [4710] = {8808,"함택","지체할 시간이 없으니 어서 행동에 옮기시지요.","지체할 시간이 없으니 어서 행동에 옮기시지요.","황개","","",},
        [4711] = {8808,"황개","서신을 이미 손을 봐두었소.","서신을 이미 손을 봐두었소.","함택","","",},
        [4712] = {8808,"함택","저는 늙은 어부로 변장해 배를 타고 북쪽 강기슭으로 가겠습니다.","저는 늙은 어부로 변장해 배를 타고 북쪽 강기슭으로 가겠습니다.","황개","","",},
        [4713] = {8809,"조조","그대는 동오의 참모인데 여기는 무슨 일로 왔소?","그대는 동오의 참모인데 여기는 무슨 일로 왔소?","함택","","",},
        [4714] = {8809,"함택","황개는 동오의 삼세를 거친 오랜 신하인데, 주유가 장수들 앞에서 심하게 때려 분해하고 있습니다. 승상께 투항하려는 일을 내게 맡겼는데, 승상이 받아들이실지 모르겠습니다.","황개는 동오의 삼세를 거친 오랜 신하인데, 주유가 장수들 앞에서 심하게 때려 분해하고 있습니다. 승상께 투항하려는 일을 내게 맡겼는데, 승상이 받아들이실지 모르겠습니다.","조조","","",},
        [4715] = {8809,"조조","황개가 고육계를 써서 네게 거짓 항복 문서를 부탁해 나를 희롱하는구나! 끌고 가서 참하라.","황개가 고육계를 써서 네게 거짓 항복 문서를 부탁해 나를 희롱하는구나! 끌고 가서 참하라.","함택","","",},
        [4716] = {8809,"함택","하하하하하.","하하하하하.","조조","","",},
        [4717] = {8809,"조조","내가 너의 간계를 알았는데 왜 웃는 것이냐?","내가 너의 간계를 알았는데 왜 웃는 것이냐?","함택","","",},
        [4718] = {8809,"함택","당신을 비웃는 것이 아니고 황개가 사람을 몰라본 것을 비웃는 것이오.","당신을 비웃는 것이 아니고 황개가 사람을 몰라본 것을 비웃는 것이오.","조조","","",},
        [4719] = {8809,"조조","나는 어려서부터 병서를 숙독하여 간계를 알아낼 수 있다. 다른 사람은 속아도 나는 속일 수 없다!","나는 어려서부터 병서를 숙독하여 간계를 알아낼 수 있다. 다른 사람은 속아도 나는 속일 수 없다!","함택","","",},
        [4720] = {8809,"함택","스스로 병서를 숙독했다고 자랑하다니 부끄럽지도 않소! 병력을 거둬 돌아가는 것이 낫겠소! 주유와 싸우면 분명 사로잡힐 것이오! 배우지 못한 사람 손에 죽으니 참으로 애석하구나!","스스로 병서를 숙독했다고 자랑하다니 부끄럽지도 않소! 병력을 거둬 돌아가는 것이 낫겠소! 주유와 싸우면 분명 사로잡힐 것이오! 배우지 못한 사람 손에 죽으니 참으로 애석하구나!","조조","","",},
        [4721] = {8809,"조조","왜 내게 배우지 못했다고 하는 것이냐?","왜 내게 배우지 못했다고 하는 것이냐?","함택","","",},
        [4722] = {8809,"함택","주인을 배신할 때는 날짜를 정하지 않는다는 말도 못 들어보았소? 날짜를 정했다가 급한 일이 생기면 내통하다가 일이 탄로 날 수 있소.","주인을 배신할 때는 날짜를 정하지 않는다는 말도 못 들어보았소? 날짜를 정했다가 급한 일이 생기면 내통하다가 일이 탄로 날 수 있소.","조조","","",},
        [4723] = {8809,"함택","기회를 엿보다가 행동하면 되는 것 아니오? 이 이치를 모르고 괜한 사람을 죽이려 하니 참으로 배우지 못한 사람이구려!","기회를 엿보다가 행동하면 되는 것 아니오? 이 이치를 모르고 괜한 사람을 죽이려 하니 참으로 배우지 못한 사람이구려!","조조","","",},
        [4724] = {8809,"조조","일을 알지 못해 실수를 저질렀소. 마음에 담지 마시오.","일을 알지 못해 실수를 저질렀소. 마음에 담지 마시오.","함택","","",},
        [4725] = {8809,"함택","나와 황개가 항복하는 마음은 아기가 부모를 바라보는 것과 같은데 어찌 거짓이 있겠습니까!","나와 황개가 항복하는 마음은 아기가 부모를 바라보는 것과 같은데 어찌 거짓이 있겠습니까!","조조","","",},
        [4726] = {8809,"조조","두 사람이 큰 공을 세우면 후에 작위를 내려 크게 높이겠소.","두 사람이 큰 공을 세우면 후에 작위를 내려 크게 높이겠소.","함택","","",},
        [4727] = {8809,"함택","작위와 봉록을 바라고 온 것이 아니고 하늘의 뜻에 따라 온 것입니다.","작위와 봉록을 바라고 온 것이 아니고 하늘의 뜻에 따라 온 것입니다.","조조","","",},
        [4728] = {8810,"함택","장군이 지난번에 황개를 구하다가 주유에게 모욕을 당해 정말 화가 나오.","장군이 지난번에 황개를 구하다가 주유에게 모욕을 당해 정말 화가 나오.","감녕","","",},
        [4729] = {8810,"감녕","주유가 자신의 재주만 믿고 우리 형편은 생각하지 않소. 모욕을 당해 너무 부끄럽소.","주유가 자신의 재주만 믿고 우리 형편은 생각하지 않소. 모욕을 당해 너무 부끄럽소.","함택","","",},
        [4730] = {8810,"채화","장군은 왜 번뇌하십니까? 선생은 왜 화가 나십니까?","장군은 왜 번뇌하십니까? 선생은 왜 화가 나십니까?","감녕","","",},
        [4731] = {8810,"감녕","내 고통을 그대가 어찌 알겠소!","내 고통을 그대가 어찌 알겠소!","채화","","",},
        [4732] = {8810,"채화","동오를 배신하고 조조에게 투항하려는 것 아닙니까?","동오를 배신하고 조조에게 투항하려는 것 아닙니까?","감녕","","",},
        [4733] = {8810,"감녕","이 일이 탄로났으니 어쩔 수 없이 당신들을 죽여야겠소!","이 일이 탄로났으니 어쩔 수 없이 당신들을 죽여야겠소!","채화","","",},
        [4734] = {8810,"채화","걱정 마십시오. 저희도 알려드릴 말씀이 있습니다.","걱정 마십시오. 저희도 알려드릴 말씀이 있습니다.","감녕","","",},
