        [4820] = {9101,"주유","아아아아!","아아아아!","노숙","","",},
        [4821] = {9101,"노숙","여봐라, 도독께서 피를 토하셨다. 어서 군의를 불러오너라.","여봐라, 도독께서 피를 토하셨다. 어서 군의를 불러오너라.","주유","","",},
        [4822] = {9102,"노숙","광풍이 불어 화계를 못쓰니 주유가 병이 났소.","광풍이 불어 화계를 못쓰니 주유가 병이 났소.","제갈량","","",},
        [4823] = {9102,"제갈량","공께서는 어떻게 생각하시오?","공께서는 어떻게 생각하시오?","노숙","","",},
        [4824] = {9102,"노숙","이것은 조조의 복이고, 강동의 재앙이오.","이것은 조조의 복이고, 강동의 재앙이오.","제갈량","","",},
        [4825] = {9102,"제갈량","주유의 병은 내가 낫게 할 수 있소.","주유의 병은 내가 낫게 할 수 있소.","노숙","","",},
        [4826] = {9102,"노숙","그럴 수 있다며 천만다행이오! 어서 같이 가봅시다.","그럴 수 있다며 천만다행이오! 어서 같이 가봅시다.","제갈량","","",},
        [4827] = {9103,"노숙","도독의 병세는 어떻소?","도독의 병세는 어떻소?","주유","","",},
        [4828] = {9103,"주유","속이 울렁거리고 아프오. 가끔 혼미해지오.","속이 울렁거리고 아프오. 가끔 혼미해지오.","노숙","","",},
        [4829] = {9103,"노숙","약은 드셨소?","약은 드셨소?","주유","","",},
        [4830] = {9103,"주유","구역질이 나서 약을 삼킬 수가 없소.","구역질이 나서 약을 삼킬 수가 없소.","노숙","","",},
        [4831] = {9103,"노숙","공명을 찾아갔더니 도독의 병을 고칠 수 있다고 했소. 지금 밖에 있는데 불러서 치료를 부탁하면 어떻겠소?","공명을 찾아갔더니 도독의 병을 고칠 수 있다고 했소. 지금 밖에 있는데 불러서 치료를 부탁하면 어떻겠소?","주유","","",},
        [4832] = {9103,"주유","좋소.","좋소.","노숙","","",},
        [4833] = {9104,"제갈량","연일 못 만났는데, 언제 이렇게 되었소!","연일 못 만났는데, 언제 이렇게 되었소!","주유","","",},
        [4834] = {9104,"주유","사람에게 아침저녁으로 화와 복이 있는데 어떻게 스스로 지킬 수 있겠소?","사람에게 아침저녁으로 화와 복이 있는데 어떻게 스스로 지킬 수 있겠소?","제갈량","","",},
        [4835] = {9104,"제갈량","하늘도 바람과 구름을 예측할 수 없는데 사람이 어찌 헤아리겠소? 도독의 마음에 번뇌가 있는 것 아니오?","하늘도 바람과 구름을 예측할 수 없는데 사람이 어찌 헤아리겠소? 도독의 마음에 번뇌가 있는 것 아니오?","주유","","",},
        [4836] = {9104,"주유","좋소.","좋소.","제갈량","","",},
        [4837] = {9104,"제갈량","내게 방법이 있는데, 도독의 기를 다스릴 것이오.","내게 방법이 있는데, 도독의 기를 다스릴 것이오.","주유","","",},
        [4838] = {9104,"주유","선생께서 가르쳐 주시오.","선생께서 가르쳐 주시오.","제갈량","","",},
        [4839] = {9104,"제갈량","조공을 격파하려면 화공을 써야 하는데, 모든 준비가 마쳤으니 동풍만 불어오면 되오. 이것이 병의 원인이오.","조공을 격파하려면 화공을 써야 하는데, 모든 준비가 마쳤으니 동풍만 불어오면 되오. 이것이 병의 원인이오.","주유","","",},
        [4840] = {9104,"주유","선생께서 이미 내 병의 원인을 아니 어떤 약으로 치료해야 하오? 상황이 급하니 어서 알려주시오.","선생께서 이미 내 병의 원인을 아니 어떤 약으로 치료해야 하오? 상황이 급하니 어서 알려주시오.","제갈량","","",},
        [4841] = {9104,"제갈량","내 비록 재주는 없으나 일찍이 기인을 만나 기문둔갑의 천서를 받아 비바람을 부를 수 있소.","내 비록 재주는 없으나 일찍이 기인을 만나 기문둔갑의 천서를 받아 비바람을 부를 수 있소.","주유","","",},
        [4842] = {9104,"제갈량","동남풍이 필요할 때 남병산에 대를 짓고, 칠성단이라 명하고, 높이 9척, 3층으로 올려 120인을 써서 깃발을 들고 둘러싸게 하시오.","동남풍이 필요할 때 남병산에 대를 짓고, 칠성단이라 명하고, 높이 9척, 3층으로 올려 120인을 써서 깃발을 들고 둘러싸게 하시오.","주유","","",},
        [4843] = {9104,"제갈량","내가 대에 올라 법술을 쓰면 사흘 밤낮 동남대풍을 불게 하여 도독의 용병을 도울 수 있는데 어떻소?","내가 대에 올라 법술을 쓰면 사흘 밤낮 동남대풍을 불게 하여 도독의 용병을 도울 수 있는데 어떻소?","주유","","",},
        [4844] = {9104,"주유","사흘 밤낮이 아니라 하룻밤이라도 불면 대사를 이룰 것이오. 상황이 급하니 지체할 수 없소.","사흘 밤낮이 아니라 하룻밤이라도 불면 대사를 이룰 것이오. 상황이 급하니 지체할 수 없소.","제갈량","","",},
        [4845] = {9104,"제갈량","11월 20일 갑자에 바람을 불러서 22일 병인에 그치면 어떻겠소?","11월 20일 갑자에 바람을 불러서 22일 병인에 그치면 어떻겠소?","주유","","",},
        [4846] = {9104,"주유","오백 명의 군사를 남병산으로 보내 단을 쌓고, 120명을 뽑아 깃발을 들고 명령을 기다리라고 하겠소.","오백 명의 군사를 남병산으로 보내 단을 쌓고, 120명을 뽑아 깃발을 들고 명령을 기다리라고 하겠소.","제갈량","","",},
        [4847] = {9105,"주유","밤이 다 돼가는데 하늘이 청명하고 미풍조차 없소. 공명의 말이 틀렸소. 엄동설한에 어떻게 동남풍이 불겠소?","밤이 다 돼가는데 하늘이 청명하고 미풍조차 없소. 공명의 말이 틀렸소. 엄동설한에 어떻게 동남풍이 불겠소?","노숙","","",},
        [4848] = {9105,"노숙","공명이 틀린 말을 하지는 않았을 것이오.","공명이 틀린 말을 하지는 않았을 것이오.","주유","","",},
        [4849] = {9105,"노숙","도독, 보시오. 깃발이 서북쪽으로 나부끼며 삽시간에 동남풍이 일고 있소.","도독, 보시오. 깃발이 서북쪽으로 나부끼며 삽시간에 동남풍이 일고 있소.","주유","","",},
        [4850] = {9105,"주유","이 자는 천지조화를 빼앗는 법이 있어 귀신도 모를 술수를 부리는구나! 이 자를 살려두면, 동오의 화근이 될 것이다. 어서 죽여 우환을 없애야겠다.","이 자는 천지조화를 빼앗는 법이 있어 귀신도 모를 술수를 부리는구나! 이 자를 살려두면, 동오의 화근이 될 것이다. 어서 죽여 우환을 없애야겠다.","노숙","","",},
        [4851] = {9105,"주유","정봉, 서성은 각각 백 명을 데리고 서성은 강으로, 정봉은 육로로 가시오.","정봉, 서성은 각각 백 명을 데리고 서성은 강으로, 정봉은 육로로 가시오.","노숙","","",},
        [4852] = {9105,"주유","모두 남병산 칠성단에 도착하면 묻지도 말고 제갈량을 참수하시오. 목을 가지고 와서 공을 청하시오.","모두 남병산 칠성단에 도착하면 묻지도 말고 제갈량을 참수하시오. 목을 가지고 와서 공을 청하시오.","노숙","","",},
        [4853] = {9105,"서성","알겠습니다, 도독.","알겠습니다, 도독.","주유","","",},
        [4854] = {9106,"주인공","주유는 속이 좁아 동남풍이 불었으니 분명히 공명을 잡으러 올 거예요.","주유는 속이 좁아 동남풍이 불었으니 분명히 공명을 잡으러 올 거예요.","제갈량","","",},
        [4855] = {9106,"제갈량","이미 알고 있었소. 자룡이 강에서 도와줄 것이오.","이미 알고 있었소. 자룡이 강에서 도와줄 것이오.","주인공","","",},
        [4856] = {9106,"주인공","어젯밤 빠른 배 한 척을 앞 여울목에 정박해 놓았어요. 어서 자룡을 만나러 가요.","어젯밤 빠른 배 한 척을 앞 여울목에 정박해 놓았어요. 어서 자룡을 만나러 가요.","제갈량","","",},
        [4857] = {9107,"서성","군사는 멈추시오! 도독의 요청이오!","군사는 멈추시오! 도독의 요청이오!","제갈량","","",},
