        [4963] = {9505,"조비","조충이 병으로 죽다니 참으로 불행한 일입니다. 너무 상심하지 마십시오.","조충이 병으로 죽다니 참으로 불행한 일입니다. 너무 상심하지 마십시오.","조조","","",},
        [4964] = {9505,"조조","건안 이년, 조앙은 나를 따라 장수와 싸울 때 뒤를 막다가 죽었다.","건안 이년, 조앙은 나를 따라 장수와 싸울 때 뒤를 막다가 죽었다.","조비","","",},
        [4965] = {9505,"조조","조충은 참으로 총명한 아이인데 열세 살에 죽다니 어찌 상심하지 않을 수 있겠느냐!","조충은 참으로 총명한 아이인데 열세 살에 죽다니 어찌 상심하지 않을 수 있겠느냐!","조비","","",},
        [4966] = {9505,"조조","이것이 나에게는 불행이지만 너희들에게는 행운일 것이다.","이것이 나에게는 불행이지만 너희들에게는 행운일 것이다.","조비","","",},
        [4967] = {9601,"주유","예주께서 병력을 이곳으로 옮긴 것은 남군을 취하려는 의도입니까?","예주께서 병력을 이곳으로 옮긴 것은 남군을 취하려는 의도입니까?","유비","","",},
        [4968] = {9601,"유비","도독이 남군을 취하려고 한다는 말을 듣고 도우러 왔소. 도독이 취하지 않으면 내가 취하려 하오.","도독이 남군을 취하려고 한다는 말을 듣고 도우러 왔소. 도독이 취하지 않으면 내가 취하려 하오.","주유","","",},
        [4969] = {9601,"주유","우리 동오는 오래전부터 한강을 점령하려고 하였고, 이제 남군도 손안에 있는데 왜 취하지 않겠소?","우리 동오는 오래전부터 한강을 점령하려고 하였고, 이제 남군도 손안에 있는데 왜 취하지 않겠소?","유비","","",},
