        [5023] = {9703,"감녕","내가 군사들을 배불리 먹이고 내응을 준비하였소.","내가 군사들을 배불리 먹이고 내응을 준비하였소.","주태","","",},
        [5024] = {9704,"조홍","지금 이릉을 잃고 상황이 위급한데 승상이 남긴 계책을 어찌 뜯어 보시지 않습니까?","지금 이릉을 잃고 상황이 위급한데 승상이 남긴 계책을 어찌 뜯어 보시지 않습니까?","조인","","",},
        [5025] = {9704,"조인","내 생각과 같소.","내 생각과 같소.","조홍","","",},
        [5026] = {9704,"조인","하하하하하.","하하하하하.","조홍","","",},
        [5027] = {9704,"조인","승상께서 오경에 밥을 짓고, 새벽에 대소 군마에게 성을 포기하고, 성 위에 깃발을 꽂아 허장성세하라 명하셨소.","승상께서 오경에 밥을 짓고, 새벽에 대소 군마에게 성을 포기하고, 성 위에 깃발을 꽂아 허장성세하라 명하셨소.","조홍","","",},
        [5028] = {9704,"조홍","좋은 계책입니다.","좋은 계책입니다.","조인","","",},
        [5029] = {9705,"조인","승상의 묘책이 귀신같아, 주유와 감녕이 계략에 빠졌구나.","승상의 묘책이 귀신같아, 주유와 감녕이 계략에 빠졌구나.","","","",},
        [5030] = {9705,"조인","오군이 앞다투어 빈 성에 들어오다가 모두 구덩이에 빠지고, 양쪽에서 비 오듯 화살이 떨어지니 사상자가 무수히 많다.","오군이 앞다투어 빈 성에 들어오다가 모두 구덩이에 빠지고, 양쪽에서 비 오듯 화살이 떨어지니 사상자가 무수히 많다.","","","",},
        [5031] = {9705,"조인","주유가 급히 돌아가려다 화살을 맞고 낙마했다.","주유가 급히 돌아가려다 화살을 맞고 낙마했다.","","","",},
        [5032] = {9706,"주유","어디서 북을 치고 함성을 지르는 것인가?","어디서 북을 치고 함성을 지르는 것인가?","","","",},
        [5033] = {9706,"감녕","군중에서 사졸들을 훈련하고 있습니다.","군중에서 사졸들을 훈련하고 있습니다.","주유","","",},
        [5034] = {9706,"주유","어찌 나를 속이시오! 조병이 진영 앞에서 와서 욕하는 것을 알고 있소. 장군께서 병권을 장악하고 왜 좌시하고 계시오?","어찌 나를 속이시오! 조병이 진영 앞에서 와서 욕하는 것을 알고 있소. 장군께서 병권을 장악하고 왜 좌시하고 계시오?","감녕","","",},
        [5035] = {9706,"정보","의원이 절대로 화나게 해서는 안 된다고 해서 싸움을 걸어와도 보고하지 않았습니다.","의원이 절대로 화나게 해서는 안 된다고 해서 싸움을 걸어와도 보고하지 않았습니다.","주유","","",},
        [5036] = {9706,"주유","공들이 싸우지 않으면 계획은 어찌 되오?","공들이 싸우지 않으면 계획은 어찌 되오?","정보","","",},
        [5037] = {9706,"정보","장수들이 병사를 거둬 강동으로 잠시 돌아가려고 합니다. 상처가 나으면 다시 도모하시지요.","장수들이 병사를 거둬 강동으로 잠시 돌아가려고 합니다. 상처가 나으면 다시 도모하시지요.","주유","","",},
        [5038] = {9706,"주유","대장부가 주군의 녹봉을 받으니, 마땅히 전쟁터에서 죽어야 하오. 말가죽에 시신을 싸서 돌아가면 다행이오! 나 한 사람 때문에 국가 대사를 폐할 수 있겠소?","대장부가 주군의 녹봉을 받으니, 마땅히 전쟁터에서 죽어야 하오. 말가죽에 시신을 싸서 돌아가면 다행이오! 나 한 사람 때문에 국가 대사를 폐할 수 있겠소?","정보","","",},
        [5039] = {9707,"조인","겁쟁이 주유야, 네가 틀림없이 요절하여 우리 군을 제대로 볼 수도 없을 것이다!","겁쟁이 주유야, 네가 틀림없이 요절하여 우리 군을 제대로 볼 수도 없을 것이다!","주유","","",},
        [5040] = {9707,"주유","조인 필부야! 내 주유가 안 보이느냐!","조인 필부야! 내 주유가 안 보이느냐!","조인","","",},
        [5041] = {9707,"조인","어서 싸워보자!","어서 싸워보자!","주유","","",},
        [5042] = {9707,"주유","반장이 출전하시오.","반장이 출전하시오.","조인","","",},
        [5043] = {9708,"주유","앗! (주유가 비명을 지르고, 입에서 피를 흘리며 말에서 떨어진다.)","앗! (주유가 비명을 지르고, 입에서 피를 흘리며 말에서 떨어진다.)","","","",},
        [5044] = {9708,"정보","도독의 옥체는 어떠십니까?","도독의 옥체는 어떠십니까?","주유","","",},
        [5045] = {9708,"주유","나의 계략이오.","나의 계략이오.","정보","","",},
        [5046] = {9708,"정보","어떤 계책이 있습니까?","어떤 계책이 있습니까?","주유","","",},
        [5047] = {9708,"주유","본래 통증이 심한 것은 아니오. 조병에게 내 병세가 위중한 것처럼 속이려는 것이오.","본래 통증이 심한 것은 아니오. 조병에게 내 병세가 위중한 것처럼 속이려는 것이오.","정보","","",},
        [5048] = {9708,"주유","심복 군사를 시켜 성으로 가서 항복하면서 내가 죽었다고 하시오. 오늘 밤 조인이 진영을 급습할 것이오. 우리가 사방에 매복해 있다가 조인을 잡으면 되오.","심복 군사를 시켜 성으로 가서 항복하면서 내가 죽었다고 하시오. 오늘 밤 조인이 진영을 급습할 것이오. 우리가 사방에 매복해 있다가 조인을 잡으면 되오.","정보","","",},
        [5049] = {9708,"정보","참으로 묘책입니다!","참으로 묘책입니다!","주유","","",},
        [5050] = {9709,"조인","주유가 금창이 터저 죽었다고 하오. 모든 장수가 상복을 입고 애도하고 있소.","주유가 금창이 터저 죽었다고 하오. 모든 장수가 상복을 입고 애도하고 있소.","","","",},
        [5051] = {9709,"조인","오늘 밤 진영을 급습해 주유의 시신을 빼앗아 머리를 베어 허도로 보낼 것이오.","오늘 밤 진영을 급습해 주유의 시신을 빼앗아 머리를 베어 허도로 보낼 것이오.","","","",},
        [5052] = {9709,"조인","우금이 선봉을 맡고, 나는 중군이 되고, 조홍, 조순은 뒤를 지키시오. 진교만이 군사를 데리고 성을 지키고, 나머지는 모두 출전하시오.","우금이 선봉을 맡고, 나는 중군이 되고, 조홍, 조순은 뒤를 지키시오. 진교만이 군사를 데리고 성을 지키고, 나머지는 모두 출전하시오.","","","",},
        [5053] = {9710,"조홍","아무도 안 보이고 깃발만 꽃혀있습니다.","아무도 안 보이고 깃발만 꽃혀있습니다.","조인","","",},
        [5054] = {9710,"조인","계략에 걸렸소. 어서 철수하여 양양으로 가시오.","계략에 걸렸소. 어서 철수하여 양양으로 가시오.","조홍","","",},
        [5055] = {9801,"정보","남군성 아래 깃발이 가득 있는데, 조자룡이 성지를 뺏앗았습니다.","남군성 아래 깃발이 가득 있는데, 조자룡이 성지를 뺏앗았습니다.","주유","","",},
        [5056] = {9801,"주유","뭣이라! 병마를 정비해 성을 공격하시오.","뭣이라! 병마를 정비해 성을 공격하시오.","정보","","",},
        [5057] = {9801,"정보","성 위에서 화살이 빗발쳐 성을 공격할 수 없습니다.","성 위에서 화살이 빗발쳐 성을 공격할 수 없습니다.","주유","","",},
        [5058] = {9801,"주유","감녕에게 수 천 군마를 이끌고 형주를 취하라 하시오.","감녕에게 수 천 군마를 이끌고 형주를 취하라 하시오.","정보","","",},
        [5059] = {9801,"주유","능통은 수 천 군마를 이끌고 양양을 취하고 그 뒤에 남군을 다시 취하면 될 것이오.","능통은 수 천 군마를 이끌고 양양을 취하고 그 뒤에 남군을 다시 취하면 될 것이오.","정보","","",},
        [5060] = {9802,"정보","보고합니다! 제갈량이 직접 남군을 얻고, 병부를 써서 형주를 지키던 군마에게 구하러 오라고 속였습니다.","보고합니다! 제갈량이 직접 남군을 얻고, 병부를 써서 형주를 지키던 군마에게 구하러 오라고 속였습니다.","주유","","",},
        [5061] = {9802,"정보","장비가 형주를 급습했습니다.","장비가 형주를 급습했습니다.","주유","","",},
        [5062] = {9802,"주유","뭣이라!","뭣이라!","정보","","",},
        [5063] = {9803,"정보","보고합니다! 양양에 있는 하후돈에게 제갈량이 인편에 병부를 보내 조인이 구원을 요청한다고 속여, 하후돈을 유인한 뒤 관우가 양양을 취했습니다.","보고합니다! 양양에 있는 하후돈에게 제갈량이 인편에 병부를 보내 조인이 구원을 요청한다고 속여, 하후돈을 유인한 뒤 관우가 양양을 취했습니다.","주유","","",},
        [5064] = {9803,"정보","두 곳의 성지가 힘 하나 안들이고 유비의 것이 되었구나.","두 곳의 성지가 힘 하나 안들이고 유비의 것이 되었구나.","주유","","",},
        [5065] = {9803,"주유","제갈량은 어떻게 병부를 얻은 것인가?","제갈량은 어떻게 병부를 얻은 것인가?","정보","","",},
        [5066] = {9803,"정보","진교를 잡았기 때문에 병부도 자연스럽게 얻게 되었습니다.","진교를 잡았기 때문에 병부도 자연스럽게 얻게 되었습니다.","주유","","",},
        [5067] = {9803,"주유","앗! (주유가 소리를 지르자 금창이 터진다.)","앗! (주유가 소리를 지르자 금창이 터진다.)","정보","","",},
        [5068] = {9803,"정보","여봐라, 어서 도독을 부축해 군영으로 돌아가자.","여봐라, 어서 도독을 부축해 군영으로 돌아가자.","주유","","",},
        [5069] = {9804,"주유","제갈량 그 촌놈을 죽이지 않고서야 어찌 화가 누그러지겠소!","제갈량 그 촌놈을 죽이지 않고서야 어찌 화가 누그러지겠소!","노숙","","",},
        [5070] = {9804,"주유","군대를 일으켜 유비, 제갈량과 승부를 겨뤄 다시 성지를 빼았고 싶소. 공이 도와주시오.","군대를 일으켜 유비, 제갈량과 승부를 겨뤄 다시 성지를 빼았고 싶소. 공이 도와주시오.","노숙","","",},
        [5071] = {9804,"노숙","안 되오. 방금 조조와 대치하여 승패가 나지 않았고, 주공은 합비를 공격해 얻지 못하셨소. 우리가 서로 싸우다가 조조가 이 틈에 오면 매우 위험하오.","안 되오. 방금 조조와 대치하여 승패가 나지 않았고, 주공은 합비를 공격해 얻지 못하셨소. 우리가 서로 싸우다가 조조가 이 틈에 오면 매우 위험하오.","주유","","",},
        [5072] = {9804,"노숙","유비는 조조와 두터운 사이라 다급한 나머지 성지를 바치고 함께 동오를 치면 어찌한단 말이오?","유비는 조조와 두터운 사이라 다급한 나머지 성지를 바치고 함께 동오를 치면 어찌한단 말이오?","주유","","",},
        [5073] = {9804,"주유","우리가 계책을 쓰고 군마를 일으키고 군량을 소모하였는데, 그들이 쉽게 얻었으니 어찌 한스럽지 않겠소!","우리가 계책을 쓰고 군마를 일으키고 군량을 소모하였는데, 그들이 쉽게 얻었으니 어찌 한스럽지 않겠소!","노숙","","",},
        [5074] = {9804,"노숙","일단 참으시오! 내가 유비를 만나 이야기해 보겠소. 말이 안 통하면 그때 쳐도 늦지 않소.","일단 참으시오! 내가 유비를 만나 이야기해 보겠소. 말이 안 통하면 그때 쳐도 늦지 않소.","주유","","",},
        [5075] = {9901,"노숙","우리 오후께서 도독 주유와 함께 황숙께 뜻을 물으라 하셨소. 지난번 조조가 백만을 이끌고 온 것은 명분은 강남을 빼앗는 것이나 황숙을 도모하려고 했던 것이오.","우리 오후께서 도독 주유와 함께 황숙께 뜻을 물으라 하셨소. 지난번 조조가 백만을 이끌고 온 것은 명분은 강남을 빼앗는 것이나 황숙을 도모하려고 했던 것이오.","제갈량","","",},
        [5076] = {9901,"노숙","다행히 동오가 조조를 격퇴하여 황숙을 구했으니 형주 구군은 마땅히 동오에게 돌려주어야 하오.","다행히 동오가 조조를 격퇴하여 황숙을 구했으니 형주 구군은 마땅히 동오에게 돌려주어야 하오.","제갈량","","",},
        [5077] = {9901,"노숙","황숙께서 궤계로 형양을 빼앗았소. 강동은 헛되이 군량과 군마를 쓰고 황숙이 이익을 취하는 것은 말이 안 되오.","황숙께서 궤계로 형양을 빼앗았소. 강동은 헛되이 군량과 군마를 쓰고 황숙이 이익을 취하는 것은 말이 안 되오.","제갈량","","",},
        [5078] = {9901,"제갈량","공은 고명한 선비인데 왜 그런 말씀을 하시오? 속담에 물건은 주인에게 돌아간다고 하였소. 형양 구군은 동오의 땅이 아니라 유표의 기업이오.","공은 고명한 선비인데 왜 그런 말씀을 하시오? 속담에 물건은 주인에게 돌아간다고 하였소. 형양 구군은 동오의 땅이 아니라 유표의 기업이오.","노숙","","",},
        [5079] = {9901,"제갈량","우리 주군은 유표의 아우요. 유표가 죽었으나 아들이 살아있소. 숙부가 조카를 도와 형주를 취한 것이 잘못되었소?","우리 주군은 유표의 아우요. 유표가 죽었으나 아들이 살아있소. 숙부가 조카를 도와 형주를 취한 것이 잘못되었소?","노숙","","",},
        [5080] = {9901,"노숙","공자 유기가 점령한다면 이해하겠소. 공자는 강하에 있고 여기에 없잖소!","공자 유기가 점령한다면 이해하겠소. 공자는 강하에 있고 여기에 없잖소!","제갈량","","",},
        [5081] = {9901,"제갈량","공자를 뵙고 싶소?","공자를 뵙고 싶소?","노숙","","",},
