        [5089] = {9903,"노숙","유기가 죽으면 성지를 동오로 돌려준다고 하였소.","유기가 죽으면 성지를 동오로 돌려준다고 하였소.","주유","","",},
        [5090] = {9903,"주유","유기는 아직 젊은데, 어느 세월에 죽겠소? 형주는 언제 돌려 받을 수 있겠소?","유기는 아직 젊은데, 어느 세월에 죽겠소? 형주는 언제 돌려 받을 수 있겠소?","노숙","","",},
        [5091] = {9903,"노숙","걱정하지 마시오. 형주는 반드시 동오로 돌아올 것이오.","걱정하지 마시오. 형주는 반드시 동오로 돌아올 것이오.","주유","","",},
        [5092] = {9903,"주유","무슨 고견이 있소?","무슨 고견이 있소?","노숙","","",},
        [5093] = {9903,"노숙","내가 보니 유기가 주색에 빠져 병이 깊소. 얼굴이 수척하고, 기침에 피를 토하니 반년도 못가 죽을 것이오.","내가 보니 유기가 주색에 빠져 병이 깊소. 얼굴이 수척하고, 기침에 피를 토하니 반년도 못가 죽을 것이오.","주유","","",},
        [5094] = {9903,"노숙","그때 형주를 취하면 유비도 이유를 대지 못할 것이오.","그때 형주를 취하면 유비도 이유를 대지 못할 것이오.","주유","","",},
        [5095] = {9903,"주유","좋소.","좋소.","노숙","","",},
        [5096] = {9904,"이적","형주의 원대한 계획을 알기 원하시면서 왜 현사에게 묻지 않으십니까?","형주의 원대한 계획을 알기 원하시면서 왜 현사에게 묻지 않으십니까?","유비","","",},
        [5097] = {9904,"유비","현사가 어디 계시오?","현사가 어디 계시오?","이적","","",},
        [5098] = {9904,"이적","형양의 마 씨 다섯 형제 모두 재주가 뛰어납니다. 어린 자는 이름이 속, 자는 유상이고, 현자는 눈썹 사이에 흰털이 있으며 이름은 량이고 자는 계상입니다.","형양의 마 씨 다섯 형제 모두 재주가 뛰어납니다. 어린 자는 이름이 속, 자는 유상이고, 현자는 눈썹 사이에 흰털이 있으며 이름은 량이고 자는 계상입니다.","유비","","",},
        [5099] = {9904,"이적","고향에 '마 씨 오상 중 백미가 가장 어질다.'라는 말이 있지요. 왜 그를 찾지 않으십니까?","고향에 '마 씨 오상 중 백미가 가장 어질다.'라는 말이 있지요. 왜 그를 찾지 않으십니까?","유비","","",},
        [5100] = {9904,"유비","어서 그를 청해주시오.","어서 그를 청해주시오.","이적","","",},
        [5101] = {9905,"유비","선생께서 형양을 지킬 계책이 있다고 들었습니다.","선생께서 형양을 지킬 계책이 있다고 들었습니다.","마량","","",},
        [5102] = {9905,"마량","형양은 사방이 적의 공격을 받으니 오래 지키기가 어렵습니다. 공자 유기께서 이곳에서 요양하시니 옛사람들에게 지키라고 하고 공자를 형주자사로 삼아 민심을 안정시켜야 합니다.","형양은 사방이 적의 공격을 받으니 오래 지키기가 어렵습니다. 공자 유기께서 이곳에서 요양하시니 옛사람들에게 지키라고 하고 공자를 형주자사로 삼아 민심을 안정시켜야 합니다.","유비","","",},
        [5103] = {9905,"마량","그다음 무릉, 장사, 계양, 영릉 사군을 정복하여, 지세를 거둬 근본으로 삼으십시오. 이것은 원대한 계획입니다.","그다음 무릉, 장사, 계양, 영릉 사군을 정복하여, 지세를 거둬 근본으로 삼으십시오. 이것은 원대한 계획입니다.","유비","","",},
        [5104] = {9905,"유비","사군 가운데 어느 군을 먼저 취해야 하오?","사군 가운데 어느 군을 먼저 취해야 하오?","마량","","",},
        [5105] = {9905,"마량","상강의 서쪽, 영릉이 가장 가까우니 먼저 취하십시오. 무릉 다음 상강의 동쪽 계양, 장사의 순으로 취하십시오.","상강의 서쪽, 영릉이 가장 가까우니 먼저 취하십시오. 무릉 다음 상강의 동쪽 계양, 장사의 순으로 취하십시오.","유비","","",},
        [5106] = {9905,"유비","좋소! 그대를 종사로 삼고, 이적이 보좌하도록 하겠소. 유기는 양양으로 보내고, 관우를 형주로 보내겠소.","좋소! 그대를 종사로 삼고, 이적이 보좌하도록 하겠소. 유기는 양양으로 보내고, 관우를 형주로 보내겠소.","마량","","",},
        [5107] = {9905,"유비","영릉을 취하기 위해 장비를 선봉으로 하고, 조운이 뒤에서 지원하고, 공명과 나는 중군이 되어 인마 만오천을 이끌겠소. 관우는 형주를 지키고, 미축, 유봉은 강릉을 지키시오.","영릉을 취하기 위해 장비를 선봉으로 하고, 조운이 뒤에서 지원하고, 공명과 나는 중군이 되어 인마 만오천을 이끌겠소. 관우는 형주를 지키고, 미축, 유봉은 강릉을 지키시오.","마량","","",},
        [5108] = {10001,"제갈량","나는 남양의 제갈공명이다. 조조가 백만을 이끌고 왔으나, 나의 작은 계략으로 전군이 몰살당했다.","나는 남양의 제갈공명이다. 조조가 백만을 이끌고 왔으나, 나의 작은 계략으로 전군이 몰살당했다.","형도영","","",},
        [5109] = {10001,"제갈량","너희가 어찌 나와 싸우겠느냐? 빨리 투항하지 않고 뭐 하느냐?","너희가 어찌 나와 싸우겠느냐? 빨리 투항하지 않고 뭐 하느냐?","형도영","","",},
        [5110] = {10001,"형도영","적벽대전은 주유의 계책이오. 당신이 무슨 일을 했다고 허풍을 떠는 것이냐!","적벽대전은 주유의 계책이오. 당신이 무슨 일을 했다고 허풍을 떠는 것이냐!","제갈량","","",},
