    },
    -- data
    _data = {
        [1] = {1,"รอบที่1","วีรบุรุษปรากฏ","นายท่านกับเล่ากวนเตียวร่วมสาบานกันที่สวนท้อ ไล่ล่าตั๋งโต๊ะ ได้รับป้ายยอดขุนพล คบหากวนอิ๋นผิง เตียวซีบุกสร้างแผ่นดิน!",},
        [2] = {2,"รอบที่2","กบฎผ้าเหลือง","นายท่านพบกองทัพผ้าเหลือง เอาชนะเตียวก๊กอิเกียด ได้รับป้ายยอดขุนพล บารมีทั่วจงหยวน!",},
        [3] = {3,"รอบที่3","วุ่นวายลกเอี๋ยง","สิบขันทีวางอำนาจในราชสำนักฮั่น นายท่านฮึกเหิม ปราบกบฎ จักรพรรดิน้อยรู้สึกขอบคุณมาก มอบป้ายยอดขุนพลให้นายท่าน",},
        [4] = {4,"รอบที่4","โจโฉมอบดาบ","นายท่าน โจโฉยืมดาบลองฆ่าตั๋งโต๊ะไม่สำเร็จ แต่ความองอาจของเขาทำให้ตันก๋งยอมรับ ยินดีสละตำแหน่งขุนนางติดตาม นายท่านกับโจโฉบัญชาวีบุรุษทั่วแผ่นดินปราบปรามตั๋งโต๊ะ",},
        [5] = {5,"รอบที่5","กองทัพสิบเเปดหัวเมือง","นายท่านท้ารบเจ้าเมืองต่างๆ ทำลายสองพี่น้องตระกูอ้วน รบไม่มีแพ้ บารมีเกริกไกร",},
        [6] = {6,"รอบที่6","สี่วีรบุรุษสู้ศึกลิโป้","นายท่านกับเล่ากวนเตีย ฆ่าฮัวหยง รบลิโป้ ช่วยกองทัพสิบเเปดหัวเมืองจากอันตราย ชื่อเสียงเลื่องลือ",},
        [7] = {7,"รอบที่7","ซุนเกี๋ยนได้ลัญจกรแผ่นดิน","นายท่านเข้าลกเอี๋ยง ทำศึกพยัคฆ์ร้ายกังตั๋งซุนเกี๋ยน ชนะต่อเนื่อง ชื่อเสียงเลื่องลือ",},
        [8] = {8,"รอบที่8","ข้ามแม่น้ำโจมตีเล่าเปียว","นายท่านขี่ม้าเข้าเกงจิ๋ว เอาชนะเล่าเปียว เอาชนะซุนเซ็ก บารมีทั่ว9แคว้น",},
        [9] = {9,"รอบที่9","ยุทธ์การที่เอ๊งหยง","โจโฉรบแพ้เอ๊งหยง โชคดีที่ได้นายท่านช่วยเหลือ ตั๋งโต๊ะเพื่อหลบกระบี่ของนายท่าน หนีหัวซุกหัวซุนไปเตียงฮัน",},
        [10] = {10,"รอบที่10","ศาลาเฟิ่งอี้","นายท่านฆ่าตั๋งโต๊ะ ช่วยเตียวเสี้ยนสาวงามอันดับหนึ่งในแผ่นดิน ชาวบ้านเตียงฮันร่วมยินดี",},
        [11] = {11,"รอบที่11","บุกเตียงฮัน","นายท่านเอาชนะลิฉุยกุยกี ทำศึกกับม้าเฉียว รู้สึกถูกชะตา คบเป็นคนรู้ใจ",},
        [12] = {12,"รอบที่12","แม่ทัพม้าขาว","อ้วนเสี้ยวกองซุนจ้านแย่งชิงกิจิ๋ว นายท่านรบกับแม่ทัพม้าขาวกองซุนจ้าน ชื่อเสียงเลื่องลือ",},
        [13] = {13,"รอบที่13","ศึกสะพานจีเกี้ยว","นายท่านช่วยกองซุนจ้านเอาชนะอ้วนเสี้ยว แสดงบารมีเทพ คมกริบไร้ต้าน",},
        [14] = {14,"รอบที่14","รับปราชญ์กุนจิ๋ว","นายท่านบุกกุนจิ๋ว คบหาซุนฮกซุนฮิว เอาชนะเตียนอุย ฝีมือดีฉลาด ชื่อเสียงเกริกไกร",},
        [15] = {15,"รอบที่15","ภัยมาถึงซีจิ๋ว","โจโฉแก้แค้นให้บิดายกทัพไปซีจิ๋ว นายท่านเกลี่ยกล่อมโจโฉ โจโฉโมโหมากดึงดันยกทัพ",},
        [16] = {16,"รอบที่16","ไทสูจู้ส่งจดหมาย","โจโฉบุกตีซีจิ๋ว นายท่านช่วยโตเกี๋ยม ขงหยงปกป้องเมือง ประลองกับไทสูจู้ ไทสูจู้ยอมแพ้โดยดี",},
        [17] = {17,"รอบที่17","ยืมจูล่ง","นายท่านไปหากองซุนจ้านยืมจูล่ง ประลองกับจูล่งอีกครั้ง ท่าทางองอาจ วีรบุรุษแห่งยุค",},
        [18] = {18,"รอบที่18","กบฏปักเอี้ยง","โจโฉต่อสู้กับลิโป้เกือบตาย นายท่านช่วยโจโฉออกมาได้ โจโฉรู้สึกขอบคุณมาก ถอนทัพจากซีจิ๋ว",},
        [19] = {19,"รอบที่19","มอบเมืองซีจิ๋วสามครั้ง","โตเกี๋ยมเจ้าเมืองเอกซีจิ๋วป่วย คิดจะสละตำแหน่งเจ้าเมืองเอกให้เล่าปี่ เล่าปี่ปฏิเสธซีจิ๋ว3รอบ สุดท้ายนายท่านเกลี่ยกล่อมจนยอมรับซีจิ๋วไว้",},
        [20] = {20,"รอบที่20","ตะวันออกคืนสู่ลกเอี๋ยง","นายท่านเอาชนะลิฉุยกุยกี คบหาขุนพลซิหลง คุ้มครองฮั่นเหี้ยนเต้ไปลกเอี๋ยง จักรพรรดิเหี้ยนเต้รู้สึกขอบคุณนายท่านมาก แต่งตั้งให้นายท่านเป็นแม่ทัพกึกก้อง นายท่านไม่อยากยึดติด จึงรับแต่ตำแหน่งลอยๆ",},
        [21] = {21,"รอบที่21","จับโอรสสวรรค์บัญชาเจ้าเมือง","ฮั่นเหี้ยนเต้ตะวันออกเป็นของลกเอี๋ยงแล้ว โจโฉรับโอรสสวรรค์ ย้ายเมืองหลวงไปที่ฮูโต๋ นายท่านส่งโอรสสวรรค์ไปฮูโต๋ ได้รู้จักกุยที่ฮูโต๋ ทำให้กุยแกเลื่อมใสนัก คบเป็นสหายรู้ใจ",},
        [22] = {22,"รอบที่22","เสี่ยงทายเกาทัณฑ์","อ้วนสุดวางแผนบุกเล่าปี่ นายท่านต่อสู้กับลิโป้ ช่วยเล่าปี่",},
        [23] = {23,"รอบที่23","พยัคฆ์แห่งเจียงหวย","ลิโป้ยึดครองซีจิ๋ว พยัคฆ์แห่งเจียงหวย อ้วนสุดอยากดองเป็นญาติ นายท่านไม่พอใจนิสัยของอ้วนสุด หลี่หลิงจวีเชื่อนายท่าน ปฏิเสธไร้เยื้อใย",},
        [24] = {24,"รอบที่24","ชิงม้าเตียวหุย","เตียวหุยชิงม้าของลิโป้ ทำให้ลิโป้โกรธ นายท่านคุ้มครองเล่าปี่ไปหาโจโฉ โจโฉแต่งตั้งเล่าปี่เป็นเจ้าเมืองอิจิ๋ว นายท่านไม่อยากเป็นขุนนาง ทำให้คนชมเชย",},
        [25] = {25,"รอบที่25","ป้าอ๋องน้อยซุนเซ็ก","นายท่านพเนจรที่กังตั๋ง ช่วยซุนเซ็กปราบพวกกลุ่มอำนาจเก่า คบหาเตียวเจียวจิวยี่ ชื่อเสียงทั่วกังตั๋ง",},
        [26] = {26,"รอบที่26","ปะทะศึกไทสูจู้","นายท่านดวลกับไทสูจู้ ทำศึกเงียมแปะฮอ ช่วยซุนเซ็กยึดครองกังตั๋ง",},
        [27] = {27,"รอบที่27","หมอเทพฮัวโต๋","นายท่านช่วยซุนกวนปกป้องเมือง จิวท่ายเพื่อช่วยซุนกวนถูกทวนแทงหลายที นายท่านเชิญหมอเทพฮัวโต๋มาช่วยรักษา ทำให้ซุนกวน จิวท่ายรู้สึกขอบคุณมาก",},
        [28] = {28,"รอบที่28","เคาทูสวามิภักดิ์โจโฉ","นายท่านไปแดนเฉินตี้ปราบสมุนผ้าเหลือง บังเอิญพบเคาทู เคาทูมีกำลังแรงมาก นายท่านแนะนำเคาทูให้กับโจโฉ โจโฉดีใจมาก เคาทูได้รับสมญานามพยัคฆ์บ้า",},
        [29] = {29,"รอบที่29","ศึกอ้วนเซีย","นายท่านช่วยโจโฉบุกอ้วนเซีย เตียวสิ้วยอมแพ้ แต่เพราะนางแซ่เจาถูกฉุด ทำให้ลอบบุกค่ายโจโฉกลางดึก เตียนอุยรบจนตาย นายท่านสู้กับเตียวสิ้ว ช่วยโจโฉออกมา",},
        [30] = {30,"รอบที่30","เอาชนะเตียวสิ้ว","นายท่านช่วยโจโฉเอาชนะเตียวสิ้ว แฮหัวตุ้นกับอิกิ๋มนำทหารมาช่วย อิกิ๋มไม่กลัวข่าวลือ ปกครองทหารเคร่งครัด นายท่านชื่นชมอิกิ๋มมาก คบหาเป็นสหายรู้ใจ",},
        [31] = {31,"รอบที่31","อ้วนสุดเป็นกษัตริย์","อ้วนสุดตั้งตนเป็นจักรพรรดิที่ฉิวฉุน ถูกคนต่อต้าน ทำให้แผ่นดินลุกเป็นไฟ นายท่านระดมพลโจโฉลิโป้เล่าปี่ร่วมกันโจมตีอ้วนสุด เอาชนะอ้วนสุด ชื่อเสียงกึกก้องทั่วแผ่นดิน",},
        [32] = {32,"รอบที่32","หกเขตเอกภาพ","นายท่านช่วยซุนเซ็กบุกโลกั๋ง รวบรวม6แคว้นกังตั๋ง บารมีทั่วง่อก๊ก",},
        [33] = {33,"รอบที่33","สาวงามสองเกี้ยว","นายท่านพบสองเกี้ยวแห่งกังตั๋ง ไต้เกี้ยวเสี่ยวเกี้ยวสุดยอดสองสาว งามเลื่องลือแผ่นดิน!",},
        [34] = {34,"รอบที่34","รากฐานกังตั๋ง","นายท่านเกลี่ยกล่อมซุนเซ็กแตกหักอ้วนสุด ชื่อเสียงดังทั่วกังตั๋ง พวกโลซกอัจฉริยะแห่งกังตั๋งได้ยินชื่อเสียงของนายท่าน มาขอสวามิภักดิ์",},
        [35] = {35,"รอบที่35","ตัดผมแทนหัว","นายท่านแนะนำโจโฉไปปราบเตียวสิ้ว แล้วค่อยยึดเหอเป่ย ระหว่างปราบเตียวสิ้ว ม้าของโจโฉตกใจ ไปเหยียบที่นาชาวบ้าน นายท่านช่วยโจโฉตัดผมแทนหัว แก้ไขสถานการณ์อย่างมีไหวพริบ",},
        [36] = {36,"รอบที่36","ถอนธนูกลืนลูกตา","ลิโป้บุกเล่าปี่ โจโฉส่งแฮหัวตุ้นนำทหารมาช่วยสนับสนุน แฮหัวตุ้นปะทะโกซุ่น ถูกธนูยิงที่ตาข้างซ้าย นายท่านรีบไปช่วยแฮหัวตุ้น แฮหัวตุ้นรู้สึกขอบคุณมาก เกิดเหตุการณ์ถอนธนูกลืนลูกตา",},
        [37] = {37,"รอบที่37","น้ำท่วมแห้ฝือ","ลิโป้ทิ้งซีจิ๋ว เหลือแค่เมืองแห้ฝือ นายท่านใช้น้ำท่วมแห้ฝือ ล้อมลิโป้ เอาชนะเตียวเลี้ยว ชาวบ้านเมืองแห้ฝือแตกตื่น เฮาเสงขโมยม้ามามอบให้",},
        [38] = {38,"รอบที่38","หอประตูขาว","นายท่านกับลิโป้ต่อสู้กันที่หอประตูขาว จับเป็นลิโป้ ชื่อเสียงเลื่องลือ",},
        [39] = {39,"รอบที่39","ศึกอิวจิ๋ว","นายท่านไปอิวจิ๋วช่วยกองซุนจ้าน กองซุนจ้านยอมตายไม่ยอมแพ้ เผาตัวเองตาย ขุนพลกองซุนจ้านล่มสลาย น่าเสียดายยิ่งนัก",},
        [40] = {40,"รอบที่40","ล้อมวงล่าสัตว์","เล่าปี่ถูกโอรสสวรรค์แต่งตั้งเป็นพระเจ้าอา นายท่านกับโอรสสวรรค์ไปล่าสัตว์ โจโฉควบม้าแซงโอรสสวรรค์ เผยความทะเยอทะยาน ผู้คนตกตะลึง",},
        [41] = {41,"รอบที่41","ราชโองการในเสื้อ","ฮั่นเหี้ยนเต้ใช้เลือดเขียนราชโองการเย็บไว้ในเสื้อ แอบส่งให้ตังสิน ตังสินเชิญนายท่าน เล่าปี่มา ร่วมวางแผนสังหารโจโฉ",},
        [42] = {42,"รอบที่42","ชิงเหมยอุ่นเหล้า","นายท่าน โจโฉ เล่าปี่ อุ่นเหล้าวิจารณ์วีรบุรุษ ท่าทางองอาจ โจโฉขยาดบารมีนายท่าน เกิดความระแวงในใจ",},
        [43] = {43,"รอบที่43","เล่าปี่รอดอันตราย","เล่าปี่รับคำสั่งสกัดอ้วนสุด โจโฉส่งคนไปไล่โจมตี นายท่านเอาชนะเคาทู ต่อสู้ทางปัญญากับกุยแก ช่วยเล่าปี่หนีรอดมาได้",},
        [44] = {44,"รอบที่44","การตายของอ้วนสุด","ทหารอ้วนสุดบุกซีจิ๋ว นายท่านฆ่ากิเหลง เอาชนะอ้วนสุด อ้วนสุดโกรธจนกระอักเลือดตาย ลัญจกรแผ่นดินของอ้วนสุดสุดท้ายตกเป็นของโจโฉ",},
        [45] = {45,"รอบที่45","กวนอูฆ่ากีเหมา","แผนราชโองการใต้เสื้อแตก โจโฉฆ่าจี๋ผิงตังสิน นายท่านช่วยเล่าปี่หนีจากโจโฉ ฆ่ากีเหมา ชิงซีจิ๋ว",},
        [46] = {46,"รอบที่46","กวนเตียวจับอ๋องเล่า","นายท่านช่วยกวนอูเตียวหุยจับสองแม่ทัพอ๋องเล่า แผนพิสดาร ทำให้คนชื่นชมโจโฉโมโหมาก",},
        [47] = {47,"รอบที่47","อ้วนเสี้ยวประกาศศึกโจโฉ","นายท่านใช้แผนล้อมเว่ยช่วยเจ้า เข้ากิจิ๋วเกลี่ยกล่อมอ้วนเสี้ยวประกาศศึกโจโฉ อ้วนโจเปิดฉากทำศึก",},
        [48] = {48,"รอบที่48","กาเซี่ยงจำนนโจโฉ","นายท่านพบกาเซี่ยง เอาชนะเตียวสิ้ว เปิดศึกอ้วนโจ",},
        [49] = {49,"รอบที่49","ศึกเสียวพ่าย","นายท่านช่วยเล่าปี่ปกป้องเสียวพ่าย ปะทะกองทัพใหญ่โจโฉ เอาชนะแฮหัวตุ้น เตียวเลี้ยวตามลำดับ ทำให้กองทัพโจเสียขวัญ",},
        [50] = {50,"รอบที่50","วงล้อม้าขาว","นายท่านช่วยกวนอูปะทะทัพอ้วน ฆ่าสองแม่ทัพอ้วนเสี้ยวคืองันเหลียง บุนทิวตามลำดับ ชื่อเสียงกึกก้อง",},
        [51] = {51,"รอบที่51","ฝ่าห้าด่านสังหารหกขุนพล","นายท่านช่วยกวนอูหนีจากทัพโจ ฝ่าห้าด่านสังหารหกขุนพล ทัพโจขวัญหนีดีฝ่อ",},
        [52] = {52,"รอบที่52","พันลี้ลุยเดี่ยว","นายท่านขี่ม้าบุกเดี่ยว เกลี่ยกล่อมจิวฉองเลียวฮัวกวนเป๋ง ปะทะกวนอู มังกรปะทะพยัคฆ์",},
        [53] = {53,"รอบที่53","พบกันเมืองเก่า","นายท่านช่วยกวนอูให้ไปพบกับเล่าปี่สำเร็จ สี่พี่น้องพบกันพร้อมหน้า นายท่านท่าทางองอาจ ท้ารบจูล่ง จูล่งยอมรับว่าสู้ไม่ไหว",},
        [54] = {54,"รอบที่54","สังหารอิเกียด","นายท่านไปที่กังตั๋งอีกครั้ง ช่วยซุนเซ็กฆ่าอิเกียด สร้างชื่อในกังตั๋ง",},
        [55] = {55,"รอบที่55","ซุนเซ็กถูกลอบแทง","ซุนเซ็กถูกแทงตาย นายท่านกำชับขุนนางบุ๋นกังตั๋ง ช่วยให้ซุนกวนได้ปกครองกังตั๋ง ซุนกวนรู้สึกขอบคุณมาก",},
        [56] = {56,"รอบที่56","ซุนกวนรับปราชญ์","นายท่านประลองกับขุนนางบุ๋นกังตั๋ง สู้โลซก ชนะจิวยี่ ท่าทางองอาจ ทำให้คนตกตะลึง",},
        [57] = {57,"รอบที่57","ศึกกัวต๋อ","ศึกกัวต๋อ นายท่านท้ารบกองทัพอ้วนเสี้ยว เอาชนะขุนพลสิมโพย เตียนห้อง จอสิวตามลำดับ",},
        [58] = {58,"รอบที่58","ไฟเผาอัวเจ๋า","นายท่านช่วยโจโฉไฟเผาอัวเจ๋า เอาชนะอ้วนเสี้ยว สองคนเข้าใจกัน ร่วมมือทำศึกกับอ้วนเสี้ยว",},
        [59] = {59,"รอบที่59","เตียวคับทรยศ","นายท่านเข้ากองทัพอ้วนเสี้ยวอีกครั้ง เกลี่ยกล่อมเตียวคับให้สวามิภักดิ์โจโฉ เตียวคับซาบซึ้งใจนายท่าน ยอมทำตาม หานายใหม่",},
        [60] = {60,"รอบที่60","ศึกซองเต๋ง","เทียหยกวางแผนดักซุ่มสิบทิศ นายท่านช่วยโจโฉทำศึกซองเต๋งเอาชนะอ้วนเสี้ยว ประลองมิตรภาพกับขุนพลโจโฉ รบไม่มีแพ้ ทำให้ผู้คนตกตะลึง",},
        [61] = {61,"รอบที่61","อ้วนเสี้ยวกระอักเลือด","นายท่านเอาชนะสิมโพย ชนะกุนซือฮองกี๋ รบกับอ้วนซงผู้สืบทอดของอ้วนเสี้ยว รบชนะต่อเนื่อง อ้วนเสี้ยวตกใจมาก กระอักเลือดตาย อ้วนซงอ้วนถำหนีไป",},
        [62] = {62,"รอบที่62","สุมาอี้แกล้งป่วย","โจโฉรับตำแหน่งเจ้ากรมโยธา ได้ยินชื่อเสียงของสุมาอี้แล้ว เรียกเข้ามารับราชการ สุมาอี้หาข้ออ้างป่วยโรคไข้ข้ออักเสบ ลุกไม้ได้ ปฏิเสธโจโฉ นายท่านสงสัย ไปตรวจดู สุมาอี้เล่าความจริง และชื่นชมนายท่านมาก",},
        [63] = {63,"รอบที่63","โจฉองชั่งช้าง","ซุนกวนมอบช้างให้ราชสำนักตัวหนึ่ง โจโฉอยากรู้ว่าช้างตัวนี้หนักเท่าไหร่ ภายใต้การแนะนำของนายท่าน โจฉองน้อยคิดวิธีใช้เรือชั่งน้ำหนักได้",},
        [64] = {64,"รอบที่64","น้ำท่วมกิจิ๋ว","นายท่านเสนอแผนใช้น้ำจากแม่น้ำท่วมกิจิ๋ว ช่วยโจโฉบุกยึดกิจิ๋ว เขาฮิวคิดว่าตัวเองเก่ง ถูกเคาทูฆ่าตาย",},
        [65] = {65,"รอบที่65","รับเอียนสี","นายท่านช่วยโจโฉบุกทำลายเงียบกุ๋น เข้าตำหนักอ้วน รู้จักสาวงามเอียนสี เอียนสีหลงรักโจสิด โจโฉกลับจงใจยกเอียนสีให้กับโจผี",},
        [66] = {66,"รอบที่66","แผนสั่งเสียยึดเลียวตั๋ง","อ้วนซง อ้วนฮีหนีไปอูหวน นายท่านกับกุยแกเสนอยกทับไปบุกเลียวตั๋ง และชนะกลับมา สุดท้ายกุยแกตายเพราะเหนื่อยเกินไป นายท่านกับโจโฉเสียใจมาก",},
        [67] = {67,"รอบที่67","หอนกยูงทองแดง","นายท่านกับโจโฉเดินคุยเล่นกัน มีแสงทองแผ่เหมือนหางนกยูง โจสิดเสนอสร้างหอนกยูงทองแดง โจโฉรู้สึกยินดี เผยให้เห็นความทะเยอทะยาน",},
        [68] = {68,"รอบที่68","บุ้นกีคืนฮั่น","นายท่านรับการไหว้วานจากโจโฉ รับอัจฉริยะหญิงขุนพลซ่งหนูซัวบุ้นกี๋กลับมา ซัวบุ้นกี๋รู้สึกขอบคุณนายท่านมาก",},
        [69] = {69,"รอบที่69","สุมาอี้รับตำแหน่ง","โจโฉปลดสามพระยา ตั้งตนเป็นเสนาบดี และเรียกสุมาอี้เป็นขุนนาง นายท่านคุยกับสุมาอี้อย่างสนุกสนาน สุมาอี้เลื่อมใสนายท่านมาก",},
        [70] = {70,"รอบที่70","ฮัวโต๋ประสบภัย","โจโฉปวดหัวเลยเชิญฮัวโต๋มารักษา ฮัวโต๋เสนอให้ทำการผ่ากระโหลก โจโฉระแวงว่าฮัวโต๋จะคิดไม่ซื่อต่อตนเอง ลงสั่งประหารฮัวโต๋ นายท่านช่วยฮัวโต๋ออกมา ซ่อนตัวที่จิงเซียง",},
        [71] = {71,"รอบที่71","ควบม้าข้ามแม่น้ำตันเข","นายท่านไปจิงเซียงช่วยเล่าปี่ เล่าปี่รู้ตัวว่าแก่แล้วแต่ยังทำงานใหญ่ไม่สำเร็จ รู้สึกเสียใจ นายท่านให้กำลังใจเล่าปี่ว่าต้องทำสำเร็จแน่ เล่าปี่เกลี่ยกล่อมเล่าเปียวบุกตีโจโฉ เล่าเปียวบอกว่าตัวเองแค่อยากเฝ้าที่นี่ ไม่มีอุดมการณ์สร้างความยิ่งใหญ่ พบชัวฮูหยินชัวมอคิดจะฆ่าเล่าปี่ นายท่านช่วยเล่าปี่ขี่เต็กเลา กระโดดข้ามแม่น้ำ หนีรอดมาได้",},
        [72] = {72,"รอบที่72","ชีซีพบนายดี","นายท่านพบซินแสกระจกเงาสุมาเต๊กโช ประลองฝีมือกับชีซี นายท่านแนะนำชีซีให้กับเล่าปี่ ส่งเล่าปี่ไปซินเอี้ย",},
        [73] = {73,"รอบที่73","เยี่ยมกระท่อมสามครั้ง","โจหยินจัดวางค่ายกลแปดทวารกลอนทอง ชีซีถ่ายทอดวิธีทำลายค่ายกล นายท่านนำทหารทำลายค่ายกล เอาชนะโจหยิน โจโฉรับมารดาของชีซีไป บีบให้ชีซีออกจากเล่าปี่ไปค่ายโจโฉ ก่อนไป ชีซีแนะนำจูกัดเหลียงให้เล่าปี่ นายท่านกับเล่าปี่ไปเยี่ยมกระท่อมสามครั้ง เชิญขงเบ้งลงจากเขา",},
        [74] = {74,"รอบที่74","กำเหลงกลับง่อ","นายท่านพเนจรกังตั๋ง เกลี่ยกล่อมซุนกวนรับกำเหลง และลงมือฆ่าหองจอ ทำให้ซุนกวนได้แก้แค้นแทนพ่อ ซุนกวนรู้สึกขอบคุณนายท่านมาก",},
        [75] = {75,"รอบที่75","ขอแต่งงานอุยซี","นายท่านช่วยเล่ากี๋บีกขงเบ้งวางแผน นายท่านช่วยขงเบ้งแต่งงานกับอุยซี เป็นที่เลื่องลือ",},
        [76] = {76,"รอบที่76","ไฟเผาเนินพกบ๋อง","กองทัพโจบุกซินเอี้ย นายท่านใช้แผนของจูกัดเหลียง ดวลเดี่ยวกับแฮหัวตุ้น ไฟเผาเนินพกบ๋อง ทัพโจโฉหนีไปพันลี้",},
        [77] = {77,"รอบที่77","โจโฉฆ่าขงหยง","แฮหัวตุ้นแพ้กลับไปฮูโต๋ โจโฉนำทัพมาบุกเล่าปี่ด้วยตนเอง นายท่านไปเกลี่ยกล่อม โจโฉตัดสินใจด้วยตนเอง ฆ่าขงหยง หลังจากเรื่องนี้ นายท่านกับโจโฉแตกหักกัน",},
        [78] = {78,"รอบที่78","เปลี่ยนแปลงเกงจิ๋ว","เล่าเปียวป่วยตาย ชัวฮูหยินปลอมพินัยกรรม แต่งตั้งให้บุตรคนรองเล่าจ๋องเป็นเจ้าเมืองเกงจิ๋ว เล่าจ๋องคิดจะไปสวามิภักดิ์โจโฉ นายท่านสั่งสอนพวกเล่าจ๋องชัวมอ ไปขัดขวางทัพใหญ่โจโฉ",},
        [79] = {79,"รอบที่79","ไฟเผาซินเอี้ย","เล่าปี่สละเมืองซินเอี้ย ไปเมืองอ้วนเสียหลบทัพโจโฉ นายท่านใช้แผนของขงเบ้ง คอยโจหยินนำทหารเข้าปักหลักในเมืองซินเอี้ย แล้วใช้โจมตีไฟกลางดึก ทำให้โจหยินแพ้ราบคาบ นายท่านพบแม่ทัพอุยเอี๋ยน ประลองกับเขา อุยเอี๋ยนเลื่อมใสนายท่านมาก",},
        [80] = {80,"รอบที่80","พาชาวบ้านข้ามแม่น้ำ","โจโฉส่งอิกิ๋มฆ่าเล่าจ๋องชัวฮูหยิน และส่งและส่งชีซีเกลี่ยกล่อมนายท่าน ชีซีเห็นแก่มิตรภาพเก่า บอกนายท่านทัพใหญ่โจโฉอยู่ไม่ไกล นายท่านกับเล่าปี่เปลี่ยนไปกังเหลง ชาวบ้านนับหมื่นยินดีติดตามไปด้วย ร่วมทางกับนายท่าน",},
        [81] = {81,"รอบที่81","ขี่เดี่ยวช่วยนาย","ภรรยาเล่าปี่พลัดหลงกับทัพใหญ่ นายท่านไปหา บังเอิญพบแม่ทัพจูล่งมารับ สองคนไปช่วยอาเต๊าด้วยกัน มอบให้เล่าปี่ เล่าปี่ถอนหายใจ เพื่อเด็กทารก เกือบสูญเสียน้องสี่กับจูล่ง!",},
        [82] = {82,"รอบที่82","อาละวาดเนินเตียวปันโป๋","โจโฉบุกเกงจิ๋ว นายท่านช่วยอาเต๊ากลับมา เตียวหุยมาคอยรับที่สะพานเตียวปันเกี้ยวตังหยาง กองทัพโจโฉเห็นเตียวหุยยืนถือทวนยืนเดี่ยว ไม่มีใครกล้าต่อสู้ เตียวหุยตะโกนเสียงดัง ทำเอาแม่ทัพของโจโฉแฮหัวเจี๋ยตกใจตาย ทหารโจโฉขวัญหนีดีฝ่อ หนีตายยกใหญ่",},
        [83] = {83,"รอบที่83","สงครามลิ้น","นายท่านกับจูกัดเหลียงนั่งเรือไปกังตั๋งเจรจาร่วมมือกับง่อต้านทัพโจโฉ ถูกกุนซือง่อก๊กเหยียดหยาม สุดท้ายต่างก็ถูกจูกัดเหลียงตอกกลับได้หมด จนพูดไม่ออก",},
        [84] = {84,"รอบที่84","ปัญญายั่วจิวยี่","ทำสงครามน้ำลายกับบัณฑิตแล้วนายท่านกับขงเบ้งมาพบจิวยี่ จิวยี่แกล้งทะเลาะกับโลซก นายท่านใช้แผนยั่วยุ บอกว่าโจโฉคิดครอบรองเสี่ยวเกี้ยวภรรยาจิวยี่ จิวยี่โกรธมาก ในที่สุดก็ยินดีร่วมมือกับเล่าปี่ต้านทัพโจโฉ",},
        [85] = {85,"รอบที่85","พันธมิตรซุนเล่า","ซุนกวนรู้สึกลังเล นายท่านจงใจประลองกับจูล่งกวนอู และคุยเรื่องประวัติศาสตร์กับขงเบ้ง แสดงถึงฝีมือดีฉลาดสง่างาม สุดท้ายซุนกวนตัดสินใจร่วมมือกับเล่าปี่ ร่วมมือกับต่อต้านโจโฉ",},
        [86] = {86,"รอบที่86","เจียวก้านติดกับ","นายท่านแอบบุกค่ายโจโฉ พบว่าทหารเรือชัวมอ เตียวอุ๋นได้รับความสำคัญมาก นายท่านรู้ว่าโจโฉเป็นคนขี้ระแวง จึงใช้เจียวก้าน วางแผนยุแยง โจโฉหลงกลฆ่าสองแม่ทัพตาย",},
        [87] = {87,"รอบที่87","เรือยืมธนู","พันธมิตรซุนเล่าขาดธนูอย่างหนัก จิวยี่ให้ขงเบ้งผลิตธนู 1 แสนดอก ภายใต้แผนของขงเบ้ง นายท่านนำทหารไปค่ายโจโฉ ท่ามกลางหมอกหนัก ใช้เรือฟางยืมธนู สร้างความดีความชอบ",},
        [88] = {88,"รอบที่88","แผนยอมเจ็บตัว","อุยกายรับคำสั่งให้แกล้งสวามิภักดิ์ เพื่อหลอกโจโฉให้ไว้ใจ จิวยี่จงใจโบยอุยกาย ทำให้อุยกายแกล้งโมโหไปสวามิภักดิ์ศัตรู",},
        [89] = {89,"รอบที่89","โซ่เหล็กห่วงโซ่","เจียวก้านแนะนำบังทองให้โจโฉ ถ้าโจโฉลำบากที่ไม่มีแม่ทัพเรือที่มีประสบการณ์ทำศึก บังทองแนะนำโซ่เหล็กห่วงโซ่ แต่กลับถูกชีซีมองออก นายท่านปลอมเป็นทหารติดตาม สอนวิธีเอาตัวรอดจากชีซี",},
        [90] = {90,"รอบที่90","โจโฉมอบกลอน","เรือเล็กของกองทัพโจโฉร้อยโซ่เหล็กห่วงโซ่ มีความนิ่งเหมือนบก โจโฉยินดีมาก ขับกวีบทกวีต่วนเกอสิงนายท่านกับบังทองหาโอกาสหนีออกจากค่ายโจโฉ กลับไปกังตั๋ง",},
        [91] = {91,"รอบที่91","ยืมลมตะวันออก","จิวยี่ตัดสินใจใช้ไฟโจมตีค่ายโจโฉ แต่มีลมตะวันตกเฉียงเหนือพัดมาต่อเนื่อง จิวยี่ห่วย จูกัดเหลียงตั้งแห่งเจ็ดดาว ยืมลมตะวันออก จิวยี่ส่งคนมาลอบฆ่าจูกัดเหลียง นายท่านปกป้องขงเบ้งกลับไปแฮเค้าอย่างปลอดภัย",},
        [92] = {92,"รอบที่92","ไฟเผาผาแดง","อุยกายแกล้งทำเป็นสวามิภักดิ์โจโฉ เรือไฟสิบกว่าลำยืมลมตะวันออกเฉียงใต้พุ่งชนทัพเรือของโจโฉ นายท่านนำทหารเผาผาแดง ทัพโจโฉเสียหายหนัก นังแต่นี้กลายเป็นกระถางสามขา โจ ซุน เล่า",},
        [93] = {93,"รอบที่93","กวนอูทำสัญญาทหาร","หลังศึกผาแดง ในค่ายใหญ่เล่าปี่ กุนซือจูกัดเหลียงปรับกำลังทหาร ออกตามล่ากองทัพโจโฉที่เหลือรอด มีแต่กวนอูที่ไม่ถูกสั่งงาน นายท่านรู้สึกไม่พอใจแทนกวนอู ทำให้กวนอูซาบซึ้งใจนัก",},
        [94] = {94,"รอบที่94","เส้นทางฮัวหยง","หลังผาแดงโจโฉหนีหัวซุกหัวซุน จูกัดเหลียงส่งกวนอูไปดักซุ่มที่เส้นทางฮัวหยง กวนอูเป็นแก่มิตรภาพเก่า ไม่สนใจคำสั่งทหาร ปล่อยโจโฉไป นายท่านเรียกเล่าปี่มาขอชีวิตกวนอู",},
        [95] = {95,"รอบที่95","โจฉองป่วยตาย","โจโฉหนีซมซาน แสดงท่าทางอ่อนข้อให้นายท่าน นายท่านเห็นแก่มิตรภาพเก่า ส่งโจโฉกลับฮูโต๋ โจฉองป่วยหนัก นายท่านหาหมอดีไปทั่ว ยังหาใครมาช่วยรักษาโจฉองไม่ได้ เสียใจมาก",},
        [96] = {96,"รอบที่96","จู่โจมอี๋หลิง","จิวยี่ไปถึงลำกุ๋นบุกตีโจหยิน แต่ปราบไม่ได้ กำเหลงจู่โจมอี๋หลิงฉับพลัน บีบขนาบโจหยิน กลับถูกทหารสนับสนุนวุยก๊กล้อม นายท่านนำทหารไปช่วยกำเหลงด้วยตนเอง ขับไล่ทหารศัตรู",},
        [97] = {97,"รอบที่97","จิวยี่ทำศึกโจหยิน","โจหยินยั่วยุ จิวยี่ไม่ทนอาการป่วย สวมเกราะออกรบ หน้าค่ายจงใจตกม้า แกล้งปล่อยข่าวตาย ล่อให้โจหยินติดกับ นายท่านกับกองทัพง่อก๊ก เอาชนะโจหยิน โจหยินแพ้หนีไปซงหยง",},
        [98] = {98,"รอบที่98","ขงเบ้งยั่วโมโหจิวยี่","จูกัดเหลียงวางแผนแยบยล ให้นายท่านไปเอาตราทหารลำกุ๋นกับจิวยี่ แกล้งหลอกทหารตั้งรับเกงจิ๋วมาช่วย โจมตียึดเกงจิ๋วกับซงหยงของวุยก๊ก จิวยี่ฟังแล้วโมโหมาก แผลกำเริบ",},
        [99] = {99,"รอบที่99","ม้าเลี้ยงเสนอแผน","นายท่านแนะนำม้าเลี้ยงให้กับเล่าปี่ ม้าเลี้ยงเสนอให้เล่ากี๋เป็นผู้ว่าแคว้นเกงจิ๋ว เพื่อปกป้องชาวบ้าน แล้วบุกลงใต้ปราบ 4 แคว้นบุเหลง เตียงสา ฮุยเอี๋ยง เลงเหลง สะสมเสบียง ตั้งรากฐาน",},
        [100] = {100,"รอบที่100","ปัญญายึดเลงเหลง","นายท่านทำศึกสิงเต้าหรง ใช้ปัญญายึดเมืองเลงเหลง สร้างผลงานใหญ่ ทุกคนเลื่อมใสนัก คิดลองทำกับอีกสามเมืองบ้าง",},
        [101] = {101,"รอบที่101","แผนจูล่งยึดฮุยเอี๋ยง","จูล่งทำสัญญากฏทหาร ไปฮุยเอี๋ยงกับนายท่าน เตียวหอมใช้แผนสาวงามจัดการจูล่งก่อน จูล่งไม่สนใจ; ภายหลังแกล้งใช้แผนสวามิภักดิ์กับจูล่ง แต่ถูกนายท่านมองออก เลยฆ่าฮุยเอี๋ยงซะ",},
        [102] = {102,"รอบที่102","เตียวหุยยึดบุเหลง","เตียวหุยเห็นจูล่งนายท่านได้เมืองคนละเมือง ร้อนใจมาก ทำสัญญากฏทหารไปยึดบุเหลง นายท่านกับเตียวซีแอบคอยช่วยเหลือ ช่วยเตียวหุยยึดบุเหลงได้",},
        [103] = {103,"รอบที่103","กวนอูปล่อยฮองตง","เตียงสาเจ้าเมืองเอกฮันเหียน มีนิสัยใจร้อน ชอบฆ่าคน เป็นที่รู้กันทั่ว กวนอูคิดจะยึดเตียงสา นำทหารในสังกัด 500 คนไป ขงเบ้งเป็นห่วงกลัวกวนอูสู้ไม่ได้ ให้นายท่านไปช่วยด้วย นายท่านต่อสู้กับฮองตงลูกน้องฮันเหียนชนะต่อเนื่อง กวนอูยอมปล่อยฮองตง ชื่อเสียงเลื่องลือ",},
        [104] = {104,"รอบที่104","รับอุยเอี๋ยนสวามิภักดิ์","ฮันเหียนสงสัยฮองตงร่วมมือกับกวนอู คิดจะฆ่าฮองตง อุยเอี๋ยนรีบมาฆ่าฮันเหียนช่วยฮองตง ไปคาราวะนายท่าน นายท่านแนะนำอุยเอี๋ยนให้ไปพบเล่าปี่ขงเบ้ง ขงเบ้งดูหลังกระโหลกของเขา เคาะแรงๆทีหนึ่งแล้วก็ปราบอุยเอี๋ยนสำเร็จ",},
        [105] = {105,"รอบที่105","เล่าปี่ยืมเกงจิ๋ว","เล่าปี่ได้รับสี่เมืองเกงจิ๋ว แต่ลำกุ๋นยังเป็นของซุนกวน นายท่านไปฉิวฉุนด้วยตนเอง ร่วมมือกับโลซก วางแผนร้ายกาจ มอบลำกุ๋นให้เล่าปี่ยืม",},
        [106] = {106,"รอบที่106","แรกศึกหับป๋า","โจโฉแพ้อนาถในศึกผาแดง แต่ทัพใหญ่ยังปักหลักอยู่ทางภาคเหนือของเกงจิ๋ว ซุนกวนส่งจิวยี่บุกโจมตีโจหยินที่กังเหลง ส่งเตียวเจียวบุกตังถูของจิ่วเจียง ส่วนตัวเองบุกเตียวเลี้ยวที่หับป๋า นายท่านช่วยซุนกวนด้วยตนเอง เอาชนะเตียวเลี้ยว",},
        [107] = {107,"รอบที่107","ไทสูจู้หลงกล","เตียวเลี้ยวส่งคนมามอบสาสน์ท้ารบ ซุนกวนโมโหมากรับสาสน์ท้ารบด้วยตนเอง แต่กลับถูกเตียวเลี้ยวฆ่าซงเขียม ไทสูจู้นำทหาร5000คนไปแก้แค้น ร่วมมือกับเกอติงคนบ้านเดียวกันร่วมมือบุกเมือง แต่ถูกเตียวเลี้ยวจับได้ ไทสูจู้ถูกธนูยิงหลายดอก แต่ยังดูห้าวหาญ",},
        [108] = {108,"รอบที่108","โลซกขอคืนเกงจิ๋ว","เล่ากี๋ป่วยตายแล้ว โลซกรับคำสั่งให้มาเอาเกงจิ๋วคืน นายท่านวางแผนร้ายกาจ เสนอช่วยเล่าปี่ยึดซีชวน จะคืนเกงจิ๋วให้ โลซกโมโหกลับไปง่อก๊ก",},
        [109] = {109,"รอบที่109","วัดกันลั่วซื่อ","กำฮูหยินของเล่าปี่ป่วยตาย จิวยี่เสนอให้จัดงานแต่งซุนเล่า ขงเบ้งรับปาก แอบมอบถุงให้นายท่าน3ถุง นายท่านคุ้มกันเล่าปี่มาถึงง่อก๊ก ซุนกวนแอบซุ่มมือขวานดาบ ถูกนายท่านจับได้ แก้สถานการณ์ทันท่วงที",},
        [110] = {110,"รอบที่110","ชุมนุมวีรบุรุษเนินหม่าโป","เล่าปี่ นายท่านกับซุนกวนประลองกำลังกันลับๆ ตัดหินหน้าตำหนัก ขี่ม้าขึ้นเนิน ประลองฝีมือ ประลองขี่ม้า มองภูเขาลำธาร ความรู้สึกฮึกเหิม",},
        [111] = {111,"รอบที่111","คู่สร้างคู่สม","นายท่านเป็นห่วงกลัวเล่าปี่ถูกทำร้าย วางแผนให้ง่อก๊กไถ้ปกป้องเล่าปี่ นายท่านช่วยเล่าปี่ผ่านการทดสอบของซุนซ่างเซียง ในที่สุดเล่าปี่กับซุนซ่างเซียงก็ได้แต่งงานกัน เข้าสู่ห้องหอ ชีวิตมีความสุข สมปรารถนา",},
        [112] = {112,"รอบที่112","ปัญญากระตุ้นเหล่าฮูหยิน","เล่าปี่แต่งงานแล้ว ลุ่มหลงสตรี ไม่สนใจเกงจิ๋ว นายท่านเปิดถุงที่สองของของเบ้ง เตือนเล่าปี่ ใช้ปัญญากระตุ้นซุนซ่างเซียง นายท่านคุ้มกันเล่าปี่กลับเกงจิ๋ว เอาชนะแม่ทัพง่อก๊กที่มาขัดขวาง ชนะอย่างสง่างาม ไม่มีใครต้านได้",},
        [113] = {113,"รอบที่113","ยั่วจิวยี่หนสอง","จิวยี่นำทหารมาขวางเล่าปี่กับนายท่าน ขงเบ้งส่งคนมารับแต่แรกแล้ว นายท่านกับกวนอูจูล่งประลองฝีมือกัน และเอาชนะทหารง่อก๊ก ทุกคนพูดเป็นเสียงเดียวกันว่า: อุบายจิวยี่แสนแยบยล เสียฮูหยินเสียซ้ำขุนศึกจิวยี่ได้ยินแผลเก่ากำเริบ",},
        [114] = {114,"รอบที่114","โจโฉรับสมัครคนเก่ง","ตั้งแต่ที่โจโฉรบแพ้ผาแดงแล้ว คิดจะแก้แค้น นายท่านไปเกลี่ยกล่อมโจโฉสถานการณ์กำหนดแล้วอย่าวู่วาม ภายใต้คำแนะนำของนายท่าน โจโฉรับคำสั่งรับสมัครปราชญ์ ระดมคนเก่งทั่วแผ่นดิน",},
        [115] = {115,"รอบที่115","งานเลี้ยงหอนกยูงทองแดง","ฤดูใบไม้ผลิเกี้ยงอังปี15 สร้างหอนกยูงทองแดงสำเร็จ โจโฉจัดงานเลี้ยงหอนกยูงทองแดง เชิญนายท่าน นายท่านกับขุนพลของโจโฉประลองฝีมือกัน ชนะอย่างสง่างาม",},
        [116] = {116,"รอบที่116","ขึ้นปราสาท","หอนกยูงทองแดงสำเร็จ โจโฉสั่งให้จัดงานขุนพลขี่ม้ายิงธนูเพื่อความบันเทิง นายท่านเสนอแนะให้ขุนนางบุ๋นแต่งกลอน ในบรรดากลองทั้งหมด โจสิดแต่งกลอนม้วนแรก ชื่อว่าขึ้นปราสาทนกยูงทำให้โจโฉชื่นชมโจสิดเป็นอย่างมาก",},
        [117] = {117,"รอบที่117","เทียหยกเสนอแผน","หลังจากที่ซุนเล่าแต่งงานกัน ซุนกวนส่งฮัวหิมมาแต่งตั้งเล่าปี่เป็นเจ้าเมืองเกงจิ๋ว โจโฉได้ยินแล้วตื่นตระหนก เทียหยกเสนอแผน แต่งตั้งจิวยี่เป็นเจ้าเมืองเอกลำกุ๋น เทียเภาเป็นเจ้าเมืองเอกกังแฮ ยั่วยุให้ตระกูลเล่าซุนแตกแยกกัน นายท่านได้ยินรีบกลับไปเกงจิ๋วเตือนเล่าปี่",},
        [118] = {118,"รอบที่118","ยั่วจิวยี่หนสาม","จิวยี่รับลำกุ๋น ส่งโลซกไปขอเกงจิ๋วคืน บอกว่ารับซีชวนจริงๆเพื่อยึดเกงจิ๋ว แผนนี้ถูกขงเบ้งมอบออก นายท่านเตือนจิวยี่ ยึดซีชวนจริงๆ โจโฉต้องถือโอกาสบุกแน่ จิวยี่ถอนหายใจยาว แผลเก่ากำเริบ จนเสียชีวิต",},
        [119] = {119,"รอบที่119","มังกรหลับร้องไห้อาลัยจิวยี่","ขงเบ้งไปง่อก๊กไว้อาลัย นายท่านนำทหารมาคุ้มกัน ขงเบ้งรินเหล้าคาราวะด้วยตนเอง ท่องกลอนว่า: โศกาท่านจิวยี่ โชคร้ายจากไป; ได้ยินข่าวร้าย เจ็บปวดใจเหมือนไส้ขาด…อาลัยจบ ขงเบ้งก็ร้องไห้ยกใหญ่ น้ำตาเป็นบ่อ ทำให้โลซกรู้สึกสงสารมาก",},
        [120] = {120,"รอบที่120","บังทองคืนจ๊ก","โลซกแนะนำบังทองให้ซุนกวน ไม่นึกว่าซุนกวนรังเกียจที่บังทองอัปลักษณ์ นายท่านเกลี่ยกล่อมบังทองไปเกงจิ๋วสวามิภักด์เล่าปี่ นับแต่นี้ มังกรหลับ หงส์ดรุณก็อยู่ในสังกัดเล่าปี่ เกงจิ๋วคนเก่งเต็มไปหมด ก้าวหน้าทุกชั่ววัน",},
        [121] = {121,"รอบที่121","ม้าเท้งประสบภัย","โจโฉคิดยกทัพลงใต้ กลัวเสเหลียงก่อกบฏ จึงสั่งให้ม้าเท้งแอบเข้าเมืองหลวง ตอนแรกม้าเท้งรับบัญชาฮ่องเต้เข้าวัง เข้าวังมาแล้ววางแผนกับหวางขุยลอบฆ่าโจโฉ โชคร้ายที่ภรรยาน้อยของหวางขุยเผยความลับ ทำให้ม้าเท้งต้องตาย",},
        [122] = {122,"รอบที่122","ตันกุ๋นเสนอแผน","โจโฉฆ่าม้าเท้งแล้ว ตัดสินใจยกทัพลงใต้ ได้ยินว่าเล่าปี่คิดบุกซีชวน ตันกุ๋นเสนอแผนยกทัพง่อก๊ก เล่าปี่ต้องมาช่วยเหลือแน่ แผนนี้ถูกขงเบ้งมองออก ขงเบ้งส่งนายท่านไปเสเหลียง ดักม้าเฉียว",},
        [123] = {123,"รอบที่123","ม้าเฉียวบุกเตียงฮัน","ม้าเฉียวหันซุยยกทัพ2แสน บุกไปเตียงฮัน จงฮิวเจ้าเมืองเตียงฮันยกทัพมาต้านศัตรู เมืองเตียงฮันป้องกันแน่นหนา บุกไม่แตกซักที นายท่านใช้ปัญญา วางแผน นำทหารบุกไปช่วยม้าเฉียวที่แนวหน้าตีเตียงฮันมาได้",},
        [124] = {124,"รอบที่124","ศึกตงก๋วน","โจโฉรู้ว่าสูญเสียเตียงฮัน ไม่กล้ายกทัพลงใต้ต่อ ส่งซิหลงโจหองพาไพร่พลไปรักษาตงก๋วนแทนจงฮิว ห้ามออกรบอีก ม้าเฉียวนำทัพมาด่าโจโฉ จงใจทำให้โจหองโกรธบุกออกจากด่านมา นายท่านถือโอกาสบุกฆ่า ยึดตงก๋วนได้",},
        [125] = {125,"รอบที่125","ตัดหนวดหนีตาย","โจโฉส่งทหารบุกตงก๋วน สู้ม้าเฉียวไม่ได้หนีซมซาน ม้าเฉียวนำทหารนับร้อยไล่จับโจโฉ ภายหลังมีคนเขียนกลอนว่า: ศึกตงก๋วนแพ้หนีซมซาน เมิ่งเต๋อแตกตื่นผ้าคลุมปลิว กระบี่ตัดหนวดเพราะโศกา ม้าเฉียวชื่อเสียงเลื่องลือ",},
        [126] = {126,"รอบที่126","เคาทูโชว์กล้ามสู้ม้าเฉียว","เคาทูส่งสาสน์ท้ารบบุกเดี่ยวไปสู้กับม้าเฉียว สองคนปะทะกันร้อยรอบ ไม่รู้แพ้รู้ชนะ เคาทูร้อนใจ บุกกลับค่าย ถอดเกราะหมวก รู้สึกฮึกเหิม ตัวแดงถือดาบ พลิกตัวขึ้นมาไปรบกับม้าเฉียว สองทัพปะทะใหญ่",},
        [127] = {127,"รอบที่127","ยุแยงหันซุย","โจโฉทำตามแผนกาเซี่ยงลบสาสน์ สร้างความแตกแยกระหว่างม้าเฉียวหันซุย ม้าเฉียวหันซุยหลงกล ฆ่ากันเอง โจโฉถือโอกาสชิงชัยชนะ นายท่านช่วยม้าเฉียวในเวลาคับขันพอดี",},
        [128] = {128,"รอบที่128","อ๋องฮั่นหนิงเตียวฬ่อ","ข่าวที่โจโฉเอาชนะเสเหลียงได้ลือไปถึงฮั่นต๋ง ทำให้เตียวฬ่อเจ้าเมืองเอกฮั่นหนิงตกใจ เตียวฬ่อคิดจะยกทัพไปชิงเอ็กจิ๋วคืนแล้วตั้งตนเป็นอ๋องฮั่นหนิง เล่าเจี้ยงเจ้าเมืองเอ็กจิ๋วได้ยินข่าวนี้แล้ว ส่งเตียวสงไปฮูโต๋เกลี่ยกล่อมโจโฉส่งทหารมายึดฮั่นต๋ง",},
        [129] = {129,"รอบที่129","ตำราใหม่โจโฉ","เตียวสงมาถึงฮูโต๋ เตรียมมอบแผนที่ซีชวนให้โจโฉ เอียวสิ้วกับเตียวสงทำสงครามน้ำลายกัน เตียวสงอ่านตำราใหม่โจโฉรอบเดียวท่องได้ทั้งหมด แต่โจโฉดูแคลนหน้าตาเตียวสง ไม่ต้อนรับด้วยมารยาท แถมโบกด้วยกระบองไล่ไป",},
        [130] = {130,"รอบที่130","เตียวสงมอบภาพซีชวน","เตียวสงหนีออกจากฮูโต๋ยามดำ ผ่านเกงจิ๋ว ได้รับการดูแลจากเล่าปี่กับนายท่านอย่างดี เตียวสงมอบแผนที่ซีชวนให้นายท่าน นายท่านไปเอ็กจิ๋ว เกลี่ยกล่อมหวดเจ้ง เบ้งตัดเป็นแนวไส้ศึกภายใน เตรียมการให้เล่าปี่บุกซีชวน",},
        [131] = {131,"รอบที่131","บังทองคิดยึดจ๊กตะวันตก","หวดเจ้งมาเกงจิ๋วเกลี่ยกล่อมเล่าปี่ข้ามแม่น้ำ บังทองเสนอจัดงานเลี้ยงกำจัดเล่าเจี้ยง เล่าปี่ใจอ่อน นายท่านแนะนำเล่าปี่ไปด่านแฮบังก๋วนต้านรับเตียวฬ่อ เล่าปี่ลังเลแต่ในที่สุดก็ตกลงข้ามแม่น้ำ",},
        [132] = {132,"รอบที่132","ข้ามน้ำช่วยอาเต๊า","ได้ยินว่าเล่าปี่ข้ามแม่น้ำ ซุนกวนวางแผนขวางเล่าปี่ โดยโกหกว่าง่อก๊กไถ้ป่วยหนักอยากรับซุนซ่างเซียงกลับมาง่อก๊ก นายท่านมองแผนง่อก๊กออก ส่งจูล่งข้ามน้ำไปช่วยอาเต๊า",},
        [133] = {133,"รอบที่133","กล่องเปล่าฆ่าซุนฮก","โจโฉขอรับแต่งตั้งเป็นวุยกง ซุนฮกคัดค้าน อยากให้โจโฉนำทัพไปเจียงหนาน สั่งให้ซุนฮกติดตาม ซุนฮกป่วยอยู่ที่ฉิวฉุน โจโฉส่งคนมามอบกล่องเปล่าให้ซุนฮก ซุุนฮกรู้ว่าโจโฉอยากฆ่าตนเอง เลยกินยาพิษฆ่าตัวตาย",},
        [134] = {134,"รอบที่134","มีบุตรให้ได้อย่างซุนกวน","เกี้ยงอังปีที่18 โจโฉนำทหาร4แสนบุกปากน้ำหรูซูของง่อก๊ก ซุนกวนนำทัพ7หมื่นมาต่อต้าน นายท่านไปช่วยซุนกวน โจโฉเห็นขุนพลง่อก๊กป้องกันแน่นหนา ซุนกวนกล้าหาญผิดปกติ ถอนหายใจมีบุตรให้ได้อย่างซุนกวน",},
        [135] = {135,"รอบที่135","บังทองสามแผน","เพราะโจโฉบุกปากน้ำหรูซู เล่าปี่ส่งนายท่านไปเฉิงตูขอทหารกับเสบียบ เล่าเจี้ยงให้แต่ทหารแก่มา เล่าปี่โมโหมาก บังทองมอบแผนสามข้อ นายท่านเกลี่ยกล่อมเล่าปี่ให้ใช้แผน ยึดซีจ๊ก หนึ่งม้าเรียบแม่น้ำ",},
        [136] = {136,"รอบที่136","ยึดเพ่ยกวาน","นายท่านคุ้มกันเล่าปี่กลับเมืองโปยเสีย ขอให้เอียวหวย โกภายออกจากด่านมาอำลา สองคนคิดฆ่าเล่าปี่ แต่กลับถูกนายท่านฆ่าแทน นายท่านใช้สองคนนำกองทัพ บุกเข้าด่านโปย ทหารจ๊กยอมแพ้ นายท่านชูธงชนะ ท่าทางองอาจ",},
        [137] = {137,"รอบที่137","บุกเมืองลกเสีย","เล่าปี่คิดยึดเมืองลกเสีย ฮองตงกับอุยเอี๋ยนอยากสร้างความชอบ บังทองสั่งให้สองคนแยกกันไปตีค่ายทหารของเหลงเปา เตงเหียน อุยเอี๋ยนละโมบอยากสร้างผลงาน คิดตีทั้งสองค่าย สุดท้ายหนีซมซาน โชคดีที่นายท่านกับฮองตงมาช่วยเหลือ",},
        [138] = {138,"รอบที่138","ลกห้องโห","จูกัดเหลียงมอบท้องฟ้า ส่งจดหมายเตือนเล่าปี่ ส่วนบังทองเข้าใจผิดคิดว่าขงเบ้งอยากแย่งผลงาน เล่าปี่แบ่งกองทัพเป็นสองทางบุกตีเมืองลกเสีย เล่าปี่มอบม้าเต็กเลาให้บังทอง บังทองขี่เต็กเลาข้ามลกห้องโห ติดกับดัก ตาย",},
        [139] = {139,"รอบที่139","บังทองร้องไห้","เทศกาลวันชีซี ขงเบ้งชมดาวตกที่เกงจิ๋ว ร้องไห้ให้บังทอง เล่าปี่ส่งนายท่านมาส่งจดหมาย ขอกองหนุนจากกุนซือขงเบ้ง ขงเบ้งมอบเกงจิ๋วให้กวนอูดูแล",},
        [140] = {140,"รอบที่140","คุณธรรมปล่อยเงียมหงัน","เตียวหุยนำทหารบุกปากุ๋น เงียมหงันเจ้าเมืองเอกปากุ๋นตั้งรับไม่ออกมา เตียวหุยบุกหลายครั้ง ถูกธนูยิงกลับมา นายท่านวางแผน สั่งทหารตัดไม้เปิดทาง ล่อทหารซุ่มโจมตี จับเป็นเงียมหงัน ยึดครองปากุ๋น",},
