        [241] = {2001,"","ตำนานเตียวเสี้ยน","เตียวเสี้ยน หนึ่งในสี่สุดยอดสามงาม โฉมงามจันทร์หลบ งามล่มเมือง พื่อตอบแทนบุญคุณอ้องอุ้นที่ชุบเลี้ยง ยอมสละตนใช้แผนสาวงามกำจัดตั๋งโต๊ะ",},
        [242] = {2002,"","ตำนานโฮจิ๋น","โฮจิ๋น ชื่อรองซุ่ยเกา น้องสาวเป็นคนโปรดของจักรพรรดิเลนเต้ถูกแต่งตั้งเป็นฮองเฮา เมื่อเกิดกบฏผ้าเหลือง โฮจิ๋นเป็นแม่ทัพใหญ่ ควบคุมเมืองหลวง",},
        [243] = {2003,"","ตำนานฮัวหยง","ฮัวหยง ขุนพลพยัคฆ์ลูกน้องตั๋งโต๊ะ ตอนที่กองทัพสิบเเปดหัวเมืองปราบตั๋งโต๊ะ ถูกกวนอูฆ่า ช่วงหนึ่งของตำนานความสำเร็จเหล้าอุ่นสังหารฮัวหยง",},
        [244] = {2004,"","ตำนานซุนฮก","ซุนฮก ชื่อรองเหวินรั่ว หน้าตาหมดจรด มีปัญญาดี เสนอแผนรวมแผ่นดินภาคเหนือให้กับโจโฉ",},
        [245] = {2005,"","ตำนานเตียวก๊ก","เตียวก๊ก ผู้นำทหารผ้าเหลือง ผู้ก่อตั้งลัทธิไท้ผิง เรียกตนเองว่าเป็นเจ้าพระยาสวรรค์ นำผู้คนก่อกบฎ ประวัติศาสตร์จารึกว่าเป็นกบฎผ้าเหลือง",},
        [246] = {2006,"","ตำนานซุนเกี๋ยน","ซุนเกี๋ยน ชื่อรองเหวินไถ คนที่เป็นรากฐานงานใหญ่ของง่อก๊ก ถูกเรียกว่าพยัคฆ์ร้ายกังตั๋ง ถนัดใช้ดาบใหญ่ ชอบโพกผ้าแดง เคยปราบผ้าเหลืองกับตั๋งโต๊ะ",},
        [247] = {2007,"","ตำนานไทสูจู้","ไทสูจู้ ชื่อรองจื้ออี้ ขุนพลง่อก๊ก เชี่ยวชาญการขี่ม้ายิงธนู ยิงธนูแม่นมาก มีคุณธรรมรักษาคำพูด เดิมเป็นลูกน้องเล่าอิ้ว ภายหลังติดตามซุนเซ็ก",},
        [248] = {2008,"","ตำนานไต้เกี้ยว","ไต้เกี้ยว สาวงามเลื่องชื่อในสามก๊ก เชี่ยวชาญดนตรี กลอน ผิวขาวดั่งหิมะ คิ้วงามดั่งวาด รอยยิ้มทำให้คนหลงใหล",},
        [249] = {2009,"","ตำนานกองซุนจ้าน","กองซุนจ้าน ชื่อรองปั๋วกุย ผู้นำแถบอิวจิ๋ว หน้าตางดงาม ฉลาดมีไหวพริบ ต่อสู้กับชนท้องถิ่นเผ่าภาคเหนือ กล้าหาญและรบดี มีบารมีตามชายแดน ",},
        [250] = {2010,"","ตำนานกาเซี่ยง","กาเซี่ยง ชื่อรองเหวินเหอ กุนซือชื่อดัง วางแผนให้ลิฉุยกุยกีตลบหลังโจมตีเตียงฮัน ถูกเรียกว่าปราชญ์พิษ ภายหลังไปเข้ากับโจโฉ เป็นกุนซือคนสำคัญของวุยก๊ก",},
        [251] = {2011,"","ตำนานชีซี","ชีซี ชื่อรองเหวียนจื่อ เคยอยู่สังกัดเล่าปี่ ภายหลังเพราะมารดาถูกโจโฉจับไปขังทำให้จำเป็นต้องทอดทิ้งเล่าปี่ไปเข้ากับโจโฉ ก่อนออกเดินทางได้แนะนำจูกัดเหลียงให้เล่าปี่",},
        [252] = {2012,"","ตำนานเตียวซี","เตียวซี บุตรีของเตียวหุย ว่ากันว่าเคยเรียนวิชาแพทย์กับฮัวโต๋ เมื่อโตแล้วได้แต่งงานกับเล่าเสี้ยนผู้ปกครองจ๊กก๊กช่วงหลัง ถูกแต่งตั้งเป็นฮองเฮา",},
        [253] = {2013,"","ตำนานแฮหัวเอี๋ยน","แฮหัวเอี๋ยน ชื่อรองเมี่ยวไฉ หนึ่งในแปดขุนพลม้าพยัคฆ์แห่งวุยก๊ก ถนัดการขี่ม้าตะบึงพันลี้ทำศึก ไปมาดั่งลม ทำให้คนป้องกันไม่ทัน!",},
        [254] = {2014,"","ตำนานฮัวโต๋","ฮัวโต๋ ชื่อรองหยวนฮั่ว หมอเทพในยุคสามก๊ก ฝีมือแพทย์เลิศล้ำ ถนัดด้านศัลยกรรม เชี่ยวชาญการผ่าตัด ถูกยกย่องให้เป็นปรมาจารย์ด้านศัลยกรรม",},
        [255] = {2015,"","ตำนานซุนเซ็ก","ซุนเซ็ก ชื่อรองป๋อฝู ถูกยกย่องเป็นป้าอ๋องน้อย หน้าตาหล่อ นิสัยเปิดเผย ชื่อเสียงเกริกไกร ภายหลังได้สาบานเป็นพี่น้องกับจิวยี่",},
        [256] = {2016,"","ตำนานอ้วนสุด","อ้วนสุด ชื่อรองกงลู่ ผู้นำที่แข็งแกร่งที่สุดในห้วยหนำ เพราะได้รับลัญจกรแผ่นดิน เลยตั้งตนเป็นจักรพรรดิ ถูกซุนเซ็กลิโป้โจโฉร่วมมือบุก ภายหลังกระอักเลือดตาย",},
        [257] = {2017,"","ตำนานโจสิด","โจสิด ชื่อรองจื่อเจี้ยน ฉลาดปกคลุม เหนือกว่าคนอื่น ผลงานเลื่องชื่อมีภาพลั่วเสินฟู่บทกวีโจสิดเป็นต้น เป็นหนึ่งในเจ็ดกวีเกี้ยงอัง",},
        [258] = {2018,"","ตำนานจกหยง","จกหยง ว่ากันว่าเป็นทายาทของตระกูลเทพแห่งไฟจกหยง ฝีมือต่อสู้ดี ถนัดใช้มีดบิน ภรรยาของอ๋องหนานหมานเบ้งเฮ็ก ภายหลังติดตามเบ้งเฮ็กสวามิภักดิ์จ๊กก๊ก",},
        [259] = {2019,"","ตำนานขงหยง","ขงหยง ชื่อรองเหวินจู่ นักปราชญ์ปลายยุคฮั่น หลานรุ่นที่20ของขงจื้อ ฉลาดตั้งแต่เด็ก เรื่องสร้างชื่อคือขงหยงสละสาลี่",},
        [260] = {2020,"","ตำนานกุยแก","กุยแก ชื่อรองฟ่งเสี้ยว กุนซือที่เก่งที่สุดของวุยก๊กช่วงแรก หนึ่งในห้ากุนซือโจโฉ รับตำแหน่งจี้จิ่ว ฉลาดมาก วางแผนแพรวพราว",},
        [261] = {2021,"","ตำนานโจฉอง","โจฉอง ชื่อรองชางชู บุตรโจโฉ เป็นเด็กฉลาดมีเมตตา เด่นเหนือคนอื่น เป็นที่รักของโจโฉ เรื่องสร้างชื่อคือโจฉองชั่งช้าง",},
        [262] = {2022,"","ตำนานตั๋งโต๊ะ","ตั๋งโต๊ะ ชื่อรองจ้งอิ่ง ผู้นำแถบเลียงจิ๋วปลายยุคฮั่น ภายหลังเข้าเป็นขุนนางที่ลกเอี๋ยง ตำแหน่งไท้ซือ มีอำนาจในราชสำนัก",},
        [263] = {2023,"","ตำนานเล่งทอง","เล่งทอง ชื่อรองกงจี้ ขุนพลง่อก๊ก บุตรของเล่งโฉ เคยติดตามซุนกวนปราบแถบกังแฮ บุกเข้าแฮเค้าก่อน ฆ่าแนวหน้าหองจอ สร้างความดีความชอบ",},
        [264] = {2024,"","ตำนานอิเกียด","อิเกียด นักพรตชื่อดังปลายยุคฮั่น เคยรักษาชาวบ้านแถบหวูจวิ้น ไคว่จี เป็นที่รักใครของชาวบ้าน ถูกซุนเซ็กโมโหฆ่าตาย",},
        [265] = {2025,"","ตำนานปู้ฮูหยิน","ปู้ฮูหยิน สนมคนโปรดของซุนกวน สวยอ่อนโยน นิสัยใจดี วางตัวดี ใจกว้างกับผู้คน เมื่อตายแล้ว ได้รับการแต่งตั้งย้อนหลังเป็นฮองเฮา",},
        [266] = {2026,"","ตำนานอ้วนเสี้ยว","อ้วนเสี้ยว ชื่อรองเปิ่นชู เจ้าเมืองที่แข็งแกร่งที่สุดของเหอเป่ยช่วงปลายยุคฮั่น เกิดในตระกูลใหญ่ ตระกูลอ้วนในรับการขนานนามว่าเป็นสามพระยาสี่รุ่น แต่แพ้ให้กับโจโฉในศึกกัวต๋อ ภายหลังป่วยตาย",},
        [267] = {2027,"","ตำนานตันก๋ง","ตันก๋ง ชื่อรองกงไถ เคยติดตามโจโฉ ภายหลังเป็นกุนซือในสังกัดของลิโป้ นิสัยซื่อตรง นักวางแผน ชีวิตอาภัพ",},
        [268] = {2028,"","ตำนานอิกิ๋ม","อิกิ๋ม ชื่อรองเหวินเจ๋อ หนึ่งในห้าขุนพลพยัคฆ์แห่งวุยของโจโฉ เดิมเป็นขุนพลของเปาสิ้น ภายหลังไปเข้ากับโจโฉ ยอมสวามิภักดิ์หลังแพ้กวนอู ขุนพลแห่งยุคพลาดยามแก่",},
        [269] = {2029,"","ตำนานแฮหัวตุ้น","แฮหัวตุ้น[dūn] ชื่อรองหยวนร่าง สังกัดโจโฉ หนึ่งในแปดขุนพลม้าพยัคฆ์แห่งวุยก๊ก ลูกพี่ลูกน้องของแฮหัวเอี๋ยน เรื่องสร้างชื่อคือ[ถอนธนูกลืนลูกตา]",},
        [270] = {2030,"","ตำนานซัวบุ้นกี๋","ซัวเอี๋ยม ชื่อรองบุ้นกี เป็นสาวอัจฉริยะในยุคสามก๊ก ลูกสาวของปราชญ์ฮั่นซัวหยง[yōng]  ฉลาดรอบรู้ ถนัดด้านตำรา ดนตรี อักษร",},
        [271] = {2031,"","ตำนานเคาทู","เคาทู[chǔ] ชื่อรองจ้งคัง ขุนพลโจโฉ ตอนหนุ่มเคยใช้มือเดียวล้มวัวเดินร้อยก้าว ทำให้โจรตกใจถอย",},
        [272] = {2032,"","ตำนานอาเต๊า","จักรพรรดิยุคปลายจ๊กฮั่นเล่าเสี้ยน[shàn] ชื่อรองกงซื่อ[sì] ชื่อเล่นอาเต๊า จักรพรรดิองค์ที่สองของจ๊กฮั่น เรื่องสร้างชื่อคือการสร้างความสำราญไม่สนใจราชกิจ",},
        [273] = {2033,"","ตำนานเตียวคับ","เตียวคับ[hé] ชื่อรองจวิ้นอี้[jùn yì] หนึ่งในห้าขุนพลพยัคฆ์แห่งวุย เคยรับใช้อ้วนเสี้ยว มาสวามิภักดิ์โจโฉช่วงศึกกัวต๋อ",},
        [274] = {2034,"","ตำนานอุยกาย","อุยกาย ชื่อรองกงฟู่ รับใช้ซุนเกี๋ยน ซุนเซ็ก ซุนกวน เป็นคนเข้มงวด กล้าหาญ หนึ่งในขุนนางคนสำคัญในศึกผาแดง",},
        [275] = {2035,"","ตำนานหวดเจ้ง","หวดเจ้ง ชื่อรองเสี้ยวจื๋อ กุนซือในสังกัดของเล่าปี่ เดิมเป็นลูกน้องเล่าเจี้ยง เกลี่ยกล่อมเล่าเจี้ยงสวามิภักดิ์ ภายหลังช่วยเล่าปี่ยึดฮั่นต๋ง ตัดหัวแฮหัวเอี๋ยน",},
        [276] = {2036,"","ตำนานโลซก","โลซก ชื่อรองจื้อจิง ขุนพลใหญ่ง่อก๊ก เคยรับตำแหน่งแม่ทัพภาค รูปร่างหน้าตาดี นิสัยเปิดเผย ชอบอ่านหนังสือ เก่งด้านขี่ม้ายิงธนู ร่ำรวยมาก เห็นแก่คุณธรรมมากกว่าเงินทอง",},
        [277] = {2037,"","ตำนานบังทอง","บังทอง ชื่อรองซื่อหยวน ฉายาหงส์ดรุณ กุนซือคนสำคัญของเล่าปี่ ฉลาดตีคู่มากับจูกัดเหลียง คิดแผนห่วงโซ่ในศึกผาแดง ใช้ไฟโจมตีทัพใหญ่โจโฉ",},
        [278] = {2038,"","ตำนานเตียวเจียว","เตียวเจียว ชื่อรองจื่อปู้ ขุนนางสำคัญง่อก๊ก ก่อนที่ซุนเซ็กจะตาย ได้ฝากฝังซุนกวนให้กับเตียวเจียวและจิวยี่ มีคำสั่งเสียว่างานภายนอกถามจิวยี่ งานภายในถามเตียวเจียว",},
        [279] = {2039,"","ตำนานนี่ฮ้วง","นี่ฮ้วง ชื่อรองเจิ้งผิง นักปราชญ์ปลายยุคฮั่นตะวันออก ฉลาดคารมดีตั้งแต่เด็ก แต่นิสัยซื่อตรงยิ่งยโส ใช้ปัญญาข่มคนอื่น สนิทกับขงหยง",},
        [280] = {2040,"","ตำนานบุนทิว","บุนทิว ขุนพลในสังกัดของอ้วนเสี้ยว กล้าหาญสามเหล่าทัพ ชื่อเสียงเลื่องลือ หลังจากที่งันเหลียงตาย เพราะต้องการแก้แค้นให้งันเหลียง กลับถูกกวนอูฆ่าตายบนหลังม้า",},
        [281] = {2041,"","ตำนานชัวมอ","ชัวมอ ชื่อรองเต๋อกุย ชนชั้นสูงของเกงจิ๋วช่วงปลายยุคฮั่นตะวันออก หลังจากเล่าเปียวป่วยตาย ยกเล่าจ๋องสืบทอดตำแหน่งต่อ ภายหลังไปสวามิภักดิ์โจโฉ",},
        [282] = {2042,"","ตำนานเตียนอุย","ขุนพลเตียนอุย โจโฉ รูปร่างกำยำ กำลังเยอะ ขับไล่เสือร้ายได้ ถนัดใช้ง้าวคู่ ตอนที่เตียวสิ้วกบฏ ยอมตายเพื่อปกป้องโจโฉ",},
        [283] = {2043,"","ตำนานลิฉุย","ลิฉุย ชื่อรองจื้อหราน ขุนพลคนสำคัญของตั๋งโต๊ะ นิสัยกล้าหาญชอบวางแผน ถนัดใช้ทหาร มีความสามารถ ปกครองกองทัพหมีบินแห่งเสเหลียง",},
        [284] = {2044,"","ตำนานเสี่ยวเกี้ยว","เสี่ยวเกี้ยว ภรรยาของจิวยี่ เป็นสาวงามเลื่อชื่อของสามก๊ก ได้รับฉายาสองเกี้ยวแห่งกังตั๋งร่วมกับพี่ไต้เกี้ยว",},
        [285] = {2045,"","ตำนานอองลอง","อองลอง ชื่อรองจิ่งซิ่ง ขุนนางคนสำคัญของโจโฉ เคยรับตำแหน่งซือคง ซือถูก นักปราชญ์ มีความรู้ดี คิดการรอบคอบ ประหยัด",},
        [286] = {2046,"","ตำนานเตียวเหยียง","เตียวเหยียง ขุนนางปลายยุคฮั่นตะวันออก หัวหน้าสิบขันที ถูกเรียกว่าเป็นพวกล่มแผ่นดิน ละโมบวางอำนาจ เป็นที่โปรดปรานของจักรพรรดิเลนเต้",},
        [287] = {2047,"","ตำนานนางแซ่เจา","นางแซ่เจา ภรรยาของเตียวเจช่วงปลายยุคฮั่นตะวันออก อาซ้อของเตียวสิ้ว เมื่อโจโฉทำศึกอ้วนเซีย เตียวสิ้วรบแพ้; โจโฉฟังคำยั่วยุ ยึดครองนางแซ่เจา",},
        [288] = {2048,"","ตำนานงักจิ้น","งักจิ้น ชื่อรองเหวินเชียน หนึ่งในห้าขุนพลพยัคฆ์แห่งวุย ใช้ความกล้าหาญติดตามโจโฉ อยู่ในกองทัพหลายปี รบทั้งเหนือและใต้ สร้างผลงานรบนับไม่ถ้วน",},
        [289] = {2049,"","ตำนานลิยู","ลิยู ชื่อรองเหวินโยว ผู้นำแถบเสเหลียงช่วงสามก๊กลูกเขยตั๋งโต๊ะ หัวหน้ากุนซือ เป็นคนสนิทของตั๋งโต๊ะ วางแผนไม่เคยพลาด ได้รับการยกย่องว่าปัญญาดี",},
        [290] = {2050,"","ตำนานหลี่หลิงจวี","หลี่หลิงจวี ว่ากันว่าเป็นลูกสาวของลิโป้กับเตียวเสี้ยน หน้าตาสวยไม่ด้อยกว่าเตียวเสี้ยน ฝีมือดีไม่แพ้บิดา บารมีล้นเหลือแต่เหงา กล้าหาญแต่ทำตัวเป็นแค่พลทหาร",},
        [291] = {2051,"","ตำนานเทียหยก","เทียหยก ชื่อรองจ้งเต๋อ หนึ่งในห้าปราชญ์ของวุยก๊ก เข้าร่วมการบุกครั้งสำคัญอาทิลิโป้ อ้วนเสี้ยว เล่าปี่ ซุนกวน วางแผนแยบยลมาตลอด",},
        [292] = {2052,"","ตำนานกำเหลง","กำเหลง ชื่อรองซิงป้า ขุนพลง่อก๊ก ชอบเป็นจอมยุทธ์ตั้งแต่เด็ก ยิงนกตกปลา พกกระดิ่งติดตัว ชุดแพรไหม ผู้คนเรียกว่าโจรใบแพร",},
        [293] = {2053,"","ตำนานซิหลง","ซิหลง ชื่อรองกงหมิง หนึ่งในห้าขุนพลพยัคฆ์แห่งวุย ถนัดใช้ขวาน เคยรับใช้เอียวฮอง ภายหลังตามโจโฉทำศึกเหนือใต้ คุณูปการมากมาย",},
        [294] = {2054,"","ตำนานม้าอวิ๋นลู่","ม้าอวิ๋นลู่ ธิดาของม้าเท้ง ภรรยาของจูล่ง น้องสาวของม้าเฉียว นิสัยตรงไปตรงมาไร้เดียงสา เรียนการต่อสู้ตั้งแต่เด็ก ฝีมือทวนดีมาก ชายหนุ่มธรรมดาไม่คู่ควร",},
        [295] = {2055,"","ตำนานจอสิว","จอสิว กุนซือในสังกัดของอ้วนเสี้ยว มีปณิธานใหญ่ตั้งแต่เด็ก ถนัดวางแผน มักจะวางแผนให้อ้วนเสี้ยว แต่หลายครั้งที่อ้วนเสี้ยวไม่ทำตาม",},
        [296] = {2056,"","ตำนานโจหยิน","โจหยิน ชื่อรองจื่อเสี้ยว ขุนพลวุยก๊กในช่วงสามก๊ก หนึ่งในแปดขุนพลม้าพยัคฆ์แห่งวุยก๊ก ปกครองทหารเข้มงวด ถนัดด้านการป้องกัน",},
        [297] = {2057,"","ตำนานซุนซ่างเซียง","ซุนซ่างเซียง น้องสาวของซุนกวน ในศึกผาแดงแต่งงานเป็นภรรยาเล่าปี่ นางมีความงาม มีปัญญาและว่องไว เป็นคนดื้อ วีรสตรีไม่เป็นรองบุรุษ",},
        [298] = {2058,"","ตำนานโลติด","โลติด ชื่อรองจื่อกัน นักปราชญ์ ขุนพลปลายยุคฮั่นตะวันออก นิสัยเด็ดเดี่ยว มีคุณธรรมสูง รับตำแหน่งเจ้าเมืองเอกจิ่วเจียง โลกั๋งตามลำดับ",},
        [299] = {2059,"","ตำนานจิวท่าย","จิวท่าย ชื่อรองโย่วผิง ขุนพลคนสำคัญของง่อก๊ก ปกป้องซุนกวนจากอันตรายหลายครั้ง มีบาดแผลบนตัวหลายสิบแผล",},
        [300] = {2060,"","ตำนานโจจู๋","โจจู๋ ชื่อรองเหยียนฟ่าง นักพรตชื่อดังช่วงปลายยุคฮั่นตะวันออก เชี่ยวชาญอู่จิง ดูดาว ฉีเหมินตุ้นเจี่ย ว่ากันว่าสามารถใช้งานผีเทพได้ มือเดียวสร้างก๊ก",},
        [301] = {2061,"","ตำนานอ้วนถำ","อ้วนถำ ชื่อรองเสี่ยนซือ บุตรคนโตของอ้วนเสี้ยว เคยบุกเต๊งไก๋ ขงหยง เพราะอ้วนถำไม่อาจรับสืบทอดตำแหน่ง ทำให้เกิดความแค้น ร่วมมือกับโจโฉบุกตีอ้วนซง",},
        [302] = {2062,"","ตำนานเอียนสี","เอียนสี สาวงามเลื่องชื่อในยุคสามก๊ก มีอีกชื่อว่าอิงสีหรือเจินฝู[fú] เหวินเจาเจินฮองเฮา งามล่มเมือง สุดยอดแห่งยุค เป็นภรรยาเอกของโจผี",},
        [303] = {2063,"","ตำนานจูกัดกิ๋น","จูกัดกิ๋น ชื่อรองจื่ออวี้ ขุนนางคนสำคัญของง่อก๊ก พี่ชายของจูกัดเหลียง บิดาของจูกัดเก๊ก เป็นคนเปิดเผย นิสัยอ่อนโยนซื่อสัตย์ เป็นคนที่ซุนกวนไว้วางใจ",},
        [304] = {2064,"","ตำนานอุยซี","อุยซี ภรรยาของจูกัดเหลียง ธิดาของฮองเสงหงันคนดังเกงจิ๋ว ว่ากันว่ามีความงาม แต่เพราะขี้อาย เลยใช้งอบและผ้าแพรปิดบังใบหน้า",},
        [305] = {2065,"","ตำนานพัวเจี้ยง","พัวเจี้ยง ชื่อรองเหวินกุย ผู้นำง่อก๊ก ทำสงครามกล้าหาญ เลื่อนขั้นไว ชีวิตนี้เพื่อซุนกวนพิชิตออกปราบตก ผลงานรบนับไม่ถ้วน",},
        [306] = {2066,"","ตำนานซุนกวน","ซุนกวน ชื่อรองจ้งโหมว ปฐมจักรพรรดิของง่อก๊ก ตอนเด็กติดตามพี่ชายซุนเซ็กยึดครองกังตั๋ง ภายหลังซุนเซ็กถูกแทงตาย ซุนกวนสืบทอดตำแหน่งเป็นผู้นำกังตั๋ง",},
        [307] = {2067,"","ตำนานงันเหลียง","งันเหลียง ขุนพลในสังกัดอ้วนเสี้ยว กล้าหาญสามเหล่าทัพ มีชื่อเสียงเลื่องลือ ได้รับฉายาสองวีรบุรุษเหอเป่ยคู่กับบุนทิว",},
        [308] = {2068,"","ตำนานหวังยี่","หวังยี่ ภรรยาของจ้าวอั๋งผู้ว่าแคว้นเอ็กจิ๋ว จ้าวอั๋งมารับตำแหน่งที่เมืองกิ หวังยี่ติดตามสามีมาอยู่ที่เมืองกิ",},
        [309] = {2069,"","ตำนานกวนเป๋ง","กวนเป๋ง ขุนพลจ๊กก๊ก บุตรของกวนอู ติดตามกวนอูพิชิตออกปราบตก กล้าหาญชาญชัย ไม่แพ้บิดา เคยติดตามเล่าปี่ออกรบซีชวน สร้างผลงานรบ",},
        [310] = {2070,"","ตำนานเบ้งเฮ็ก","เบ้งเฮ็ก อ๋องหนานหมาน ผู้นำชนเผ่าเขตหนานจงในยุคสามก๊ก ถูกจูกัดเหลียงนำกองทัพจับ7ปล่อย7จนยอมสวามิภักดิ์ หลังจากนั้นไม่ทรยศอีก",},
        [311] = {2071,"","ตำนานฮั่นเหี้ยนเต้","ฮั่นเหี้ยนเต้ นามเล่าเหียบ ชื่อรองปั๋วเหอ จักรพรรดิองค์สุดท้ายของฮั่นตะวันออก ตั๋งโต๊ะบุกเมืองหลวงแล้ว ปลดจักรพรรดิน้อยเล่าเปียน แต่งตั้งเล่าเหียบเป็นจักรพรรดิ",},
        [312] = {2072,"","ตำนานฮันต๋ง","ฮันต๋ง ชื่อรองอี้กง ขุนพลง่อก๊ก ถนัดการยิงธนู ขี่ม้าและมีกำลังแขนดี รับใช้ซุนเกี๋ยน ซุนเซ็ก ซุนกวน คุณูปการล้นเหลือ",},
        [313] = {2073,"","ตำนานเตียวซุนฮัว","เตียวซุนฮัว ภรรยาของสุมาอี้ มารดาของสุมาเจียว ภายหลังถูกแต่งงานเป็นเสวียนมู่ฮองเฮา ฉลาดเกินใครตั้งแต่เด็ก นักวางแผนที่ดี",},
        [314] = {2074,"","ตำนานลิบอง","ลิบอง ชื่อรองจื่อหมิง ขุนพลง่อก๊ก เคยรับตำแหน่งแม่ทัพภาค ผลงานสร้างชื่อที่สุดคือลอบจู่โจมเกงจิ๋ว เอาชนะกวนอู เรื่องที่สร้างชื่อคือชุดขาวข้ามแม่น้ำ",},
        [315] = {2075,"","ตำนานบังเต๊ก","บังเต๊ก ชื่อรองลิ่งหมิง เดิมเป็นลูกน้องม้าเฉียว ภายหลังติดตามโจโฉ มักขี่ม้าขาว เด็ดเดี่ยวกล้าหาญ บารมีล้นเหลือ",},
        [316] = {2076,"","ตำนานกวนอิ๋นผิง","กวนอิ๋นผิง ธิดาของกวนอู มีความงาม ในความงามยังมีความองอาจแบบบิดา ว่ากันว่าภายหลังกวนอิ๋นผิงกราบจูล่งเป็นอาจารย์",},
        [317] = {2077,"","ตำนานกวนสก","กวนสก ว่ากันว่าเป็นบุตรคนที่สามของกวนอู แต่งเปาซานเนียงเป็นภรรยา หลังจากเกงจิ๋วถูกยึดพาเปาซานเนียงไปอยู่กับจ๊กก๊ก และติดตามจูกัดเหลียงรบแดนใต้",},
        [318] = {2078,"","ตำนานลกซุน","ลกซุน ชื่อรองป๋อเหยียน ขุนพลใหญ่ง่อก๊ก รับตำแหน่งแม่ทัพภาคง่อก๊ก เป็นแม่ทัพใหญ่  เสนาบดี ที่อี๋หลิงเอาชนะกองทัพจ๊กที่นำโดยเล่าปี่ สร้างชื่อในศึกเดียว",},
        [319] = {2079,"","ตำนานง่อก๊กไถ้","ง่อก๊กไถ้ ภรรยาของซุนเกี๋ยน มารดาของซุนซ่างเซียง ในวรรณกรรมสามก๊กร่วมมือกับเกียวก๊กโลช่วยให้เล่าปี่ได้แต่งงานกับซุนซ่างเซียง",},
        [320] = {2080,"","ตำนานเตียวเลี้ยว","เตียวเลี้ยว ชื่อรองเหวินหยวน ลิโป้ ภายหลังรับใช้โจโฉ หนึ่งในห้าขุนพลพยัคฆ์แห่งวุย เคยทำศึกเป๊กตุ้น บุกอูหวน",},
        [321] = {2081,"","ตำนานเอียวสิ้ว","เอียวสิ้ว ชื่อรองเต๋อจู่ นักปราชญ์ปลายยุคฮั่นตะวันออก มีความรู้ดี ปัญญารอบวิชา เคยรับตำแหน่งอารักษ์ในตำหนักเสนาบดีโจโฉ แม้แต่โจโฉก็เทียบไม่ติด",},
        [322] = {2082,"","ตำนานสุมาเจียว","สุมาเจียว ชื่อรองจื่อซ่าง ขุนนางวุยก๊กในยุคสามก๊ก หนึ่งในรากฐานก่อตั้งราชวงศ์จิ้นตะวันตก บุตรคนรองของสุมาอี้กับเตียวซุนฮัว",},
        [323] = {2083,"","ตำนานตันกุ๋น","ตันกุ๋น ชื่อรองจ่างเหวิน ขุนนางสำคัญของวุยก๊ก ผู้ก่อตั้งระบบคัดเลือกขุนนางเก้าขั้นกับกฏหมายวุยก๊กกฏหมายวุย",},
        [324] = {2084,"","ตำนานกวนซิง","กวนซิง ชื่อรองอันกั๋ว บุตรคนรองของกวนอู ขุนพลคนสำคัญช่วงปลายจ๊กก๊ก มีชื่อเสียงตั้งแต่เด็ก จูกัดเหลียงชื่นชมในตัวเขามาก",},
        [325] = {2085,"","ตำนานม้าต้าย","ม้าต้าย น้องชายของม้าเฉียว ขุนพลจ๊กก๊กในยุคสามก๊ก ติดตามม้าเฉียวทำศึกโจโฉ ตลบหลัง ล้อมบุกเฉิงตู ศึกฮั่นต๋งเป็นต้น",},
        [326] = {2086,"","ตำนานจิวฉอง","จิวฉอง ชื่อรองหยวนฝู องครักษ์ของกวนอูแม่ทัพจ๊กก๊ก รูปร่างสูงใหญ่ หน้าดำไว้หนวด เดิมเป็นทหารผ้าเหลือง ภายหลังติดตามกวนอูออกรบทั่วแผ่นดิน",},
        [327] = {2087,"","ตำนานโกซุ่น","โกซุ่น ขุนพลอันดับหนึ่งในสังกัดลิโป้ช่วงปลายยุคฮั่นตะวันออก ตำแหน่งเจ้ากรม เป็นคนที่มีบารมีมือสะอาด ไม่ชอบดื่มเหล้า กองทัพในสังกัดเป็นทหารฝีมือดี",},
        [328] = {2088,"","ตำนานเลียวฮัว","เลียวฮัว ชื่อรองเหยียนเจี้ยน มีชื่อในด้านความกล้าหาญซื่อตรง เป็นขุนพลคนสำคัญช่วงปลายจ๊กก๊ก ว่ากันว่าเดิมเป็นทหารผ้าเหลือง ภายหลังไปเข้ากับเล่าปี่ สังกัดกวนอู",},
        [329] = {2089,"","ตำนานบิต๊ก","บิต๊ก ชื่อรองจื่อจ้ง ขุนนางคนสำคัญในสังกัดเล่าปี่จ๊กก๊ก เดิมเป็นเศรษฐีซีจิ๋ว โตเกี๋ยมตายแล้ว ทำตามคำสั่งเสียต้อนรับเล่าปี่",},
        [330] = {2090,"","ตำนานโจจิ๋น","โจจิ๋น ชื่อรองจื่อตาน หนึ่งในแปดขุนพลม้าพยัคฆ์แห่งวุยก๊ก บิดาของเขาถูกฆ่าตายตอนที่โจโฉรวบรวมกำลังพล ถูกโจโฉรับเป็นลูกบุญธรรม",},
        [331] = {2091,"","ตำนานชีเซ่ง","ชีเซ่ง ชื่อรองเหวินเซี่ยง ขุนพลใหญ่ง่อก๊ก ฉลาดคารมดี เข้าร่วมสงครามหลายครั้ง",},
        [332] = {2092,"","ตำนานเทียเภา","เทียเภา ชื่อรองเต๋อโหมว ขุนพลง่อก๊ก รับใช้ซุนเกี๋ยน ซุนเซ็ก ซุนกวน ถูกผู้คนยกย่องว่า[ท่านเทีย]",},
        [333] = {2093,"","ตำนานตันเต๋ง","ตันเต๋ง ชื่อรองหยวนหลง เป็นคนเปิดเผย นิสัยเงียบขรึม ฉลาดเฉลียว เมื่อโตเกี๋ยมป่วยตายตันเต๋งสนับสนุนเล่าปี่รับสืบทอดเจ้าเมืองซีจิ๋ว",},
        [334] = {2094,"","ตำนานเตงงาย","เตงงาย ชื่อรองซื่อไต้ นักการทหาร ขุนพลโดดเด่นในยุคปลายวุยก๊ก เก่งทั้งบุ๋นและบู้ ชำนาญพิชัยยุทธ์ อยู่ชายแดงตะวันตกวุยก๊กหลายปีปกป้องแผ่นดินจากเกียงอุยของจ๊กก๊ก",},
        [335] = {2095,"","ตำนานเบ้งตัด","เบ้งตัด ชื่อรองจื่อตู้ ชาวเมืองเหมย ขุนพลจ๊กก๊กในยุคสามก๊ก เดิมอยู่สังกัดเล่าเจี้ยง ภายหลังไปเข้าร่วมกับเล่าปี่",},
        [336] = {2096,"","ตำนานเจียวก้าน","เจียวก้าน ชื่อรองจื่ออี้ ขุนนางบุ๋นวุยก๊กปลายยุคฮั่น เจียวก้านในประวัติศาสตร์เป็นนักปราชญ์ นักวิจารณ์ชื่อดังแห่งยุค",},
        [337] = {2097,"","ตำนานอ้องอุ้น","อ้องอุ้น ชื่อรองจื่อชือ ขุนนางใหญ่ปลายยุคฮั่นตะวันออก เกิดในตระกูลดัง มีสมบัติมากมาย ได้รับการยกย่องว่ามีความรู้เต็มท้องตั้งแต่เด็ก ฝีมือดีฉลาดรอบรู้ไปทุกแขนง",},
        [338] = {2098,"","ตำนานเล่าเปียว","เล่าเปียว ชื่อรองจิ่งเซิง เจ้าเมืองเกงจิ๋วปลายยุคฮั่น บุคลิกอ่อนน้อม มีชื่อเสียงตั้งแต่หนุ่ม เป็นคนหล่อเหลา",},
        [339] = {2099,"","ตำนานเตงฮอง","เตงฮอง ชื่อรองเฉิงยวน ขุนพลปลายยุคง่อก๊ก ชำนาญการรบตั้งแต่หนุ่ม ฆ่าศัตรูอย่างกล้าหาญ สร้างคุณูปการมากมาย",},
        [340] = {2100,"","ตำนานลกข้อง","ลกข้อง ชื่อรองโย่วเจี๋ย บุตรชายคนรองของลกซุน ถูกยกย่องว่าเป็นขุนพลคนสุดท้ายของง่อก๊ก อายุ12รับสืบทอดตำแหน่งเจ้าเมืองกังเหลงจากบิดา",},
        [341] = {2101,"","ตำนานเตียนห้อง","เตียนห้อง ชื่อรองหยวนฮ่าว ชาวกิลกกุ๋น  กุนซือในสังกัดอ้วนเสี้ยวปลายยุคฮั่นตะวันออก ตำแหน่งเลขากิจิ๋ว",},
        [342] = {2102,"","ตำนานลิเงียม","ลิเงียม ชื่อรองเจิ้งฟัง ขุนนางสำคัญจ๊กก๊กในยุคสามก๊ก เป็นขุนนางฝากฝังก่อนตายของเล่าปี่คู่กับจูกัดเหลียง",},
        [343] = {2103,"","ตำนานเจียวขิม","เจียวขิม ชื่อรองกงอี้ ขุนพลง่อก๊ก ช่วงแรกติดตามซุนเซ็กปราบสี่แคว้น ปราบโจร ถูกตันซิ่วยกย่องเป็นขุนนางพยัคฆ์แห่งเจียงเปี่ยว",},
        [344] = {2104,"","ตำนานพัวหอง","พัวหอง ถนัดใช้ขวาน เขารับคำสั่งฮันฮกไปท้ารบขุนพลฮัวหยงลูกน้องสังกัดตั๋งโต๊ะหน้าด่านกิสุยก๋วน แพ้ถูกฆ่า",},
        [345] = {2105,"","ตำนานกุยห้วย","กุยห้วย ขุนพลปลายวุยก๊ก เมื่อแฮหัวเอี๋ยนตายกุยห้วยรวบรวมทหารที่เหลือ ควบคุมสถานการณ์",},
        [346] = {2106,"","ตำนานแฮหัวป๋า","แฮหัวป๋า ชื่อรองจ้งฉวน ขุนพลคนสำคัญปลายจ๊กก๊กและวุยก๊กยุคสามก๊ก บุตรคนรองแฮหัวเอี๋ยน",},
        [347] = {2107,"","ตำนานจงโฮย","จงโฮย ชื่อรองสื้อจี้ ขุนพลวุย บุตรของจงฮิว เป็นนักวางแผนกับกุนซือคนสำคัญยุคปลายวุยก๊ก",},
        [348] = {2108,"","ตำนานโจงั่ง","โจงั่ง ชื่อรองจื่อซิว บุตรคนโตของโจโฉ นิสัยอ่อนน้อมและฉลาด เป็นที่รักของโจโฉ",},
        [349] = {2109,"","ตำนานโจหอง","โจหอง ชื่อรองจื่อเหลียน หนึ่งในแปดขุนพลม้าพยัคฆ์แห่งวุยก๊ก ช่วงต้นติดตามโจโฉปราบกลุ่มต่างๆ ยึดกุนจิ๋ว ปราบเล่าเปียว ปราบจู้ปี้ สร้างผลงานรบมากมาย",},
        [350] = {2110,"","ตำนานสุมาเต๊กโช","สุมาเต๊กโช ชื่อรองเต๊กโช นักปราชญ์ชื่อดังปลายยุคฮั่นตะวันออก เชี่ยวชาญวิชาเต๋า ฉีเหมิน พิชัยยุทธ์ คัมภีร์ มีฉายาว่าซินแสกระจกเงา",},
        [351] = {2111,"","ตำนานซุนฮิว","ซุนฮิว ชื่อรองกงต๋า หนึ่งในห้าปราชญ์ของวุยก๊ก ถูกโจโฉแต่งตั้งเป็นกุนซือ วางแผนศึกได้แยบยล",},
        [352] = {2112,"","ตำนานกิเหลง","กิเหลง ชื่อรองฝูซี ขุนพลสังกัดอ้วนสุด กล้าหาญมาก ตอนที่ปราบซีจิ๋วรบกับกวนอู300รอบเสมอ",},
        [353] = {2113,"","ตำนานม้าเท้ง","ม้าเท้ง ชื่อรองโซ่วเฉิง บิดาของม้าเฉียว ผู้นำแถบเลียงจิ๋วช่วยปลายยุคฮั่นตะวันออก",},
        [354] = {2114,"","ตำนานเตียวสิ้ว","เตียวสิ้ว บุตรของแม่ทัพอาชาเตียวเจ ผู้นำแถบอ้วนเซียปลายยุคฮั่น",},
        [355] = {2115,"","ตำนานเตียวเหลียง","เตียวเหลียง หนึ่งในผู้นำกองทัพกบฏผ้าเหลืองปลายยุคฮั่นตะวันออก น้องสามของเตียวก๊ก ถูกเรียกว่า[เจ้าพระยามนุษย์]",},
        [356] = {2116,"","ตำนานม้าเจ๊ก","ม้าเจ๊ก ชื่อรองโย่วฉาง น้องชายม้าเลี้ยง ขุนพลจ๊กก๊กในยุคสามก๊ก เป็นคนฉลาด วางแผนการรบดี จูกัดเหลียงให้ความสำคัญกับเขามาก",},
        [357] = {2117,"","ตำนานม้าเลี้ยง","ม้าเลี้ยง ชื่อรองจี้ฉาง ขุนนางจ๊กก๊ก พี่ชายม้าเจ๊ก เพราะคิ้วมีแต้มขาว เลยถูกเรียกว่าม้าเลี้ยงคิ้วขาว",},
        [358] = {2118,"","ตำนานบิฮูหยิน","บิฮูหยิน น้องสาวของบิต๊กขุนนางจ๊กก๊ก ภรรยาเล่าปี่",},
        [359] = {2119,"","ตำนานเล่าเจี้ยง","เล่าเจี้ยง ชื่อรองจี้อวี้ หนึ่งในผู้นำปลายยุคฮั่นตะวันออก สืบทอดปกครองเมืองเอ็กจิ๋วต่อจากเล่าเอี๋ยนบิดา",},
        [360] = {2120,"","ตำนานโกะหยง","โกะหยง ชื่อรองหยวนทั่น ขุนนางบุ๋นคนสำคัญของง่อก๊ก กราบซัวหยงเป็นอาจารย์ตั้งแต่เด็ก เรียนพิณกับอักษร ฉลาดมีไหวพริบ เป็นคนมีจิตใจสงบ",},
    }
}

return story_chapter