        [75] = {15,"กวนอิ๋นผิง","นี่คือดาบกู่ติ้ง! ดาบนี้มีที่มา วีรบุรุษหลายคนชอบใช้!","นี่คือดาบกู่ติ้ง! ดาบนี้มีที่มา วีรบุรุษหลายคนชอบใช้!","","story_gyp_15","story_gyp_15",},
        [76] = {15,"เตียวซี","นายท่าน โปรดไปติดตั้ง#อาวุธ#เถอะ!","นายท่าน โปรดไปติดตั้ง#อาวุธ#เถอะ!","","story_zxc_19","story_zxc_19",},
        [77] = {16,"เตียวซี","นายท่าน ข้างหน้ายังมีคนตอแยเก่งๆอีก พวกเราต้อง#อัปเกรด#อาวุธเพิ่มพลังรบ!","นายท่าน ข้างหน้ายังมีคนตอแยเก่งๆอีก พวกเราต้อง#อัปเกรด#อาวุธเพิ่มพลังรบ!","กวนอิ๋นผิง","story_zxc_20","story_zxc_20",},
        [78] = {17,"เตียวซี","อาวุธหลังจาก#อัปเกรด#แล้วเจ๋งไปเลย! พลังโจมตีเพิ่มจากเดิม2เท่า!","อาวุธหลังจาก#อัปเกรด#แล้วเจ๋งไปเลย! พลังโจมตีเพิ่มจากเดิม2เท่า!","กวนอิ๋นผิง","story_zxc_21","story_zxc_21",},
        [79] = {17,"เตียวซี","ดังนั้น นายท่านต่อไปอย่าลืม#อัปเกรด#อาวุธบ่อยๆล่ะ!","ดังนั้น นายท่านต่อไปอย่าลืม#อัปเกรด#อาวุธบ่อยๆล่ะ!","","story_zxc_22","story_zxc_22",},
        [80] = {17,"กวนอิ๋นผิง","ข้างหน้ามีศัตรูอีกแล้ว นายท่านรีบไปลองอานุภาพของอาวุธที่อัปเกรดแล้วสิ!","ข้างหน้ามีศัตรูอีกแล้ว นายท่านรีบไปลองอานุภาพของอาวุธที่อัปเกรดแล้วสิ!","","story_gyp_16","story_gyp_16",},
        [81] = {18,"เตียวซี","ฝีมือของนายท่านเพิ่มขึ้นอย่างมาก ได้เวลาสร้างทีมแล้ว","ฝีมือของนายท่านเพิ่มขึ้นอย่างมาก ได้เวลาสร้างทีมแล้ว","กวนอิ๋นผิง","story_zxc_34","story_zxc_34",},
        [82] = {18,"กวนอิ๋นผิง","นายท่าน #แพ็คประสานโจมตีขุนพล#มีขุนพลม่วงประสานโจมตี1ทีม มีพวกเขามาเข้าร่วม นายท่านเหมือนเสือติดปีก!","นายท่าน #แพ็คประสานโจมตีขุนพล#มีขุนพลม่วงประสานโจมตี1ทีม มีพวกเขามาเข้าร่วม นายท่านเหมือนเสือติดปีก!","","story_gyp_17","story_gyp_17",},
        [83] = {19,"เตียวซี","ว้าว ดีจังเลย มีขุนพลประสานโจมตีใหม่แล้ว! พวกเขาดูท่าทางเก่งมาก!","ว้าว ดีจังเลย มีขุนพลประสานโจมตีใหม่แล้ว! พวกเขาดูท่าทางเก่งมาก!","กวนอิ๋นผิง","story_zxc_35","story_zxc_35",},
        [84] = {19,"เตียวซี","ไว้นายท่านอัปถึง#Lv.9#กับ#Lv.17# เปิดตำแหน่งกระบวนทัพใหม่ พวกเขาก็สามารถลงทัพทำศึกพร้อมกับนายท่านได้แล้ว!","ไว้นายท่านอัปถึง#Lv.9#กับ#Lv.17# เปิดตำแหน่งกระบวนทัพใหม่ พวกเขาก็สามารถลงทัพทำศึกพร้อมกับนายท่านได้แล้ว!","","story_zxc_36","story_zxc_36",},
        [85] = {19,"กวนอิ๋นผิง","นายท่านรีบพาอิ๋นผิงไปต่อสู้อัปเลเวลเถอะ!","นายท่านรีบพาอิ๋นผิงไปต่อสู้อัปเลเวลเถอะ!","","story_gyp_18","story_gyp_18",},
        [86] = {20,"เตียวซี","นายท่าน ฝีมือของท่านก้าวหน้าไวมาก สามารถไป#สนามประลอง#ท้ารบวีรบุรุษใต้หล้าแล้ว!","นายท่าน ฝีมือของท่านก้าวหน้าไวมาก สามารถไป#สนามประลอง#ท้ารบวีรบุรุษใต้หล้าแล้ว!","","story_zxc_59","story_zxc_59",},
        [87] = {20,"เตียวซี","ให้เตียวซีพาท่านไปดูที่#สนามประลอง#เถอะ","ให้เตียวซีพาท่านไปดูที่#สนามประลอง#เถอะ","","story_zxc_60","story_zxc_60",},
        [88] = {21,"เตียวซี","วีรบุรุษในนายท่าน #สนามประลอง#เยอะมาก เอาชนะผู้เล่นคนอื่น ก็จะเพิ่มอันดับ","วีรบุรุษในนายท่าน #สนามประลอง#เยอะมาก เอาชนะผู้เล่นคนอื่น ก็จะเพิ่มอันดับ","กวนอิ๋นผิง","story_zxc_61","story_zxc_61",},
        [89] = {21,"กวนอิ๋นผิง","นายท่าน ทุกครั้งที่#อันดับ#เพิ่มขึ้น ก็จะได้รับรางวัลล่ะ! อันดับยิ่งสูง รางวัลยิ่งดี","นายท่าน ทุกครั้งที่#อันดับ#เพิ่มขึ้น ก็จะได้รับรางวัลล่ะ! อันดับยิ่งสูง รางวัลยิ่งดี","","story_gyp_38","story_gyp_38",},
        [90] = {21,"เตียวซี","นายท่าน พวกเราไปดูที่ร้านประลองเถอะ!","นายท่าน พวกเราไปดูที่ร้านประลองเถอะ!","","story_zxc_62","story_zxc_62",},
        [91] = {22,"เตียวซี","นายท่าน ทุกครั้งที่เอาชนะศัตรูในสนามประลอง ก็จะได้รับบารมีกับอันดับ ถึงอันดับค่าหนึ่งแล้ว ยังมีรางวัลโบนัสด้วยล่ะ!","นายท่าน ทุกครั้งที่เอาชนะศัตรูในสนามประลอง ก็จะได้รับบารมีกับอันดับ ถึงอันดับค่าหนึ่งแล้ว ยังมีรางวัลโบนัสด้วยล่ะ!","กวนอิ๋นผิง","story_zxc_63","story_zxc_63",},
        [92] = {22,"เตียวซี","ทุกครั้งที่อันดับเพิ่มจะได้รับรางวัลตำลึงจำนวนมาก! และเมื่ออันดับของท่านยิ่งสูง รางวัลบารมีที่ได้รับทุกวันก็ยิ่งสูง","ทุกครั้งที่อันดับเพิ่มจะได้รับรางวัลตำลึงจำนวนมาก! และเมื่ออันดับของท่านยิ่งสูง รางวัลบารมีที่ได้รับทุกวันก็ยิ่งสูง","","story_zxc_64","story_zxc_64",},
        [93] = {22,"กวนอิ๋นผิง","ไป! ไปเพิ่มอันดับในสนามประลองเถอะ! ข้าจะช่วยให้นายท่านได้อันดับที่1!","ไป! ไปเพิ่มอันดับในสนามประลองเถอะ! ข้าจะช่วยให้นายท่านได้อันดับที่1!","","story_gyp_39","story_gyp_39",},
        [94] = {23,"เตียวซี","นายท่าน ฝีมือของท่านก้าวหน้าขึ้นอย่างมาก สามารถไป#พเนจร#ได้แล้ว!","นายท่าน ฝีมือของท่านก้าวหน้าขึ้นอย่างมาก สามารถไป#พเนจร#ได้แล้ว!","กวนอิ๋นผิง","story_zxc_40","story_zxc_40",},
        [95] = {23,"กวนอิ๋นผิง","นับแต่โบราณมา ดินแดนเสินโจวมีตราทหารคัมภีร์พิชัยยุทธ์ตกหล่นไว้มากมาย ขอเพียงเดินทางพเนจรก็จะได้รับ#สมบัติ#พวกนี้!","นับแต่โบราณมา ดินแดนเสินโจวมีตราทหารคัมภีร์พิชัยยุทธ์ตกหล่นไว้มากมาย ขอเพียงเดินทางพเนจรก็จะได้รับ#สมบัติ#พวกนี้!","","story_gyp_21","story_gyp_21",},
        [96] = {23,"เตียวซี","นอกจากสมบัติ พเนจรยังเปิด#อัศจรรย์#ต่างๆ ได้รับEXPจำนวนมากมาอัปเลเวลล่ะ","นอกจากสมบัติ พเนจรยังเปิด#อัศจรรย์#ต่างๆ ได้รับEXPจำนวนมากมาอัปเลเวลล่ะ","","story_zxc_41","story_zxc_41",},
        [97] = {23,"เตียวซี","ขอให้นายท่านตามเตียวซีไปพเนจร#สวนท้อ#เถอะ!","ขอให้นายท่านตามเตียวซีไปพเนจร#สวนท้อ#เถอะ!","","story_zxc_42","story_zxc_42",},
        [98] = {24,"กวนอิ๋นผิง","ซินแสกระจกเงามีความรู้กว้างขวาง ขอเพียงตอบคำถามของเขาถูก ก็จะได้รับตำลึงมากมาย!","ซินแสกระจกเงามีความรู้กว้างขวาง ขอเพียงตอบคำถามของเขาถูก ก็จะได้รับตำลึงมากมาย!","","story_gyp_22","story_gyp_22",},
        [99] = {24,"กวนอิ๋นผิง","นายท่าน คำถามนี้ข้าตอบได้!","นายท่าน คำถามนี้ข้าตอบได้!","","story_gyp_23","story_gyp_23",},
        [100] = {25,"เตียวซี","ยินดีด้วยนายท่านได้รับ#สมบัติ#!สมบัติมี 2 ชนิด พิชัยยุทธ์กับตราทหาร พิชัยยุทธ์สามารถเพิ่มโจมตี ตราทหารสามารถเพิ่มHP","ยินดีด้วยนายท่านได้รับ#สมบัติ#!สมบัติมี 2 ชนิด พิชัยยุทธ์กับตราทหาร พิชัยยุทธ์สามารถเพิ่มโจมตี ตราทหารสามารถเพิ่มHP","กวนอิ๋นผิง","story_zxc_43","story_zxc_43",},
        [101] = {25,"กวนอิ๋นผิง","อูจื้อเล่มนี้คือพิชัยยุทธ์ที่ขุนพลยุคชุนชิวอูฉี่เป็นคนเขียนขึ้น มีผลในการเพิ่งพลังรบให้นายท่านอย่างมาก!","อูจื้อเล่มนี้คือพิชัยยุทธ์ที่ขุนพลยุคชุนชิวอูฉี่เป็นคนเขียนขึ้น มีผลในการเพิ่งพลังรบให้นายท่านอย่างมาก!","","story_gyp_24","story_gyp_24",},
        [102] = {25,"เตียวซี","นายท่านตามข้ามา! พวกเราไปกระบวนทัพสวมสมบัติเถอะ","นายท่านตามข้ามา! พวกเราไปกระบวนทัพสวมสมบัติเถอะ","","story_zxc_44","story_zxc_44",},
        [103] = {26,"เตียวซี","นายท่าน พวกเรามาอัปเกรดสมบัติกันเถอะ!","นายท่าน พวกเรามาอัปเกรดสมบัติกันเถอะ!","","story_zxc_45","story_zxc_45",},
        [104] = {26,"เตียวซี","พิชัยยุทธ์ตราทหารสามารถอัปเกรดได้ทางแท่นหมึก อัปเกรดแล้วสมบัติจะมีอานุภาพรุนแรงขึ้น","พิชัยยุทธ์ตราทหารสามารถอัปเกรดได้ทางแท่นหมึก อัปเกรดแล้วสมบัติจะมีอานุภาพรุนแรงขึ้น","","story_zxc_46","story_zxc_46",},
        [105] = {27,"กวนอิ๋นผิง","นายท่านดู! อัปเกรดสมบัติแล้ว พลังรบเพิ่มขึ้นเยอะเลย","นายท่านดู! อัปเกรดสมบัติแล้ว พลังรบเพิ่มขึ้นเยอะเลย","","story_gyp_25","story_gyp_25",},
        [106] = {27,"กวนอิ๋นผิง","นายท่าน พวกเราไปพเนจรต่อเถอะ ได้รับสมบัติเยอะขึ้น!","นายท่าน พวกเราไปพเนจรต่อเถอะ ได้รับสมบัติเยอะขึ้น!","","story_gyp_26","story_gyp_26",},
        [107] = {28,"เตียวซี","ยินดีด้วยนายท่าน ระบบ#ยศขุนนาง#เปิดแล้ว!","ยินดีด้วยนายท่าน ระบบ#ยศขุนนาง#เปิดแล้ว!","","story_zxc_65","story_zxc_65",},
        [108] = {28,"เตียวซี","นายท่าน ฝีมือของท่านชื่อเสียงเลื่องลือ ให้เตียวซีพาท่านไปเลื่อนยศขุนนางเถอะ!","นายท่าน ฝีมือของท่านชื่อเสียงเลื่องลือ ให้เตียวซีพาท่านไปเลื่อนยศขุนนางเถอะ!","","story_zxc_66","story_zxc_66",},
        [109] = {29,"เตียวซี","ยินดีด้วยนายท่านเลื่อนขั้นแล้ว! ยศขุนนางทำให้พลังรบของขุนพลทั้งหมดเพิ่มขึ้น ทำให้นายท่านเก่งยิ่งขึ้น กลายเป็นผู้แข็งแกร่งที่แท้จริง!","ยินดีด้วยนายท่านเลื่อนขั้นแล้ว! ยศขุนนางทำให้พลังรบของขุนพลทั้งหมดเพิ่มขึ้น ทำให้นายท่านเก่งยิ่งขึ้น กลายเป็นผู้แข็งแกร่งที่แท้จริง!","กวนอิ๋นผิง","story_zxc_50","story_zxc_50",},
        [110] = {29,"กวนอิ๋นผิง","ว้าว พลังรบระเบิด! คนเก่งผงาด!","ว้าว พลังรบระเบิด! คนเก่งผงาด!","","story_gyp_32","story_gyp_32",},
        [111] = {29,"กวนอิ๋นผิง","นายท่าน พวกเรารีบไปลงดันเจี้ยนสายหลักกำจัดศัตรู ทำศึกต่อไปเถอะ!","นายท่าน พวกเรารีบไปลงดันเจี้ยนสายหลักกำจัดศัตรู ทำศึกต่อไปเถอะ!","","story_gyp_33","story_gyp_33",},
        [112] = {30,"เตียวซี","นายท่าน ท่านมีชื่อเสียงเลื่องลือแล้ว สามารถไปท้ารบ#ฝ่าด่านสังหารขุนพล#รับอุปกรณ์อย่างดี!","นายท่าน ท่านมีชื่อเสียงเลื่องลือแล้ว สามารถไปท้ารบ#ฝ่าด่านสังหารขุนพล#รับอุปกรณ์อย่างดี!","","story_zxc_51","story_zxc_51",},
        [113] = {31,"เตียวซี","นายท่าน ฝ่าด่านสังหารขุนพลแต่ละด่านมีความยาก3ระดับ ความยากยิ่งสูง ดาวกับเหล็กหล่อที่ได้รับยิ่งเยอะ!","นายท่าน ฝ่าด่านสังหารขุนพลแต่ละด่านมีความยาก3ระดับ ความยากยิ่งสูง ดาวกับเหล็กหล่อที่ได้รับยิ่งเยอะ!","กวนอิ๋นผิง","story_zxc_52","story_zxc_52",},
        [114] = {31,"กวนอิ๋นผิง","เหล็กหล่อสามารถแลกอุปกรณ์อย่างดีได้ที่ร้านอุปกรณ์ สะสมดาวถึงจำนวนหนึ่งยังจะได้รับรางวัลโบนัสด้วย","เหล็กหล่อสามารถแลกอุปกรณ์อย่างดีได้ที่ร้านอุปกรณ์ สะสมดาวถึงจำนวนหนึ่งยังจะได้รับรางวัลโบนัสด้วย","","story_gyp_34","story_gyp_34",},
        [115] = {31,"เตียวซี","นายท่าน ท่านเพิ่งได้รับเหล็กหล่อจำนวนมาก ตอนนี้โปรดไปดูที่ร้านอุปกรณ์สิ","นายท่าน ท่านเพิ่งได้รับเหล็กหล่อจำนวนมาก ตอนนี้โปรดไปดูที่ร้านอุปกรณ์สิ","","story_zxc_53","story_zxc_53",},
        [116] = {32,"เตียวซี","ยินดีด้วยนายท่านได้รับกระบี่เหมันต์! เยี่ยมไปเลย!","ยินดีด้วยนายท่านได้รับกระบี่เหมันต์! เยี่ยมไปเลย!","กวนอิ๋นผิง","story_zxc_54","story_zxc_54",},
        [117] = {32,"เตียวซี","นายท่าน อยากได้รับอุปกรณ์อย่างดี ทุกวันอย่าลืมมาท้ารบฝ่าด่านสังหารขุนพลล่ะ!","นายท่าน อยากได้รับอุปกรณ์อย่างดี ทุกวันอย่าลืมมาท้ารบฝ่าด่านสังหารขุนพลล่ะ!","","story_zxc_55","story_zxc_55",},
        [118] = {32,"กวนอิ๋นผิง","นายท่าน อิ๋นผิงอยากได้อุปกรณ์อย่างดีเยอะๆ! เร็วๆๆ พวกเราไปฝ่าด่านสังหารขุนพลต่อเถอะ!","นายท่าน อิ๋นผิงอยากได้อุปกรณ์อย่างดีเยอะๆ! เร็วๆๆ พวกเราไปฝ่าด่านสังหารขุนพลต่อเถอะ!","","story_gyp_35","story_gyp_35",},
        [119] = {33,"เตียวซี","นายท่าน #ดันเจี้ยนรายวัน#เปิดแล้วล่ะ! ดันเจี้ยนรายวันเป็นสถานที่ที่ดี!","นายท่าน #ดันเจี้ยนรายวัน#เปิดแล้วล่ะ! ดันเจี้ยนรายวันเป็นสถานที่ที่ดี!","กวนอิ๋นผิง","story_zxc_56","story_zxc_56",},
        [120] = {33,"กวนอิ๋นผิง","นายท่าน ลงดันเจี้ยนรายวัน จะได้รับทรัพยากรจำนวนมาก! อย่างเช่นเงินทอง ยาทะลวง หินสกัดต่างๆเป็นต้น ทำให้พลังรบเพิ่มขึ้นก้าวกระโดด!","นายท่าน ลงดันเจี้ยนรายวัน จะได้รับทรัพยากรจำนวนมาก! อย่างเช่นเงินทอง ยาทะลวง หินสกัดต่างๆเป็นต้น ทำให้พลังรบเพิ่มขึ้นก้าวกระโดด!","","story_gyp_36","story_gyp_36",},
        [121] = {33,"เตียวซี","ว้าว ดันเจี้ยนรายวันเยี่ยมขนาดนี้เชียว พวกเรารีบไปดูเถอะ!","ว้าว ดันเจี้ยนรายวันเยี่ยมขนาดนี้เชียว พวกเรารีบไปดูเถอะ!","","story_zxc_57","story_zxc_57",},
        [122] = {34,"เตียวซี","นายท่าน ลงสนามฆ่าศัตรูคนเดียวไม่ได้! ระบบ#กองทัพ#เปิดแล้ว พวกเราไปดูเถอะ!","นายท่าน ลงสนามฆ่าศัตรูคนเดียวไม่ได้! ระบบ#กองทัพ#เปิดแล้ว พวกเราไปดูเถอะ!","กวนอิ๋นผิง","story_zxc_58","story_zxc_58",},
        [123] = {34,"กวนอิ๋นผิง","นายท่านเข้าร่วมกองทัพ ยังจะเปิดการเล่นอื่นอีก! รีบยื่นสมัครเข้าร่วมกองทัพสิ!","นายท่านเข้าร่วมกองทัพ ยังจะเปิดการเล่นอื่นอีก! รีบยื่นสมัครเข้าร่วมกองทัพสิ!","","story_gyp_37","story_gyp_37",},
        [124] = {205,"ตัวเอก","บัดซบ! ฆ่าเตียวก๊กแล้ว อิเกียดกลับหนีไปได้!","บัดซบ! ฆ่าเตียวก๊กแล้ว อิเกียดกลับหนีไปได้!","โจโฉ","story_zj_8","story_nzj_8",},
        [125] = {205,"โจโฉ","ขุนพลน้อยฝีมือดี อนาคตต้องสร้างผลงานแน่! วันหน้าถ้ามาลกเอี๋ยง ต้องมาหาข้านะ! ข้าโจโฉมีงานอื่น ขอตัวก่อน","ขุนพลน้อยฝีมือดี อนาคตต้องสร้างผลงานแน่! วันหน้าถ้ามาลกเอี๋ยง ต้องมาหาข้านะ! ข้าโจโฉมีงานอื่น ขอตัวก่อน","","story_cc_3","story_cc_3",},
        [126] = {205,"เตียวซี","นายท่าน คำพูดของอิเกียดซ่อนความลับ แต่เหมือนโจโฉไม่นำมาใส่ใจ…","นายท่าน คำพูดของอิเกียดซ่อนความลับ แต่เหมือนโจโฉไม่นำมาใส่ใจ…","ตัวเอก","story_zxc_24","story_zxc_24",},
        [127] = {205,"เตียวซี","นายท่าน เจ้าว่าโจโฉเป็นคนดีหรือคนเลว?","นายท่าน เจ้าว่าโจโฉเป็นคนดีหรือคนเลว?","","story_zxc_25","story_zxc_25",},
        [128] = {205,"ตัวเอก","คนดีแล้วยังไง คนเลวแล้วยังไง! โจโฉวันนี้ช่วยข้า วันหน้าถ้าต้องการ ข้าก็ยินดีช่วยเขา","คนดีแล้วยังไง คนเลวแล้วยังไง! โจโฉวันนี้ช่วยข้า วันหน้าถ้าต้องการ ข้าก็ยินดีช่วยเขา","","story_zj_9","story_nzj_9",},
        [129] = {205,"เตียวซี","นายท่าน นี่คือ#ป้ายยอดขุนพล#ที่อิเกียดทำตกไว้ตอนที่หนีไป!","นายท่าน นี่คือ#ป้ายยอดขุนพล#ที่อิเกียดทำตกไว้ตอนที่หนีไป!","","story_zxc_26","story_zxc_26",},
        [130] = {205,"ตัวเอก","#ป้ายยอดขุนพล#อีกชิ้นแล้ว! ดีจังเลย!","#ป้ายยอดขุนพล#อีกชิ้นแล้ว! ดีจังเลย!","","story_zj_10","story_nzj_10",},
        [131] = {205,"เตียวซี","นายท่าน ได้ยินว่าในมือโฮจิ๋นแม่ทัพใหญ่พี่ชายฮองเฮาก็มี#ป้ายยอดขุนพล# พวกเราไปลกเอี๋ยงหาโฮจิ๋นเถอะ","นายท่าน ได้ยินว่าในมือโฮจิ๋นแม่ทัพใหญ่พี่ชายฮองเฮาก็มี#ป้ายยอดขุนพล# พวกเราไปลกเอี๋ยงหาโฮจิ๋นเถอะ","","story_zxc_27","story_zxc_27",},
        [132] = {205,"ตัวเอก","ดี ข้ากำลังอยากไปพเนจรลกเอี๋ยงพอดี!","ดี ข้ากำลังอยากไปพเนจรลกเอี๋ยงพอดี!","","story_zj_11","story_nzj_11",},
