        [806] = {2018902,"เล่าปี่","กองซุนจ้าน เจ้านี่ โชคดีชะมัด!","กองซุนจ้าน เจ้านี่ โชคดีชะมัด!","กองซุนจ้าน","","",},
        [807] = {2018902,"กองซุนจ้าน","เล่าปี่ ท่านคือทายาทของจิ้งอ๋องแห่งจงซาน ข้าจะเทียบท่านได้อย่างไร!","เล่าปี่ ท่านคือทายาทของจิ้งอ๋องแห่งจงซาน ข้าจะเทียบท่านได้อย่างไร!","เล่าปี่","","",},
        [808] = {2018902,"เล่าปี่","ได้ดีแล้วยังมาทำไขสือ! ท่านเพิ่งได้เป็นลูกเขยของเจ้าเมืองเอกจัวจวิ้น พ่อตาของท่านมอบสาสน์ให้ท่านฉบับหนึ่ง ให้ท่านไปเป็นเรียนกับโลติด อนาคตก้าวไกล","ได้ดีแล้วยังมาทำไขสือ! ท่านเพิ่งได้เป็นลูกเขยของเจ้าเมืองเอกจัวจวิ้น พ่อตาของท่านมอบสาสน์ให้ท่านฉบับหนึ่ง ให้ท่านไปเป็นเรียนกับโลติด อนาคตก้าวไกล","กองซุนจ้าน","","",},
        [809] = {2018902,"กองซุนจ้าน","ฮ่าๆ ท่านก็จะไปเรียนกับโลติดเหมือนกันนี่! พูดดีพูดดี ท่านกับข้าได้เรียนด้วยกัน ต่อไปก็คอยดูแลกันเถอะ","ฮ่าๆ ท่านก็จะไปเรียนกับโลติดเหมือนกันนี่! พูดดีพูดดี ท่านกับข้าได้เรียนด้วยกัน ต่อไปก็คอยดูแลกันเถอะ","เล่าปี่","","",},
        [810] = {2018903,"กองซุนจ้าน","ข้าคือแม่ทัพม้าขาวกองซุนจ้าน! รับหน้าที่สำคัญในราชสำนัก ปกป้องชายแดง พวกโจรอูหวน พวกเจ้าชอบมาบุกรุก ออกมารับความตายเถอะ!","ข้าคือแม่ทัพม้าขาวกองซุนจ้าน! รับหน้าที่สำคัญในราชสำนัก ปกป้องชายแดง พวกโจรอูหวน พวกเจ้าชอบมาบุกรุก ออกมารับความตายเถอะ!","อ๋องอูหวน","","",},
        [811] = {2018903,"อ๋องอูหวน","ท่านแม่ทัพ ท่านกับข้าต่างทำตามหน้าที่ ใยต้องฆ่าแกงกัน ต่างคนต่างหลับตาคนละข้างไม่ดีหรือ","ท่านแม่ทัพ ท่านกับข้าต่างทำตามหน้าที่ ใยต้องฆ่าแกงกัน ต่างคนต่างหลับตาคนละข้างไม่ดีหรือ","กองซุนจ้าน","","",},
        [812] = {2018903,"กองซุนจ้าน","ปล่อยมารดาเจ้าเถอะ! พวกเจ้าเป็นโจรกล้ามาบุกรุก ข้ากองซุนจ้านถือดาบ มาคนฆ่าคน! จะไม่ยอมให้เจ้าเหิมเกริม! ทหาร ลุย!","ปล่อยมารดาเจ้าเถอะ! พวกเจ้าเป็นโจรกล้ามาบุกรุก ข้ากองซุนจ้านถือดาบ มาคนฆ่าคน! จะไม่ยอมให้เจ้าเหิมเกริม! ทหาร ลุย!","อ๋องอูหวน","","",},
        [813] = {2018903,"อ๋องอูหวน","ดุขนาดนี้ นึกไม่ถึงว่าในชาวฮั่นจะมีวีรบุรุษที่กล้าหาญเช่นนี้?! ได้ได้ได้ พวกเราชาวอูหวนเลื่อมใสคนกล้า ทุกคน รีบถอย!","ดุขนาดนี้ นึกไม่ถึงว่าในชาวฮั่นจะมีวีรบุรุษที่กล้าหาญเช่นนี้?! ได้ได้ได้ พวกเราชาวอูหวนเลื่อมใสคนกล้า ทุกคน รีบถอย!","กองซุนจ้าน","","",},
        [814] = {2018903,"อ๋องอูหวน","ต่อไปเห็นกลุ่มม้าขาว พวกเราก็รีบถอย! ปล้นสมบัติเรื่องเล็ก รักษาชีวิตเรื่องใหญ่!","ต่อไปเห็นกลุ่มม้าขาว พวกเราก็รีบถอย! ปล้นสมบัติเรื่องเล็ก รักษาชีวิตเรื่องใหญ่!","กองซุนจ้าน","","",},
        [815] = {20181001,"คนในตระกูลกบฏ","วันนี้โชคดี จับชาวฮั่นได้สิบคน ฆ่าพวกเขาแล้ว พวกเราไม่เสียเปรียบ! ฮ่าๆๆ!","วันนี้โชคดี จับชาวฮั่นได้สิบคน ฆ่าพวกเขาแล้ว พวกเราไม่เสียเปรียบ! ฮ่าๆๆ!","กาเซี่ยง","","",},
        [816] = {20181001,"กาเซี่ยง","(โชคดีจัง ระหว่างทางบ้านเมืองพบโจรกลุ่มหนึ่ง! ข้ากาเซี่ยงจะตายที่นี่ไม่ได้ ข้าต้องหาทางรักษาชีวิต!)","(โชคดีจัง ระหว่างทางบ้านเมืองพบโจรกลุ่มหนึ่ง! ข้ากาเซี่ยงจะตายที่นี่ไม่ได้ ข้าต้องหาทางรักษาชีวิต!)","คนในตระกูลกบฏ","","",},
        [817] = {20181001,"กาเซี่ยง"," (หมอนี่จับพวกเราไว้ คิดจะทำอะไร? ปล้นสมบัติหรือเอาชีวิต! มีแล้ว!) พวกเจ้ารู้ว่าข้าเป็นใครไหม?"," (หมอนี่จับพวกเราไว้ คิดจะทำอะไร? ปล้นสมบัติหรือเอาชีวิต! มีแล้ว!) พวกเจ้ารู้ว่าข้าเป็นใครไหม?","คนในตระกูลกบฏ","","",},
