        [819] = {20181001,"กาเซี่ยง","ท่านต้วนที่มีชื่อเสียง พวกเจ้ารู้จักไหม?","ท่านต้วนที่มีชื่อเสียง พวกเจ้ารู้จักไหม?","คนในตระกูลกบฏ","","",},
        [820] = {20181001,"คนในตระกูลกบฏ","เจ้าหมายถึงต้วนจ่งที่มีชื่อเสียงในราชวงศ์ฮั่นหรอ ถูกเรียกว่า3แม่ทัพแห่งเลียงจิ๋วร่วมกับ ฮองฮู เตียวฮ่วนน่ะหรอ","เจ้าหมายถึงต้วนจ่งที่มีชื่อเสียงในราชวงศ์ฮั่นหรอ ถูกเรียกว่า3แม่ทัพแห่งเลียงจิ๋วร่วมกับ ฮองฮู เตียวฮ่วนน่ะหรอ","กาเซี่ยง","","",},
        [821] = {20181001,"กาเซี่ยง","ถูกต้อง! ข้าคือหลานของท่านต้วน พวกเจ้าอย่าทำร้ายข้า คนที่บ้านข้าจะต้องมีเงินไถ่ตัวอย่างงามแน่ พวกเจ้าได้เงินแล้วก็จะได้ไปไกลๆ","ถูกต้อง! ข้าคือหลานของท่านต้วน พวกเจ้าอย่าทำร้ายข้า คนที่บ้านข้าจะต้องมีเงินไถ่ตัวอย่างงามแน่ พวกเจ้าได้เงินแล้วก็จะได้ไปไกลๆ","คนในตระกูลกบฏ","","",},
        [822] = {20181001,"คนในตระกูลกบฏ","พวกเจ้าชาวฮั่นเจ้าเล่ห์ เจ้าสาบานหนักๆแล้วข้าจะปล่อยเจ้าไป! (กาเซี่ยงสาบานหนัก โจรเลยปล่อยเขาไป คนอื่นๆถูกฆ่าตายหมด กาเซี่ยงอาศัยสติปัญญารักษาชีวิตตัวเองไว้)","พวกเจ้าชาวฮั่นเจ้าเล่ห์ เจ้าสาบานหนักๆแล้วข้าจะปล่อยเจ้าไป! (กาเซี่ยงสาบานหนัก โจรเลยปล่อยเขาไป คนอื่นๆถูกฆ่าตายหมด กาเซี่ยงอาศัยสติปัญญารักษาชีวิตตัวเองไว้)","กาเซี่ยง","","",},
        [823] = {20181002,"ลิฉุย","อ้องอุ้นตาแก่หัวดื้อ ไม่ยอมปล่อย เมื่อเป็นเช่นนี้ พวกเราก็รีบหนีเอาตัวรอดเถอะ!","อ้องอุ้นตาแก่หัวดื้อ ไม่ยอมปล่อย เมื่อเป็นเช่นนี้ พวกเราก็รีบหนีเอาตัวรอดเถอะ!","กาเซี่ยง","","",},
        [824] = {20181002,"กาเซี่ยง","ถ้าท่านทิ้งกองทัพเดินทางคนเดียว จะถูกนายอำเภอจับง่ายๆ ใยไม่รวมไพร่พลแถบนี้ บุกข้าเตียงฮันแก้แค้นกับตั๋งโต๊ะ","ถ้าท่านทิ้งกองทัพเดินทางคนเดียว จะถูกนายอำเภอจับง่ายๆ ใยไม่รวมไพร่พลแถบนี้ บุกข้าเตียงฮันแก้แค้นกับตั๋งโต๊ะ","ลิฉุย","","",},
