        [1359] = {20184103,"ชัวมอ","ทหาร คนพูดนี้พูดจาเหลวไหล ลากออกไปตัดหัว! นายท่านมีคำสั่งให้แต่งตั้งเล่าจ๋องเป็นผู้ปกครองเกงจิ๋ว!","ทหาร คนพูดนี้พูดจาเหลวไหล ลากออกไปตัดหัว! นายท่านมีคำสั่งให้แต่งตั้งเล่าจ๋องเป็นผู้ปกครองเกงจิ๋ว!","ลีกุ๋ย","","",},
        [1360] = {20184104,"ชัวมอ","น้องสาว เล่าปี่ส่งทหารปักหลักอยู่นอกเมือง ส่วนตนเองอยู่ในเกงจิ๋ว นานเข้าต้องเป็นภัยแน่","น้องสาว เล่าปี่ส่งทหารปักหลักอยู่นอกเมือง ส่วนตนเองอยู่ในเกงจิ๋ว นานเข้าต้องเป็นภัยแน่","ชัวฮูหยิน","","",},
        [1361] = {20184104,"ชัวฮูหยิน","ข้าได้ยินว่าเกงจิ๋วมีคนคบหาเล่าปี่ไม่น้อย ไม่ป้องกันคงไม่ได้","ข้าได้ยินว่าเกงจิ๋วมีคนคบหาเล่าปี่ไม่น้อย ไม่ป้องกันคงไม่ได้","ชัวมอ","","",},
        [1362] = {20184104,"ชัวมอ","ข้าว่า ไปฆ่าเขาเลยดีไหม แล้วค่อยไปบอกนายท่าน","ข้าว่า ไปฆ่าเขาเลยดีไหม แล้วค่อยไปบอกนายท่าน","ชัวฮูหยิน","","",},
        [1363] = {20184104,"อีเจี้ย","ชัวมอคิดทำร้ายนายท่าน นอกเมืองตะวันออก ใต้ เหนือ มีทหารปักหลักอยู่ มีแต่ทางตะวันตกที่ไปได้ ท่านรีบหนีเร็ว!","ชัวมอคิดทำร้ายนายท่าน นอกเมืองตะวันออก ใต้ เหนือ มีทหารปักหลักอยู่ มีแต่ทางตะวันตกที่ไปได้ ท่านรีบหนีเร็ว!","ชัวมอ","","",},
        [1364] = {20184104,"ชัวมอ","ใยท่านต้องหนี?","ใยท่านต้องหนี?","อีเจี้ย","","",},
        [1365] = {20184104,"เล่าปี่","ข้ากับท่านไม่มีความแค้น ใยท่านต้องทำร้ายข้า? ดีที่ข้ามีเต็กเลา ขี่ม้ากระโดดข้ามลำธาร ไม่อย่างนั้นต้องตายแน่แล้ว!","ข้ากับท่านไม่มีความแค้น ใยท่านต้องทำร้ายข้า? ดีที่ข้ามีเต็กเลา ขี่ม้ากระโดดข้ามลำธาร ไม่อย่างนั้นต้องตายแน่แล้ว!","ชัวมอ","","",},
        [1366] = {20184105,"ชัวมอ","นายท่าน โจโฉนำทัพใหญ่ไปซงหยง! นายท่านมอบ9แคว้นเกงจิ๋วซงจิ๋ว มอบให้โจโฉ โจโฉต้องต้อนรับนายท่านแน่","นายท่าน โจโฉนำทัพใหญ่ไปซงหยง! นายท่านมอบ9แคว้นเกงจิ๋วซงจิ๋ว มอบให้โจโฉ โจโฉต้องต้อนรับนายท่านแน่","เล่ากี๋","","",},
        [1367] = {20184105,"เล่ากี๋","ข้ารับงานใหญ่ต่อมา ยังไม่ทันไร จะให้มอบให้คนอื่นได้อย่างไร?","ข้ารับงานใหญ่ต่อมา ยังไม่ทันไร จะให้มอบให้คนอื่นได้อย่างไร?","ชัวมอ","","",},
        [1368] = {20184105,"ชัวมอ","ตอนนี้โจโฉยกทัพบุกเหนือใต้ ในนามราชสำนัก นายท่านปฏิเสธ ไม่ดีกับชื่อเสียง และนายท่านเพิ่งรับตำแหน่ง ภัยภายนอกยังไม่สงบ ภัยภายในยังไม่จัดการ จะสู้ศัตรูได้อย่างไร? นายท่านอย่าได้สงสัยเลย","ตอนนี้โจโฉยกทัพบุกเหนือใต้ ในนามราชสำนัก นายท่านปฏิเสธ ไม่ดีกับชื่อเสียง และนายท่านเพิ่งรับตำแหน่ง ภัยภายนอกยังไม่สงบ ภัยภายในยังไม่จัดการ จะสู้ศัตรูได้อย่างไร? นายท่านอย่าได้สงสัยเลย","เล่ากี๋","","",},
        [1369] = {20184105,"เล่ากี๋","เช่นนี้ ก็เอาตามที่ท่านแม่ทัพกล่าว มอบเกงจิ๋วให้โจโฉ","เช่นนี้ ก็เอาตามที่ท่านแม่ทัพกล่าว มอบเกงจิ๋วให้โจโฉ","ชัวมอ","","",},
        [1370] = {20184106,"โจโฉ","ศึกปากสามแม่น้ำ ทหารง่อก๊กมีน้อย เจ้ากลับแพ้ แสดงว่าเจ้าไม่ตั้งใจ!","ศึกปากสามแม่น้ำ ทหารง่อก๊กมีน้อย เจ้ากลับแพ้ แสดงว่าเจ้าไม่ตั้งใจ!","ชัวมอ","","",},
        [1371] = {20184106,"ชัวมอ","ทัพเรือเกงจิ๋ว ไม่ได้ฝึกมานาน ทหารเฉงจิ๋ว ซีจิ๋ว ไม่เคยฝึกรบทางน้ำ ย่อมแพ้ ควรตั้งค่ายทางน้ำ ใช้ทหารเฉงจิ๋ว ซีจิ๋ว ยกเว้นเกงจิ๋ว ทุกวันฝึกทางน้ำให้คุ้นเคย ก็ได้แล้ว","ทัพเรือเกงจิ๋ว ไม่ได้ฝึกมานาน ทหารเฉงจิ๋ว ซีจิ๋ว ไม่เคยฝึกรบทางน้ำ ย่อมแพ้ ควรตั้งค่ายทางน้ำ ใช้ทหารเฉงจิ๋ว ซีจิ๋ว ยกเว้นเกงจิ๋ว ทุกวันฝึกทางน้ำให้คุ้นเคย ก็ได้แล้ว","โจโฉ","","",},
        [1372] = {20184106,"โจโฉ","เมื่อท่านเป็นแม่ทัพทางน้ำ ก็สังการไปได้เลย ไม่ต้องบอกข้า!","เมื่อท่านเป็นแม่ทัพทางน้ำ ก็สังการไปได้เลย ไม่ต้องบอกข้า!","ชัวมอ","","",},
        [1373] = {20184106,"ชัวมอ","ทหารทางน้ำมอบให้ข้าชัวมอ ท่านเสนาบดีวางใจได้ ข้าจะพยายามฝึกฝนเต็มที่ จับจิวยี่มาให้ได้!","ทหารทางน้ำมอบให้ข้าชัวมอ ท่านเสนาบดีวางใจได้ ข้าจะพยายามฝึกฝนเต็มที่ จับจิวยี่มาให้ได้!","โจโฉ","","",},
        [1374] = {20184107,"เจียวก้าน","ท่านเสนาบดี ข้าไปง่อก๊กครั้งนี้ ถึงไม่อาจเกลี่ยกล่อมจิวยี่ได้ แต่ก็สืบเรื่องหนึ่งให้ท่านเสนาบดี คือสองแม่ทัพชัวมอเตียวอุ๋นแอบสมคบจิวยี่ คิดว่าไม่เป็นผลดีต่อท่านเสนาบดี!","ท่านเสนาบดี ข้าไปง่อก๊กครั้งนี้ ถึงไม่อาจเกลี่ยกล่อมจิวยี่ได้ แต่ก็สืบเรื่องหนึ่งให้ท่านเสนาบดี คือสองแม่ทัพชัวมอเตียวอุ๋นแอบสมคบจิวยี่ คิดว่าไม่เป็นผลดีต่อท่านเสนาบดี!","โจโฉ","","",},
        [1375] = {20184107,"โจโฉ","โจรสองคนนี้ไร้มารยาทนัก! เชิญชัวมอเตียวอุ๋น! ข้าอยากให้เจ้าสองคนนำทหารบุก","โจรสองคนนี้ไร้มารยาทนัก! เชิญชัวมอเตียวอุ๋น! ข้าอยากให้เจ้าสองคนนำทหารบุก","เจียวก้าน","","",},
