        [2094] = {1701,"กวนอิ๋นผิง","ว่ากันว่าสวมเกราะมังกรขาว ทวนเงินสะท้อนสีรุ้ง ขี่ม้าฝุ่นตลบ เป็นคนที่มีความกล้าสูงส่ง!","ว่ากันว่าสวมเกราะมังกรขาว ทวนเงินสะท้อนสีรุ้ง ขี่ม้าฝุ่นตลบ เป็นคนที่มีความกล้าสูงส่ง!","","","",},
        [2095] = {1701,"กวนอิ๋นผิง","แม่ทัพจูล่งอายุน้อย งามสง่า พลังรบสูง ไม่ควรชื่นชมหรือ?","แม่ทัพจูล่งอายุน้อย งามสง่า พลังรบสูง ไม่ควรชื่นชมหรือ?","","","",},
        [2096] = {1701,"เตียวซี","ได้ได้ได้ ควรเลื่อมใส! แต่ในใจของข้า นายท่านถึงจะเป็นคนที่ควรเคารพที่สุดในโลก!","ได้ได้ได้ ควรเลื่อมใส! แต่ในใจของข้า นายท่านถึงจะเป็นคนที่ควรเคารพที่สุดในโลก!","","","",},
        [2097] = {1701,"ตัวเอก","ฮ่าๆๆ! เตียวซีชมข้าอีกแล้ว! วางใจ ข้าจะไม่ทำให้เจ้าผิดหวัง!","ฮ่าๆๆ! เตียวซีชมข้าอีกแล้ว! วางใจ ข้าจะไม่ทำให้เจ้าผิดหวัง!","","","",},
        [2098] = {1702,"ตัวเอก","เต๊งไก๋ เรื่องที่โจโฉบุกตีซีจิ๋วท่านต้องเคยได้ยินมาใช่ไหม ปักไฮขงหยงรับปากช่วยซีจิ๋วแล้ว ท่านล่ะ?","เต๊งไก๋ เรื่องที่โจโฉบุกตีซีจิ๋วท่านต้องเคยได้ยินมาใช่ไหม ปักไฮขงหยงรับปากช่วยซีจิ๋วแล้ว ท่านล่ะ?","เต๊งไก๋","","",},
        [2099] = {1702,"เต๊งไก๋","ซีจิ๋วกับเฉงจิ๋วเหมือนลิ้นกับฟัน ตามหลักการแล้วข้าเฉงจิ๋วควรช่วยเหลือ แต่ตอนนี้เฉงจิ๋วกำลังทำศึกกับอ้วนเสี้ยวที่กิจิ๋ว ถ้าแยกกำลังโดยพลการ คงจะไม่ดี……","ซีจิ๋วกับเฉงจิ๋วเหมือนลิ้นกับฟัน ตามหลักการแล้วข้าเฉงจิ๋วควรช่วยเหลือ แต่ตอนนี้เฉงจิ๋วกำลังทำศึกกับอ้วนเสี้ยวที่กิจิ๋ว ถ้าแยกกำลังโดยพลการ คงจะไม่ดี……","","","",},
        [2100] = {1702,"เต๊งไก๋","แต่ว่า โจโฉบุกซีจิ๋วไม่นานก็คงมาบุกเฉงจิ๋ว……ทำยังไงดี……","แต่ว่า โจโฉบุกซีจิ๋วไม่นานก็คงมาบุกเฉงจิ๋ว……ทำยังไงดี……","","","",},
        [2101] = {1702,"ตัวเอก","ปกติข้าเกลียดคนที่ลังเลเหยาะแหยะที่สุด เจ้าว่า มาหรือไม่มา!","ปกติข้าเกลียดคนที่ลังเลเหยาะแหยะที่สุด เจ้าว่า มาหรือไม่มา!","","","",},
        [2102] = {1702,"เต๊งไก๋","ขุนพลน้อยคอยซักครู่ โจโฉท่าทางดุดัน หนักว่าอ้วนเสี้ยว เต๊งไก๋สนับสนุนซีจิ๋วก็แล้วกัน!","ขุนพลน้อยคอยซักครู่ โจโฉท่าทางดุดัน หนักว่าอ้วนเสี้ยว เต๊งไก๋สนับสนุนซีจิ๋วก็แล้วกัน!","","","",},
        [2103] = {1702,"ตัวเอก","ดี อย่างนี้ค่อยดีหน่อย!","ดี อย่างนี้ค่อยดีหน่อย!","","","",},
