        [2287] = {41,"ตัวเอก","รู้แล้ว ขอบคุณเตียวซี!","รู้แล้ว ขอบคุณเตียวซี!","","","",},
        [2288] = {41,"เตียวซี","นายท่านไม่ต้องเกรงใจ! เตียวซียินดีปกป้องนายท่านตลอดไป!","นายท่านไม่ต้องเกรงใจ! เตียวซียินดีปกป้องนายท่านตลอดไป!","","","",},
        [2289] = {42,"กวนอิ๋นผิง","นายท่านท่านLv.70แล้ว ฝีมือสั่นสะเทือนแผ่นดิน พลังรบสูงส่ง แม้แต่อสูรเทพก็ยินดีรบเคียงบ่าเคียงไหล่ท่าน!","นายท่านท่านLv.70แล้ว ฝีมือสั่นสะเทือนแผ่นดิน พลังรบสูงส่ง แม้แต่อสูรเทพก็ยินดีรบเคียงบ่าเคียงไหล่ท่าน!","ตัวเอก","","",},
        [2290] = {42,"ตัวเอก","อสูรเทพ? คือประเภทมังกรเขียว พยัคฆ์ขาว หงส์ไฟ เต่าดำหรอ?","อสูรเทพ? คือประเภทมังกรเขียว พยัคฆ์ขาว หงส์ไฟ เต่าดำหรอ?","","","",},
        [2291] = {42,"กวนอิ๋นผิง","ใช่แล้ว บนแผ่นดินเทพของพวกเรา นับแต่โบราณมีอสูรเทพต่างๆสืบทอดต่อกันมา มีแต่คนที่ชะตาลิขิตกับคนเก่งที่แท้จริงถึงจะได้พบพวกเขา พ่อข้ากวนอูก็เคยได้รับการคุ้มครองจากมังกรเขียว","ใช่แล้ว บนแผ่นดินเทพของพวกเรา นับแต่โบราณมีอสูรเทพต่างๆสืบทอดต่อกันมา มีแต่คนที่ชะตาลิขิตกับคนเก่งที่แท้จริงถึงจะได้พบพวกเขา พ่อข้ากวนอูก็เคยได้รับการคุ้มครองจากมังกรเขียว","","","",},
        [2292] = {42,"ตัวเอก","มิน่าพี่รองถึงเก่งอย่างนี้ ที่แท้มีอสูรเทพคุ้มครอง! ข้าก็อยากมีอสูรเทพของตนเอง!","มิน่าพี่รองถึงเก่งอย่างนี้ ที่แท้มีอสูรเทพคุ้มครอง! ข้าก็อยากมีอสูรเทพของตนเอง!","","","",},
        [2293] = {42,"กวนอิ๋นผิง","นายท่านไม่ต้องรีบร้อน พวกเราเตรียมอสูรเทพให้ท่านตัวหนึ่งแล้ว ตอนนี้อิ๋นผิงจะพาท่านไปดู","นายท่านไม่ต้องรีบร้อน พวกเราเตรียมอสูรเทพให้ท่านตัวหนึ่งแล้ว ตอนนี้อิ๋นผิงจะพาท่านไปดู","","","",},
        [2294] = {43,"กวนอิ๋นผิง","นายท่าน นี่คืออสูรเทพตัวแรกของท่าน!","นายท่าน นี่คืออสูรเทพตัวแรกของท่าน!","ตัวเอก","","",},
        [2295] = {43,"ตัวเอก","เป็นทีมแพนด้าพ่อลูก! แพนด้าพ่อดูท่าทางสู้เก่ง แพนด้านน้อยน่ารัก!","เป็นทีมแพนด้าพ่อลูก! แพนด้าพ่อดูท่าทางสู้เก่ง แพนด้านน้อยน่ารัก!","","","",},
        [2296] = {43,"กวนอิ๋นผิง","นายท่าน อสูรเทพตอนที่ต่อสู้อยู่ในตำแหน่งที่7 เท่ากับขุนพลเก่งๆหลายคน","นายท่าน อสูรเทพตอนที่ต่อสู้อยู่ในตำแหน่งที่7 เท่ากับขุนพลเก่งๆหลายคน","","","",},
        [2297] = {43,"ตัวเอก","อิ๋นผิง แพนด้ามีเฉพาะคุณภาพฟ้า ถ้าข้าอยากได้รับอสูรเทพที่เก่งกว่านี้ ต้องไปที่ไหน?","อิ๋นผิง แพนด้ามีเฉพาะคุณภาพฟ้า ถ้าข้าอยากได้รับอสูรเทพที่เก่งกว่านี้ ต้องไปที่ไหน?","","","",},
        [2298] = {43,"กวนอิ๋นผิง","นายท่านไปที่ร้านอสูรเทพใช้#จิตอสูร#แลกอสูรเทพ","นายท่านไปที่ร้านอสูรเทพใช้#จิตอสูร#แลกอสูรเทพ","","","",},
