        [2301] = {44,"กวนอิ๋นผิง","นายท่าน ดูนี่ นี่ก็คือบ้านอสูรเทพ","นายท่าน ดูนี่ นี่ก็คือบ้านอสูรเทพ","ตัวเอก","","",},
        [2302] = {44,"กวนอิ๋นผิง","ในบ้านอสูรเทพมีต้นไม้เทพต้นหนึ่ง ต้นไม้นี้ดูดพลังไอทิพย์จากฟ้าดิน สามารถรวมเป็นจิตอสูรที่ล่ออสูรเทพออกมาได้ เลเวลของต้นไม้เทพยิ่งสูงจิตอสูรที่เกิดยิ่งเยอะ","ในบ้านอสูรเทพมีต้นไม้เทพต้นหนึ่ง ต้นไม้นี้ดูดพลังไอทิพย์จากฟ้าดิน สามารถรวมเป็นจิตอสูรที่ล่ออสูรเทพออกมาได้ เลเวลของต้นไม้เทพยิ่งสูงจิตอสูรที่เกิดยิ่งเยอะ","","","",},
        [2303] = {44,"ตัวเอก","ข้าเข้าใจแล้ว! อัปเลเวลต้นไม้เทพ จะได้รับจิตอสูรเยอะๆ! มีจิตอสูร ก็ไปที่ร้านอสูรเทพแลกอสูรเทพขั้นสูง","ข้าเข้าใจแล้ว! อัปเลเวลต้นไม้เทพ จะได้รับจิตอสูรเยอะๆ! มีจิตอสูร ก็ไปที่ร้านอสูรเทพแลกอสูรเทพขั้นสูง","","","",},
        [2304] = {44,"กวนอิ๋นผิง","นายท่านฉลาดจริงๆ แค่นี้ก็เข้าใจ ดังนั้นนายท่านต้องจำไว้อย่าลืมอัปเลเวลต้นไม้เทพ!","นายท่านฉลาดจริงๆ แค่นี้ก็เข้าใจ ดังนั้นนายท่านต้องจำไว้อย่าลืมอัปเลเวลต้นไม้เทพ!","","","",},
        [2305] = {44,"ตัวเอก","แน่นอน ข้าอยากมีอสูรเทพที่ร้ายกาจที่สุดในโลก!","แน่นอน ข้าอยากมีอสูรเทพที่ร้ายกาจที่สุดในโลก!","","","",},
