        [4475] = {8504,"จิวยี่","บัดนี้มากังตั๋ง รบกวนท่านช่วยเกลี้ยกล่อมให้น้องชายท่านทิ้งเล่าปี่มาทำงานให้ง่อก๊กแทน เช่นนั้นนายท่านจะได้ผู้ช่วยอีกคน ส่วนท่านจะได้อยู่ด้วยกันพี่น้อง ท่านเห็นว่าเป็นเรื่องดีหรือไม่?","บัดนี้มากังตั๋ง รบกวนท่านช่วยเกลี้ยกล่อมให้น้องชายท่านทิ้งเล่าปี่มาทำงานให้ง่อก๊กแทน เช่นนั้นนายท่านจะได้ผู้ช่วยอีกคน ส่วนท่านจะได้อยู่ด้วยกันพี่น้อง ท่านเห็นว่าเป็นเรื่องดีหรือไม่?","จูกัดกิ๋น","","",},
        [4476] = {8504,"จูกัดกิ๋น","นับแต่ข้ามาถึงกังตั๋ง ยังไร้ซึ่งผลงาน บัดนี้แม่ทัพมีคำสั่ง ย่อมต้องทำอย่างเต็มที่","นับแต่ข้ามาถึงกังตั๋ง ยังไร้ซึ่งผลงาน บัดนี้แม่ทัพมีคำสั่ง ย่อมต้องทำอย่างเต็มที่","จิวยี่","","",},
        [4477] = {8505,"จูกัดกิ๋น","เจ้าจะยอมทิ้งเล่าปี่มาทำงานให้ง่อก๊กหรือไม่?","เจ้าจะยอมทิ้งเล่าปี่มาทำงานให้ง่อก๊กหรือไม่?","จูกัดเหลียง","","",},
        [4478] = {8505,"จูกัดเหลียง ","ต้องเป็นจิวยี่ให้มาเกลี้ยกล่อมข้าเป็นแน่","ต้องเป็นจิวยี่ให้มาเกลี้ยกล่อมข้าเป็นแน่","จูกัดกิ๋น","","",},
        [4479] = {8505,"จูกัดกิ๋น","รู้เรื่องปั๋วหยี ซูฉีหรือไม่?","รู้เรื่องปั๋วหยี ซูฉีหรือไม่?","จูกัดเหลียง","","",},
        [4480] = {8505,"จูกัดกิ๋น","แม้ปั๋วหยีและซูฉีต้องอดตายอยู่เชิงเขาโส่ยหยาง แต่สองพี่น้องก็ได้อยู่ด้วยกัน ข้ากับเจ้าเป็นพี่น้องแท้ๆ แต่ทำงานต่างนาย ไม่อาจอยู่ด้วยกันได้ เมื่อเทียบกับปั๋วหยีและซูฉีแล้ว นับว่าเสียดาย?","แม้ปั๋วหยีและซูฉีต้องอดตายอยู่เชิงเขาโส่ยหยาง แต่สองพี่น้องก็ได้อยู่ด้วยกัน ข้ากับเจ้าเป็นพี่น้องแท้ๆ แต่ทำงานต่างนาย ไม่อาจอยู่ด้วยกันได้ เมื่อเทียบกับปั๋วหยีและซูฉีแล้ว นับว่าเสียดาย?","จูกัดเหลียง","","",},
        [4481] = {8505,"จูกัดเหลียง ","ที่ท่านพี่พูดมานั้นคือความเป็นญาติ ส่วนที่น้องปกปักนั้นคือคุณธรรม น้องและท่านพี่ล้วนเป็นชาวฮั่น","ที่ท่านพี่พูดมานั้นคือความเป็นญาติ ส่วนที่น้องปกปักนั้นคือคุณธรรม น้องและท่านพี่ล้วนเป็นชาวฮั่น","จูกัดกิ๋น","","",},
        [4482] = {8505,"จูกัดเหลียง ","ท่านอาเล่าเป็นเชื้อพระวงศ์ หากท่านพี่สามารถไปง่อก๊ก ทำงานให้กับท่านอาเล่าด้วยกัน นับว่าเป็นขุนนางราชวงศ์ฮั่นโดยแท้ อีกทั้งพี่น้องได้อยู่ด้วยกัน เช่นนี้จะมิผิดทั้งญาติและคุณธรรม","ท่านอาเล่าเป็นเชื้อพระวงศ์ หากท่านพี่สามารถไปง่อก๊ก ทำงานให้กับท่านอาเล่าด้วยกัน นับว่าเป็นขุนนางราชวงศ์ฮั่นโดยแท้ อีกทั้งพี่น้องได้อยู่ด้วยกัน เช่นนี้จะมิผิดทั้งญาติและคุณธรรม","จูกัดกิ๋น","","",},
