        [30] = {30,"Thái Văn Cơ bị cướp đến Hung Nô, dâng cho Tả Hiền Vương. Sau đó, ai đã đón Thái Văn Cơ về Trung Nguyên?","Tào Tháo","Tào Phi","Tư Mã Ý","Tuân Úc",},
        [31] = {31,"Trong Trận Di Lăng, ai đã hỏa thiêu liên doanh, đánh lui đại quân Lưu Bị?","Gia Cát Cẩn","Lỗ Túc","Tôn Sách","Lục Tốn",},
        [32] = {32,"Vị danh tướng nào được gọi là Cẩm Phàm Du Hiệp?","Lữ Mông","Cam Ninh","Chu Thái","Lăng Thống",},
        [33] = {33,"Tương truyền Tào Tháo có một sở thích đặc biệt, người nào sau đây liên quan đến sở thích này?","Hà Thái Hậu","Vương Dị","Bào Tam Nương","Trâu Thị",},
        [34] = {34,"Người sau khi qua đời được Tôn Quyền truy phong là hoàng hậu là?","Tinh Thái","Bộ Luyện Sư","Ngân Bình","Điêu Thuyền",},
        [35] = {35,"Trong 3 nhân vật tam quốc dưới đây, ai không thích ăn lê nhất?","Tinh Thái","Khổng Dung","A Đẩu","Tào Xung",},
