        [40] = {40,"Ai là tướng mà chúng ta công nhận là quan trọng nhất trong Tam Quốc Danh Tướng Truyện?","A Đẩu","Đổng Trác","Phan Phượng","Hứa Chử",},
        [41] = {41,"Tử Ngọ Cốc Kỳ Mưu, là vị danh tướng nào đưa ra, nhưng cuối cùng không được Khổng Minh chấp nhận?","Khương Duy","Ngụy Diên","Triệu Vân","Bàng Thống",},
        [42] = {42,"Đây là kế Đuổi hổ diệt sói! Xin hỏi, đây là lời của vị danh tướng nào???","Chu Du","Tuân Úc","Gia Cát Lượng","Lữ Bố",},
        [43] = {43,"Ta thà phụ người, chứ không để người phụ ta! Xin hỏi, đây là lời của vị danh tướng nào???","Tào Phi","Tào Thực","Tào Nhân","Tào Tháo",},
        [44] = {44,"Tiểu nhân, dám làm ta bị thương! Xin hỏi, đây là lời của vị danh tướng nào???","Hạ Hầu Đôn","Hạ Hầu Uyên","Tào Nhân","Điển Vi",},
        [45] = {45,"Chúa công, chạy mau... Xin hỏi, đây là lời của vị danh tướng nào???","Triệu Vân","Chu Thái","Điển Vi","Hứa Chử",},
        [46] = {46,"Ai dám đến đấu với ta 300 lượt! Xin hỏi, đây là lời của vị danh tướng nào???","Bàng Đức","Hứa Chử","Tôn Kiên","Lữ Bố",},
        [47] = {47,"Kế thừa chí nguyện của thừa tướng, dẹp nghịch tặc Hắn! Xin hỏi, đây là lời của vị danh tướng nào???","Quách Gia","Tuân Úc","Khương Duy","Lỗ Túc",},
        [48] = {48,"Đệ tử Giang Đông, có gì mà phải sợ? Xin hỏi, đây là lời của vị danh tướng nào???","Tôn Sách","Tôn Quyền","Tôn Kiên","Thượng Hương",},
        [49] = {49,"Đại trượng phu, phải cầm kiếm ba xích, lập công lớn! Xin hỏi, đây là lời của vị danh tướng nào???","Thái Sử Từ","Chu Du","Mã Siêu","Lữ Bố",},
        [50] = {50,"Đợi đã, để ta suy nghĩ! Xin hỏi, đây là lời của vị danh tướng nào???","Trương Chiêu","Tôn Quyền","Trần Cung","Lỗ Túc",},
        [51] = {51,"Công thành vi hạ, công tâm vi thượng. Xin hỏi, đây là lời của vị danh tướng nào???","Nhạc Tiến","Vu Cấm","Lữ Mông","Cam Ninh",},
        [52] = {52,"Dĩ hòa vi quý, dĩ hỏa vi quý! Xin hỏi, đây là lời của vị danh tướng nào?","Gia Cát Cẩn","Lỗ Túc","Tôn Sách","Lục Tốn",},
        [53] = {53,"Mỹ nhân~cho thơm cái nào! Xin hỏi, đây là lời của vị danh tướng nào??","Đổng Trác","Tào Tháo","Tôn Quyền","Lưu Bị",},
        [54] = {54,"Ta muốn canh ba lấy mạng ngươi, kẻ nào dám cho ngươi đến canh năm! Đây là câu nói của vị danh tướng nào?","Lý Nho","Giả Hủ","Bàng Thống","Trần Cung",},
        [55] = {55,"Bi ca khả dĩ đương khấp, viễn vọng khả dĩ đương quy. Đây là câu nói của vị danh tướng nào?","Thái Văn Cơ","Bộ Luyện Sư","Ngân Bình","Điêu Thuyền",},
        [56] = {56,"Bọn tép riu, các ngươi sẽ bị nhấn chìm dưới cơn thịnh nộ của ta! Đây là câu nói của vị danh tướng nào?","Cam Ninh","Lữ Mông","Chu Thái","Lăng Thống",},
        [57] = {57,"Niệp chỉ câu huyền, khí phá vạn quân Đây là câu nói của vị danh tướng nào?","Gia Cát Lượng","Chu Du","Tào Thực","Xuân Hoa",},
        [58] = {58,"Kẻ nào dám diệt ta? Đây là câu nói của vị danh tướng nào?","Ngụy Diên","Nhạc Tiến","Trương Liêu","Trương Hợp",},
        [59] = {59,"Hãy tìm khắp nơi cho ta! Đây là câu nói của vị danh tướng nào?","Bàng Thống","Tào Phi","Khương Duy","Mã Siêu",},
        [60] = {60,"Đạo dùng binh, biến hóa vạn thiên. Đây là câu nói của vị danh tướng nào?","Trương Hợp","Trương Giác","Trương Bảo","Trương Chiêu",},
        [61] = {61,"Bản thị đồng căn sinh, tương tiên hà thái cấp. Đây là câu nói của vị danh tướng nào?","Tào Thực","Viên Thiệu","Viên Thuật","Chân Cơ",},
        [62] = {62,"Người phàm mắt trần sao có thể hiểu được sự biến hóa của thần tiên? Đây là câu nói của vị danh tướng nào?","Gia Cát Lượng","Tả Từ","Trương Giác","Vu Cát",},
        [63] = {63,"Phi đạm bạc vô dĩ minh chí, phi ninh tịnh vô dĩ trí viễn, đây là câu nói của vị danh tướng nào?","Gia Cát Lượng","Lý Nho","Thư Thụ","Quách Gia",},
        [64] = {64,"Trí giả vụ kì thật, ngu giả tranh hư danh, đây là câu nói của vị danh tướng nào?","Khổng Dung","Tư Mã Ý","A Đẩu","Tào Xung",},
        [65] = {65,"Trượng phu chí tứ hải, vạn lí do bỉ lân, đây là câu nói của vị danh tướng nào?","Tào Thực","Trương Liêu","Trương Hợp","Trương Phi",},
        [66] = {66,"Thanh thanh tử câm, du du ngã tâm, đãn vi quân cố, trầm ngâm chí kim, đây là câu nói của vị danh tướng nào?","Vu Cấm","Tào Tháo","Trương Liêu","Tào Thực",},
        [67] = {67,"Vật dĩ thiện tiểu nhi bất vi, vật dĩ ác tiểu nhi vi chi, đây là câu nói của vị danh tướng nào?","Bàng Thống","Từ Thứ","Khương Duy","Lưu Bị",},
        [68] = {68,"Ta xem Nhan Lương chỉ như trò hề, đây là câu nói của vị danh tướng nào?","Trương Giác","Trương Lương","Quan Vũ","Trương Phi",},
        [69] = {69,"Lão kí phục lịch, chí tại thiên lí. Liệt sĩ mộ niên, tráng tâm bất dĩ, đây là câu nói của vị danh tướng nào?","Hoàng Trung","Tào Tháo","Phan Phượng","Trương Giác",},
        [70] = {70,"Đại trượng phu trong thiên hạ, sao có thể mãi dưới trướng người khác?, đây là câu nói của vị danh tướng nào?","Thái Sử Từ","Ngụy Diên","Triệu Vân","Lữ Bố",},
        [71] = {71,"Kiếp này ta không thể lấy người làm thê tử, không xứng là anh hùng, đây là câu nói của vị danh tướng nào?","Chu Du","Lưu Bị","Gia Cát Lượng","Lữ Bố",},
        [72] = {72,"Trong Tam Quốc Diễn Nghĩa Thân cao tám trượng, đầu đeo khăn, thần thái tựa thần, là vị danh tướng nào??","Tào Phi","Tào Thực","Tào Nhân","Gia Cát Lượng",},
        [73] = {73,"Trong Tam Quốc Diễn Nghĩa Thân cao tám trượng, giọng nói như tiếng sấm, thế tựa bôn mã, là miêu tả vị danh tướng nào?","Hạ Hầu Đôn","Hạ Hầu Uyên","Trương Phi","Điển Vi",},
        [74] = {74,"Trong Tam Quốc Diễn Nghĩa Thân hình cao to, hay đeo cung tên, tay cầm kích, cưỡi Xích Thố Mã, là vị danh tướng nào?","Triệu Vân","Chu Thái","Lữ Bố","Hứa Chử",},
        [75] = {75,"Trong Tam Quốc Diễn Nghĩa Dáng vẻ thư sinh, nhưng sức mạnh vô biên, mặc áo giáp màu bạc, là miêu tả vị danh tướng nào?","Mã Siêu","Hứa Chử","Tôn Kiên","Lữ Bố",},
        [76] = {76,"Trong Tam Quốc Diễn Nghĩa Dáng vẻ oai hùng, mắt phượng, mày ngài, là miêu tả vị danh tướng nào?","Hoa Hùng","Quan Vũ","Khương Duy","Điển Vi",},
        [77] = {77,"Trong Tam Quốc Diễn Nghĩa Cặp lông mày đậm, râu rậm, hình dạng kỳ quái, là miêu tả vị danh tướng nào?","Tôn Sách","Tôn Quyền","Tôn Kiên","Bàng Thống",},
        [78] = {78,"Trong Tam Quốc Diễn Nghĩa Thân cao tám trượng, cặp lông mày đậm, oai phong lẫm liệt, là miêu tả vị danh tướng nào?","Thái Sử Từ","Triệu Vân","Mã Siêu","Lữ Bố",},
        [79] = {79,"Trong Tam Quốc Diễn Nghĩa Mặt tựa ngọc, mắt sáng như sao, là miêu tả vị danh tướng nào?","Trương Chiêu","Tôn Quyền","Trương Liêu","Trần Cung",},
        [80] = {80,"Trong Tam Quốc Diễn Nghĩa Người xinh như ngọc, môi đỏ tựa son, dung mạo xinh đẹp , là miêu tả vị danh tướng nào?","Chu Du","Vu Cấm","Lữ Mông","Cam Ninh",},
        [81] = {81,"Vị danh tướng nào sau đây hai tay đều cầm vũ khí?","Tả Từ","Điêu Thuyền","Trương Giác","Đổng Trác",},
        [82] = {82,"Vị danh tướng nào sau đây vũ khí cao nhất?","Đổng Trác","Hạ Hầu Đôn","Nhan Lương","Ngân Bình",},
        [83] = {83,"Vị danh tướng nào sau đây không có thú cưng?","Tư Mã Ý","Tiểu Kiều","Vu Cát","A Đẩu",},
        [84] = {84,"Bát Mâu là vũ khí của vị danh tướng nào?","Quan Vũ","Trương Phi","Mã Siêu","Hoàng Trung",},
        [85] = {85,"Vị danh tướng nào sau đây vũ khí là cây kéo? ","Cam Ninh","Lữ Mông","Trương Nhượng","Lăng Thống",},
