        [9] = {14,"Nhân vật","Hậu duệ danh tướng, tướng mạo xinh đẹp, kết huynh muội với Lưu Bị, Quan Vũ, Trương Phi, được gọi là Tứ muội","Ta là nhân vật nữ, ta lợi hại hơn nhân vật nam","Ta là nhân vật nữ, ta lợi hại hơn nhân vật nam","","","","",0,0,},
        [10] = {15,"Nhân vật","Hậu duệ danh tướng, tướng mạo xinh đẹp, kết huynh muội với Lưu Bị, Quan Vũ, Trương Phi, được gọi là Tứ muội","Ta là nhân vật nữ, ta lợi hại hơn nhân vật nam","Ta là nhân vật nữ, ta lợi hại hơn nhân vật nam","","","","",0,0,},
        [11] = {101,"Tư Mã Ý","Tư Mã Ý, tự Trọng Đạt, quân sư bậc nhất của Tào Ngụy trong giai đoạn sau. Tương truyền, do ông có tướng mạo khiến người khác cảm thấy nguy hiểm, nên bị Tào Tháo kiêng dè. Ông có tài năng quân sự vượt trội, giỏi phòng thủ, từng bẻ gãy nhiều cuộc tấn công của Gia Cát Lượng. Ông cũng là người đặt nền móng cho nhà Tây Tấn thay thế nhà Tào Ngụy.","Nhà chính trị, nhà quân sự kiệt xuất của Ngụy.","Người đặt nền móng của vương triều Tây Tấn.","Bất Tử-Sát Thương","[Ưng Lang]","Tấn công gây sát thương cho phe địch.","Thi triển kỹ năng có xác suất tăng 1 lần tấn công kỹ năng.",0,1,},
        [12] = {102,"Tuân Úc","Tuân Úc, tự Văn Nhược, một mưu sĩ có tài thời Đông Hán. Ông nổi tiếng tao nhã thanh thoát, có con mắt chiến lược, biết nhìn người, từng tiến cử những mưu sĩ có tài khác, giúp Tào Tháo gây dựng sự nghiệp. Do ông giữ chức Thượng Thư Lệnh một thời gian dài, người điển trai, lại rất mê dùng mùi hương xông áo, để lại điển tích Tuân Lệnh Lưu Hương, hậu thế dùng để chỉ sức hút của mỹ nam.","Mưu thần và công thần hàng đầu của Tào Tháo khi thống nhất phương bắc.","Được cho là có tài phò vua giúp nước.","Trị Liệu","[Lưu Hương]","Trị liệu cho 3 mục tiêu có sinh lực thấp nhất của phe ta.","Trị liệu kỹ năng tăng.",1,1,},
        [13] = {103,"Tào Tháo","Tào Tháo, tự Mạnh Đức, tên lúc nhỏ là A Man, là nhà chính trị, quân sự kiệt xuất cuối thời Đông Hán, lập nên Tào Ngụy thời Tam Quốc","Hùng Tai Ngụy Thái Tổ, thiên hạ tảo lang yên.","Động tĩnh giai tồn trí, cao đê thiện dụng hiền.","Bất Tử-Hỗ Trợ","[Khí Thế]","Gây sát thương cho địch ở hàng trước.","Tăng xác suất bạo kích của tất cả Tướng ra trận.",0,1,},
        [14] = {104,"Quách Gia","Quách Gia, tự Phụng Hiếu, là mưu sĩ trọng yếu của Tào Tháo trong thời kỳ cuối của nhà Đông Hán và thời kỳ đầu của Tam Quốc. Trong 11 năm phục vụ cho Tào Tháo, ông giữ chức Quân Sư Tế Tửu, đáng tiếc ông mất sớm khiến Tào Tháo vô cùng thương tiếc.","Trời sinh Quách Phụng Hiếu, hào kiệt quán quần hùng.","Bụng đầy kinh sử, ngực có giáp binh.","Bất Tử-Khống Chế","[Ngao Tường]","Gây sát thương cho địch ở hàng trước.","Thi triển hồi nộ khí bản thân.",1,1,},
        [15] = {105,"Hạ Hầu Đôn","Hạ Hầu Đôn, tự Nguyên Nhượng, là công thần khai quốc nhà Tào Ngụy. Ông là anh họ của Hạ Hầu Uyên và cũng là một trong những tướng trung thành với Tào Tháo, góp công trong những trận đánh với Lưu Bị, Tôn Quyền, Lữ Bố","Người lập công mở nước Tào Ngụy.","Kiên cường, dũng mãnh không biết sợ.","Bất Tử-Đỡ Đòn","[Khai Thiên]","Gây sát thương cho địch ở hàng dọc.","Bị tấn công thường hồi phục nộ khí bản thân.",1,1,},
        [16] = {106,"Tào Nhân","Tào Nhân, biểu tự Tử Hiếu, là danh tướng của Tào Ngụy, một trong 8 Hổ Kỵ, sở trường phòng thủ, theo Tào Tháo chinh chiến nhiều năm, từng giữ chức Đại Tư Mã","Một lòng tận trung, không ngại nguy hiểm.","Tâm vi khí thành, binh vi thành thành.","Bất Tử-Khống Chế","[Vững Vàng]","Gây sát thương cho địch ở hàng dọc.","Khi tử vong choáng diệt tướng của bản thân.",1,1,},
        [17] = {107,"Điển Vi","Điển Vi, võ tướng của Tào Tháo, sức mạnh vô địch, sở trường sử dụng song kích, lúc Trương Tú làm loạn đã hy sinh thân mình để cứu Tào Tháo lấy thân mình đỡ cho Tào Tháo khiến Tào Tháo vô cùng thương tiếc.","Cầm 2 Thiết Kích 80 cân, lập công trạng ngoài Bộc Dương Thành.","Điển Vi cứu chủ truyền thiên hạ, dũng mãnh bậc nhất.","Bất Tử-Sát Thương","[Nghịch Chuyển]","Gây sát thương cho địch ở hàng dọc.","Tăng sát thương kỹ năng.",1,1,},
        [18] = {108,"Hứa Chử","Hứa Chử, tự Trọng Khang, biệt danh Hổ Si, là tướng hầu cận bên Tào Tháo, nổi tiếng không chỉ với sức khỏe phi thường mà còn với sự trung thành, tận tụy, hết lòng vì chủ nên được Tào Tháo coi trọng.","Uy phong trấn giữ Cửu Châu, năm xưa Hứa Chử quả như hổ.","Chích nhân Mạnh Khởi quân tiền kiến, thiên hạ tòng từ bá hổ hầu.","Bất Tử-Sát Thương","[Áp Đỉnh]","Gây sát thương cho địch ở hàng trước.","Diệt mục tiêu giúp bản thân hồi nộ khí.",1,1,},
        [19] = {109,"Trương Liêu","Trương Liêu, tự là Văn Viễn, một trong Ngũ Hổ Tướng của Tào Ngụy, từng tham gia nhiều trận đánh lớn và nổi tiếng nhất qua trận Hợp Phì với quân Đông Ngô","Nam chinh bắc chiến thống nhất phương bắc cho Tào Tháo.","Vào sinh ra tử, lập công lao hãn mã.","Bất Tử-Sát Thương","[Bôn Tập]","Gây sát thương cho địch ở hàng sau.","Mỗi lượt giúp bản thân hồi nộ khí.",1,1,},
        [20] = {110,"Trương Hợp","Trương Hợp, tự là Tuấn Nghệ, một trong Ngũ Hổ Tướng của Tào Ngụy, theo Tào Tháo chinh chiến nhiều năm, sau ở lần Bắc Phạt thứ 4 của Gia Cát Lượng bị trúng mai phục nên tử trận","Cáp khéo ứng biến, khéo bày trận.","Giỏi đoán địa hình, tình hình chiến trận.","Bất Tử-Hỗ Trợ","[Thấu Cốt]","Gây sát thương cho một mục tiêu địch ở hàng sau.","Tăng xác suất bạo kích của tất cả Tướng ra trận.",1,1,},
        [21] = {111,"Tào Phi","Tào Phi, tự Tử Hoàn, con thứ của Tào Tháo, là vị Hoàng đế đầu tiên của Tào Ngụy, văn võ song toàn","Khoáng đại chi độ, lịch dĩ công bằng chi thành.","Mại chí tồn đạo, khắc quảng đức tâm.","Bất Tử-Sát Thương","[Nhiếp Hồn]","Gây sát thương cho mục tiêu gần phe địch (10 chữ).","Mục tiêu càng ít, sát thương kỹ năng càng cao.",1,1,},
        [22] = {112,"Chân Cơ","Chân Cơ, mỹ nữ nổi tiếng thời Tam Quốc, phu nhân của Ngụy Văn Đế Tào Phi, vị Hoàng đế đầu tiên nhà Tào Ngụy. Nhưng tương truyền từng yêu Tào Thực, Lạc Thần Phú là tác phẩm Tào Thực viết về nàng.","Quang minh chi chí, thịnh cửu nhi bất muội giả dã.","Người phụ nữ này rất cao quý.","Bất Tử-Hỗ Trợ","[Lạc Thần Nộ]","Gây sát thương cho một mục tiêu địch ở hàng trước.","Mỗi lượt giúp bản thân hồi nộ khí.",1,1,},
        [23] = {113,"Nhạc Tiến","Nhạc Tiến, tự Văn Khiêm, là một võ tướng dưới quyền Tào Tháo cuối thời Đông Hán. Ông được miêu tả là có phần thấp bé nhưng rất dũng mãnh trên chiến trường. Nhạc Tiến góp công lớn trong chiến dịch của Tào Tháo chống lại Lữ Bố, Lưu Bị, tàn dư quân Hoàng Cân và chiến dịch đánh Viên Thiệu.","Người thấp bé, tác chiến dũng mãnh.","Đi theo Tào Tháo, nhiều lần lập công đầu.","Bất Tử-Sát Thương","[Thiên Vận]","Gây sát thương cho địch ở hàng trước.","Tấn công thường giúp bản thân hồi sinh lực.",0,1,},
        [24] = {114,"Từ Hoảng","Từ Hoảng, tự Công Minh, là vị tướng được đánh giá là xuất sắc nhất của triều đình Tào Ngụy. Trong sự nghiệp của mình, ông lập được vô số chiến công, nổi bật nhất là việc đánh thắng Quan Vũ trong trận chiến Phàn Thành.","Hàng minh quyền thành hậu, tranh tân định sách cao.","Nổi tiếng tấn công bất ngờ, quyết chiến với địch.","Bất Tử-Sát Thương","[Phong Vân]","Gây sát thương cho địch ở hàng dọc.","Lượt đầu bạo kích.",0,1,},
        [25] = {115,"Tào Xung","Tào Xung tự là Thương Thư, là người con trai của thừa tướng Tào Tháo thời nhà Hán, ông là con của Tào Tháo với người vợ thứ tư là Hoàn phu nhân, Tào Xung chết khi còn rất trẻ, ông là một trong những người con được Tào Tháo yêu quý.","Thông minh từ khi còn nhỏ.","Có trí thông minh như người lớn.","Bất Tử-Khống Chế","[Thần Đồng]","Gây sát thương cho địch ở hàng dọc.","Tăng xác suất khiến mục tiêu trầm mặc.",0,1,},
        [26] = {116,"Tào Thực","Tào Thực, tự là Tử Kiến, con của Tào Tháo, em trai Tào Phi. Ông là người tài hoa xuất chúng, văn thơ lai láng, tác phẩm nổi tiếng nhất có thể kể đến Lạc Thần Phú, Bạch Mã Thiên. Ông cùng Tào Phi, Tào Tháo được xưng tụng là Tam Tào, đều thuộc hàng những văn nhân góp công lớn cho sự phát triển của văn học, đặt nền móng cho đời sau.","Dĩ công tử chi hào, hạ bút lâm lang.","Bính thể mạo anh dật, cố tuấn tài vân chưng.","Bất Tử-Khống Chế","[Bảy Bước]","Gây sát thương cho một mục tiêu địch ở hàng trước.","Tăng xác suất gây choáng mục tiêu.",0,1,},
        [27] = {117,"Hạ Hầu Uyên","Hạ Hầu Uyên tự Diệu Tài, một trong 8 Hổ Kỵ, là tướng quân phe Tào Ngụy. Ông là em họ của Hạ Hầu Đôn, khi trấn thủ Hán Trung bị Hoàng Trung tập kích và tử trận","Điển quân hiệu úy Hạ Hầu Uyên.","3 ngày 500, 6 ngày 1000.","Bất Tử-Hỗ Trợ","[Nhanh Như Chớp]","Gây sát thương cho địch ở hàng dọc.","Chuyển hóa sát thương kỹ năng thành trị liệu.",0,1,},
        [28] = {118,"Xuân Hoa","Trương Xuân Hoa là vợ chính của Tư Mã Ý, là quyền thần nổi tiếng nhà Tào Ngụy. Bà là mẹ của 3 người con trai và một con gái, trong đó có hai người con nổi tiếng và tài năng nhất là Tư Mã Sư và Tư Mã Chiêu.","Từ nhỏ có đức hạnh, thông minh hơn người.","Bảo vận quy kỳ hậu dận, cái hữu mẫu nghi chi trợ yên.","Bất Tử-Hỗ Trợ","[Tuyệt Kiếm]","Gây sát thương cho một mục tiêu địch ở hàng trước.","Hộ Thuẫn bảo vệ đồng đội.",0,1,},
        [29] = {119,"Vu Cấm","Vu Cấm, tự Văn Tắc, là một võ tướng cuối thời Đông Hán, thuộc hạ của Tào Tháo, đã chinh chiến rất nhiều chiến dịch cho Tào Tháo, tạo dựng nên nền tảng nước Ngụy.","Vu Cấm đúng là biết lo cho đại cục, đúng là tướng tài.","Chỉ tranh đấu cho quốc gia, không suy tính thiệt hơn cho bản thân.","Bất Tử-Đỡ Đòn","[Tật Phong]","Gây sát thương cho một mục tiêu địch ở hàng trước.","Bị sát thương kỹ năng sẽ hồi phục sinh lực bản thân.",0,1,},
        [30] = {150,"Tử Thượng","Tư Mã Chiêu, tự Tử Thượng, con trai thứ của Tư Mã Ý và Xuân Hoa. Quyền thần Tào Ngụy, người đặt nền móng cho nhà Tây Tấn. Tham gia chinh phạt từ thời trẻ, hiểu rõ binh pháp, lập nhiều chiến công. Con trai Tư Mã Viêm sau này xưng đế, truy phong Tư Mã Chiêu làm Tấn Văn Đế.","Tự xưng Tấn Công, sau gọi Tấn Vương","Tay nắm binh quyền, thống trị quốc gia.","Động Cơ Bất Tử-Khống Chế ST","[Diêm La Quỷ Vực]","Gây sát thương cho tất cả nữ tướng phe địch.","Hồi nộ, tăng ST.",0,1,},
        [31] = {201,"Triệu Vân","Triệu Vân, tự là Tử Long, người vùng Thường Sơn, một trong Ngũ Hổ Thượng Tướng của Thục Hán, Ông chính là vị tướng làm nên kỳ tích một mình cứu chúa trong trận Trường Bản","Dũng cảm, là đại soái nhưng vẫn dấn thân trong làn tên mũi đạn.","Xung phong đi đầu, làm gương cho tướng sĩ.","Bạo Lực-Sát Thương Nhóm","[Uy Long]","Gây sát thương cho địch ở hàng dọc.","Diệt mục tiêu thêm 1 lần tấn công thường.",0,1,},
        [32] = {202,"Lưu Bị","Lưu Bị, tự Huyền Đức, Hoàng đế khai quốc Thục Hán thời Tam Quốc. Là người nhân hậu, biết trọng dụng người tài. Kết nghĩa đào viên với Trương Phi, Quan Vũ, rồi lập bá nghiệp.","Kiên nghị rộng lượng, trọng người tài.","Hào khí anh hùng, ngàn đời còn mãi.","Trị Liệu","[Ân Trạch]","Trị liệu cho tất cả mục tiêu phe ta.","Bị sát thương kỹ năng chuyển hóa thành trị liệu.",1,1,},
        [33] = {203,"Gia Cát Lượng","Gia Cát Lượng, tự Khổng Minh, hiệu Ngọa Long tiên sinh, quân sự kiệt xuất của nước Thục, giúp Lưu Bị cùng với Tôn Quyền đánh bại Tào Tháo ở Xích Bích, lấy Kinh Châu, định Tây Xuyên, dựng nước ở đất Thục. Ông phát minh ra Xe Ngựa Gỗ, Đèn Khổng Minh, Liên Nỏ.","Người? Thần? Tiên?","Người ta không biết đó là Ngọa Long!","Bạo Lực-Khống Chế Nhóm","[Chiêm Tinh]","Gây sát thương cho địch ở hàng sau.","Thi triển kỹ năng giảm nộ khí mục tiêu.",0,1,},
        [34] = {204,"Nguyệt Anh","Hoàng Nguyệt Anh, con gái của danh sĩ Hoàng Thừa Ngạn, vợ của Gia Cát Lượng. Tương truyền bà có trí tuệ hơn người, cùng chồng phát minh ra Mộc Ngưu Lưu Mã, một phương thức vận chuyển tiến bộ nhất thời đó.","Biết chu toàn cho việc lớn, giúp chồng phò tá Thục Hán","Học thức phong phú, tài năng xuất chúng.","Bạo Lực-Hỗ Trợ Nhóm","[Vận Chuyển]","Gây sát thương cho một mục tiêu địch ở hàng sau.","Thi triển kỹ năng tăng nộ khí Tướng PK.",1,1,},
        [35] = {205,"Quan Vũ","Quan Vũ, tự Vân Trường, đứng đầu trong Ngũ Hổ Thượng Tướng, nổi tiếng với điển tích Rượu Ấm Trảm Hoa Hùng, Quá Ngũ Quan Trảm Lục Tướng. Ông là người trung nghĩa, anh dũng nên được thiên hạ tôn làm Võ Thánh","Qua 5 ải, trảm 6 tướng!","Một mình một ngựa, chống lại vạn người!","Bạo Lực-Sát Thương Nhóm","[Thanh Long]","Gây sát thương cho một mục tiêu địch ở hàng trước.","Diệt mục tiêu giúp bản thân hồi nộ khí.",1,1,},
        [36] = {206,"Trương Phi","Trương Phi là danh tướng nhà Thục Hán, cùng Lưu Bị và Quan Vũ kết bái huynh đệ ở vườn đào. Ông là người dũng mãnh phi phàm, tính nóng như lửa, từng khiến 10 vạn quân Tào lui binh chỉ với một tiếng thét ở Cầu Đương Dương","Các tướng của Thục, Trương Phi là anh hùng nhất.","Địch vạn người, hành quân khắp nơi, yêu mến người tài.","Bạo Lực-Sát Thương Nhóm","[Yến Sư]","Gây sát thương cho địch ở hàng trước.","Sinh lực bản thân càng ít, tấn công càng cao.",1,1,},
        [37] = {207,"Mã Siêu","Mã Siêu, tự Mạnh Khởi, một trong Ngũ Hổ Thượng Tướng của Thục Hán, tác chiến vô cùng dũng mãnh, từng nhiều lần đánh bại quân Tào","Tứ Xuyên Mã Mạnh Khởi, danh tiếng lẫy lừng.","Vị kiều thi lục chiến, an Thục tấu toàn công.","Bạo Lực-Đỡ Đòn Nhóm","[Xông Pha]","Gây sát thương cho địch ở hàng trước.","Trị liệu nhận được tăng.",1,1,},
        [38] = {208,"Hoàng Trung","Hoàng Trung, tự Hán Thăng, một trong Ngũ Hổ Thượng Tướng, tuy đã già nhưng sức khỏe và tài năng chẳng thua kém ai, từng trảm Hạ Hầu Uyên thuộc hạ của Tào Tháo ở trận Định Quân Sơn. Trước khi đầu quân cho nhà Thục của Lưu Bị, ông theo Lưu Biểu","Gặp kẻ địch, tỏ rõ thần uy.","Hiến quắc công huân trọng, khai cương triển đế kỳ.","Bạo Lực-Sát Thương Nhóm","[Thiện Xạ]","Gây sát thương cho địch ở hàng trước.","Diệt mục tiêu thêm 1 lần tấn công thường.",1,1,},
        [39] = {209,"Khương Duy","Khương Duy, tự Bá Ước, là một đại tướng nhà Thục Hán. Ban đầu ông phục vụ Tào Ngụy như là một tùy tướng, nhưng sau đó đi theo Gia cát Lượng về Thục Hán. Sau khi Khổng Minh qua đời, Khương Duy tiếp tục thống lĩnh đại quân Bắc Phạt tấn công nước Ngụy nhiều lần.","Thiên thủy khoe anh tuấn, Lương Châu có người tài.","Hệ tòng thượng phụ xuất, thuật phụng vũ hầu lai.","Bạo Lực-Sát Thương Nhóm","[Xuyên Tâm]","Gây sát thương cho địch ở hàng dọc.","Lượt chiến đấu càng dài, tấn công càng cao.",1,1,},
        [40] = {210,"Ngụy Diên","Ngụy Diên, tên tự là Văn Trường, là đại tướng nhà Thục Hán. Sau khi Gia Cát Lượng qua đời vì tranh đoạt binh quyền với Dương Nghi nên bị Mã Đại xử trảm.","Dũng cảm, được nhiều người tin tưởng và quý trọng.","Giỏi luyện binh sĩ, dũng cảm hơn người.","Bạo Lực-Khống Chế Nhóm","[Thị Huyết]","Gây sát thương cho địch ở hàng dọc.","Tăng xác suất choáng.",1,1,},
        [41] = {211,"Bàng Thống","Bàng Thống, tự Sĩ Nguyên, hiệu Phượng Sồ, mưu sĩ quan trọng của Lưu Bị thời Tam Quốc. Ông có tài năng sánh ngang với Ngọa Long Khổng Minh, góp phần quan trọng trong chiến thắng Xích Bích của liên minh Tôn Lưu trước đại quân Tào Ngụy. Về sau không may trúng tên tử trận khi đang tiến đánh Lưu Chương, từ đó có địa danh Lạc Phụng Pha, nơi Phượng Hoàng nằm lại.","Bàng Thống nho nhã, am hiểu kinh học.","Kẻ thức thời là trang tuấn kiệt.","Bạo Lực-Hỗ Trợ Nhóm","[Niết Bàn]","Gây sát thương cho địch ở hàng dọc.","Tăng xác suất bạo kích của tất cả tướng ra trận.",1,1,},
        [42] = {212,"Từ Thứ","Từ Thứ, tự là Nguyên Trực, quân sư của Lưu Bị và sau đó là Tào Tháo. Thoạt đầu ông đi theo phò tá Lưu Bị, nhưng vì mẹ ông đang ở lãnh địa Tào Tháo, buộc Từ Thứ phải qua đầu quân cho Tào Tháo. Trước khi đi, ông đã tiến cử Gia Cát Lượng cho Lưu Bị.","Thống hận cao hiền bất tái phùng, lâm kỳ khấp biệt lưỡng tình nồng.","Phiến ngôn khước tự xuân lôi chấn, năng sử Hoa Dương khởi Ngọa Long.","Bạo Lực-Khống Chế Nhóm","[Im Lặng]","Gây sát thương cho một mục tiêu địch ở hàng sau.","Thi triển hồi nộ khí bản thân.",1,1,},
        [43] = {213,"Mạnh Hoạch","Mạnh Hoạch, thủ lĩnh của các bộ lạc ở phía nam đất Thục, dũng mãnh kiên cường, chân thành đức độ, rất được lòng dân. Gia Cát Lượng đã đích thân dẫn quân đến, 7 lần bắt và tha cho Mạnh Hoạch, nhằm thu phục lòng dân của các bộ lạc khu vực này.","Vững chắc, tên nhọn không xuyên qua được.","Có sức mạnh kinh người, tác chiến hoành tảo thiên quân.","Bạo Lực-Sát Thương Nhóm","[Rung Chuyển]","Gây sát thương cho một mục tiêu địch ở hàng trước.","Diệt mục tiêu tấn công tăng.",0,1,},
        [44] = {214,"Chúc Dung","Chúc Dung, vợ của Mạnh Hoạch, cũng là chị ruột của Đái Lai Động Chủ. Chúc Dung giỏi dùng phi đao, võ nghệ cao cường, từng cùng chồng dẫn quân chinh chiến. Sau khi Gia Cát Lượng thu phục được Mạnh Hoạch, Chúc Dung cũng cùng chồng đầu quân cho Thục Hán.","Giỏi dùng phi đao, bách phát bách trúng.","Là hậu duệ của hỏa thần Chúc Dung Thị.","Bạo Lực-Khống Chế Nhóm","[Chông Gai]","Gây sát thương cho một mục tiêu địch ở hàng trước.","Khi tử vong thi triển 1 lần kỹ năng.",0,1,},
        [45] = {215,"A Đẩu","Lưu Thiện, tự là Công Tự, tên lúc nhỏ là A Đẩu, con trai của Lưu Bị và Cam Phu Nhân. Lưu Thiện là hoàng đế thứ hai và cuối cùng của nhà Thục Hán, ở ngôi 41 năm, lâu nhất trong các vua chúa thời Tam Quốc. Năm 263, tướng Tào Ngụy là Đặng Ngải đem quân áp sát kinh đô Thục Hán, Lưu Thiện đầu hàng, được Tào Ngụy phong làm An Lạc Công, đưa về Lạc Dương, để lại điển tích Vui quá quên về.","Nhanh nhẹn, mến người hiền tài.","Nhậm hiền tướng tắc vi tuân lý chi quân.","Bạo Lực-Đỡ Đòn Nhóm","[Lăn Vòng]","Gây sát thương cho một mục tiêu địch ở hàng trước.","Tăng cho bản thân hộ thuẫn giảm thương.",0,1,},
        [46] = {216,"Tinh Thái","Trương Tinh Thái, con gái của Trương Phi, có vẻ đẹp hoạt bát nhí nhảnh. Nghe nói từng theo Hoa Đà học y thuật, lớn lên gả cho Lưu Thiện, được phong làm hoàng hậu.","Trưởng nữ của Trương Phi.","Sau trở thành hoàng hậu của Thục Hán hậu chủ Lưu Thiện.","Trị Liệu","[Nguyệt Hà]","Trị liệu cho mục tiêu có sinh lực thấp nhất của phe ta.","Khi tử vong thi triển 1 lần kỹ năng.",0,1,},
        [47] = {217,"Ngân Bình","Quan Ngân Bình là cô con gái được Quan Vũ gọi là Hổ Nữ bởi thừa hưởng toàn bộ uy phong vũ dũng từ cha, dù thân là nữ nhi nhưng 18 tuổi cô đã sớm được Gia Cát Lượng tin dùng, đem theo hỗ trợ phạt thảo Mạnh Hoạch.","Xinh đẹp thông minh.","18 tuổi đã giỏi võ.","Bạo Lực-Sát Thương Nhóm","[Lưu Quang]","Gây sát thương cho địch ở hàng trước.","Diệt mục tiêu hồi phục HP.",0,1,},
        [48] = {218,"Quan Bình","Quan Bình, con của Quan Vũ, tướng Thục Hán, theo Quan Vũ chinh chiến, tài nghệ hơn người, lập nhiều chiến công.","Kim qua xông sát khí, thiết mã tiệt chinh trần.","Giàu tinh thần báo quốc.","Bạo Lực-Sát Thương Nhóm","[Long Ưng]","Gây sát thương cho địch ở hàng dọc.","Tăng sát thương kỹ năng.",0,1,},
        [49] = {219,"Pháp Chính","Pháp Chính tự Hiếu Trực, một trong những mưu sĩ hàng đầu của thế lực quân phiệt Lưu Bị thời Tam Quốc. Ban đầu, Pháp Chính là thủ hạ của Lưu Chương - quân phiệt Tây Xuyên. Sau này, khi Lưu Bị vào đất Thục, Pháp Chính về làm mưu thần cho Bị, được bị rất tín nhiệm và kính trọng.","Chính vụ huyền hoặc chi thuật, vi quý thượng chi phong.","Người như Quách Ngỗi, không ai sánh bằng.","Bạo Lực-Khống Chế Nhóm","[Ân Oán]","Gây sát thương cho một mục tiêu địch ở hàng trước.","Tăng xác suất Tê Liệt.",0,1,},
        [50] = {250,"Thủy Kính","Thủy Kính, tên thật là Tư Mã Huy, danh sĩ thời Đông Hán, tinh thông đạo học, binh pháp. Học cao hiểu rộng, tài cán hơn người, được người đời xem trọng.","Tinh thông đạo học kỳ môn, binh pháp kinh học.","Học rộng tài cao, tài cán hơn người","Bạo Lực-Sát Thương Nhóm","[Sóng Gió]","Gây ST cho địch ở 2 hàng dọc.","Tướng tử vong sẽ tăng ST.",0,1,},
        [51] = {301,"Tôn Sách","Tôn Sách, tự Bá Phù, là một viên tướng và một lãnh chúa trong thời kỳ cuối của Đông Hán. Ông là người đặt nền móng quan trọng trong việc hình thành nên Đông Ngô, đã được người em trai Tôn Quyền kế thừa và hoàn thiện sau khi ông mất.","Xinh đẹp, hay nói đùa.","Tính cách phóng khoáng, biết cách dùng người.","Thiêu Đốt-Sát Thương Nhóm","[Bá Vương]","Gây sát thương cho địch ở hàng sau.","Tân công thiêu đốt mục tiêu tăng tỉ lệ bạo kích.",0,1,},
        [52] = {302,"Đại Kiều","Đại Kiều là vợ của Tôn Sách, một trong những mỹ nhân thời kỳ Tam Quốc. Cùng em gái Tiểu Kiều được thiên hạ ca tụng là Giang Đông Nhị Kiều.","Vợ Tôn Sách, chị gái Tiểu Kiều.","Là mỹ nhân tuyệt thế.","Trị Liệu","[Uyển Chuyển]","Trị liệu cho tất cả mục tiêu phe ta.","Hiệu quả trị liệu kèm kỹ năng tăng.",1,1,},
        [53] = {303,"Chu Du","Chu Du, tên tự Công Cẩn, đương thời gọi Chu Lang, là danh tướng và khai quốc công thần của nước Đông Ngô. Chu Du nổi tiếng với chiến thắng ở Trận Xích Bích trước quân Tào Tháo","Du có tài về âm nhạc,.","Có tài ăn nói, văn chương, hiểu biết sâu rộng, thông minh hơn người.","Thiêu Đốt-Phóng Hỏa Nhóm","[Hỏa Nguyên]","Gây sát thương cho tất cả mục tiêu phe địch.","Bị tấn công thường có xác suất thiêu đốt kẻ tấn công.",0,1,},
        [54] = {304,"Tiểu Kiều","Tiểu Kiều, vợ Chu Du, mỹ nhân nổi tiếng Tam Quốc, cùng chị gái Đại Kiều được thiên hạ ca tụng là Giang Đông Nhị Kiều.","Thu nương huỳnh hỏa vãn tinh thiên, xảo tiếu minh mâu cố nguyệt nhan.","Tình phi đắc dĩ tình bất dĩ, ái tự lên thời nhân diệc liên.","Thiêu Đốt-Hỗ Trợ Nhóm","[Thiên Hương]","Gây sát thương cho địch ở hàng trước.","Thi triển kỹ năng kèm thuẫn hút sinh lực cho đồng đội.",1,1,},
        [55] = {305,"Thái Sử Từ","Thái Sử Từ, tự Tử Nghĩa, vốn là thuộc hạ của Lưu Dao sau giúp Tôn Sách càn quét Giang Đông. Giỏi cưỡi ngựa bắn tên, tử trận tại Hợp Phì.","Đại trượng phu sinh vào thời loạn.","Mang kiếm 3 xích lập công hiển hách.","Thiêu Đốt-Phóng Hỏa Nhóm","[Phần Thiên]","Gây sát thương cho địch ở hàng trước.","Bị tấn công thường có xác suất kèm thiêu đốt kẻ tấn công.",1,1,},
        [56] = {306,"Tôn Quyền","Tôn Quyền, tự là Trọng Mưu, người sáng lập của chính quyền Đông Ngô. Ông thừa hưởng quyền kiểm soát đất Giang Đông từ tay huynh trưởng Tôn Sách. Sau cùng Lưu Bị thiết lập Liên minh Tôn Lưu đánh bại vạn đại quân Tào Tháo","Cát cứ Giang Đông, đất rộng hàng ngàn dặm.","Binh lực hùng mạnh, anh hùng lạc nghiệp.","Thiêu Đốt-Sát Thương Nhóm","[Quyền Kiếm]","Gây sát thương cho địch ở hàng trước.","Diệt mục tiêu thiêu đốt, hồi phục sinh lực bản thân.",1,1,},
        [57] = {307,"Lữ Mông","Lữ Mông, tên tự là Tử Minh, được xưng tụng là Lã Hổ Uy, là danh tướng cuối thời Đông Há, chiến tích nổi tiếng nhất là hạ Quan Vũ, lấy Kinh Châu","Cần cù bù thông minh, hiếu học.","Kẻ sĩ 3 ngày không gặp, phải nhìn bằng con mắt khác.","Thiêu Đốt-Khống Chế Nhóm","[Văn Phạt]","Gây sát thương cho tất cả mục tiêu phe địch.","Tăng xác suất Choáng.",1,1,},
        [58] = {308,"Cam Ninh","Cam Ninh, tự là Hưng Bá, mãnh tướng Giang Đông, theo Chu Du lập công lớn trong trận Xích Bích, đánh bại quân của Tào Tháo, nhân đó hạ được thành Di Lăng. Tôn Quyền từng ca tụng Tào Tháo có Trương Liêu, ta có Hưng Bá","Phiên nhiên thoát ki hàm, độ giang đắc anh chủ.","Lập được công trạng, vang danh thiên cổ.","Thiêu Đốt-Sát Thương Nhóm","[Phàm Dạ]","Gây sát thương cho một mục tiêu địch ở hàng sau.","Tấn công thường tăng sát thương cho mục tiêu thiêu đốt.",1,1,},
        [59] = {309,"Tôn Kiên","Tôn Kiên, tên tự là Văn Đài, là người đặt nền móng xây dựng nước Đông Ngô. Từng tham gia chiến dịch thảo phạt Hoàng Cân và Đổng Trác.","Ai nói Giang Nam thiếu tướng tài? Sao sáng đêm đêm chiếu văn đài.","Muốn diệt Đổng Trác an thiên hạ, Thái Thú Phượng Tường đến dẫn đầu.","Thiêu Đốt-Đỡ Đòn Nhóm","[Đông Hổ]","Gây sát thương cho một mục tiêu địch ở hàng trước.","Khi bị kẻ địch ở trạng thái thiêu đốt tấn công sẽ giảm sát thương.",1,1,},
        [60] = {310,"Thượng Hương","Con gái Tôn Kiên và là em gái của Tôn Sách và Tôn Quyền, sau lấy Lưu Bị làm phu quân","Bộ chướng minh châu sự diễu mang, phu nhân quy quốc thúy vi lương.","Giang Đông thị tỳ nghênh lang nhật, do ký đao quang mãn động phòng.","Thiêu Đốt-Sát Thương Nhóm","[Tuyệt Cơ]","Gây sát thương cho một mục tiêu địch ở hàng trước.","Thi triển kỹ năng tăng 1 lần tấn công thường.",1,1,},
        [61] = {311,"Lục Tốn","Lục Tốn, biểu tự Bá Ngôn, từng giữ chức Đại Đô Đốc Đông Ngô, Thượng Đại Tướng Quân, Thừa Tướng, nổi tiếng sau trận Di Lăng tiêu diệt vạn đại quân Thục của Lưu Bị","Trí dũng song toàn, phẩm chất cao thượng.","Võ có thể an bang, văn có thể trị quốc.","Thiêu Đốt-Phóng Hỏa Nhóm","[Thất Sát]","Gây sát thương cho địch ở hàng sau.","Thi triển kỹ năng kèm tỉ lệ Thiêu Đốt tăng.",1,1,},
        [62] = {312,"Lỗ Túc","Lỗ Túc, tên tự là Tử Kính, là một chính trị gia, tướng lĩnh quân sự và nhà ngoại giao phục vụ dưới trướng Tôn Quyền vào cuối thời Đông Hán","Giỏi lập mưu kế.","Nhà giàu có, thích giúp đỡ người khác.","Thiêu Đốt-Khống Chế Nhóm","[Chiêu Tài]","Gây sát thương cho địch ở hàng dọc.","Thi triển hồi nộ khí bản thân.",1,1,},
        [63] = {313,"Lăng Thống","Lăng Thống, tự Công Tích, con của Lăng Tháo. Nổi danh từ nhỏ, có phong thái của bậc quốc sĩ. Từng theo Tôn Quyền chinh chiến, lập nhiều chiến công. Tuy có thù với Cam Ninh, về sau đã giảng hòa.","Mảnh Hổ Thần","Có phong cách người người tài.","Thiêu Đốt-Phóng Hỏa Nhóm","[Toàn Phong]","Gây sát thương cho một mục tiêu địch ở hàng trước.","Bị tấn công thường có xác suất kèm thiêu đốt cho kẻ tấn công.",0,1,},
        [64] = {314,"Hoàng Cái","Hoàng Cái, tên tự là Công Phúc, là công thần khai quốc nước Đông Ngô, mãnh tướng thiện chiến, trận Xích Bích Hoàng Cái dùng khổ nhục kế giả hàng Tào thừa dịp phóng hỏa đại phá quân Tào","Mãnh khí phân vân, hùng tâm tứ cứ.","Bách chiến bách thắng, quân đội hùng mạnh.","Thiêu Đốt-Đỡ Đòn Nhóm","[Dẻo Dai]","Gây sát thương cho một mục tiêu địch ở hàng trước.","Hiệu quả trị liệu tăng.",0,1,},
        [65] = {315,"Trương Chiêu","Trương Chiêu, tự Tử Bố, khai quốc công thần của nước Đông Ngô. Trước khi Tôn Sách qua đời đã nhờ Trương Chiêu và Chu Du trợ giúp Tôn Quyền","Nhận lệnh phò tá, lập nhiều công trạng.","Chính trực, không có ý nghĩ tư lợi.","Thiêu Đốt-Hỗ Trợ Nhóm","[Tung Hoành]","Gây sát thương cho một mục tiêu địch ở hàng trước.","Kèm Hộ Thuẫn cho đồng đội.",0,1,},
        [66] = {316,"Chu Thái","Chu Thái tự là Ấu Bình, võ tướng quan trọng của Đông Ngô, công thần khai quốc nước Đông Ngô, nhiều lần bảo vệ Tôn Quyền","3 lần cứu chủ thoát vòng vây, trung dũng hiếm thấy.","Người đầy thương tích vẫn uống say.","Thiêu Đốt-Sát Thương Nhóm","[Dũng Mãnh]","Gây sát thương cho một mục tiêu địch ở hàng trước.","Diệt mục tiêu tăng Nộ khí bản thân.",0,1,},
        [67] = {317,"Bộ Luyện Sư","Bộ Luyện Sư, hay còn gọi là Bộ phu nhân, vợ của Tôn Quyền hoàng đế sáng lập của nhà nước Đông Ngô trong thời Tam Quốc.","Không đố kỵ, sau này được sắc phong làm hoàng hậu.","Xinh đẹp nên được Tôn Quyền sủng ái nhưng không lập làm hoàng hậu.","Thiêu Đốt-Khống Chế Nhóm","[Tri Âm]","Gây sát thương cho một mục tiêu địch ở hàng trước.","Tăng xác suất Choáng.",0,1,},
        [68] = {318,"Hàn Đương","Hàn Đương tự Nghĩa Công, giỏi cung tiễn, kỵ thuật. Làm quan 3 đời cho Tôn Kiên, Tôn Sách, Tôn Quyền, có công lớn trong bá nghiệp Giang Đông.","Chinh chiến khắp nơi, nhiều lần mạo hiểm.","Ra trận bắt giặc, lập nhiều chiến công.","Thiêu Đốt-Sát Thương Nhóm","[Phong Xạ]","Gây sát thương cho địch ở hàng dọc.","Sát thương dành cho mục tiêu thiêu đốt tăng.",0,1,},
        [69] = {319,"Gia Cát Cẩn","Gia Cát Cẩn, tự Tử Du, trọng thần Đông Ngô, anh của Gia Cát Lượng, cha của Gia Cát Khác, rất được Tôn Quyền tín nhiệm, ra sức đóng góp cho mối quan hệ Đông Ngô và Thục Hán","Cẩn có gương mặt suy tư.","Vu thời phục kỳ hoằng nhã.","Thiêu Đốt-Phóng Hỏa Nhóm","[Hỏa Đồ]","Gây sát thương cho địch ở hàng dọc.","Khi bị tấn công kỹ năng có xác suất kèm thiêu đối cho kẻ tấn công.",0,1,},
        [70] = {350,"Chu Cơ","Chu Cơ, tên thật Chu Triệt, con gái của Chu Du và Tiểu Kiều. Sau gả cho Tôn Đăng trở thành thái tử phi, gọi là Chu Phi. Chu Cơ văn võ song toàn, lương thiện yêu dân.","Chu Cơ thừa hưởng nhan sắc của mẹ, tài cán của cha","Văn võ song toàn, lương thiện yêu dân.","Đội Thiêu Đốt-Hỗ Trợ ST","[Nghê Hoàng Nghiệp Hỏa]","Gây ST cho kẻ địch nam.","Gây thêm ST, xóa khống chế.",0,1,},
        [71] = {401,"Tả Từ","Tả Từ, tự Nguyên Phóng, đạo sĩ nổi tiếng thời Đông Hán, tinh thông kỳ môn độn giáp, có thể sai khiến quỷ thần, sau lên núi luyện linh đơn","Biết thần đạo, có thể sai khiến quỷ thần.","Hiểu Ngũ kinh, giỏi Chiêm tinh học, học đạo thuật, hiểu Lục giáp.","Giảm Nộ-Khống Chế Nhóm","[Kiếm Tàng]","Gây sát thương cho địch ở hàng trước.","Tăng xác suất Choáng.",0,1,},
        [72] = {402,"Hoa Đà","Hoa Đà, tự Nguyên Hóa, tinh thông y thuật, là một thầy thuốc nổi tiếng thời cuối Đông Hán và đầu thời Tam Quốc. Ông được xưng tụng như một Thần y nổi tiếng.","Nhà y học nổi tiếng cuối năm Đông Hán.","Tài chữa bệnh của Hoa Đà rất tuyệt vời.","Trị Liệu","[Hồi Xuân]","Trị liệu cho tất cả mục tiêu phe ta.","Mục tiêu càng ít sinh lực, trị liệu càng cao.",1,1,},
        [73] = {403,"Lữ Bố","Lữ Bố tự là Phụng Tiên, mãnh tướng số một tam quốc, cưỡi Xích Thố, tay cầm Họa Kích, oai phong lẫm liệt, được ca tụng là chiến thần!","Lữ Bố giỏi kích pháp, rất dũng mãnh.","Dũng mãnh, thiện chiến.","Giảm Nộ-Sát Thương Nhóm","[Thần Diệt]","Gây sát thương cho tất cả mục tiêu phe địch.","Diệt mục tiêu giúp bản thân hồi nộ khí.",0,1,},
        [74] = {404,"Điêu Thuyền","Điêu Thuyền, một trong tứ đại mỹ nhân, nghĩa nữ của Vương Doãn, để đền ơn nghĩa phụ đã tình nguyện hiến thân hoàn thành liên hoàn kế diệt Đổng Trác","Kinh hồng uyển chuyển chưởng trung thân, chích nghi phi quá động đình xuân.","Hảo hoa phong niễu nhất chi tân, họa đường hương noãn bất thắng xuân.","Giảm Nộ-Hỗ Trợ Nhóm","[Khuynh Thành]","Gây sát thương cho một mục tiêu địch ở hàng trước.","Chuyển hóa sát thương kỹ năng thành trị liệu.",1,1,},
        [75] = {405,"Đổng Trác","Đổng Trác, tự Trọng Dĩnh, là một tướng quân phiệt và quyền thần nhà Đông Hán, đầu thời Tam Quốc. Ông nổi tiếng là một gian thần, một quân phiệt tàn bạo. Sau này trúng mỹ nhân kế của Vương Doãn và bị nghĩa tử Lữ Bố trảm sát","To béo, sức mạnh vô song.","Một đời tàn bạo, đầy tư lợi và dã tâm.","Giảm Nộ-Đỡ Đòn Nhóm","[Thiên Đao]","Gây sát thương cho một mục tiêu địch ở hàng trước.","Hiệu quả Giảm Thương Thuẫn tăng.",1,1,},
        [76] = {406,"Hoa Hùng","Hoa Hùng, hổ tướng dưới trướng Đổng Trác, khi thập bát lộ chư hầu thảo phạt Đổng Trác đã bị Quan Vũ tiêu diệt.","Hiếu chiến.","Tự cho mình là thiên hạ vô địch.","Giảm Nộ-Sát Thương Nhóm","[Diệu Võ]","Gây sát thương cho địch ở hàng dọc.","Diệt mục tiêu giúp bản thân hồi nộ khí.",1,1,},
        [77] = {407,"Giả Hủ","Giả Hủ, tự là Văn Hòa, mưu sĩ nổi tiếng trong thời Tam Quốc, ban đầu theo phò trợ Đổng Trác, sau khi Đổng Trác bị tiêu diệt đã hàng Tào Tháo và trở thành quân sư thân cận của Tào Tháo","Trung với xã tắc.","Thần nhân vô công, đạt nhân vô tích.","Giảm Nộ-Sát Thương Nhóm","[Ám Nha]","Gây sát thương cho một mục tiêu địch ở hàng trước.","Tỉ lệ trúng độc kèm thi triển kỹ năng tăng.",1,1,},
        [78] = {408,"Công Tôn Toản","Công Tôn Toản tự Bá Khuê, tướng nhà Hán, Thái thú Bắc Bì. Lập nhiều chiến công khi thảo phạt dị tộc phương bắc, được gọi là Bạch Mã Tướng.","Ung dung tự tại.","Văn võ song toàn.","Giảm Nộ-Khống Chế Nhóm","[Phá Thiên]","Gây sát thương cho một mục tiêu địch ở hàng sau.","Thi triển hồi nộ khí bản thân.",1,1,},
        [79] = {409,"Trương Giác","Trương Giác là thủ lĩnh cuộc khởi nghĩa Khăn Vàng, hay còn gọi là quân Khăn Vàng vào cuối thời kỳ nhà Đông Hán. Ông thành lập và lãnh đạo đội quân khăn vàng chống lại triều đình nhà Hán nhưng đã bị triều đình đàn áp.","Làm lung lay nền móng vương triều Đông Hán.","Trực tiếp dẫn đến chuyện quân phiệt cát cứ cuối năm Đông Hán.","Giảm Nộ-Khống Chế Nhóm","[Hoàng Thiên]","Gây sát thương cho địch ở hàng trước.","Thi triển kỹ năng giảm nộ khí mục tiêu.",1,1,},
        [80] = {410,"Vu Cát","Là đạo sĩ được lòng người, trị bệnh cho bá tánh tại vùng Ngô Quận, Cối Kê. Bị Tôn Sách dùng tội danh Mê hoặc lòng người truy bắt. Tôn Sách nói nếu gọi được mưa sẽ tha cho, cuối cùng dù gọi được mưa nhưng vẫn bị xử tử.","Lúc đầu ở phương Đông, qua lại miền Ngô Hội.","Thắp hương đọc sách đạo, làm nước bùa trị bệnh.","Giảm Nộ-Sát Thương Nhóm","[Cổ Độc]","Gây sát thương cho địch ở hàng dọc.","Thi triển kỹ năng giảm nộ khí mục tiêu.",1,1,},
        [81] = {411,"Viên Thiệu","Viên Thiệu, tự Bản Sơ, thuộc dòng dõi Viên Thị, là bạn học thời nhỏ của Tào Tháo. Sau trở thành minh chủ liên minh chống Đổng Trác. Ở trận Quan Độ đại bại dưới tay Tào Tháo rồi sinh bệnh mà mất.","Hán Mạt đại loạn, quần hùng nổi lên.","Khoan dung, thương dân.","Giảm Nộ-Khống Chế Nhóm","[Tề Xạ]","Gây sát thương cho tất cả mục tiêu phe địch.","Xác suất thi triển kỹ năng gây Tê Liệt kẻ địch tăng.",1,1,},
        [82] = {412,"Viên Thuật","Viên Thuật, tự Công Lộ, hậu duệ Viên Gia. Lấy Ngọc Tỉ làm điều kiện mượn binh cho Tôn Sách. Nhưng khi Tôn Sách trả binh lại không chịu trả Ngọc Tỉ và dùng để xưng đế nên bị Tào Tháo, Tôn Kiên, Lữ Bố đồng loạt tấn công thổ huyết qua đời, Ngọc Tỉ cũng bị thất lạc.","Viên Thuật xấc xược.","Không hưởng được vinh hoa, tự chuốc lấy diệt vong.","Giảm Nộ-Hỗ Trợ Nhóm","[Thiên Tàn]","Gây sát thương cho một mục tiêu địch ở hàng trước.","Thi triển hồi nộ khí bản thân.",1,1,},
        [83] = {413,"Nhan Lương","Là tướng giỏi của Viên Thiệu. Tiêu diệt Ngụy Tục, Tống Hiến và thắng luôn cả Từ Hoảng trong cuộc chiến với Tào Tháo. Sau đó bị trảm dưới tay Quan Vũ trong doanh trại quân Tào.","Tướng tiên phong của quân đội Viên Thiệu.","Nổi tiếng vì sự dũng cảm.","Giảm Nộ-Sát Thương Nhóm","[Thiên Liệt]","Gây sát thương cho một mục tiêu địch ở hàng trước.","Lượt 1 chắc chắn bạo kích.",0,1,},
        [84] = {414,"Văn Xú","Tướng lĩnh của Viên Thiệu, ngang tài ngang sức Triệu Vân. Từng đánh bại Trương Liêu, Từ Hoảng. Sau trúng kế của Tào Tháo nên bị trảm dưới tay Quan Vũ trong cuộc chiến ở Diên Tân.","Đại tướng của quân đội Viên Thiệu.","Võ công cao cường, dũng mãnh.","Giảm Nộ-Sát Thương Nhóm","[Phách Sơn]","Gây sát thương cho địch ở hàng trước.","Tấn công thường giảm Nộ khí của mục tiêu.",0,1,},
        [85] = {415,"Thái Văn Cơ","Thái Diễm tự Văn Cơ, con gái Thái Ung, tài nữ cuối Đông Hán, bác học đa tài, tinh thông âm luật. Bị Hung Nô bắt giữ. 12 năm sau, Tào Tháo thống nhất phương bắc, dùng số tiền lớn chuộc Thái Văn Cơ về và gả cho Đổng Tự.","Nhu mì thục nữ, ngang hàng với nam nhi.","Chí nhu động cương, đồng quản vĩ tiết.","Giảm Nộ-Khống Chế Nhóm","[Thiên Nhai]","Gây sát thương cho một mục tiêu địch ở hàng trước.","Tăng xác suất Choáng.",0,1,},
        [86] = {416,"Trần Cung","Trần Cung, tên tự là Công Đài, từng theo Tào Tháo, sau làm mưu sĩ cho Lữ Bố đầu thời Tam Quốc. Lữ Bố chiến bại, Trần Cung không chịu hàng Tào nên Tào Tháo đành gạt nước mắt mà xử tử","Thức du ngư bất thức long, yếu tru Huyền Đức cự Tào Công.","Tuy làm trái ý trời, nhưng lòng trung vẫn sáng ngời.","Giảm Nộ-Sát Thương Nhóm","[Tài Năng]","Gây sát thương cho địch ở hàng dọc.","Tăng sát thương kỹ năng.",0,1,},
        [87] = {417,"Khổng Dung","Khổng Dung tự Văn Cử, là quan nhà Đông Hán và quân phiệt đầu thời Tam Quốc nhưng được người đời nhớ đến hơn qua điển cố Khổng Dung nhường lê","Hiểu rộng biết nhiều.","Hải thiên dật, âm tình đốn tỏa.","Giảm Nộ-Hỗ Trợ Nhóm","[Trúc Phi]","Gây sát thương cho một mục tiêu địch ở hàng trước.","Tấn công thường tăng thêm Nộ khí.",0,1,},
        [88] = {418,"Lữ Linh Thư ","Lữ Linh Thư, tương truyền là con gái Lữ Bố và Điêu Thuyền","Đứng yên như núi, nhanh như lửa","Nữ anh hùng tuyệt đối không kém nam tử hán.","Giảm Nộ-Đỡ Đòn Nhóm","[Chiến Cơ]","Gây sát thương cho một mục tiêu địch ở hàng trước.","Tấn công thường giúp bản thân hồi sinh lực.",0,1,},
        [89] = {419,"Trương Nhượng","Trương Nhượng, quan thần cuối thời Đông Hán, đứng đầu Thập Thường Thị, là thân tín của Linh Đế, vũ khí thường dùng là một cây kéo khổng lồ, tên là Tuyệt Tôn Tiễn.","Hoàng Cân hải phí vu Cửu Châu.","Quần yêm độc lưu vu tứ hải.","Giảm Nộ-Khống Chế Nhóm","[Tuyệt Tự]","Gây sát thương cho một mục tiêu địch ở hàng trước.","Tăng xác suất Trầm Mặc.",0,1,},
        [90] = {450,"Nam Hoa","Nam hoa lão tiên, tay cầm gậy mây. Vì tặng Thái Bình Thuật cho Trương Giác nên được người đời biết đến. Từng dạy dỗ Trương Giác phải phổ độ chúng sinh.","Chỉ dẫn giáo nghĩa","Phổ Độ Chúng Sinh","Giảm Nộ-Sát Thương Nhóm","[Ma Quang Thiên Nhẫn]","Gây ST cho 3 địch ngẫu nhiên.","Tăng ST kỹ năng và đánh thường.",0,1,},
        [91] = {120,"Tuân Du","Tuân Du, tự là Công Đạt, mưu sĩ giỏi của Tào Tháo thời Tam Quốc, từng bày kế hủy công nhấn chìm thành Hạ Phì bắt sống Lữ Bố","Lam Tướng, chưa miêu tả","Lam Tướng, chưa miêu tả","","","","",0,1,},
        [92] = {121,"Trình Dục","Trình Dục tự Trọng Đức, là một quân sư của Tào Tháo trong thời kỳ Tam Quốc, hiến kế Thập Diện Mai Phục đánh bại Viên Thiệu.","Lam Tướng, chưa miêu tả","Lam Tướng, chưa miêu tả","","","","",0,1,},
        [93] = {122,"Bàng Đức","Bàng Đức, tự là Lệnh Minh là viên võ tướng Tây Lương và sau đó là tướng của phe Tào Ngụy cuối thời Đông Hán, nổi danh trong việc tham gia hai trận chiến Đồng Quan và Phàn Thành","Lam Tướng, chưa miêu tả","Lam Tướng, chưa miêu tả","","","","",0,1,},
        [94] = {123,"Vương Dị","Vương Dị, vợ Thứ Sử Ích Châu Triệu Ngang, nổi tiếng là nữ tướng trung thành và thông minh. Khi Mã Siêu tấn công Dực Thành, Vương Dị đích thân khoác chiến bào giúp chồng thủ thành, lấy trang sức của mình ban thưởng cho quân sĩ, ổn định lòng quân.","Lam Tướng, chưa miêu tả","Lam Tướng, chưa miêu tả","","","","",0,1,},
        [95] = {124,"Tào Ngang","Tào Ngang, tự Tử Tu, con trưởng Tào Tháo, tính tình khiêm nhường, thông minh nên rất được Tào Tháo thương yêu. Khi Tào Tháo bị Trương Tú hành thích, Tào Ngang đã cùng Điển Vi hy sinh ở Uyển Thành để bảo vệ Tào Tháo","Lam Tướng, chưa miêu tả","Lam Tướng, chưa miêu tả","","","","",0,1,},
        [96] = {125,"Quách Chiếu","Quách Chiếu, tự Nữ vương, con gái của Quách Vĩnh, hoàng hậu của Ngụy Văn Đế Tào Phi. Mồ côi cha mẹ từ nhỏ, sau này vào Đông Cung, nhan sắc xinh đẹp nên được Tào Phi sủng ái.","Lam Tướng, chưa miêu tả","Lam Tướng, chưa miêu tả","","","","",0,1,},
        [97] = {126,"Tào Duệ","Tào Duệ, tự Nguyên Trọng, là vị Hoàng đế thứ hai của triều Tào Ngụy, con của Tào Phi và Chân Cơ","Lam Tướng, chưa miêu tả","Lam Tướng, chưa miêu tả","","","","",0,1,},
        [98] = {127,"Chung Hội","Chung Hội, tự Sĩ Quý, là tướng nhà Tào Ngụy thời Tam Quốc. Ông có công ổn định bờ cõi nước Ngụy và tham gia chiến dịch tiêu diệt nước Thục Hán năm 263. Chung Hội tinh nhanh giỏi giang, mưu lược cao siêu. Sau khi diệt Thục, vì nổi dã tâm nên cuối cùng thân bại danh liệt","Lam Tướng, chưa miêu tả","Lam Tướng, chưa miêu tả","","","","",0,1,},
        [99] = {128,"Đặng Ngải","Đặng Ngải, tự Sĩ Tái, là một đại tướng trứ danh của Tào Ngụy dưới thời Tam Quốc, văn võ song toàn, giỏi binh pháp. Nhiều năm tại biên cảnh Tào Ngụy phòng ngự Thục Hán Khương Duy. Năm 263 sau công nguyên cùng với Chung Hội tấn công Thục Hán, cuối cùng tấn công vào kinh đô, khiến Thục Hán diệt vong. ","Lam Tướng, chưa miêu tả","Lam Tướng, chưa miêu tả","","","","",0,1,},
        [100] = {129,"Quách Hoài","Danh tướng nước Thục, khi Hạ Hầu Uyên qua đời, Quách Hoài thu thập tàn binh, ổn định thế cuộc. Khi Tào Phi còn làm Ngũ quan tướng, cho mời Quách Hoài vào làm môn hạ dưới trướng, sau đổi làm Thừa tướng Binh tào Nghị lệnh sử. Quách Hoài có vai trò khá lớn trong việc bảo vệ biên cương phía tây Tào Ngụy bên cạnh Tư Mã Ý và Tào Chân.","Lam Tướng, chưa miêu tả","Lam Tướng, chưa miêu tả","","","","",0,1,},
        [101] = {130,"Trâu Thị","Là vợ của Trương Tế. Sau khi được Tào An Dân dẫn vào yết kiến Tào Tháo thì bị ép làm thiếp của Tào Tháo.","Lam Tướng, chưa miêu tả","Lam Tướng, chưa miêu tả","","","","",0,1,},
        [102] = {131,"Dương Tu","Dương Tu, tự Đức Tổ, mưu sĩ của Tào Ngụy, phục vụ dưới trướng của Tào Tháo, về sau đi theo trợ giúp con thứ tư của Tào Tháo là Tào Thực trong giai đoạn tranh ngôi vị Thế tử giữa anh em Tào Phi cùng Tào Xung và Tào Chương","Lam Tướng, chưa miêu tả","Lam Tướng, chưa miêu tả","","","","",0,1,},
        [103] = {132,"Tân Hiến Anh","Họ Tân, tự Hiến Anh, con gái Tân Tì, thê tử Dương Đam, tài nữ thời Ngụy Tấn. Thông minh lanh lợi, biết trước Chung Hội sẽ tạo phản.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [104] = {133,"Vương Lãng","Vương Lãng, tự Cảnh Hưng, tướng cuối thời Đông Hán, công thần khai quốc nhà Tào Ngụy thời Tam Quốc. Từng giữ nhiều chức vụ quan trọng, để lại nhiều tác phẩm văn chương.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [105] = {134,"Tào Hưu","Tào Hưu, tự Văn Liệt, tướng lĩnh nhà Ngụy, con nuôi của Tào Tháo, một trong những võ tướng nổi danh thời Tam Quốc. Ông từng đánh lui Trương Phi trong chiến dịch Hán Trung, về sau chỉ huy phòng tuyến phía đông của nhà Tào Ngụy. Qua đời năm 228 sau khi thất bại trong chiến dịch tấn công Đông Ngô.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [106] = {135,"Mãn Sủng","Mãn Sủng, tự Bá Ninh, đại thần nhà Tào Ngụy, giữ chức vụ tư pháp, nổi tiếng nghiêm minh. Ông từng tham gia chiến dịch Xích Bích, giúp Tào Nhân giữ Phàn Thành, về sau thường xuyên trấn giữ các phòng tuyến quan trọng của Tào Ngụy, lập nhiều chiến công.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [107] = {136,"Tư Mã Chiêu","Tư Mã Chiêu, tự Tử Thượng, quyền thần Tào Ngụy, người đặt nền móng cho nhà Tây Tấn. Ông là con thứ của Tư Mã Ý, từng theo cha cầm quân chống Thục. Từ khi họ Tư Mã nắm quyền, nuôi dã tâm đoạt ngôi Tào Ngụy, để lại câu điển cố đời sau: Dã tâm Tư Mã Chiêu, người đi đường đều rõ.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [108] = {137,"Tư Mã Viêm","Tư Mã Viên, tự Thế An, hoàng đế khai quốc nhà Tây Tấn, hiệu Tấn Vũ Đế. Nổi tiếng là một hoàng đế nhân từ, rộng lượng. Năm 265 lập quốc, năm 280 diệt Đông Ngô, hoàn thành nhất thống. Lúc đầu là một minh quân, về sau chỉ lo hưởng lạc, để lại mầm họa to lớn.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [109] = {138,"Vương Cơ","Vương Cơ, tự Bá Dư, tướng Tào Ngụy thời Tam Quốc. Văn võ song toàn, tài cán hơn người, được trọng dụng trong các chiến dịch đánh dẹp nổi loạn chống nhà Tư Mã, được phong tước Đông Vũ Hầu, Đô Đốc Kinh Châu.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [110] = {139,"Nguyên Cơ","Vương Nguyên Cơ, hay còn gọi là Văn Minh Vương hoàng hậu là vợ chính của Tư Mã Chiêu. Bà là mẹ của Tấn Vũ Đế Tư Mã Viêm, người sáng lập ra nhà Tấn, chấm dứt thời kì Tam Quốc trứ danh. Vì lẽ đó, bà trở thành Hoàng thái hậu, vị Thái hậu đầu tiên của triều đại này","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [111] = {140,"Gia Cát Đản","Gia Cát Đản, tự Công Hưu, tướng lĩnh Tào Ngụy, em cùng họ tộc với Gia Cát Lượng. Từng tham gia đánh dẹp nổi loạn chống nhà Tư Mã, nhưng vì lo sợ cho kết cục của mình nên về sau cũng làm phản. Tuy được Đông Ngô trợ giúp, nhưng vẫn bị đánh bại, quyết không đầu hàng nên bị sát hại cùng hàng trăm thuộc hạ.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [112] = {141,"Tào Chương","Tào Chương; tự là Tử Văn, là hoàng tử và tướng nhà Tào Ngụy thời Tam Quốc","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [113] = {142,"Tào Hồng","Tào Hồng, tự Tử Liêm, là công thần khai quốc nước Tào Ngụy thời Tam Quốc","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [114] = {143,"Tưởng Can","Tưởng Can, tự Tử Dực, văn thần cuối đời Hán đầu thời Ngụy. Trong sử sách ghi chép ông là một danh sĩ đương thời, một nhà hùng biện có tài, nhưng trong tiểu thuyết Tam Quốc Diễn Nghĩa lại bị miêu tả như một kẻ a dua bất tài.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [115] = {144,"Tôn Tư","Tôn Tư, tự Ngạn Long, trọng thần Tào Ngụy. Mồ côi từ nhỏ, được gia đình huynh trưởng nuôi nấng. Sau khi phụng sự Tào Tháo, lần lượt giữ nhiều chức vụ trong quân, được Thượng Thư Tuân Úc khen ngợi. Khi nhà Ngụy được thành lập, Tôn Tư từng bước đạt đến đỉnh cao danh vọng.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [116] = {145,"Trần Quần","Trần Quần, tự Trường Văn, trọng thần Tào Ngụy. Ông xuất thân danh môn, giỏi nhìn người, phục vụ cho ba đời vua nhà Ngụy, nổi tiếng tài năng và tận tụy. Thành tựu nổi bật nhất của ông là đã định hình quy chế phẩm trật quan lại triều đình, ảnh hưởng lớn đến đời sau.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [117] = {146,"Chung Dao","Chung Dao, tự Nguyên Thường, chính trị gia, thư pháp gia nổi tiếng của Tào Ngụy. Từ nhỏ ông đã nổi tiếng thông minh hơn người, từng giữ các chức vụ cận thần trong triều đình Hán Hiến Đế, về sau được Tào Tháo trọng dụng. Thư pháp của ông rất nổi tiếng, đặc biệt ông được xem như ông tổ của kiểu viết chữ khải thịnh hành sau này.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [118] = {147,"Tào Chân","Tào Chân, tự Tử Đan, tướng lĩnh Tào Ngụy, thủ lĩnh Hổ Báo Kỵ. Cả đời chinh chiến, có công trấn giữ lãnh địa Tào Ngụy, phò tá nhiều đời vua, nhất là đẩy lùi được cuộc Bắc Phạt của Gia Cát Lượng vào năm 228 tại Trần Thương.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [119] = {148,"Hàn Hạo","Hàn Hạo, tự Nguyên Tự, tướng lĩnh dưới trướng Tào Tháo. Ban đầu nổi danh vì đã từ chối chiêu dụ của Đổng Trác, về sau theo Hạ Hầu Đôn chinh chiến khắp nơi, trở thành tâm phúc của Tào Tháo, cai quản cấm vệ quân.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [120] = {220,"Mã Đại","Mã Đại, tự Bá Chiêm, tướng Thục thời Tam Quốc. Ban đầu đi theo Mã Đằng và Mã Siêu, gia tộc hùng cứ vùng Tây Lương. Sau khi trải qua nhiều bôn ba, cuối cùng ông cùng Mã Siêu đầu quân cho Lưu Bị, phục vụ cho nhà Thục. Mã Đại được Gia Cát Lượng tin tưởng, giao những nhiệm vụ quan trọng.","Lam Tướng, chưa miêu tả","Lam Tướng, chưa miêu tả","","","","",0,1,},
        [121] = {221,"Mã Tốc","Mã Tốc, tự Ấu Thường, em của Mã Lương, tướng lĩnh Thục Hán. Có tài cán, thích luận việc binh, được Gia Cát Lượng xem trọng. Năm 228, Mã Tốc theo Gia Cát Lượng Bắc Phạt, ông làm trái với chỉ đạo của Gia Cát Lượng, dẫn đến thất thủ Nhai Đình, buộc quân Thục phải lui binh, vì vậy bị chém để nghiêm quân lệnh.","Lam Tướng, chưa miêu tả","Lam Tướng, chưa miêu tả","","","","",0,1,},
        [122] = {222,"Liêu Hóa","Liêu Hóa, tự Nguyên Kiệm, tướng lĩnh Thục Hán, nổi danh can trường cương trực. Lúc đầu là quan thư lại nhỏ ở Kinh Châu, về sau đi theo Lưu Bị, trở thành tùy tướng của Quan Vũ. Sau khi Quan Vũ bại vong, ông bị Đông Ngô bắt, dùng kế giả chết trốn về, từng tham gia nhiều lần Bắc Phạt. Sau khi nhà Thục Hán diệt vong, ông bị triệu về Lạc Dương, nửa đường thì mất.","Lam Tướng, chưa miêu tả","Lam Tướng, chưa miêu tả","","","","",0,1,},
        [123] = {223,"Lưu Tông","Lưu Tông, con thứ của Lưu Biểu, Châu Mục của Kinh Châu đầu thời Tam Quốc. Ông kế nghiệp Lưu Biểu trong một thời gian ngắn và quy hàng Tào Tháo, được phong làm Thứ Sử Thanh Châu, sau đổi làm Gián Nghị Đại Phu, phong tước Liệt Hầu.","Lam Tướng, chưa miêu tả","Lam Tướng, chưa miêu tả","","","","",0,1,},
        [124] = {224,"Lưu Phong","Lưu Phong là tướng nhà Thục Hán thời Tam Quốc. Ông là con nuôi Lưu Bị - vua sáng lập nước Thục Hán. ","Lam Tướng, chưa miêu tả","Lam Tướng, chưa miêu tả","","","","",0,1,},
        [125] = {225,"Trương Bao","Trương Bao là con trai cả của Trương Phi, danh tướng nhà Thục Hán thời Tam Quốc","Lam Tướng, chưa miêu tả","Lam Tướng, chưa miêu tả","","","","",0,1,},
        [126] = {226,"Tư Mã Huy","Tư Mã Huy, tự Đức Tháo, hiệu Thủy Kính, còn gọi là Thủy Kính tiên sinh, người Dĩnh Xuyên, không rõ năm sinh năm mất, là một nhân vật lịch sử cuối thời Đông Hán. Tương truyền ông là danh sĩ có tài kinh bang tế thế, kiến thức hơn người.","Lam Tướng, chưa miêu tả","Lam Tướng, chưa miêu tả","","","","",0,1,},
        [127] = {227,"Cam Phu Nhân","Cam Phu Nhân, vợ lẽ của Lưu Bị, mẹ ruột của Lưu Thiện. Cam Phu Nhân thường giúp Lưu Bị lo liệu việc trong nhà, theo Lưu Bị đến Kinh Châu, rồi mất ở Nam Quận. Khi Lưu Thiện nối ngôi, ban chiếu truy tôn Cam Phu Nhân làm Chiêu Liệt Hoàng Hậu.","Lam Tướng, chưa miêu tả","Lam Tướng, chưa miêu tả","","","","",0,1,},
        [128] = {228,"My Phu Nhân","My phu nhân, vợ của Lưu Bị, là em gái My Trúc và My Phương ","Lam Tướng, chưa miêu tả","Lam Tướng, chưa miêu tả","","","","",0,1,},
        [129] = {229,"Hạ Hầu Quyên","Hạ Hầu Quyên, vợ Trương Phi, cháu gái của Hạ Hầu Uyên. Bà có 2 con gái, sau này đều trở thành hoàng hậu của Lưu Thiện. ","Lam Tướng, chưa miêu tả","Lam Tướng, chưa miêu tả","","","","",0,1,},
        [130] = {230,"Bào Tam Nương","Bào Tam Nương, con gái út của một địa chủ nhỏ. Từ nhỏ thông minh lanh lợi, văn võ song toàn, về sau trở thành vợ của Quan Sách. Nghe nói Quan Vũ rất hài lòng với con dâu, từng đích thân chỉ dạy võ nghệ. Sau khi Kinh Châu thất thủ, vợ chồng Quan Sách quay về Thục Hán, tham gia chinh phạt Mạnh Hoạch, rồi thay mặt Gia Cát Lượng trấn thủ nơi đó.","Lam Tướng, chưa miêu tả","Lam Tướng, chưa miêu tả","","","","",0,1,},
        [131] = {231,"Sa Ma Kha","Sa Ma Kha, thủ lĩnh vùng Ngũ Khê, từng trợ giúp Lưu Bị chinh phạt Đông Ngô. Sử sách chép rất ít về ông, trong tiểu thuyết Tam Quốc Diễn Nghĩa, ông được miêu tả là người hạ sát Cam Ninh, sau khi Lưu Bị đại bại Hào Đình, Sa Ma Kha bị Chu Thái đánh bại.","Lam Tướng, chưa miêu tả","Lam Tướng, chưa miêu tả","","","","",0,1,},
        [132] = {232,"Mã Lương","Mã Lương, tự Quý Thường, quan văn nhà Thục Hán. Tương truyền do lông mi của ông có màu trắng, nên có hiệu là Bạch Mi. Ông được xem là người có tài nhất trong năm huynh đệ của mình. Khi Lưu Bị chinh phạt Đông Ngô, từng sai ông chiêu nạp thế lực Ngũ Khê. Sau trận đại bại Hào Đình, Mã Lương tử trận.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [133] = {233,"Mạnh Đạt","Mạnh Đạt, tự Tử Độ, tướng lĩnh Thục Hán thời Tam Quốc. Ông vốn là thuộc hạ của Lưu Chương, sau hàng Lưu Bị. Trong chiến dịch Tương Phàn, do ông từ chối phát binh cứu Quan Vũ gây hậu quả lớn, khiến Lưu Bị tức giận, nên chạy đến Tào Ngụy. Về sau có ý quay lại với Thục, sự việc bại lộ và vong mạng.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [134] = {234,"Chu Thương","Chu Thương, tự Nguyên Phúc, tướng hộ vệ của Quan Vũ. Hình tượng của ông được miêu tả là thân hình vạm vỡ, mặt đen râu xồm, trung can nghĩa đảm. Ông vốn là tướng lĩnh Hoàng Cân, một lòng đi theo Quan Vũ, tuyệt đối trung thành. Sau khi Quan Vũ bị sát hại, ông cũng tự vẫn. Trong các miếu thờ Quan Thánh Đế, hai bức tượng bên cạnh Quan Thánh chính là Chu Thương và Quan Bình.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [135] = {235,"Quan Hưng","Quan Hưng, tự An Quốc, con thứ của Quan Vũ, quan lại nhà Thục Hán. Từ nhỏ đã có tiếng tăm, được Gia Cát Lượng xem trọng. Sau khi Quan Vũ mất, kế thừa tước vị Hán Thọ Đình Hầu, nhưng không lâu sau thì mất. Trong Tam Quốc Diễn Nghĩa, Quan Hưng từng theo Lưu Bị chinh phạt Đông Ngô, diệt được kẻ thù Phan Chương, đoạt lại Thanh Long Đao, về sau còn theo Gia Cát Lượng Bắc Phạt, lập nhiều chiến công.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [136] = {236,"Gia Cát Chiêm","Gia Cát Chiêm, tự Tư Viễn, con trai của Gia Cát Lượng, thông minh từ nhỏ. Khi Đặng Ngải thảo phạt nước Thục, Gia Cát Chiêm trấn thủ Cẩm Trúc, sau bị bao vây, cuối cùng tự vẫn.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [137] = {237,"Giản Ung","Giản Ung, tự Hiến Hòa, mưu sĩ dưới trướng Lưu Bị, thuyết khách nổi tiếng, Lưu Bị bao vây Thành Đô, Giản Ung khuyên Lưu Chương đầu hàng. Giỏi biện luận, nghị sự, thẳng thắn và không giữ lễ tiết.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [138] = {238,"Lý Nghiêm","Lý Nghiêm, tự Chính Phương, trọng thần thời Thục Hán, từng cùng Gia Cát Lượng, Pháp Chính tạo ra Thục Khoa, sau này trở thành pháp luật Thục Hán. Năm 231, Thục quân bắc phạt, Lý Nghiêm vận chuyển lương thảo chậm trễ, buộc Gia Cát Lượng phải rút binh, Lý Nghiêm đổ trách nhiệm cho Gia Cát Lượng, từ đó mang tội, bị phế thành dân thường.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [139] = {239,"Quan Sách","Quan Sách, con trai thứ 3 của Quan Vũ, lấy Bào Tam Nương làm thê tử. Khi Kinh Châu thất thủ dẫn theo Bào Tam Nương trở về Thục, cùng Gia Cát Lượng nam chinh, sau này thay Gia Cát Lượng trấn thủ Nam Trung.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [140] = {240,"Hạ Hầu Bá","Hạ Hầu Bá, tự Trọng Quyền, tướng lĩnh quan trọng thời Ngụy và Thục Hán, con trai Hạ Hầu Uyên. Năm 249, Tư Mã Ý dấy quân tru sát Tào Sảng, Hạ Hầu Bá từng được Tào Sảng chiếu cố, bất hòa với Quách Hoài. Tào Sảng bị Tư Mã Ý diệt, ông đầu quân Thục Hán, bổ nhiệm thành Xa Kỵ Tướng, nhiều lần theo Khương Duy thảo phạt nước Ngụy.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [141] = {241,"Mã Vân Lộc","Mã Vân Lộc, con gái Mã Đằng, thê tử Triệu Vân, muội muội Mã Siêu. Tính tình ngây thơ thẳng thắn, am hiểu thương thuật.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [142] = {242,"Hoàng Hạo","Hoàng Hạo, hoạn quan Thục Hán. Khi Lưu Thiện tại vị, rất tin tưởng Hoàng Hạo. Đổng Doãn luôn trách mắng Hoàng Hạo, nên ông rất sợ Đổng Doãn. Khi Đổng Doãn qua đời, Hoàng Hạo nắm triều chính, tìm cách chèn ép Khương Duy. Sau khi nước Thục diệt vong, Tư Mã Chiêu đã xử tử Hoàng Hạo.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [143] = {243,"My Trúc","My Trúc, tự Tử Trọng, trọng thần dưới trướng Lưu Bị. Vốn là phú thương Từ Châu, Đào Khiêm qua đời, theo lệnh nghênh đón Lưu Bị, giúp đỡ Lưu Bị trong lúc khó khăn. My Trúc rộng lượng, giỏi kỵ xạ, tuy chưa từng dẫn quân, nhưng luôn được Lưu Bị đối xử tốt. Lưu Bị vào Ích Châu, cử My Trúc làm An Hán Tướng Quân, địa vị trên cả Gia Cát Lượng. Lữ Mông chiếm Kinh Châu, My Phương  đầu hàng khiến Quan Vũ tử vong, My Trúc đến thỉnh tội, Lưu Bị khuyên răn, nhưng không lâu sau qua đời do bệnh.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [144] = {244,"Trương Nghi","Trương Nghi, tự Bá Kỳ, danh tướng Thục Hán. Theo Mã Trung nhiều lần dẹp loạn Dị Tộc, được phong Thái Thú, sau bị triệu về Thành Đô, phong Quan Nội Hầu. Năm 254, giao chiến với Từ Chất, tử trận sa trường, sau đó được lập miếu thờ.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [145] = {245,"Trương Tùng","Trương Tùng, tự Tử Kiều, khuyên Lưu Chương mời Lưu Bị để đánh Trương Lỗ, sau bị Trương Túc tố giác, bị Lưu Chương nổi giận xử trảm.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [146] = {246,"Lưu Chương","Lưu Chương, tự Quý Ngọc, quân phiệt cát cứ một vùng cuối thời Đông Hán, kế thừa Lưu Yên đảm nhiệm Ích Châu Mục. Tính cách nhu nhược đa nghi, nghe theo Trương Tùng, Pháp Chính, đón Lưu Bị vào Ích Châu. Năm 214 đầu hàng Lưu Bị, bị đưa đến Kinh Châu và qua đời ở đó.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [147] = {247,"Ngô Ý","Ngô Ý, tự Tử Viễn, tướng lĩnh Thục Hán, anh của Mục Hoàng Hậu nhà Thục Hán. Ban đầu ông theo Lưu Yên vào đất Thục, rồi theo phò Lưu Chương. Khi Lưu Bị tấn công Lưu Chương, Ngô Ý đầu hàng Lưu Bị, về sau theo Gia Cát Lượng bắc phạt Tào Ngụy, cùng Ngụy Diên đại phá Quách Hoài tại Dương Khê. Sau khi Gia Cát Lượng qua đời, ông trấn thủ Hán Trung, được triều đình xem trọng.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [148] = {248,"Lưu Kham","Lưu Kham, cháu của Lưu Bị, con của Lưu Thiện, được phong làm Bắc Địa Vương. Năm 263, khi Đặng Ngải tấn công đến Thành Đô, Lưu Thiện quyết định đầu hàng, Lưu Kham khuyên can vô hiệu, tự vẫn ở Chiêu Liệt Miếu.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [149] = {320,"Trình Phổ","Trình Phổ, tự Đức Mưu, danh tướng Tam Quốc, lão thần Đông Ngô, theo phò 3 đời vua Tôn Kiên, Tôn Sách, Tôn Quyền. Ông theo Tôn Kiên phạt Hoàng Cân, Đổng Trác, giúp Tôn Sách dẹp yên Giang Đông, cùng Trương Chiêu phò tá Tôn Quyền. Ông từng cùng Chu Du làm Tả Hữu Đô Đốc tham gia chiến dịch Xích Bích, đánh bại Tào Nhân ở chiến dịch Nam Quận, chiến công lừng lẫy.","Lam Tướng, chưa miêu tả","Lam Tướng, chưa miêu tả","","","","",0,1,},
        [150] = {321,"Cố Ung","Cố Ung, tự Nguyên Thán, văn thần quan trọng của Đông Ngô. Lúc nhỏ theo học Thái Ung, nổi danh nhanh nhạy và chuyên tâm, được thầy khen ngợi. Về sau theo về với Tôn Quyền, từng bước đi lên, trở thành thừa tướng thứ hai của Đông Ngô.","Lam Tướng, chưa miêu tả","Lam Tướng, chưa miêu tả","","","","",0,1,},
        [151] = {322,"Ngô Quốc Thái","Ngô Quốc Thái, nhân vật trong Tam Quốc Diễn Nghĩa, vợ của Tôn Kiên, mẹ của Tôn Thượng Hương, cùng Kiều Quốc Lão tác thành mối nhân duyên của Lưu Bị và Tôn Thượng Hương.","Lam Tướng, chưa miêu tả","Lam Tướng, chưa miêu tả","","","","",0,1,},
        [152] = {323,"Tôn Lỗ Dục","Tôn Lỗ Dục, tự Tiểu Hổ, con gái út của Tôn Quyền và Bộ Luyện Sư, được gọi là [Tiểu Công Chúa]. Thông minh lanh lợi, xinh xắn dễ thương, về sau gả cho Tả Tướng Quân Chu Cứ, do đó cũng được gọi là [Chu Công Chúa].","Lam Tướng, chưa miêu tả","Lam Tướng, chưa miêu tả","","","","",0,1,},
        [153] = {324,"Tôn Như ","Tôn Như, em gái út của Tôn Kiên. Từ nhỏ đã thông minh, được Tôn Kiên yêu quý. Khi Tôn Sách muốn vượt sông sang Giang Đông, vì không đủ thuyền nên phải đóng quân chờ đợi, Tôn Như đã hiến kế kết thành bè lau, giải quyết được bài toán khó.","Lam Tướng, chưa miêu tả","Lam Tướng, chưa miêu tả","","","","",0,1,},
        [154] = {325,"Chu Nhiên","Chu Nhiên hay Thi Nhiên, tự Nghĩa Phong, là một tướng của nhà Đông Ngô trong thời kỳ Tam Quốc, từng theo Lữ Mông trảm Quan Vũ, trận Di Lăng cùng Lục Tốn đại phá Lưu Bị.","Lam Tướng, chưa miêu tả","Lam Tướng, chưa miêu tả","","","","",0,1,},
        [155] = {326,"Ngu Phiên","Ngu Phiên, tự Trọng Tường, văn thần Đông Ngô, công thần khai quốc nhà Đông Ngô thời Tam Quốc","Lam Tướng, chưa miêu tả","Lam Tướng, chưa miêu tả","","","","",0,1,},
        [156] = {327,"Lục Kháng","Lục Kháng tự Ấu Tiết là một vị tướng và là một quân sư của Đông Ngô trong thời Tam Quốc. Lục Kháng là con của Lục Tốn và là cháu ngoại của Tôn Sách. Lục Kháng được ca ngợi là người có tài và thông thái. ","Lam Tướng, chưa miêu tả","Lam Tướng, chưa miêu tả","","","","",0,1,},
        [157] = {328,"Từ Thịnh","Từ Thịnh tự Văn Hương là một tướng lĩnh Đông Ngô trong thời Tam Quốc. Ông tham gia nhiều trận đánh, phục vụ cho nhiều đời chúa của Đông Ngô","Lam Tướng, chưa miêu tả","Lam Tướng, chưa miêu tả","","","","",0,1,},
        [158] = {329,"Gia Cát Khác","Gia Cát Khác, tự Nguyên Tốn, là tướng lĩnh và phụ chính đại thần của Đông Ngô trong thời Tam Quốc. Ông là con trưởng của đại thần Gia Cát Cẩn công thần khai quốc của Đông Ngô, là cháu gọi thừa tướng Gia Cát Lượng nước Thục Hán bằng chú.","Lam Tướng, chưa miêu tả","Lam Tướng, chưa miêu tả","","","","",0,1,},
        [159] = {330,"Khám Trạch","Hám Trạch, tên tự là Đức Nhuận, người Sơn Âm, Cối Kê, quan viên, học giả nhà Đông Ngô thời Tam Quốc ","Lam Tướng, chưa miêu tả","Lam Tướng, chưa miêu tả","","","","",0,1,},
        [160] = {331,"Chu Trị","Chu Trị, tự là Quân Lý, quan nhà Đông Ngô, từng theo Tôn Kiên, Tôn Sách chinh phạt, sau phò trợ Tôn Quyền, công trạng to lớn, được Tôn Quyền kính trọng.","Lam Tướng, chưa miêu tả","Lam Tướng, chưa miêu tả","","","","",0,1,},
        [161] = {332,"Tôn Tịnh","Tôn Tịnh, tự Ấu Đài, em của Tôn Kiên, chú của Tôn Sách và Tôn Quyền. Khi Tôn Kiên mới khởi binh, Tôn Tịnh tập hợp con em dòng tộc được năm sáu trăm người theo về. Khi Tôn Sách đánh chiếm Cối Kê, Tôn Tịnh hiến kế giúp đánh bại Vương Lãng, dẹp yên Cối Kê. Tôn Tịnh không muốn làm quan, xin về lo việc gia tộc đến hết đời.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [162] = {333,"Lăng Tháo","Lăng Tháo, danh tướng Đông Ngô, cha của Lăng Thống, người hiệp nghĩa và dũng cảm, theo Tôn Sách chinh chiến nhiều năm, là thủy tướng tài ba, đáng tiếc khi thảo phạt Hoàng Tổ đã trúng tên của Cam Ninh mà qua đời","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [163] = {334,"Chu Hằng","Chu Hằng, tự là Hưu Mục, tướng lãnh nhà Đông Ngô thời Tam Quốc, từng đánh bại Tạo Nhân, sau theo phò trợ Lục Tốn đánh bại Tào Hưu","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [164] = {335,"Tưởng Khâm","Tưởng Khâm tự Công Dịch, người Thọ Xuân, Cửu Giang, tướng lĩnh, công thần khai quốc nước Ngô thời Tam Quốc. Luôn giữ chữ tín, giỏi cung thuật. Vốn là tặc phỉ, sau theo hàng Tôn Sách. Từng cùng Chu Thái, Hàn Đương thống lĩnh thủy quân đại phá quân địch ở Tam Giang Khẩu.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [165] = {336,"Lục Tích","Lục Tích, tự Công Kỷ, con Thái Thú Lư Giang Lục Khang. Từ nhỏ thông minh, biết lễ nghĩa. Vì câu chuyện giấu quýt đem về biếu mẹ khi còn nhỏ mà nổi tiếng là người hiếu thảo. Khi trưởng thành, học cao hiểu rộng, giỏi thiên văn lịch pháp, về sau theo về với Tôn Quyền. Đáng tiếc đoản mệnh, bệnh mất năm 32 tuổi.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [166] = {337,"Lưu Tán","Lưu Tán, tự Chính Minh, tướng lĩnh Đông Ngô. Thời trẻ từng diệt được tướng lĩnh Hoàng Cân, nhưng cũng bị thương ở chân. Tính cách kiên cường, thích đọc binh thư, sẵn sàng xông pha trận mạc, lập nhiều chiến công. Khi tuổi đã cao vẫn theo quân chinh chiến, bị địch bao vây, quyết chiến đến cùng, thọ 73 tuổi.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [167] = {338,"Tôn Hưu","Tôn Hưu, tự là Tử Liệt, sau này trở Ngô Cảnh Hoàng Đế, vị quân vương thứ ba của nhà Đông Ngô trong thời kỳ Tam Quốc","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [168] = {339,"Đinh Phụng","Đinh Phụng, tự Thừa Uyên, tướng lĩnh Đông Ngô. Ông chủ yếu chiến đấu với thế lực Tào Ngụy, thiện chiến dũng mãnh, lập nhiều công trạng, từ thời Tào Tháo đến thời Tư Mã Viêm, trải suốt 4 đời vua Đông Ngô, chứng kiến toàn bộ quá trình hưng vong của Tam Quốc.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [169] = {340,"Tôn Lỗ Ban","Tôn Lỗ Ban, tự là Đại Hổ, con gái lớn của Tôn Quyền và Bộ Luyện Sư, được gả cho con trai Chu Du là Chu Tuần, sau này lại gả cho danh tướng Toàn Tông. Khi Tôn Lượng kế vị, Tôn Lỗ Ban từng liên hợp Tôn Tuấn bài trừ thế lực đối lập, nắm hết quyền hành trong triều. Về sau định lập mưu diệt trừ quyền thần Tôn Lâm, việc bại lộ bị lưu đày.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [170] = {341,"Tổ Mậu","Tổ Mậu tự Đại Vinh, bộ tướng phục vụ dưới trướng của lãnh chúa Tôn Kiên trong thời kỳ Tam Quốc. Tổ Mậu là một trong bốn vị tướng cùng với Trình Phổ, Hoàng Cái và Hàn Đương theo Tôn Kiên tham gia cuộc chiến tranh chống lãnh chúa có mưu đồ bá quyền là Đổng Trác.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [171] = {342,"Tôn Hạo","Tôn Hạo, tự Nguyên Tông, cháu của Tôn Quyền, vị vua cuối cùng của nhà Đông Ngô. Trong thời gian tại vị, lúc đầu rất cần mẫn trị nước, nhưng không lâu sau đắm chìm tửu sắc, ngày càng bạo ngược. Năm 280, Đông Ngô bị Tây Tấn tiêu diệt, Tôn Hạo đầu hàng, được phong làm Quy Mệnh Hầu, bốn năm sau mất ở Lạc Dương.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [172] = {343,"Tôn Lượng","Tôn Lượng, tự là Tử Minh, con thứ bảy của Tôn Quyền, hoàng đế đời thứ hai của nhà Đông Ngô. Năm 15 tuổi tự coi việc triều chính, một năm sau bị quyền thần Tôn Lâm phế truất, sau đó không lâu bị ép uống độc, thọ 18 tuổi.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [173] = {344,"Sầm Hôn","Sầm Hôn, hoạn quan, nịnh thần bên cạnh Tôn Hạo, vị vua cuối cùng của Đông Ngô. Được phong đến chức quan thuộc hàng Cửu Khanh, hay bày chuyện hao tài tốn của, hại dân hại nước, cuối cùng vì dân tình sục sôi, bị Tôn Hạo xử tử. Sầm Hôn của Ngô và Hoàng Hạo của Thục trở thành gương xấu xu nịnh cho đời sau.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [174] = {345,"Phan Chương","Phan Chương, tự Văn Khuê, tướng lĩnh Đông Ngô. Mãnh tướng một đời chinh chiến, lập nhiều chiến công, thăng tiến không ngừng. Thành tựu quan trọng nhất phải kể đến chuyện dẫn quân bắt sống được Quan Vũ. Ông cũng là kẻ tham lam và xa xỉ, mang nhiều tiếng xấu, nhưng vì có công nên Tôn Quyền vẫn cho qua. Trong tiểu thuyết Tam Quốc Diễn Nghĩa, Phan Chương bắt Quan Vũ, đoạt Thanh Long Đao và Xích Thố Mã, nhưng lại bị Quan Hưng trả thù trong chiến dịch Di Lăng.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [175] = {346,"Toàn Tông","Toàn Tông, tự là Tử Hoàng, khai quốc công thần của Đông Ngô. Có tài mưu lược, từng tham luận kế sách cho nhiều chiến dịch quan trọng. Tôn Quyền gả con gái Tôn Lỗ Ban cho Toàn Tông, khiến gia tộc ông trở thành danh môn đất Ngô, nhưng bản thân ông không kiêu căng tự phụ, mà vẫn đối xử với người khác một cách khiêm nhường.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [176] = {347,"Tôn Đăng","Tôn Đăng, tự là Tử Cao, con trưởng của Tôn Quyền. Khi Tôn Quyền xưng đế, Tôn Đăng là Thái Tử. Ông biết kính trên nhường dưới, hành xử cẩn trọng khéo léo, nhiều lần khuyên can Tôn Quyền, giúp được nhiều cho chính sự. Đáng tiếc chỉ 33 tuổi đã mất, lúc lâm chung còn dâng sớ tiến cử hiền tài, mong Đông Ngô hưng thịnh. Tôn Quyền xem sớ vô cùng đau lòng, nước mắt khôn nguôi.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [177] = {348,"Bộ Chất","Bộ Chất, tự là Tử Sơn, quan đại thần của Đông Ngô thời Tam Quốc. Ông không thích thể hiện thái độ ra ngoài mặt, đối xử với ai cũng từ tốn khiêm nhường. Khi Tôn Quyền xưng đế, ông được phong làm Phiêu Kỵ Tướng Quân, Đô Đốc Tây Lăng, sau đó trở thành Thừa Tướng thay Lục Tốn. Ông từng trấn thủ Tây Lăng 20 năm, khoan dung độ lượng, rất được lòng dân.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [178] = {420,"Trương Lương","Trương Lương, một trong ba thủ lĩnh cuộc khởi nghĩa Hoàng Cân cuối thời Đông Hán, em thứ hai của Trương Giác, được xưng hiệu là [Nhân Công Tướng Quân]. Về sau ông giao chiến với quân triều đình, bị Hoàng Phủ Tung tập kích và bại vong.","Lam Tướng, chưa miêu tả","Lam Tướng, chưa miêu tả","","","","",0,1,},
        [179] = {421,"Trương Bảo","Trương Bảo, một trong ba thủ lĩnh cuộc khởi nghĩa Hoàng Cân cuối thời Đông Hán, em của Trương Giác, anh của Trương Lương, được xưng hiệu là [Địa Công Tướng Quân]. Về sau ông giao chiến với quân triều đình, bị Hoàng Phủ Tung đánh bại ở Khúc Dương.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [180] = {422,"Thư Thụ","Thư Thụ, mưu sĩ dưới trướng Viên Thiệu. Tuổi trẻ chí lớn, mưu lược kiệt xuất, thường hiến kế hay cho Viên Thiệu, rất được xem trọng. Về sau Viên Thiệu kiêu căng tự phụ, phớt lờ ý kiến mưu thần, dẫn đến đại bại trong Quan Độ Chiến. Thư Thụ bị bắt quyết không đầu hàng, bị Tào Tháo xử tử.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [181] = {423,"Lưu Biểu","Lưu Biểu, tự Cảnh Thăng, chư hầu trấn thủ Kinh Châu thời Hán. Dáng người to lớn, ôn hòa, từ trẻ đã nổi danh tuấn kiệt. Trong thời gian cai trị Kinh Châu, Lưu Biểu thưởng phạt nghiêm minh, mở mang trường lớp, lãnh địa trải mấy ngàn dặm, quân đông mười mấy vạn người, xưng hùng một cõi Kinh Giang. Từng đánh bại Tôn Kiên, về sau kháng cự Tào Tháo suốt nhiều năm.","Lam Tướng, chưa miêu tả","Lam Tướng, chưa miêu tả","","","","",0,1,},
        [182] = {424,"Lý Nho","Lý Nho, tự Văn Ưu, tham mưu của Đổng Trác, lạnh lùng tàn bạo. Từng sát hại tiên đế Hoằng Nông Vương và Hà thái hậu. Nhưng giỏi dụng binh, từng hiến kế cho Từ Vinh đánh bại quân Tào.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [183] = {425,"Hán Hiến Đế","Hán Hiến Đế, tên thật là Lưu Hiệp, tên tự là Bá Hòa, là vị Hoàng đế thứ 14 của nhà Đông Hán và là hoàng đế cuối cùng của nhà Hán","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [184] = {426,"Hà Thái Hậu","Hà Thái Hậu, em gái Hà Tiến, hoàng hậu của Hán Linh Đế, mẹ của Hán Thiếu Đế Lưu Biện. Sau khi Linh Đế qua đời, vì giành ngôi cho Lưu Biện, bà hạ độc mẹ của Hiến Đế. Sau này hai mẹ con cùng bị Đổng Trác sát hại.","Lam Tướng, chưa miêu tả","Lam Tướng, chưa miêu tả","","","","",0,1,},
        [185] = {427,"Hà Tiến","Hà Tiến, tự là Toại Cao, nhờ em gái mà trở thành đại tướng, con người thiếu tính quyết đoán, sau bị Trương Nhượng hãm hại.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [186] = {428,"Phan Phượng","Thuộc hạ của Hàn Phức, có sở trường dùng búa lớn, cuối cùng bị Hoa Hùng tiêu diệt. Hàn Phức nói: Ta có Phan Phượng có thể tiêu diệt Hoa Hùng.","Lam Tướng, chưa miêu tả","Lam Tướng, chưa miêu tả","","","","",0,1,},
        [187] = {429,"Vương Doãn","Vương Doãn, tự Tử Sư. Quan Tư Đồ triều Hán. Khi Hà Tiến mất đã lợi dụng Điêu Thuyền thực hiện Liên hoàn kế để mượn tay diệt Đổng Trác. Do không ân xá cho phe cánh của Đổng Trác dẫn đến phản loạn và bị sát hại. ","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [188] = {430,"Viên Đàm","Viên Đàm, tự Hiển Tư, con trưởng Viên Thiệu. Khi Viên Thiệu qua đời, do tranh đoạt tước vị đã bị Tào Tháo lợi dụng và diệt hết.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [189] = {431,"Lý Thôi","Lý Thôi, tự Trĩ Nhiên, tướng của Đổng Trác. Khi Đổng Trác mất, liên kết tướng cũ đánh bại Lữ Bố, diệt Vương Doãn, chiếm Trường An, về sau bị quân Tào Tháo tiêu diệt.","Lục Tướng, chưa miêu tả?","Lục Tướng, chưa miêu tả?","","","","",0,1,},
        [190] = {432,"Trần Đăng","Trần Đăng, tên tự là Nguyên Long, là mưu sĩ thời Tam Quốc, từng hiến kế diệt Lữ Bố cho Tào Tháo, sau được Lưu Bị thu phục, phản lại Tào.","Lam Tướng, chưa miêu tả","Lam Tướng, chưa miêu tả","","","","",0,1,},
        [191] = {433,"Đạp Đốn","Đạp Đốn là một thủ lĩnh của người Ô Hoàn trong thời kỳ Tam Quốc. Ông là một Thiền Vu đã quật khởi trong thời kỳ loạn lạc này. Ông đã chứa chấp Viên Thượng và Viên Hy hai anh em con của Viên Thiệu và chống lại Tào Tháo, về sau bị Trương Liêu của Tào Ngụy tiêu diệt","Lam Tướng, chưa miêu tả","Lam Tướng, chưa miêu tả","","","","",0,1,},
        [192] = {434,"Đào Khiêm","Đào Khiêm, tự Cung Tổ, một trong những chư hầu tham gia thảo phạt Đổng Trác. Cầu viện Khổng Dung, Lưu Bị cứu viện khi bị Tào Tháo đánh chiếm. Sau bệnh mất.","Lam Tướng, chưa miêu tả","Lam Tướng, chưa miêu tả","","","","",0,1,},
        [193] = {435,"Kỷ Linh","Kỉ Linh, tự là Phục Nghĩa, tướng dưới quyền của Viên Thuật, ông cầm 1 cây đinh ba nặng hơn 50 cân, từng giao chiến với Quan Vũ, Lữ Bố ở Từ Châu","Lam Tướng, chưa miêu tả","Lam Tướng, chưa miêu tả","","","","",0,1,},
