        [135] = {301,"Trương Nhượng","Người đâu! Giả truyền ý chỉ thái hậu, lừa Hà Tiến vào cung, diệt trừ hắn!","Người đâu! Giả truyền ý chỉ thái hậu, lừa Hà Tiến vào cung, diệt trừ hắn!","Nhân vật","",},
