        [137] = {301,"Tinh Thái","Chúa công đoán rất đúng! Người này chính là Đại Thái Giám Trương Nhượng, thủ lĩnh Thập Thường Thị.","Chúa công đoán rất đúng! Người này chính là Đại Thái Giám Trương Nhượng, thủ lĩnh Thập Thường Thị.","Nhân vật","",},
        [138] = {301,"Tinh Thái","Hà Tiến là Đại Tướng Quân đương triều, anh của Hà Thái Hậu. Tào Tháo và Viên Thiệu khuyên Hà Tiến diệt thế lực hoạn quan để lấy lòng dân.","Hà Tiến là Đại Tướng Quân đương triều, anh của Hà Thái Hậu. Tào Tháo và Viên Thiệu khuyên Hà Tiến diệt thế lực hoạn quan để lấy lòng dân.","Nhân vật","",},
        [139] = {301,"Tinh Thái","Nhưng không biết tại sao tin này lại bị lộ, Trương Nhượng biết được bèn ra tay mai phục trước.","Nhưng không biết tại sao tin này lại bị lộ, Trương Nhượng biết được bèn ra tay mai phục trước.","Nhân vật","",},
        [140] = {301,"Nhân vật","Thì ra là thái giám và đại thần tranh quyền!","Thì ra là thái giám và đại thần tranh quyền!","Tinh Thái","",},
        [141] = {301,"Tinh Thái","Chúa công, chúng ta mau tìm Hà Tiến, không thể để Trương Nhượng giành được Danh Tướng Lệnh trước chúng ta.","Chúa công, chúng ta mau tìm Hà Tiến, không thể để Trương Nhượng giành được Danh Tướng Lệnh trước chúng ta.","Nhân vật","",},
        [142] = {302,"Hà Thái Hậu","Tiện dân to gan, dám xông vào Tiêu Phòng Điện của ta! Người đâu, bắt hết bọn chúng cho ta!","Tiện dân to gan, dám xông vào Tiêu Phòng Điện của ta! Người đâu, bắt hết bọn chúng cho ta!","Tinh Thái","",},
        [143] = {302,"Tinh Thái","Đây là Thái Hậu sao? Trẻ quá! Nhưng có vẻ không xinh lắm....","Đây là Thái Hậu sao? Trẻ quá! Nhưng có vẻ không xinh lắm....","","",},
        [144] = {302,"Hà Thái Hậu","Hai ả nha đầu đáng ghét, dám phỉ báng ai gia! Bắt chúng lại cho ta!","Hai ả nha đầu đáng ghét, dám phỉ báng ai gia! Bắt chúng lại cho ta!","","",},
        [145] = {303,"Tinh Thái","Hà Tiến, cuối cùng ta cũng tìm được ngươi, nghe nói ngươi có #Danh Tướng Lệnh#?","Hà Tiến, cuối cùng ta cũng tìm được ngươi, nghe nói ngươi có #Danh Tướng Lệnh#?","Hà Tiến","",},
        [146] = {303,"Hà Tiến","Các ngươi là ai? Sao dám xông vào hoàng cung! Bắt lấy chúng!","Các ngươi là ai? Sao dám xông vào hoàng cung! Bắt lấy chúng!","","",},
        [147] = {303,"Ngân Bình","A! Lại được đánh nhau nữa!","Ồ, lại được đánh nhau!","","",},
        [148] = {304,"Tinh Thái","Hà Tiến, Trương Nhượng đang lập mưu làm hại ngươi, đừng vào cung!","Hà Tiến, Trương Nhượng đang lập mưu làm hại ngươi, đừng vào cung!","Hà Tiến","",},
        [149] = {304,"Hà Tiến","Hoạn quan chuyên quyền, quyết không thể tha! Hơn nữa muội muội ta còn ở hoàng cung, ta sao có thể bỏ mặc?","Hoạn quan chuyên quyền, quyết không thể tha! Hơn nữa muội muội ta còn ở hoàng cung, ta sao có thể bỏ mặc?","","",},
        [150] = {304,"Tinh Thái","Hà Tiến tuy kiêu căng tự phụ, nhưng rất thương muội muội, biết nguy hiểm mà vẫn vào cung!","Hà Tiến tuy kiêu căng tự phụ, nhưng rất thương muội muội, biết nguy hiểm mà vẫn vào cung!","Ngân Bình","",},
        [151] = {304,"Ngân Bình","Chúng ta mau đi tìm Trương Nhượng, dẹp yên cuộc loạn này!","Chúng ta mau đi tìm Trương Nhượng, dẹp yên cuộc loạn này!","","",},
        [152] = {305,"Trương Nhượng","Ha ha, Hà Tiến, ngươi mở to mắt ra mà xem, giờ ai mới là người nắm quyền? Không phải ngươi mà là ta đây!","Ha ha, Hà Tiến, ngươi mở to mắt ra mà xem, giờ ai mới là người nắm quyền? Không phải ngươi mà là ta đây!","","",},
        [153] = {305,"Trương Nhượng","Ngươi muốn đối phó ta, cho gọi Đổng Trác vào kinh. Nhưng ngươi có biết Đổng Trác là người như thế nào không?","Ngươi muốn đối phó ta, cho gọi Đổng Trác vào kinh. Nhưng ngươi có biết Đổng Trác là người như thế nào không?","","",},
        [154] = {305,"Trương Nhượng","Ha ha, Đổng Trác vào kinh, loạn thế chỉ mới bắt đầu thôi!","Ha ha, Đổng Trác vào kinh, loạn thế chỉ mới bắt đầu thôi!","","",},
        [155] = {305,"Tinh Thái","Đến trễ rồi! Trương Nhượng đã sát hại Hà Tiến! Thiếu Đế cũng bị Trương Nhượng uy hiếp!","Đến trễ rồi! Trương Nhượng đã sát hại Hà Tiến! Thiếu Đế cũng bị Trương Nhượng uy hiếp!","Trương Nhượng","",},
        [156] = {305,"Ngân Bình","Trương Nhượng, sao ngươi dám uy hiếp Thiếu Đế!? Mau giao Thiếu Đế và Danh Tướng Lệnh cho ta, tha mạng cho ngươi!","Trương Nhượng, sao ngươi dám uy hiếp Thiếu Đế!? Mau giao Thiếu Đế và Danh Tướng Lệnh cho ta, tha mạng cho ngươi!","","",},
        [157] = {305,"Trương Nhượng","Dựa vào các ngươi sao? Hãy hỏi vũ khí trong tay ta trước! Ta bắt các ngươi nếm mùi đoạn tử tuyệt tôn!","Dựa vào các ngươi sao? Hãy hỏi vũ khí trong tay ta trước! Ta bắt các ngươi nếm mùi đoạn tử tuyệt tôn!","","",},
        [158] = {306,"Trương Nhượng","Thập Thường Thị thật ra cũng chỉ là những kẻ yếu ớt, nay đã thất thế, đành xin các vị hãy bảo vệ Thiếu Đế thật tốt!","Thập Thường Thị thật ra cũng chỉ là những kẻ yếu ớt, nay đã thất thế, đành xin các vị hãy bảo vệ Thiếu Đế thật tốt!","Thiếu Đế","",},
        [159] = {306,"Thiếu Đế","Trương Thường Thị! Đừng!","Trương Thường Thị! Đừng!","Trương Nhượng","",},
        [160] = {306,"Trương Nhượng","Số mệnh đã ban bài, mong bệ hạ bảo trọng (Trương Nhượng nhảy sông tự vẫn).","Số mệnh đã ban bài, mong bệ hạ bảo trọng (Trương Nhượng nhảy sông tự vẫn).","Thiếu Đế","",},
        [161] = {306,"Thiếu Đế","Dù sao cũng cảm tạ các vị đã dẹp yên phản loạn, #Danh Tướng Lệnh# này tặng cho các vị.","Dù sao cũng cảm tạ các vị đã dẹp yên phản loạn, #Danh Tướng Lệnh# này tặng cho các vị.","Tinh Thái","",},
        [162] = {306,"Tinh Thái","Chúa công, Thập Thường Thị vừa dẹp yên, nay Đổng Trác lại vào kinh! Nguy to rồi!","Chúa công, Thập Thường Thị vừa dẹp yên, nay Đổng Trác lại vào kinh! Nguy to rồi!","Ngân Bình","",},
        [163] = {306,"Ngân Bình","Lần trước triều đình lệnh Đổng Trác diệt Hoàng Cân, hắn dám mượn cớ đưa quân Tây Lương đến đoạt Danh Tướng Lệnh của ta, thật là quá đáng!","Lần trước triều đình lệnh Đổng Trác diệt Hoàng Cân, hắn dám mượn cớ đưa quân Tây Lương đến đoạt Danh Tướng Lệnh của ta, thật là quá đáng!","Tinh Thái","",},
        [164] = {306,"Tinh Thái","Chúa công, chúng ta đi tìm Tào Tháo đi. Tào Tháo thuộc dòng dõi quan lại, quen nhiều biết rộng, chắc sẽ có cách đối phó Đổng Trác.","Chúa công, chúng ta đi tìm Tào Tháo đi. Tào Tháo thuộc dòng dõi quan lại, quen nhiều biết rộng, chắc sẽ có cách đối phó Đổng Trác.","Nhân vật","",},
        [165] = {306,"Ngân Bình","Không chừng trên tay Tào Tháo còn có manh mối #Danh Tướng Lệnh# khác","Không chừng trên tay Tào Tháo còn có manh mối #Danh Tướng Lệnh# khác","Nhân vật","",},
        [166] = {306,"Nhân vật","Vậy cũng tốt! Đến Lạc Dương tìm Tào Tháo nào!","Vậy cũng tốt! Đến Lạc Dương tìm Tào Tháo nào!","Ngân Bình","",},
        [167] = {401,"Tào Tháo","Tiểu anh hùng, ngươi đến thật đúng lúc! Đổng Trác kéo quân vào kinh đô, làm loạn triều cương, làm nhiều chuyện bạo ngược!","Tiểu anh hùng, ngươi đến thật đúng lúc! Đổng Trác kéo quân vào kinh đô, làm loạn triều cương, làm nhiều chuyện bạo ngược!","Nhân vật","",},
        [168] = {401,"Tào Tháo","Đổng Trác muốn diệt Viên Thiệu, nay Viên Thiệu đã chạy đến Ký Châu. Trước khi đi, Viên Thiệu nhờ ta gửi thư này cho Vương Tư Đồ, cùng bàn cách đối phó với Đổng Trác.","Đổng Trác muốn diệt Viên Thiệu, nay Viên Thiệu đã chạy đến Ký Châu. Trước khi đi, Viên Thiệu nhờ ta gửi thư này cho Vương Tư Đồ, cùng bàn cách đối phó với Đổng Trác.","Nhân vật","",},
        [169] = {401,"Nhân vật","Cùng đi nào! Ta đến cũng vì chuyện Đổng Trác vào kinh.","Cùng đi nào! Ta đến cũng vì chuyện Đổng Trác vào kinh.","Tào Tháo","",},
        [170] = {401,"Vương Doãn","(Vương Doãn sau khi xem thư khóc lớn) Đổng Trác lộng quyền, xã tắc khó giữ, lão phu thật hổ thẹn.","(Vương Doãn sau khi xem thư khóc lớn) Đổng Trác lộng quyền, xã tắc khó giữ, lão phu thật hổ thẹn.","Tào Tháo","",},
        [171] = {401,"Tào Tháo","Tư Đồ đừng lo, Tào Tháo tuy bất tài, nhưng cũng bằng lòng đi diệt Đổng Trác.","Tư Đồ đừng lo, Tào Tháo tuy bất tài, nhưng cũng bằng lòng đi diệt Đổng Trác.","Vương Doãn","",},
        [172] = {401,"Nhân vật","Đổng Trác hại người vô số, ai cũng muốn diệt trừ hắn!","Đổng Trác hại người vô số, ai cũng muốn diệt trừ hắn!","Vương Doãn","",},
        [173] = {401,"Vương Doãn","Có các anh hùng đây, triều đình có hi vọng rồi! Đổng Trác đến từ Tây Lương, thế như hùm sói, nếu mọi người không đồng lòng, rất dễ bị hắn đánh bại.","Có các anh hùng đây, triều đình có hi vọng rồi! Đổng Trác đến từ Tây Lương, thế như hùm sói, nếu mọi người không đồng lòng, rất dễ bị hắn đánh bại.","Tào Tháo","",},
        [174] = {401,"Vương Doãn","Nếu các ngươi có thể đánh bại cao thủ trong phủ ta, chứng minh thực lực của các ngươi, ta sẽ tặng cho các ngươi Thất Tinh Đao, giúp các ngươi diệt Đổng Trác!","Nếu các ngươi có thể đánh bại cao thủ trong phủ ta, chứng minh thực lực của các ngươi, ta sẽ tặng cho các ngươi Thất Tinh Đao, giúp các ngươi diệt Đổng Trác!","Tào Tháo","",},
        [175] = {401,"Nhân vật","Việc này có gì khó, chẳng qua một tên thị vệ, có đáng là gì! Tào huynh cứ nghỉ ngơi, để ta!","Việc này có gì khó, chẳng qua một tên thị vệ, có đáng là gì! Tào huynh cứ nghỉ ngơi, để ta!","Tào Tháo","",},
        [176] = {402,"Vương Doãn","Hai vị quả là lợi hại! Thất Tinh Đao này tặng hai vị, nếu lần này thành công, đó là phúc của thiên hạ!","Hai vị quả là lợi hại! Thất Tinh Đao này tặng hai vị, nếu lần này thành công, đó là phúc của thiên hạ!","Tào Tháo","",},
        [177] = {402,"Tào Tháo","Vương Tư Đồ chỉ cần chờ nghe tin lành là được!","Vương Tư Đồ chỉ cần chờ nghe tin lành là được!","Vương Doãn","",},
        [178] = {402,"Tào Tháo","Tiểu anh hùng, chút nữa vào phủ Đổng Trác, hãy giả làm tùy tùng của ta, tùy cơ ứng biến. Nếu ta ho hai tiếng, đó là tín hiệu ra tay!","Tiểu anh hùng, chút nữa vào phủ Đổng Trác, hãy giả làm tùy tùng của ta, tùy cơ ứng biến. Nếu ta ho hai tiếng, đó là tín hiệu ra tay!","Nhân vật","",},
        [179] = {402,"Nhân vật","Được, Tào huynh cứ yên tâm!","Được, Tào huynh cứ yên tâm!","Tào Tháo","",},
        [180] = {403,"Đổng Trác","Mạnh Đức, hôm nay đến chỗ ta có chuyện gì?","Mạnh Đức, hôm nay đến chỗ ta có chuyện gì?","Tào Tháo","",},
        [181] = {403,"Tào Tháo","Tào Tháo có một bảo đao, muốn dâng cho ân tướng. Khụ khụ!","Tào Tháo có một bảo đao, muốn dâng cho ân tướng. Khụ khụ!","Đổng Trác","",},
        [182] = {403,"Đổng Trác","Để lão phu xem thử! Sắc bén như vậy, quả là đao tốt!","Để lão phu xem thử! Sắc bén như vậy, quả là đao tốt!","Tào Tháo","",},
        [183] = {403,"Nhân vật","Lúc này không ra tay, còn đợi đến khi nào? Lão tặc Đổng Trác, nộp mạng đi!","Lúc này không ra tay, còn đợi đến khi nào? Lão tặc Đổng Trác, nộp mạng đi!","Đổng Trác","",},
        [184] = {404,"Đổng Trác","Người đâu, có Thích Khách! Con ta Phụng Tiên ở đâu? Mau đến cứu ta!","Người đâu, có Thích Khách! Con ta Phụng Tiên ở đâu? Mau đến cứu ta!","Lữ Bố","",},
        [185] = {404,"Lữ Bố","Kẻ nào dám đụng đến nghĩa phụ của ta?","Kẻ nào dám đụng đến nghĩa phụ của ta?","Nhân vật","",},
        [186] = {404,"Nhân vật","Hừ! Nếu Lữ Bố không đến kịp lúc, Đổng tặc hôm nay đã phải đền tội!","Hừ! Nếu Lữ Bố không đến kịp lúc, Đổng tặc hôm nay đã phải đền tội!","Đổng Trác","",},
        [187] = {404,"Đổng Trác","Lại là ngươi! Lần trước chính ngươi đã cướp #Danh Tướng Lệnh#! Đúng lúc lắm! Phụng Tiên, diệt sạch bọn chúng cho ta!","Lại là ngươi! Lần trước chính ngươi đã cướp #Danh Tướng Lệnh#! Đúng lúc lắm! Phụng Tiên, diệt sạch bọn chúng cho ta!","Nhân vật","",},
        [188] = {404,"Tào Tháo","Tiểu anh hùng chạy mau, đừng cố nữa! Lữ Bố bản lĩnh cao cường, phủ Đổng Trác người đông thế mạnh, tạm thời lánh nạn, chạy! (Thiếu niên và Tào Tháo cưỡi ngựa ra khỏi thành, chạy về hướng Đông Nam)","Tiểu anh hùng chạy mau, đừng cố nữa! Lữ Bố bản lĩnh cao cường, phủ Đổng Trác người đông thế mạnh, tạm thời lánh nạn, chạy! (Thiếu niên và Tào Tháo cưỡi ngựa ra khỏi thành, chạy về hướng Đông Nam)","Nhân vật","",},
        [189] = {405,"Trần Cung","Ta là quan Huyện Lệnh Trung Mâu, Trần Cung, các ngươi là ai?","Ta là quan Huyện Lệnh Trung Mâu, Trần Cung, các ngươi là ai?","Tào Tháo","",},
        [190] = {405,"Tào Tháo","Ta là khách buôn qua đường, ta họ Hoàng Phủ.","Ta là khách buôn qua đường, ta họ Hoàng Phủ.","Trần Cung","",},
        [191] = {405,"Trần Cung","Lúc ta còn ở Lạc Dương, từng biết mặt ngươi là Tào Tháo! Đổng Thừa Tướng đối đãi với ngươi không tệ, sao ngươi dám thích sát ngài!?","Lúc ta còn ở Lạc Dương, từng biết mặt ngươi là Tào Tháo! Đổng Thừa Tướng đối đãi với ngươi không tệ, sao ngươi dám thích sát ngài!?","Tào Tháo","",},
        [192] = {405,"Trần Cung","Người đâu! Nhốt hai người này vào nhà lao, ngày mai giải đi kinh sư nhận thưởng.","Người đâu! Nhốt hai người này vào nhà lao, ngày mai giải đi kinh sư nhận thưởng.","Tào Tháo","",},
        [193] = {405,"Tào Tháo","Kẻ tầm thường sao hiểu được hoài bão lớn! Giờ ta đã bị lật tẩy rồi, cứ đánh một trận đi!","Kẻ tầm thường sao hiểu được hoài bão lớn! Giờ ta đã bị lật tẩy rồi, cứ đánh một trận đi!","Trần Cung","",},
        [194] = {406,"Trần Cung","Đổng Trác cậy thế lộng quyền, ta nhận bổng lộc triều đình, nếu không có lòng báo quốc, thì khác gì cầm thú! Vừa rồi ta chỉ thử lòng hai vị thôi!","Đổng Trác cậy thế lộng quyền, ta nhận bổng lộc triều đình, nếu không có lòng báo quốc, thì khác gì cầm thú! Vừa rồi ta chỉ thử lòng hai vị thôi!","Tào Tháo","",},
        [195] = {406,"Trần Cung","Hai vị anh hùng quả là người trung nghĩa! Trần Cung nguyện từ quan đi theo hai vị.","Hai vị anh hùng quả là người trung nghĩa! Trần Cung nguyện từ quan đi theo hai vị.","Tào Tháo","",},
        [196] = {406,"Tào Tháo","Đầu tiên gặp được tiểu anh hùng, nay lại được Công Đài giúp đỡ, Tào Tháo quả thật may mắn! Nào nào, hôm nay chúng ta cùng uống no say!","Đầu tiên gặp được tiểu anh hùng, nay lại được Công Đài giúp đỡ, Tào Tháo quả thật may mắn! Nào nào, hôm nay chúng ta cùng uống no say!","Trần Cung","",},
        [197] = {406,"Tào Tháo","Rượu gặp tri kỷ, ngàn ly không say! Cạn! Uống rượu vui ca, đời người được mấy!","Rượu gặp tri kỷ, ngàn ly không say! Cạn! Uống rượu vui ca, đời người được mấy!","Trần Cung","",},
