        [258] = {605,"Nhân vật","Bắt giặc phải bắt kẻ cầm đầu, chi bằng tấn công Lạc Dương, bắt Đổng Trác thì sẽ nhổ cỏ tận gốc!","Bắt giặc phải bắt kẻ cầm đầu, chi bằng tấn công Lạc Dương, bắt Đổng Trác thì sẽ nhổ cỏ tận gốc!","","",},
        [259] = {701,"Tào Tháo","Thám tử đến báo, nay Đổng Trác lo sợ uy danh của các hộ chư hầu, hiện đã hỏa thiêu Lạc Dương, cướp thiên tử đến Trường An, các vị thấy sao?","Thám tử đến báo, nay Đổng Trác lo sợ uy danh của các hộ chư hầu, hiện đã hỏa thiêu Lạc Dương, cướp thiên tử đến Trường An, các vị thấy sao?","Nhân vật","",},
        [260] = {701,"Nhân vật","Đổng Trác tội không thể tha, chúng ta hãy thừa thế truy kích. Tấn công Lạc Dương, bắt sống Đổng Trác, còn chờ đến khi nào!","Đổng Trác tội không thể tha, chúng ta hãy thừa thế truy kích. Tấn công Lạc Dương, bắt sống Đổng Trác, còn chờ đến khi nào!","","",},
        [261] = {701,"Viên Thiệu","Binh lính mệt mỏi, Đổng Trác người đông thế mạnh, chi bằng tạm hoãn để bàn tính. Nếu nhất quyết đuổi theo ắt sẽ bại.","Binh lính mệt mỏi, Đổng Trác người đông thế mạnh, chi bằng tạm hoãn để bàn tính. Nếu nhất quyết đuổi theo ắt sẽ bại.","","",},
        [262] = {701,"Tào Tháo","Ngươi không đủ tư cách bàn việc lớn với ta!","Ngươi không đủ tư cách bàn việc lớn với ta!","","",},
        [263] = {701,"Nhân vật","Viên Thiệu quả nhiên không thể làm nên việc lớn!","Viên Thiệu quả nhiên không thể làm nên việc lớn!","","",},
        [264] = {701,"Tào Tháo","Nay chư hầu theo Viên Thiệu, chỉ có tiểu anh hùng là hào kiệt. Được quen biết tiểu anh hùng là may mắn của Tháo.","Nay chư hầu theo Viên Thiệu, chỉ có tiểu anh hùng là hào kiệt. Được quen biết tiểu anh hùng là may mắn của Tháo.","","",},
        [265] = {701,"Tào Tháo","Tôn Kiên đã dẫn quân đến Lạc Dương, tiểu anh hùng, chúng ta hãy chia ra hai hướng, ta đuổi theo Đổng Trác, ngươi giúp Tôn Kiên, thế nào?","Tôn Kiên đã dẫn quân đến Lạc Dương, tiểu anh hùng, chúng ta hãy chia ra hai hướng, ta đuổi theo Đổng Trác, ngươi giúp Tôn Kiên, thế nào?","","",},
        [266] = {701,"Nhân vật","Được, xin cáo biệt, ta đến Lạc Dương giúp Tôn Kiên đây!","Được, xin cáo biệt, ta đến Lạc Dương giúp Tôn Kiên đây!","","",},
        [267] = {702,"Hoàng Cái","Ngươi là ai, sao lại làm loạn trong thành Lạc Dương?","Ngươi là ai, sao lại làm loạn trong thành Lạc Dương?","Tinh Thái","",},
        [268] = {702,"Tinh Thái","Chúng ta đến giúp Tôn Kiên diệt trừ tàn dư của Đổng Trác!","Chúng ta đến giúp Tôn Kiên diệt trừ tàn dư của Đổng Trác!","","",},
        [269] = {702,"Hoàng Cái","Ta thấy các ngươi trẻ tuổi háo thắng, chắc là gian tế do Đổng Trác phái đến!","Ta thấy các ngươi trẻ tuổi háo thắng, chắc là gian tế do Đổng Trác phái đến!","","",},
        [270] = {702,"Tinh Thái","Ngươi thật không biết đúng sai gì cả, sớm muộn gì cũng rước khổ vào thân!","Ngươi thật không biết đúng sai gì cả, sớm muộn gì cũng rước khổ vào thân!","","",},
        [271] = {702,"Ngân Bình","Hừ, không biết đúng sai, hãy đánh cho hắn phục rồi tính.","Hừ, không biết đúng sai, hãy đánh cho hắn phục rồi tính.","","",},
        [272] = {703,"Tinh Thái","Chúa công, Tôn Kiên này vốn là bộ hạ của Viên Thuật, chúng ta hãy thử hắn xem sao!","Chúa công, Tôn Kiên này vốn là bộ hạ của Viên Thuật, chúng ta hãy thử hắn xem sao!","Ngân Bình","",},
        [273] = {703,"Ngân Bình","Tinh Thái muội muội lại nghịch ngợm nữa rồi!","Tinh Thái muội muội lại nghịch ngợm nữa rồi!","","",},
        [274] = {703,"Tinh Thái","Hi hi!","Hi hi!","","",},
        [275] = {703,"Tinh Thái","Tôn Kiên, thừa tướng có con gái, dung mạo xinh đẹp, muốn gả cho con tướng quân, tướng quân thấy sao?","Tôn Kiên, thừa tướng có con gái, dung mạo xinh đẹp, muốn gả cho con tướng quân, tướng quân thấy sao?","Tôn Kiên","",},
        [276] = {703,"Tôn Kiên","Đổng Trác làm chuyện trái đạo lý, ta hận không diệt cửu tộc của hắn, làm gì có chuyện kết thông gia với nghịch tặc!","Đổng Trác làm chuyện trái đạo lý, ta hận không diệt cửu tộc của hắn, làm gì có chuyện kết thông gia với nghịch tặc!","","",},
        [277] = {704,"Nhân vật","Tinh Thái, đừng nghịch nữa! Tôn tướng quân là chân anh hùng, là tiên phong khi thảo phạt Hoàng Cân và Đổng Trác, trung thành với Hán thất.","Tinh Thái, đừng nghịch nữa! Tôn tướng quân là chân anh hùng, là tiên phong khi thảo phạt Hoàng Cân và Đổng Trác, trung thành với Hán thất.","Tôn Kiên","",},
        [278] = {704,"Tôn Kiên","Tiểu anh hùng quá khen! Tiểu anh hùng chiến công lẫy lừng, thanh danh vang xa, Tôn Kiên tự thấy không bằng, #Danh Tướng Lệnh# này, ta lấy được trong thành Lạc Dương, xin hãy nhận cho.","Tiểu anh hùng quá khen! Tiểu anh hùng chiến công lẫy lừng, thanh danh vang xa, Tôn Kiên tự thấy không bằng, #Danh Tướng Lệnh# này, ta lấy được trong thành Lạc Dương, xin hãy nhận cho.","","",},
        [279] = {704,"Nhân vật","Đa tạ Tôn tướng quân! Vậy ta nhận nhé!","Đa tạ Tôn tướng quân! Vậy ta nhận nhé!","","",},
        [280] = {704,"Nhân vật","Tôn tướng quân sau này định thế nào?","Tôn tướng quân sau này định thế nào?","","",},
        [281] = {704,"Tôn Kiên","Ôi, chư hầu lừa gạt lẫn nhau, Tôn mỗ quyết định trở về Giang Đông.","Ôi, chư hầu lừa gạt lẫn nhau, Tôn mỗ quyết định trở về Giang Đông.","","",},
        [282] = {704,"Nhân vật","Như vậy rất tốt, ta và tướng quân chí hướng giống nhau, ta sẽ theo tướng quân đến Giang Đông du ngoạn một chuyến.","Như vậy rất tốt, ta và tướng quân chí hướng giống nhau, ta sẽ theo tướng quân đến Giang Đông du ngoạn một chuyến.","","",},
        [283] = {704,"Tôn Kiên","Hiện giờ Viên Thiệu vẫn là minh chủ chư hầu, ta sẽ đến chào từ biệt ông ta.","Hiện giờ Viên Thiệu vẫn là minh chủ chư hầu, ta sẽ đến chào từ biệt ông ta.","","",},
        [284] = {801,"Tôn Kiên","Tôn Kiên thân mang bệnh, muốn về Phượng Tường, đến để từ biệt.","Tôn Kiên thân mang bệnh, muốn về Phượng Tường, đến để từ biệt.","Viên Thiệu","",},
        [285] = {801,"Viên Thiệu","Ngọc tỷ là bảo vật triều đình, ngươi lại giấu mang đi, muốn gì đây?","Ngọc tỷ là bảo vật triều đình, ngươi lại giấu mang đi, muốn gì đây?","","",},
        [286] = {801,"Tôn Kiên","Ngọc tỷ không ở chỗ ta! Tôn Kiên ta thề với trời đất, nếu giấu giếm ngọc tỷ, sau này không được yên thân!","Ngọc tỷ không ở chỗ ta! Tôn Kiên ta thề với trời đất, nếu giấu giếm ngọc tỷ, sau này không được yên thân!","","",},
        [287] = {801,"Viên Thiệu","Ngươi đã thề độc như vậy, nếu không cho đi thì hóa ra ta là người hẹp hòi! (Viên Thiệu đồng ý, Tôn Kiên từ biệt.)","Ngươi đã thề độc như vậy, nếu không cho đi thì hóa ra ta là người hẹp hòi! (Viên Thiệu đồng ý, Tôn Kiên từ biệt.)","Tôn Kiên","",},
        [288] = {801,"Viên Thiệu","Người đâu! Mau viết thư cho Kinh Châu Mục Lưu Biểu, nói ông ta chặn đường Tôn Kiên đoạt lại ngọc tỷ.","Người đâu! Mau viết thư cho Kinh Châu Mục Lưu Biểu, nói ông ta chặn đường Tôn Kiên đoạt lại ngọc tỷ.","Nhân vật","",},
        [289] = {801,"Tinh Thái","Chúa công, Viên Thiệu trong ngoài bất nhất, là kẻ tiểu nhân. Chúa công, theo ngài Tôn Kiên có giấu giếm ngọc tỷ thật không?","Chúa công, Viên Thiệu trong ngoài bất nhất, là kẻ tiểu nhân. Chúa công, theo ngài Tôn Kiên có giấu giếm ngọc tỷ thật không?","Nhân vật","",},
        [290] = {801,"Nhân vật","Dù ngọc tỷ trong tay Tôn Kiên, cũng là do ông ta có công giành được! Hơn nữa, chúng ta đến Kinh Châu du ngoạn, ngọc tỷ nằm trong tay ai thì liên quan gì tới ta!","Dù ngọc tỷ trong tay Tôn Kiên, cũng là do ông ta có công giành được! Hơn nữa, chúng ta đến Kinh Châu du ngoạn, ngọc tỷ nằm trong tay ai thì liên quan gì tới ta!","","",},
        [291] = {801,"Tinh Thái","Chúa công, nghe nói Lưu Biểu cũng có #Danh Tướng Lệnh#, chi bằng nhân cơ hội này hãy đến thăm hỏi.","Chúa công, nghe nói Lưu Biểu cũng có #Danh Tướng Lệnh#, chi bằng nhân cơ hội này hãy đến thăm hỏi.","","",},
        [292] = {801,"Nhân vật","Lưu Biểu có #Danh Tướng Lệnh#! Vậy chúng ta còn chờ gì nữa! Đi, đến Kinh Châu!","Lưu Biểu có #Danh Tướng Lệnh#! Vậy chúng ta còn chờ gì nữa! Đi, đến Kinh Châu!","","",},
        [293] = {802,"Thái Mạo","Hai tiểu mỹ nữ ở đâu đến vậy, hãy theo Thái Mạo nào!","Hai tiểu mỹ nữ ở đâu đến vậy, hãy theo Thái Mạo nào!","Tinh Thái","",},
        [294] = {802,"Tinh Thái","Ngươi là ai? Có bản lĩnh gì?","Ngươi là ai? Có bản lĩnh gì?","","",},
        [295] = {802,"Thái Mạo","Ta là thổ hào trong Kinh Châu Tương Dương, gần đây muội muội ta gả cho Lưu Biểu, Thái gia ta càng thêm nổi tiếng!","Ta là thổ hào trong Kinh Châu Tương Dương, gần đây muội muội ta gả cho Lưu Biểu, Thái gia ta càng thêm nổi tiếng!","","",},
        [296] = {802,"Tinh Thái","Hừ, muội muội ngươi chỉ làm tiểu thiếp cho Lưu Biểu thôi mà, làm gì mà đắc ý như vậy!","Hừ, muội muội ngươi chỉ làm tiểu thiếp cho Lưu Biểu thôi mà, làm gì mà đắc ý như vậy!","","",},
