        [298] = {803,"Lưu Biểu","Tiểu tử kia, không ngờ ngươi đánh bại được Thái Mạo Hoàng Tổ, thực lực không thể xem thường.","Tiểu tử kia, không ngờ ngươi đánh bại được Thái Mạo Hoàng Tổ, thực lực không thể xem thường.","Tinh Thái","",},
        [299] = {803,"Tinh Thái","Chúa công, Lưu Biểu này khi trẻ từng vào Kinh Châu, bình định 9 quân Kinh Châu, rất lợi hại.","Chúa công, Lưu Biểu này khi trẻ từng vào Kinh Châu, bình định 9 quân Kinh Châu, rất lợi hại.","","",},
